export default function KeyboardNavigableList({
  items,
  onSelect,
  selectedId,
  className,
  itemClassName,
  role = 'listbox',
  orientation = 'vertical',
  wrap: _wrap = true,
}: KeyboardNavigableListProps) {
  const handleSelect = useCallback(
    (index: number) => {
      const item = items[index];
      if (item && !item.disabled) {
        onSelect(item);
      }
    },
    [items, onSelect]
  );

  const containerRef = useArrowKeyNavigation(items.length, handleSelect);

  const getItemRole = () => {
    switch (role) {
      case 'listbox':
        return 'option';
      case 'menu':
        return 'menuitem';
      case 'tablist':
        return 'tab';
      default:
        return 'option';
    }
  };

  const itemRole = getItemRole();

  return (
    <div
      ref={containerRef as React.RefObject<HTMLDivElement>}
      role={role}
      aria-orientation={orientation}
      className={cn(
        'focus:outline-none',
        orientation === 'horizontal' ? 'flex flex-row' : 'flex flex-col',
        className
      )}
    >
      {items.map((item, index) => (
        <button
          key={item.id}
          role={itemRole}
          aria-selected={selectedId === item.id}
          aria-disabled={item.disabled}
          tabIndex={index === 0 ? 0 : -1}
          onClick={() => !item.disabled && onSelect(item)}
          className={cn(
            'flex items-center gap-2 px-3 py-2 text-left transition-colors',
            'focus:outline-none focus:ring-2 focus:ring-inset focus:ring-blue-500',
            selectedId === item.id
              ? 'bg-blue-50 text-blue-700 dark:bg-blue-900/20 dark:text-blue-400'
              : 'hover:bg-gray-100 dark:hover:bg-gray-800',
            item.disabled && 'cursor-not-allowed opacity-50',
            itemClassName
          )}
          disabled={item.disabled}
        >
          {item.icon && <span className="shrink-0">{item.icon}</span>}
          <span className="grow">{item.label}</span>
        </button>
      ))}
    </div>
  );
}

export function ListItem({
  children,
  selected,
  disabled,
  onClick,
  className,
}: ListItemComponentProps) {
  return (
    <div
      role="option"
      aria-selected={selected}
      aria-disabled={disabled}
      tabIndex={selected ? 0 : -1}
      onClick={!disabled ? onClick : undefined}
      onKeyDown={e => {
        if ((e.key === 'Enter' || e.key === ' ') && !disabled && onClick) {
          e.preventDefault();
          onClick();
        }
      }}
      className={cn(
        'flex cursor-pointer items-center gap-2 px-3 py-2 transition-colors',
        'focus:outline-none focus:ring-2 focus:ring-inset focus:ring-blue-500',
        selected
          ? 'bg-blue-50 text-blue-700 dark:bg-blue-900/20 dark:text-blue-400'
          : 'hover:bg-gray-100 dark:hover:bg-gray-800',
        disabled && 'cursor-not-allowed opacity-50',
        className
      )}
    >
      {children}
    </div>
  );
}

const FormValidationComponent = () => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');

  const validateEmail = (value: string) => {
    if (!value) {
      setError('Email is required');
    } else if (!/\S+@\S+\.\S+/.test(value)) {
      setError('Please enter a valid email address');
    } else {
      setError('');
    }
  };

  return (
    <div className="w-full max-w-md space-y-4">
      <div>
        <label htmlFor="email" className="mb-2 block text-sm font-medium text-gray-700">
          Email Address
        </label>
        <input
          id="email"
          type="email"
          value={email}
          onChange={e => {
            setEmail(e.target.value);
            validateEmail(e.target.value);
          }}
          onBlur={e => validateEmail(e.target.value)}
          className={`w-full rounded-md border px-3 py-2 focus:outline-none focus:ring-2 ${
            error
              ? 'border-red-300 focus:border-red-500 focus:ring-red-500'
              : 'border-gray-300 focus:border-blue-500 focus:ring-blue-500'
          }`}
          placeholder="Enter your email"
          aria-invalid={!!error}
          aria-describedby={error ? 'email-error' : undefined}
        />

        {error && (
          <div id="email-error" className="mt-1">
            <LiveRegion message={error} type="polite" visible={true} clearAfter={0} />
          </div>
        )}
      </div>
    </div>
  );
};

