render: () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState<string[]>([]);
  const [isSearching, setIsSearching] = useState(false);

  // Mock data
  const mockData = [
    'React', 'Vue.js', 'Angular', 'Svelte', 'Next.js',
    'Nuxt.js', 'Gatsby', 'TypeScript', 'JavaScript', 'Node.js',
    'Express', 'Fastify', 'MongoDB', 'PostgreSQL', 'Redis'
  ];

  const performSearch = async (query: string) => {
    if (!query.trim()) {
      setResults([]);
      return;
    }

    setIsSearching(true);

    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 300));

    const filtered = mockData.filter(item =>
      item.toLowerCase().includes(query.toLowerCase())
    );

    setResults(filtered);
    setIsSearching(false);
  };

  const handleSearch = (query: string) => {
    performSearch(query);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    // Debounced search as user types
    performSearch(e.target.value);
  };

  const handleClear = () => {
    setSearchTerm('');
    setResults([]);
    setIsSearching(false);
  };

  return (
    <div className="w-80">
      <SearchBar
        value={searchTerm}
        onChange={handleInputChange}
        onSearch={handleSearch}
        onClear={handleClear}
        placeholder="Search technologies..."
      />

      {isSearching && (
        <div className="mt-4 p-3 text-sm text-gray-600 bg-gray-50 rounded">
          Searching...
        </div>
      )}

      {results.length > 0 && !isSearching && (
        <div className="mt-4 border rounded-md">
          <div className="p-2 bg-gray-50 border-b text-sm font-medium text-gray-700">
            Found {results.length} result{results.length !== 1 ? 's' : ''}
          </div>
          <ul className="divide-y">
            {results.map((result, index) => (
              <li key={index} className="p-3 hover:bg-gray-50 cursor-pointer">
                {result}
              </li>
            ))}
          </ul>
        </div>
      )}

      {searchTerm && results.length === 0 && !isSearching && (
        <div className="mt-4 p-3 text-sm text-gray-500 bg-gray-50 rounded">
          No results found for "{searchTerm}"
        </div>
      )}
    </div>
  );
},

