const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
  if (e.key === 'Enter') {
    e.preventDefault();
    onSearch?.(e.currentTarget.value);
  } else if (e.key === 'Escape') {
    e.preventDefault();
    e.currentTarget.blur();
    onClear?.();
  }
  onKeyDown?.(e);
};

const handleClear = () => {
  onClear?.();
  // Focus back to input after clearing
  if (ref && 'current' in ref && ref.current) {
    ref.current.focus();
  }
};

