const MockThemeProvider = ({ children, initialTheme = 'system' }: { children: React.ReactNode; initialTheme?: 'light' | 'dark' | 'system' }) => {
  const [theme, setTheme] = useState<'light' | 'dark' | 'system'>(initialTheme);
  const [systemTheme] = useState<'light' | 'dark'>('light');

  const actualTheme = theme === 'system' ? systemTheme : theme;

  const toggleTheme = () => {
    setTheme(current => {
      if (current === 'light') return 'dark';
      if (current === 'dark') return 'system';
      return 'light';
    });
  };

  // Mock context value
  const contextValue = {
    theme,
    actualTheme,
    toggleTheme,
    setTheme,
  };

  return (
    <div className={actualTheme === 'dark' ? 'dark' : ''}>
      <div className="min-h-screen bg-white dark:bg-gray-900 text-gray-900 dark:text-white p-4">
        {/* Provide mock context via global variable for component to use */}
        {React.Children.map(children, child =>
          React.isValidElement(child) && child.type === ThemeToggle
            ? React.cloneElement(child as React.ReactElement<any>, {
                onClick: () => {
                  child.props.onClick?.();
                  toggleTheme();
                },
              })
            : child
        )}
      </div>
    </div>
  );
};

