export default function SearchBar({
  placeholder = 'Search...',
  onSearch,
  onClear,
  filterOptions = [],
  initialSearchTerm = '',
  initialFilters = [],
  showAdvancedFilters = false,
  className = '',
}: SearchBarProps) {
  const [searchTerm, setSearchTerm] = useState(initialSearchTerm);
  const [filters, setFilters] = useState<SearchFilter[]>(initialFilters);
  const [showFilters, setShowFilters] = useState(showAdvancedFilters);
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState(searchTerm);

  // Debounce search term
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  // Trigger search when debounced term or filters change
  useEffect(() => {
    onSearch(debouncedSearchTerm, filters);
  }, [debouncedSearchTerm, filters, onSearch]);

  const handleAddFilter = () => {
    if (filterOptions.length > 0) {
      const newFilter: SearchFilter = {
        field: filterOptions[0].field,
        operator: 'contains',
        value: '',
        label: filterOptions[0].label,
      };
      setFilters([...filters, newFilter]);
    }
  };

  const handleUpdateFilter = (index: number, updates: Partial<SearchFilter>) => {
    const updatedFilters = filters.map((filter, i) =>
      i === index ? { ...filter, ...updates } : filter
    );
    setFilters(updatedFilters);
  };

  const handleRemoveFilter = (index: number) => {
    setFilters(filters.filter((_, i) => i !== index));
  };

  const handleClear = () => {
    setSearchTerm('');
    setFilters([]);
    if (onClear) {
      onClear();
    }
  };

  const getOperatorOptions = (fieldType: string) => {
    const baseOptions = [
      { value: 'contains', label: 'Contains' },
      { value: 'equals', label: 'Equals' },
      { value: 'starts_with', label: 'Starts with' },
      { value: 'ends_with', label: 'Ends with' },
    ];

    if (fieldType === 'number' || fieldType === 'date') {
      return [
        ...baseOptions,
        { value: 'greater_than', label: 'Greater than' },
        { value: 'less_than', label: 'Less than' },
        { value: 'between', label: 'Between' },
      ];
    }

    return baseOptions;
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Main Search Bar */}
      <div className="flex items-center gap-2">
        <div className="relative flex-1">
          <InputField
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            placeholder={placeholder}
            leftIcon={
              <svg
                className="size-5 text-muted"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                />
              </svg>
            }
            rightIcon={
              searchTerm && (
                <Button
                  onClick={() => setSearchTerm('')}
                  variant="ghost"
                  size="sm"
                  iconOnly
                  className="size-4 p-0 text-muted hover:text-secondary"
                >
                  <svg className="size-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M6 18L18 6M6 6l12 12"
                    />
                  </svg>
                </Button>
              )
            }
          />
        </div>

        {filterOptions.length > 0 && (
          <Button
            variant={showFilters ? 'primary' : 'secondary-outline'}
            onClick={() => setShowFilters(!showFilters)}
            leftIcon={
              <svg className="size-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.414A1 1 0 013 6.707V4z"
                />
              </svg>
            }
          >
            Filters {filters.length > 0 && `(${filters.length})`}
          </Button>
        )}

        {(searchTerm || filters.length > 0) && (
          <Button variant="secondary-outline" onClick={handleClear}>
            Clear All
          </Button>
        )}
      </div>

      {/* Advanced Filters */}
      {showFilters && filterOptions.length > 0 && (
        <div className="space-y-4 rounded-lg border p-4 bg-surface-hover border-default">
          <div className="flex items-center justify-between">
            <h3 className="text-sm font-medium text-primary">Advanced Filters</h3>
            <Button size="sm" variant="primary-outline" onClick={handleAddFilter}>
              Add Filter
            </Button>
          </div>

          {filters.length === 0 ? (
            <p className="text-sm text-muted">
              No filters applied. Click &quot;Add Filter&quot; to add search criteria.
            </p>
          ) : (
            <div className="space-y-3">
              {filters.map((filter, index) => {
                const fieldOption = filterOptions.find(opt => opt.field === filter.field);
                const operatorOptions = getOperatorOptions(fieldOption?.type || 'text');

                return (
                  <div
                    key={index}
                    className="flex items-center gap-2 rounded border p-3 bg-surface border-default"
                  >
                    {/* Field Selection */}
                    <div className="w-40">
                      <DropdownSelect
                        value={filter.field}
                        onChange={value => {
                          const selectedOption = filterOptions.find(opt => opt.field === value);
                          handleUpdateFilter(index, {
                            field: value,
                            label: selectedOption?.label || value,
                            value: '',
                          });
                        }}
                        options={filterOptions.map(opt => ({ value: opt.field, label: opt.label }))}
                      />
                    </div>

                    {/* Operator Selection */}
                    <div className="w-32">
                      <DropdownSelect
                        value={filter.operator}
                        onChange={value =>
                          handleUpdateFilter(index, {
                            operator: value as SearchFilter['operator'],
                            value: '',
                          })
                        }
                        options={operatorOptions}
                      />
                    </div>

                    {/* Value Input */}
                    <div className="flex-1">
                      {fieldOption?.type === 'select' ? (
                        <DropdownSelect
                          value={filter.value.toString()}
                          onChange={value => handleUpdateFilter(index, { value: value })}
                          options={fieldOption.options || []}
                          placeholder="Select value..."
                        />
                      ) : filter.operator === 'between' ? (
                        <div className="flex items-center gap-2">
                          <InputField
                            type={
                              fieldOption?.type === 'number'
                                ? 'number'
                                : fieldOption?.type === 'date'
                                  ? 'date'
                                  : 'text'
                            }
                            value={
                              Array.isArray(filter.value) ? filter.value[0]?.toString() || '' : ''
                            }
                            onChange={e => {
                              const currentValue = Array.isArray(filter.value)
                                ? filter.value
                                : ['', ''];
                              handleUpdateFilter(index, {
                                value: [e.target.value, currentValue[1]],
                              });
                            }}
                            placeholder="From"
                          />
                          <span className="text-muted">to</span>
                          <InputField
                            type={
                              fieldOption?.type === 'number'
                                ? 'number'
                                : fieldOption?.type === 'date'
                                  ? 'date'
                                  : 'text'
                            }
                            value={
                              Array.isArray(filter.value) ? filter.value[1]?.toString() || '' : ''
                            }
                            onChange={e => {
                              const currentValue = Array.isArray(filter.value)
                                ? filter.value
                                : ['', ''];
                              handleUpdateFilter(index, {
                                value: [currentValue[0], e.target.value],
                              });
                            }}
                            placeholder="To"
                          />
                        </div>
                      ) : (
                        <InputField
                          type={
                            fieldOption?.type === 'number'
                              ? 'number'
                              : fieldOption?.type === 'date'
                                ? 'date'
                                : 'text'
                          }
                          value={Array.isArray(filter.value) ? '' : filter.value.toString()}
                          onChange={e => handleUpdateFilter(index, { value: e.target.value })}
                          placeholder="Enter value..."
                        />
                      )}
                    </div>

                    {/* Remove Filter Button */}
                    <Button
                      size="sm"
                      variant="danger-outline"
                      onClick={() => handleRemoveFilter(index)}
                      iconOnly
                    >
                      <svg className="size-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M6 18L18 6M6 6l12 12"
                        />
                      </svg>
                    </Button>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      )}

      {/* Active Filters Summary */}
      {filters.length > 0 && (
        <div className="flex flex-wrap gap-2">
          {filters.map((filter, index) => (
            <div
              key={index}
              className="inline-flex items-center gap-1 rounded-full bg-blue-100 px-3 py-1 text-sm text-blue-800"
            >
              <span className="font-medium">{filter.label}</span>
              <span className="text-blue-600">{filter.operator.replace('_', ' ')}</span>
              <span className="font-medium">
                {Array.isArray(filter.value)
                  ? `${filter.value[0]} - ${filter.value[1]}`
                  : filter.value}
              </span>
              <Button
                onClick={() => handleRemoveFilter(index)}
                variant="ghost"
                size="sm"
                iconOnly
                className="ml-1 size-3 p-0 text-blue-600 hover:text-blue-800"
              >
                <svg className="size-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </Button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const FormIntegrationComponent = () => {
  const [selectedBarangay, setSelectedBarangay] = React.useState('');
  const [error, setError] = React.useState('');

  const handleSubmit = () => {
    if (!selectedBarangay) {
      setError('Please select your barangay');
      return;
    }
    setError('');
    action('form-submit')(selectedBarangay);
    alert(`Form submitted with barangay: ${selectedBarangay}`);
  };

  return (
    <div className="max-w-md space-y-4">
      <div>
        <label className="mb-2 block text-sm font-medium text-gray-700">Your Barangay *</label>
        <BarangaySelector
          value={selectedBarangay}
          onChange={code => {
            setSelectedBarangay(code);
            if (error) setError(''); // Clear error when user makes selection
          }}
          error={error}
          placeholder="Search and select your barangay"
        />
      </div>
      <button
        onClick={handleSubmit}
        className="w-full rounded-md bg-blue-600 px-4 py-2 text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
      >
        Submit
      </button>
    </div>
  );
};

export default function BarangaySelector({
  value,
  onChange,
  error,
  disabled = false,
  placeholder = 'Search for your barangay...',
}: BarangaySelectorProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(false);
  const [selectedBarangay, setSelectedBarangay] = useState<any>(null);
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Fetch barangay data from API (works with or without authentication)
  const searchBarangays = async (searchTerm: string) => {
    if (searchTerm.length < 2) {
      setSearchResults([]);
      return;
    }

    try {
      setIsLoading(true);
      setIsError(false);

      // Try to get session, but don't require it
      const {
        data: { session },
      } = await supabase.auth.getSession();

      if (session?.access_token) {
        // Authenticated search - use API with jurisdiction filtering
        const response = await fetch(
          `/api/addresses/barangays?search=${encodeURIComponent(searchTerm)}`,
          {
            headers: {
              Authorization: `Bearer ${session.access_token}`,
            },
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}`);
        }

        const data = await response.json();
        setSearchResults(data.barangays || []);
      } else {
        // Public search - simplified direct query for reliability
        logSearch(searchTerm, 'BarangaySelector');

        const { data, error } = await supabase
          .from('psgc_barangays')
          .select('code, name, city_municipality_code')
          .ilike('name', `%${searchTerm}%`)
          .limit(20)
          .order('name');

        if (error) {
          logError('Public search error', { component: 'BarangaySelector', error });
          throw error;
        }

        logDataLoad('barangays', data?.length);

        // Transform to match expected format (simplified)
        const transformedData = data?.map((item: any) => ({
          code: item.code,
          name: item.name,
          city_name: 'Loading...', // Will be populated by separate queries if needed
          province_name: 'Loading...',
          region_name: 'Loading...',
          full_address: `${item.name} (Code: ${item.code})`,
        })) || [];

        setSearchResults(transformedData);
      }
    } catch (error) {
      console.error('Error searching barangays:', error);
      setIsError(true);
      setSearchResults([]);
    } finally {
      setIsLoading(false);
    }
  };

  // Get selected barangay details
  useEffect(() => {
    if (value && searchResults.length > 0) {
      const barangay = searchResults.find(b => b.code === value);
      setSelectedBarangay(barangay);
    } else if (!value) {
      setSelectedBarangay(null);
    }
  }, [value, searchResults]);

  // Transform search results to match BaseSelector format
  const options: BarangayOption[] = (searchResults || []).map(barangay => ({
    value: barangay.code,
    label: barangay.name,
    metadata: {
      code: barangay.code,
      name: barangay.name,
      city_name: barangay.city_name || 'Unknown City',
      province_name: barangay.province_name || 'Unknown Province',
      region_name: barangay.region_name || 'Unknown Region',
      full_address: `${barangay.name}, ${barangay.city_name || 'Unknown City'}, ${barangay.province_name || 'Unknown Province'}, ${barangay.region_name || 'Unknown Region'}`,
    },
  }));

  // Set search term to selected barangay name when value changes
  React.useEffect(() => {
    if (selectedBarangay && searchTerm !== selectedBarangay.name) {
      setSearchTerm(selectedBarangay.name);
    }
  }, [selectedBarangay, searchTerm]);

  const handleSearchChange = (term: string) => {
    setSearchTerm(term);

    // Clear selection if user is typing and it doesn't match selected
    if (selectedBarangay && term !== selectedBarangay.name) {
      onChange('');
    }

    // Debounce the search
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    searchTimeoutRef.current = setTimeout(() => {
      searchBarangays(term);
    }, 300);
  };

  const handleOpenChange = (open: boolean) => {
    setIsOpen(open);
  };

  const handleChange = (code: string) => {
    onChange(code);
    setIsOpen(false);
  };

  const renderOption = (option: BarangayOption, search: string) => {
    const highlightMatch = (text: string) => {
      if (!search.trim()) return text;
      const regex = new RegExp(`(${search.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi');
      const parts = text.split(regex);
      return parts.map((part, index) =>
        regex.test(part) ? (
          <mark key={`${part}-${index}`} className="bg-yellow-200 font-medium text-primary">
            {part}
          </mark>
        ) : (
          part
        )
      );
    };

    return (
      <div className="flex flex-col">
        <div className="font-medium text-primary">{highlightMatch(option.metadata.name)}</div>
        <div className="text-sm text-secondary">
          {highlightMatch(`${option.metadata.city_name}, ${option.metadata.province_name}`)}
        </div>
        <div className="text-muted text-xs">{option.metadata.region_name}</div>
      </div>
    );
  };

  const emptyMessage = isError ? (
    <>
      <svg
        className="text-muted mx-auto mb-2 size-8"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
          d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
      </svg>
      <p className="text-sm text-red-600">Unable to load barangays</p>
      <p className="mt-1 text-xs text-red-500">Please check your connection and try again</p>
    </>
  ) : (
    <>
      <svg
        className="text-muted mx-auto mb-2 size-8"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
        />
      </svg>
      <p className="text-sm">No barangays found for &quot;{searchTerm}&quot;</p>
      <p className="mt-1 text-xs">Try searching with a different term or check your spelling</p>
    </>
  );

  const searchInstructions = (
    <>
      <svg
        className="text-muted mx-auto mb-2 size-8"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
          d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
        />
      </svg>
      <p className="text-sm">Start typing to search barangays</p>
      <p className="mt-1 text-xs">
        Type at least 2 characters (e.g., &quot;San&quot;, &quot;Manila&quot;, &quot;Cebu&quot;)
      </p>
    </>
  );

  return (
    <div>
      <BaseSelector
        value={value}
        onChange={handleChange}
        options={options}
        loading={isLoading}
        error={error}
        disabled={disabled}
        placeholder={placeholder}
        searchTerm={searchTerm}
        onSearchChange={handleSearchChange}
        isOpen={isOpen}
        onOpenChange={handleOpenChange}
        renderOption={renderOption}
        emptyMessage={emptyMessage}
        searchInstructions={searchInstructions}
        minSearchLength={2}
      />
      {!error && (
        <p className="text-muted mt-1 text-xs">
          Start typing to search by barangay name, city, or province (e.g., &quot;Poblacion&quot;,
          &quot;Manila&quot;, &quot;Cebu City&quot;)
        </p>
      )}
    </div>
  );
}

