export default function DataTable<T extends Record<string, any>>({
  data,
  columns,
  actions = [],
  loading = false,
  pagination,
  selection,
  rowKey = 'id',
  onRow,
  emptyText = 'No data available',
  className = '',
  size = 'middle',
}: DataTableProps<T>) {
  const [sortField, setSortField] = useState<string>('');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

  // Get row key
  const getRowKey = (record: T, index: number): string => {
    if (typeof rowKey === 'function') {
      return rowKey(record);
    }
    return record[rowKey]?.toString() || index.toString();
  };

  // Handle sorting
  const handleSort = (columnKey: string) => {
    if (sortField === columnKey) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(columnKey);
      setSortOrder('asc');
    }
  };

  // Sort data
  const sortedData = useMemo(() => {
    if (!sortField) return data;

    return [...data].sort((a, b) => {
      const column = columns.find(col => col.key === sortField);
      let aValue = a[sortField];
      let bValue = b[sortField];

      if (column?.dataIndex && typeof column.dataIndex === 'function') {
        aValue = column.dataIndex(a);
        bValue = column.dataIndex(b);
      } else if (column?.dataIndex && typeof column.dataIndex === 'string') {
        aValue = a[column.dataIndex];
        bValue = b[column.dataIndex];
      }

      // Handle null/undefined values
      if (aValue == null && bValue == null) return 0;
      if (aValue == null) return sortOrder === 'asc' ? 1 : -1;
      if (bValue == null) return sortOrder === 'asc' ? -1 : 1;

      // Convert to strings for comparison
      const aStr = aValue.toString().toLowerCase();
      const bStr = bValue.toString().toLowerCase();

      if (aStr < bStr) return sortOrder === 'asc' ? -1 : 1;
      if (aStr > bStr) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });
  }, [data, sortField, sortOrder, columns]);

  // Handle select all
  const handleSelectAll = (checked: boolean) => {
    if (!selection) return;

    if (checked) {
      const allKeys = sortedData.map((record, index) => getRowKey(record, index));
      selection.onChange(allKeys, sortedData);
    } else {
      selection.onChange([], []);
    }
  };

  // Handle single row selection
  const handleRowSelect = (record: T, index: number, checked: boolean) => {
    if (!selection) return;

    const rowKeyValue = getRowKey(record, index);
    let newSelectedKeys = [...selection.selectedRowKeys];
    let newSelectedRows = sortedData.filter((item, idx) =>
      selection.selectedRowKeys.includes(getRowKey(item, idx))
    );

    if (checked) {
      newSelectedKeys.push(rowKeyValue);
      newSelectedRows.push(record);
    } else {
      newSelectedKeys = newSelectedKeys.filter(key => key !== rowKeyValue);
      newSelectedRows = newSelectedRows.filter(item => getRowKey(item, 0) !== rowKeyValue);
    }

    selection.onChange(newSelectedKeys, newSelectedRows);
  };

  // Get cell value
  const getCellValue = (record: T, column: TableColumn<T>, index: number) => {
    let value: any;

    if (column.dataIndex) {
      if (typeof column.dataIndex === 'function') {
        value = column.dataIndex(record);
      } else {
        value = record[column.dataIndex];
      }
    } else {
      value = record[column.key];
    }

    if (column.render) {
      return column.render(value, record, index);
    }

    return value;
  };

  const sizeClasses = {
    small: 'text-sm',
    middle: 'text-base',
    large: 'text-lg',
  };

  const paddingClasses = {
    small: 'px-3 py-2',
    middle: 'px-4 py-3',
    large: 'px-6 py-4',
  };

  if (loading) {
    return (
      <div className={`rounded-lg border bg-surface border-default ${className}`}>
        <div className="flex items-center justify-center py-12">
          <div className="flex items-center space-x-2">
            <div className="size-6 animate-spin rounded-full border-b-2 border-blue-600"></div>
            <span className="text-sm text-secondary">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`overflow-hidden rounded-lg border bg-surface border-default ${className}`}>
      {/* Table */}
      <div className="overflow-x-auto">
        <table className="border-default min-w-full divide-y">
          {/* Header */}
          <thead className="bg-background-muted">
            <tr>
              {/* Selection column */}
              {selection && (
                <th className={`${paddingClasses[size]} w-12`}>
                  <input
                    type="checkbox"
                    className="size-4 rounded text-blue-600 bg-surface border-default focus:ring-blue-500"
                    checked={
                      selection.selectedRowKeys.length === sortedData.length &&
                      sortedData.length > 0
                    }
                    onChange={e => handleSelectAll(e.target.checked)}
                  />
                </th>
              )}

              {/* Data columns */}
              {columns.map(column => (
                <th
                  key={column.key}
                  className={`${paddingClasses[size]} text-left ${sizeClasses[size]} font-medium text-primary ${
                    column.sortable ? 'cursor-pointer hover:bg-surface-hover' : ''
                  }`}
                  style={{ width: column.width }}
                  onClick={column.sortable ? () => handleSort(column.key) : undefined}
                >
                  <div className="flex items-center space-x-1">
                    <span>{column.title}</span>
                    {column.sortable && (
                      <div className="flex flex-col">
                        <svg
                          className={`size-3 ${
                            sortField === column.key && sortOrder === 'asc'
                              ? 'text-blue-600'
                              : 'text-muted'
                          }`}
                          fill="currentColor"
                          viewBox="0 0 20 20"
                        >
                          <path d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" />
                        </svg>
                      </div>
                    )}
                  </div>
                </th>
              ))}

              {/* Actions column */}
              {actions.length > 0 && (
                <th
                  className={`${paddingClasses[size]} text-right ${sizeClasses[size]} font-medium text-primary`}
                >
                  Actions
                </th>
              )}
            </tr>
          </thead>

          {/* Body */}
          <tbody className="border-default divide-y bg-surface">
            {sortedData.length === 0 ? (
              <tr>
                <td
                  colSpan={columns.length + (selection ? 1 : 0) + (actions.length > 0 ? 1 : 0)}
                  className="px-6 py-12 text-center text-secondary"
                >
                  {emptyText}
                </td>
              </tr>
            ) : (
              sortedData.map((record, index) => {
                const rowKeyValue = getRowKey(record, index);
                const rowProps = onRow?.(record, index) || {};
                const isSelected = selection?.selectedRowKeys.includes(rowKeyValue) || false;

                return (
                  <tr
                    key={rowKeyValue}
                    className={`hover:bg-surface-hover ${isSelected ? 'bg-blue-50 dark:bg-blue-900/20' : ''} ${rowProps.className || ''}`}
                    onClick={rowProps.onClick}
                    onDoubleClick={rowProps.onDoubleClick}
                  >
                    {/* Selection column */}
                    {selection && (
                      <td className={paddingClasses[size]}>
                        <input
                          type="checkbox"
                          className="size-4 rounded text-blue-600 bg-surface border-default focus:ring-blue-500"
                          checked={isSelected}
                          onChange={e => handleRowSelect(record, index, e.target.checked)}
                          disabled={selection.getCheckboxProps?.(record)?.disabled}
                        />
                      </td>
                    )}

                    {/* Data columns */}
                    {columns.map(column => (
                      <td
                        key={column.key}
                        className={`${paddingClasses[size]} ${sizeClasses[size]} text-primary`}
                        style={{ textAlign: column.align || 'left' }}
                      >
                        {getCellValue(record, column, index)}
                      </td>
                    ))}

                    {/* Actions column */}
                    {actions.length > 0 && (
                      <td className={`${paddingClasses[size]} text-right`}>
                        <div className="flex items-center justify-end space-x-2">
                          {actions
                            .filter(action => action.visible?.(record) !== false)
                            .map(action => {
                              const isDisabled = action.disabled?.(record) || false;

                              if (action.href) {
                                return (
                                  <Link
                                    key={action.key}
                                    href={action.href(record)}
                                    className="inline-flex items-center px-3 py-1 text-sm font-medium text-blue-600 hover:text-blue-800"
                                  >
                                    {action.icon && <span className="mr-1">{action.icon}</span>}
                                    {action.label}
                                  </Link>
                                );
                              }

                              return (
                                <Button
                                  key={action.key}
                                  size="sm"
                                  variant={action.variant || 'secondary-outline'}
                                  onClick={
                                    action.onClick
                                      ? () => action.onClick!(record, index)
                                      : undefined
                                  }
                                  disabled={isDisabled}
                                  leftIcon={action.icon}
                                >
                                  {action.label}
                                </Button>
                              );
                            })}
                        </div>
                      </td>
                    )}
                  </tr>
                );
              })
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      {pagination && (
        <div className="flex items-center justify-between border-t px-4 py-3 bg-surface border-default">
          <div className="flex items-center text-sm text-secondary">
            <span>
              Showing{' '}
              {Math.min((pagination.current - 1) * pagination.pageSize + 1, pagination.total)} to{' '}
              {Math.min(pagination.current * pagination.pageSize, pagination.total)} of{' '}
              {pagination.total} results
            </span>
          </div>

          <div className="flex items-center space-x-2">
            <Button
              size="sm"
              variant="secondary-outline"
              onClick={() => pagination.onChange(pagination.current - 1, pagination.pageSize)}
              disabled={pagination.current <= 1}
            >
              Previous
            </Button>

            <span className="text-sm text-secondary">
              Page {pagination.current} of {Math.ceil(pagination.total / pagination.pageSize)}
            </span>

            <Button
              size="sm"
              variant="secondary-outline"
              onClick={() => pagination.onChange(pagination.current + 1, pagination.pageSize)}
              disabled={pagination.current >= Math.ceil(pagination.total / pagination.pageSize)}
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

