export default function EducationEmployment({
  value,
  onChange,
  errors = {},
  className = '',
}: EducationEmploymentProps) {
  const handleChange = (field: keyof EducationEmploymentData, newValue: string | boolean) => {
    onChange({
      ...value,
      [field]: newValue,
    });
  };

  const handleOccupationSelect = (
    option: { occupation_code: string; level_type: string; occupation_title: string } | null
  ) => {
    if (option) {
      onChange({
        ...value,
        psocCode: option.occupation_code,
        psocLevel: option.level_type,
        occupationDescription: option.occupation_title,
        // Clear position title ID if not unit group level
        positionTitleId: option.level_type !== 'unit_group' ? '' : value.positionTitleId,
      });
    } else {
      onChange({
        ...value,
        psocCode: '',
        psocLevel: '',
        occupationDescription: '',
        positionTitleId: '',
      });
    }
  };

  return (
    <div className={`space-y-8 ${className}`}>
      <div>
        <h3 className="text-base/7 font-semibold text-zinc-950 dark:text-white">
          Education & Employment
        </h3>
        <p className="mt-1 text-sm/6 text-zinc-500 dark:text-zinc-400">
          Academic background and work information.
        </p>
      </div>

      <div className="grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-2">
        <DropdownSelect
          label="Education Attainment"
          value={value.educationAttainment}
          onChange={val => handleChange('educationAttainment', val)}
          options={EDUCATION_LEVEL_OPTIONS}
          placeholder="Select education attainment"
          errorMessage={errors.educationAttainment}
        />

        <Toggle
          label="Is Graduate"
          description="Has this person completed their highest level of education?"
          checked={value.isGraduate}
          onToggle={checked => handleChange('isGraduate', checked)}
          errorMessage={errors.isGraduate}
        />
      </div>

      <div className="grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-2">
        <div>
          <label className="block text-sm/6 font-medium text-zinc-950 dark:text-white">
            Occupation <span className="text-zinc-500">(PSOC Compliant)</span>
          </label>
          <div className="mt-2">
            <PSOCSelector
              value={value.psocCode}
              onSelect={handleOccupationSelect}
              placeholder="Search for occupation (e.g., Manager, Teacher, Engineer)"
              error={errors.occupationDescription}
            />
          </div>
          <p className="mt-1 text-xs text-zinc-500 dark:text-zinc-400">
            Search by job title or occupation category. Uses Philippine Standard Occupational
            Classification (PSOC).
          </p>
        </div>

        <DropdownSelect
          label="Employment Status"
          value={value.employmentStatus}
          onChange={val => handleChange('employmentStatus', val)}
          options={EMPLOYMENT_STATUS_OPTIONS}
          placeholder="Select employment status"
        />
      </div>

      <InputField
        label="Workplace"
        value={value.workplace}
        onChange={e => handleChange('workplace', e.target.value)}
        placeholder="Company or workplace name"
        helperText="Optional"
      />
    </div>
  );
}

export function GeographicLocationStep({
  formData,
  updateFormData,
  errors,
  required = true
}: GeographicLocationStepProps) {
  const [names, setNames] = useState({
    regionName: '',
    provinceName: '',
    cityName: '',
    barangayName: ''
  });
  const [loading, setLoading] = useState(true);

  // Fetch names for the codes
  useEffect(() => {
    const fetchNames = async () => {
      if (!formData.barangayCode) return;

      try {
        const { data: { session } } = await supabase.auth.getSession();
        if (!session?.user) return;

        const response = await fetch('/api/user/geographic-location', {
          headers: {
            'Authorization': `Bearer ${session.access_token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.ok) {
          const hierarchy = await response.json();
          setNames({
            regionName: hierarchy.region?.name || '',
            provinceName: hierarchy.province?.name || '',
            cityName: hierarchy.city?.name || '',
            barangayName: hierarchy.barangay?.name || ''
          });
        }
      } catch (error) {
        console.error('Error fetching geographic names:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchNames();
  }, [formData.barangayCode]);

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold text-gray-900">Geographic Location</h2>
        <p className="mt-2 text-gray-600">
          Select your geographic location. This information will be used for administrative purposes and data collection.
        </p>
      </div>

      <div className="bg-gray-50 border border-gray-200 rounded-lg p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Geographic Location</h3>
        <p className="text-sm text-gray-600 mb-4">
          This resident will be assigned to your barangay's jurisdiction:
        </p>

        <div className="space-y-3">
          <div className="flex justify-between">
            <span className="font-medium text-gray-700">Region:</span>
            <span className="text-gray-900">
              {loading ? 'Loading...' : 
                formData.regionCode ? `${names.regionName} (${formData.regionCode})` : 'Not assigned'
              }
            </span>
          </div>
          <div className="flex justify-between">
            <span className="font-medium text-gray-700">Province:</span>
            <span className="text-gray-900">
              {loading ? 'Loading...' : 
                formData.provinceCode ? `${names.provinceName} (${formData.provinceCode})` : 'Not assigned'
              }
            </span>
          </div>
          <div className="flex justify-between">
            <span className="font-medium text-gray-700">City/Municipality:</span>
            <span className="text-gray-900">
              {loading ? 'Loading...' : 
                formData.cityMunicipalityCode ? `${names.cityName} (${formData.cityMunicipalityCode})` : 'Not assigned'
              }
            </span>
          </div>
          <div className="flex justify-between">
            <span className="font-medium text-gray-700">Barangay:</span>
            <span className="text-gray-900 font-semibold">
              {loading ? 'Loading...' : 
                formData.barangayCode ? `${names.barangayName} (${formData.barangayCode})` : 'Not assigned'
              }
            </span>
          </div>
        </div>

        <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded">
          <p className="text-sm text-blue-800">
            âœ“ Geographic location automatically assigned from your barangay admin profile
          </p>
        </div>
      </div>

      {/* Error Display */}
      {Object.keys(errors).length > 0 && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <h4 className="text-sm font-medium text-red-800 mb-2">Please fix the following errors:</h4>
          <ul className="text-sm text-red-700 space-y-1">
            {Object.entries(errors).map(([field, error]) => (
              <li key={field}>â€¢ {error}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Information Note */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
        <h4 className="text-sm font-medium text-blue-800 mb-2">Important Information</h4>
        <ul className="text-sm text-blue-700 space-y-1">
          <li>â€¢ Location automatically populated from your barangay admin profile</li>
          <li>â€¢ Geographic location is required for proper data organization</li>
          <li>â€¢ Only the location codes are stored in the system, not the names</li>
          <li>â€¢ This information helps with statistical reporting and administration</li>
          <li>â€¢ You can search by typing the location name to change selection</li>
        </ul>
      </div>
    </div>
  );
}

render: () => {
  const [selectedHousehold, setSelectedHousehold] = React.useState<string | null>(null);
  const [step, setStep] = React.useState<'select' | 'confirm' | 'complete'>('select');

  const handleSelect = (householdCode: string | null) => {
    setSelectedHousehold(householdCode);
    if (householdCode) {
      setStep('confirm');
    }
  };

  const handleConfirm = () => {
    action('workflow-completed')(selectedHousehold);
    setStep('complete');
  };

  const handleReset = () => {
    setSelectedHousehold(null);
    setStep('select');
  };

  return (
    <div className="max-w-2xl space-y-6">
      <div className="flex items-center space-x-2 text-sm">
        <div
          className={`rounded px-3 py-1 ${step === 'select' ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-600'}`}
        >
          1. Select Household
        </div>
        <div
          className={`rounded px-3 py-1 ${step === 'confirm' ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-600'}`}
        >
          2. Confirm
        </div>
        <div
          className={`rounded px-3 py-1 ${step === 'complete' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'}`}
        >
          3. Complete
        </div>
      </div>

      {step === 'select' && (
        <div>
          <label className="mb-2 block text-sm font-medium text-gray-700">
            Select Household for New Resident
          </label>
          <HouseholdSelector
            value={selectedHousehold || ''}
            onSelect={handleSelect}
            placeholder="Search for existing household or create new"
          />
          <p className="mt-2 text-sm text-gray-500">
            Search by household code, head resident name, or address. Select "Create New
            Household" if none exist.
          </p>
        </div>
      )}

      {step === 'confirm' && selectedHousehold && (
        <div className="space-y-4">
          <div className="rounded border border-green-200 bg-green-50 p-4">
            <h3 className="mb-2 font-semibold text-green-800">Household Selected</h3>
            <p className="text-sm text-green-700">Selected Household: {selectedHousehold}</p>
            <p className="mt-1 text-xs text-green-600">
              The new resident will be added to this household.
            </p>
          </div>

          <div className="flex gap-3">
            <button
              onClick={handleConfirm}
              className="rounded bg-blue-600 px-4 py-2 text-white hover:bg-blue-700"
            >
              Confirm Selection
            </button>
            <button
              onClick={handleReset}
              className="rounded bg-gray-300 px-4 py-2 text-gray-700 hover:bg-gray-400"
            >
              Change Household
            </button>
          </div>
        </div>
      )}

      {step === 'complete' && (
        <div className="space-y-4 text-center">
          <div className="rounded border border-green-200 bg-green-50 p-6">
            <div className="mb-2 text-4xl text-green-600">âœ…</div>
            <h3 className="mb-2 font-semibold text-green-800">Household Assignment Complete</h3>
            <p className="text-sm text-green-700">
              Household {selectedHousehold} has been selected for the new resident.
            </p>
          </div>

          <button
            onClick={handleReset}
            className="rounded bg-blue-600 px-4 py-2 text-white hover:bg-blue-700"
          >
            Start Over
          </button>
        </div>
      )}
    </div>
  );
},

render: () => {
