export default function PersonalInformation({
  value,
  onChange,
  errors = {},
  className = '',
}: PersonalInformationProps) {
  const handleChange = (field: keyof PersonalInformationData, newValue: string) => {
    onChange({
      ...value,
      [field]: newValue,
    });
  };

  return (
    <div className={`space-y-8 ${className}`}>
      <div>
        <h3 className="text-base/7 font-semibold text-primary">Personal Information</h3>
        <p className="mt-1 text-sm/6 text-secondary">
          Basic details and identification information.
        </p>
      </div>

      {/* Name Fields */}
      <div className="grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-2">
        <InputField
          label="First Name"
          value={value.firstName}
          onChange={e => handleChange('firstName', e.target.value)}
          placeholder="Enter first name"
          required
          errorMessage={errors.firstName}
        />

        <InputField
          label="Middle Name"
          value={value.middleName}
          onChange={e => handleChange('middleName', e.target.value)}
          placeholder="Enter middle name"
        />

        <InputField
          label="Last Name"
          value={value.lastName}
          onChange={e => handleChange('lastName', e.target.value)}
          placeholder="Enter last name"
          required
          errorMessage={errors.lastName}
        />

        <InputField
          label="Extension Name"
          value={value.extensionName}
          onChange={e => handleChange('extensionName', e.target.value)}
          placeholder="Jr., Sr., III, etc."
        />
      </div>

      {/* Birth Date and Basic Info */}
      <div className="grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-3">
        <InputField
          label="Birth Date"
          type="date"
