          onChange={e => handleChange('birthdate', e.target.value)}
          required
          errorMessage={errors.birthdate}
        />

        <DropdownSelect
          label="Sex"
          value={value.sex}
          onChange={val => handleChange('sex', val)}
          options={SEX_OPTIONS}
          placeholder="Select sex"
