      {formData.barangayCode && (
        <div className="rounded border border-green-200 bg-green-50 p-3 text-sm">
          <p className="text-green-800">
            <strong>Selected Barangay Code:</strong> {formData.barangayCode}
          </p>
        </div>
      )}
    </div>
  );
};

export default function SimpleBarangaySelector({
  value,
  onChange,
  error,
  disabled = false,
  placeholder = 'Search for your barangay...',
}: SimpleBarangaySelectorProps) {
  const [searchTerm, setSearchTerm] = useState('');
  const [isOpen, setIsOpen] = useState(false);

  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(false);
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Check if barangays already have administrators
  const checkAdminStatus = async (barangays: any[]) => {
    try {
      console.log('ðŸ” Checking admin status for', barangays.length, 'barangays');

      const promises = barangays.map(async (barangay) => {
        try {
          const response = await fetch('/api/auth/check-barangay-admin', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ barangayCode: barangay.code }),
          });

          if (!response.ok) {
            throw new Error(`HTTP ${response.status}`);
          }

          const data = await response.json();
          return {
            code: barangay.code,
            hasAdmin: data.hasAdmin || false,
            adminCheckStatus: 'completed' as const
          };
        } catch (error) {
          console.error('Admin check failed for', barangay.code, error);
          return {
            code: barangay.code,
            hasAdmin: false, // Default to allowing signup if check fails
            adminCheckStatus: 'error' as const
          };
        }
      });

      const results = await Promise.all(promises);
      console.log('âœ… Admin check results:', results);

      // Update search results with admin status
      setSearchResults(prevResults => 
        prevResults.map(barangay => {
          const result = results.find(r => r.code === barangay.code);
          if (result) {
            return {
              ...barangay,
              hasAdmin: result.hasAdmin,
              adminCheckStatus: result.adminCheckStatus
            };
          }
          return barangay;
        })
      );

    } catch (error) {
      console.error('Error checking admin status:', error);
      // Mark all as error status if batch check fails
      setSearchResults(prevResults => 
        prevResults.map(barangay => ({
          ...barangay,
          adminCheckStatus: 'error' as const
        }))
      );
    }
  };

  // Fetch barangay data (works with or without authentication)
  const searchBarangays = async (searchTerm: string) => {
    if (searchTerm.length < 2) {
      setSearchResults([]);
      return;
    }

    try {
      setIsLoading(true);
      setIsError(false);

      // Try to get session, but don't require it
      const {
        data: { session },
      } = await supabase.auth.getSession();

      if (session?.access_token) {
        // Authenticated search - use API
        const response = await fetch(
          `/api/addresses/barangays?search=${encodeURIComponent(searchTerm)}`,
          {
            headers: {
              Authorization: `Bearer ${session.access_token}`,
            },
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}`);
        }

        const data = await response.json();
        setSearchResults(data.barangays || []);
      } else {
        // Public search - direct Supabase query
        console.log('ðŸ” Public barangay search for:', searchTerm);

        const { data, error } = await supabase
          .from('psgc_barangays')
          .select('code, name')
          .ilike('name', `%${searchTerm}%`)
          .limit(20)
          .order('name');

        if (error) {
          console.error('Public search error:', error);
          throw error;
        }

        console.log('âœ… Found barangays:', data?.length);

        // Transform to match expected format and check admin status
        const transformedData = data?.map((item: any) => ({
          code: item.code,
          name: item.name,
          full_address: item.name, // Simple version just shows name
          hasAdmin: false,
          adminCheckStatus: 'checking' as const,
        })) || [];

        setSearchResults(transformedData);

        // Check admin status for each barangay
        await checkAdminStatus(transformedData);
      }
    } catch (error) {
      console.error('Error searching barangays:', error);
      setIsError(true);
      setSearchResults([]);
    } finally {
      setIsLoading(false);
    }
  };

  // Transform search results to match BaseSelector format
  const options: BarangayOption[] = (searchResults || []).map(barangay => ({
    value: barangay.code,
    label: barangay.name,
    disabled: barangay.hasAdmin && barangay.adminCheckStatus === 'completed',
    metadata: {
      code: barangay.code,
      name: barangay.name,
      hasAdmin: barangay.hasAdmin || false,
      adminCheckStatus: barangay.adminCheckStatus || 'checking',
    },
  }));

  const handleSearchChange = (term: string) => {
    setSearchTerm(term);
    if (!term) {
      onChange('');
    }

    // Debounce the search
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    searchTimeoutRef.current = setTimeout(() => {
      searchBarangays(term);
    }, 300);
  };

  const handleOpenChange = (open: boolean) => {
    setIsOpen(open);
  };

  const handleChange = (code: string) => {
    // Find the selected barangay to check admin status
    const selectedBarangay = searchResults.find(b => b.code === code);

    if (selectedBarangay?.hasAdmin && selectedBarangay?.adminCheckStatus === 'completed') {
      // Don't allow selection of barangays with existing admins
      console.log('âŒ Cannot select barangay with existing admin:', code);
      return;
    }

    onChange(code);
    setIsOpen(false);
  };

  const renderOption = (option: BarangayOption) => {
    const { hasAdmin, adminCheckStatus } = option.metadata;

    return (
      <>
        <div className="flex items-center justify-between">
          <div className="font-medium">{option.metadata.name}</div>
          {adminCheckStatus === 'checking' && (
            <div className="text-xs text-blue-500 flex items-center">
              <div className="animate-spin rounded-full h-3 w-3 border border-blue-500 border-t-transparent mr-1"></div>
              Checking...
            </div>
          )}
          {adminCheckStatus === 'completed' && hasAdmin && (
            <div className="text-xs text-red-500 bg-red-50 px-2 py-1 rounded flex items-center">
              <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
              Admin exists
            </div>
          )}
          {adminCheckStatus === 'completed' && !hasAdmin && (
            <div className="text-xs text-green-500 bg-green-50 px-2 py-1 rounded flex items-center">
              <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
              </svg>
              Available
            </div>
          )}
          {adminCheckStatus === 'error' && (
            <div className="text-xs text-yellow-500 bg-yellow-50 px-2 py-1 rounded flex items-center">
              <svg className="w-3 h-3 mr-1" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
              Check failed
            </div>
          )}
        </div>
        <div className="text-sm text-gray-500">Code: {option.metadata.code}</div>
        {hasAdmin && adminCheckStatus === 'completed' && (
          <div className="text-xs text-red-600 mt-1">
            This barangay already has an administrator. Contact them to be added to the system.
          </div>
        )}
      </>
    );
  };

  const emptyMessage = isError
    ? 'Unable to load barangays. Please check your connection.'
    : `No barangays found matching "${searchTerm}"`;

  return (
    <BaseSelector
      value={value}
      onChange={handleChange}
      options={options}
      loading={isLoading}
      error={error}
      disabled={disabled}
      placeholder={placeholder}
      searchTerm={searchTerm}
      onSearchChange={handleSearchChange}
      isOpen={isOpen}
      onOpenChange={handleOpenChange}
      renderOption={renderOption}
      emptyMessage={emptyMessage}
      minSearchLength={2}
    />
  );
}

export function SimpleGeographicSelector({
  regionCode = '',
  provinceCode = '',
  cityCode = '',
  barangayCode = '',
  onRegionChange,
  onProvinceChange,
  onCityChange,
  onBarangayChange,
  required = false,
  disabled = false,
  autoPopulateFromUser = false,
}: SimpleGeographicSelectorProps) {
  // Options state
  const [regionOptions, setRegionOptions] = useState<DropdownOption[]>([]);
  const [provinceOptions, setProvinceOptions] = useState<DropdownOption[]>([]);
  const [cityOptions, setCityOptions] = useState<DropdownOption[]>([]);
  const [barangayOptions, setBarangayOptions] = useState<DropdownOption[]>([]);

  // Loading states
  const [loadingRegions, setLoadingRegions] = useState(false);
  const [loadingProvinces, setLoadingProvinces] = useState(false);
  const [loadingCities, setLoadingCities] = useState(false);
  const [loadingBarangays, setLoadingBarangays] = useState(false);

  // Fetch functions using public APIs
  const fetchRegions = useCallback(async () => {
    setLoadingRegions(true);
    try {
      const response = await fetch('/api/addresses/regions/public');
      if (response.ok) {
        const result = await response.json();
        const options = (result.data || []).map((region: any) => ({
          value: region.code,
          label: region.name,
        }));
        setRegionOptions(options);
      }
    } catch (error) {
      console.error('Error fetching regions:', error);
    } finally {
      setLoadingRegions(false);
    }
  }, []);

  const fetchProvinces = useCallback(async (regionCode: string) => {
    if (!regionCode) {
      setProvinceOptions([]);
      return;
    }

    setLoadingProvinces(true);
    try {
      const response = await fetch(`/api/addresses/provinces/public?regionCode=${regionCode}`);
      if (response.ok) {
        const result = await response.json();
        const options = (result.data || []).map((province: any) => ({
          value: province.code,
          label: province.name,
        }));
        setProvinceOptions(options);
      }
    } catch (error) {
      console.error('Error fetching provinces:', error);
    } finally {
      setLoadingProvinces(false);
    }
  }, []);

  const fetchCities = useCallback(async (provinceCode: string) => {
    if (!provinceCode) {
      setCityOptions([]);
      return;
    }

    setLoadingCities(true);
    try {
      const response = await fetch(`/api/addresses/cities/public?provinceCode=${provinceCode}`);
      if (response.ok) {
        const result = await response.json();
        const options = (result.data || []).map((city: any) => ({
          value: city.code,
          label: city.name,
        }));
        setCityOptions(options);
      }
    } catch (error) {
      console.error('Error fetching cities:', error);
    } finally {
      setLoadingCities(false);
    }
  }, []);

  const fetchBarangays = useCallback(async (cityCode: string) => {
    if (!cityCode) {
      setBarangayOptions([]);
      return;
    }

    setLoadingBarangays(true);
    try {
      const response = await fetch(`/api/addresses/barangays/public?cityCode=${cityCode}`);
      if (response.ok) {
        const result = await response.json();
        const options = (result.data || []).map((barangay: any) => ({
          value: barangay.code,
          label: barangay.name,
        }));
        setBarangayOptions(options);
      }
    } catch (error) {
      console.error('Error fetching barangays:', error);
    } finally {
      setLoadingBarangays(false);
    }
  }, []);

  // Auto-populate from user profile
  const autoPopulateFromUserProfile = useCallback(async () => {
    if (!autoPopulateFromUser) return;

    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session?.user) return;

      console.log('ðŸš€ Auto-populating geographic data...');

      const response = await fetch('/api/user/geographic-location', {
        headers: {
          'Authorization': `Bearer ${session.access_token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) return;

      const hierarchy = await response.json();
      console.log('âœ… Geographic data loaded:', hierarchy);

      // Trigger changes for each level
      if (hierarchy.region && onRegionChange) {
        onRegionChange(hierarchy.region.code);
      }
      if (hierarchy.province && onProvinceChange) {
        onProvinceChange(hierarchy.province.code);
      }
      if (hierarchy.city && onCityChange) {
        onCityChange(hierarchy.city.code);
      }
      if (hierarchy.barangay && onBarangayChange) {
        onBarangayChange(hierarchy.barangay.code);
      }

    } catch (error) {
      console.error('âŒ Error auto-populating:', error);
    }
  }, [autoPopulateFromUser, onRegionChange, onProvinceChange, onCityChange, onBarangayChange]);

  // Load initial data
  useEffect(() => {
    fetchRegions();
  }, [fetchRegions]);

  // Auto-populate on mount
  useEffect(() => {
    if (regionOptions.length > 0) {
      autoPopulateFromUserProfile();
    }
  }, [regionOptions.length, autoPopulateFromUserProfile]);

  // Load cascading data when parent selections change
  useEffect(() => {
    if (regionCode) {
      fetchProvinces(regionCode);
    } else {
      setProvinceOptions([]);
      setCityOptions([]);
      setBarangayOptions([]);
    }
  }, [regionCode, fetchProvinces]);

  useEffect(() => {
    if (provinceCode) {
      fetchCities(provinceCode);
    } else {
      setCityOptions([]);
      setBarangayOptions([]);
    }
  }, [provinceCode, fetchCities]);

  useEffect(() => {
    if (cityCode) {
      fetchBarangays(cityCode);
    } else {
      setBarangayOptions([]);
    }
  }, [cityCode, fetchBarangays]);

  // Handle changes
  const handleRegionChange = (value: string) => {
    onRegionChange?.(value);
    // Clear dependent selections
    onProvinceChange?.('');
    onCityChange?.('');
    onBarangayChange?.('');
  };

  const handleProvinceChange = (value: string) => {
    onProvinceChange?.(value);
    // Clear dependent selections
    onCityChange?.('');
    onBarangayChange?.('');
  };

  const handleCityChange = (value: string) => {
    onCityChange?.(value);
    // Clear dependent selections
    onBarangayChange?.('');
  };

  return (
    <div className="space-y-4">
      <DropdownSelect
        label="Region"
        placeholder={loadingRegions ? "Loading regions..." : "Select a region"}
        options={regionOptions}
        value={regionCode}
        onChange={handleRegionChange}
        disabled={disabled || loadingRegions}
        variant={required && !regionCode ? 'error' : 'default'}
      />

      <DropdownSelect
        label="Province"
        placeholder={loadingProvinces ? "Loading provinces..." : regionCode ? "Select a province" : "Select a region first"}
        options={provinceOptions}
        value={provinceCode}
        onChange={handleProvinceChange}
        disabled={disabled || !regionCode || loadingProvinces}
        variant={required && regionCode && !provinceCode ? 'error' : 'default'}
      />

      <DropdownSelect
        label="City/Municipality"
        placeholder={loadingCities ? "Loading cities..." : provinceCode ? "Select a city/municipality" : "Select a province first"}
        options={cityOptions}
        value={cityCode}
        onChange={handleCityChange}
        disabled={disabled || !provinceCode || loadingCities}
        variant={required && provinceCode && !cityCode ? 'error' : 'default'}
      />

      <DropdownSelect
        label="Barangay"
        placeholder={loadingBarangays ? "Loading barangays..." : cityCode ? "Select a barangay" : "Select a city/municipality first"}
        options={barangayOptions}
        value={barangayCode}
        onChange={onBarangayChange}
        disabled={disabled || !cityCode || loadingBarangays}
        variant={required && cityCode && !barangayCode ? 'error' : 'default'}
      />
    </div>
  );
}

export default function StreetSelector({
  value,
  onSelect,
  error,
  placeholder = 'ðŸ›£ï¸ Search streets or create new',
  subdivisionId = null,
}: StreetSelectorProps) {
  const { userProfile } = useAuth();
  const [streets, setStreets] = useState<Street[]>([]);
  const [loading, setLoading] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [newStreetName, setNewStreetName] = useState('');
  const [isCreating, setIsCreating] = useState(false);

  // Load streets for the user's barangay
  const loadStreets = useCallback(async () => {
    if (!userProfile?.barangay_code) {
      logger.debug('No barangay_code available, cannot load streets');
      return;
    }

    logger.debug('Loading streets for barangay', { 
      barangayCode: userProfile.barangay_code,
      subdivisionId 
    });
    setLoading(true);
    try {
      let query = supabase
        .from('geo_streets')
        .select(`
          id,
          name,
          barangay_code,
          subdivision_id,
          description,
          geo_subdivisions (
            name
          )
        `)
        .eq('barangay_code', userProfile.barangay_code)
        .eq('is_active', true)
        .order('name', { ascending: true });

      // Filter by subdivision if provided
      if (subdivisionId) {
        query = query.eq('subdivision_id', subdivisionId);
      } else {
        // Show streets without subdivision or null subdivision
        query = query.is('subdivision_id', null);
      }

      const { data: streetsData, error } = await query;

      if (error) {
        logger.error('Error loading streets', { error });
        return;
      }

      // Format the data
      const formattedStreets = (streetsData || []).map(street => ({
        id: street.id,
        name: street.name,
        barangay_code: street.barangay_code,
        subdivision_id: street.subdivision_id,
        subdivision_name: (street.geo_subdivisions as any)?.name || null,
        description: street.description,
      }));

      logger.debug('Loaded streets', { count: formattedStreets.length });
      setStreets(formattedStreets);
    } catch (error) {
      logError(error as Error, 'STREET_LOAD_ERROR');
    } finally {
      setLoading(false);
    }
  }, [userProfile?.barangay_code, subdivisionId]);

  // Load streets when barangay or subdivision changes
  useEffect(() => {
    loadStreets();
  }, [loadStreets]);

  // Create new street
  const createStreet = async () => {
    if (!newStreetName.trim() || !userProfile?.barangay_code) return;

    setIsCreating(true);
    try {
      logger.info('Creating new street', { name: newStreetName.trim() });

      // Derive geographic codes from barangay code
      const barangayCode = userProfile.barangay_code;
      const regionCode = barangayCode.substring(0, 2);
      const provinceCode = barangayCode.substring(0, 4);
      const cityMunicipalityCode = barangayCode.substring(0, 6);

      const { data: newStreet, error } = await supabase
        .from('geo_streets')
        .insert([{
          name: newStreetName.trim(),
          subdivision_id: subdivisionId,
          barangay_code: barangayCode,
          city_municipality_code: cityMunicipalityCode,
          province_code: provinceCode,
          region_code: regionCode,
          created_by: userProfile.id,
        }])
        .select('id, name')
        .single();

      if (error) {
        logger.error('Error creating street', { error });
        alert(`Failed to create street: ${error.message}`);
        return;
      }

      logger.info('Created new street', { streetId: newStreet.id, name: newStreet.name });

      // Select the newly created street
      onSelect(newStreet.id);
      setNewStreetName('');
      setShowCreateForm(false);
      setIsOpen(false);

      // Refresh streets list
      setTimeout(() => {
        loadStreets();
      }, 500);

    } catch (error) {
      logError(error as Error, 'STREET_CREATION_ERROR');
      alert('An unexpected error occurred while creating the street.');
    } finally {
      setIsCreating(false);
    }
  };

  // Filter streets based on search term
  const filteredStreets = streets.filter(street => {
    const searchLower = searchTerm.toLowerCase();
    return (
      street.name.toLowerCase().includes(searchLower) ||
      street.subdivision_name?.toLowerCase().includes(searchLower)
    );
  });

  const selectedStreet = streets.find(s => s.id === value);

  // Helper function to format display name
  const formatDisplayName = (street: Street) => {
    if (street.subdivision_name) {
      return `${street.name} (${street.subdivision_name})`;
    }
    return street.name;
  };

  return (
    <div className="relative">
      <div
        className={`font-montserrat relative rounded border text-base focus-within:border-transparent focus-within:ring-2 ${
          error
            ? 'border-red-500 focus-within:ring-red-500'
            : 'border-neutral-300 focus-within:ring-blue-500'
        }`}
      >
        <input
          type="text"
          value={
            selectedStreet
              ? formatDisplayName(selectedStreet)
              : searchTerm
          }
          onChange={e => {
            setSearchTerm(e.target.value);
            setIsOpen(true);
            if (!e.target.value) {
              onSelect(null);
            }
          }}
          onFocus={() => setIsOpen(true)}
          className="w-full bg-transparent px-3 py-2 outline-none"
          placeholder={placeholder}
        />

        <button
          type="button"
          onClick={() => setIsOpen(!isOpen)}
          className="absolute right-2 top-1/2 -translate-y-1/2 text-neutral-500 hover:text-neutral-700"
        >
          <svg className="size-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d={isOpen ? 'M5 15l7-7 7 7' : 'M19 9l-7 7-7-7'}
            />
          </svg>
        </button>
      </div>

      {/* Dropdown */}
      {isOpen && (
        <div className="absolute z-10 mt-1 max-h-60 w-full overflow-y-auto rounded-lg border border-neutral-300 bg-white shadow-lg">
          {loading ? (
            <div className="p-3 text-center text-neutral-500">
              <div className="animate-pulse">Loading streets...</div>
            </div>
          ) : (
            <div>
              {/* Create new street option */}
              {!showCreateForm ? (
                <button
                  type="button"
                  onClick={() => {
                    setShowCreateForm(true);
                    setNewStreetName(searchTerm);
                  }}
                  className="w-full border-b border-neutral-100 p-3 text-left hover:bg-blue-50"
                >
                  <div className="font-medium text-blue-600">+ Create New Street</div>
                  <div className="text-xs text-blue-500">
                    {searchTerm ? `Create "${searchTerm}"` : 'Add a new street to this area'}
                  </div>
                </button>
              ) : (
                <div className="border-b border-neutral-100 p-3">
                  <div className="font-medium text-blue-600 mb-2">Create New Street</div>
                  <div className="flex gap-2">
                    <input
                      type="text"
                      value={newStreetName}
                      onChange={e => setNewStreetName(e.target.value)}
                      placeholder="Enter street name"
                      className="flex-1 rounded border border-neutral-300 px-2 py-1 text-sm focus:border-blue-500 focus:outline-none"
                      onKeyDown={e => {
                        if (e.key === 'Enter') {
                          e.preventDefault();
                          createStreet();
                        } else if (e.key === 'Escape') {
                          setShowCreateForm(false);
                          setNewStreetName('');
                        }
                      }}
                      autoFocus
                    />
                    <button
                      type="button"
                      onClick={createStreet}
                      disabled={!newStreetName.trim() || isCreating}
                      className="rounded bg-blue-600 px-3 py-1 text-xs text-white hover:bg-blue-700 disabled:bg-neutral-400"
                    >
                      {isCreating ? '...' : 'Add'}
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        setShowCreateForm(false);
                        setNewStreetName('');
                      }}
                      className="rounded bg-neutral-400 px-2 py-1 text-xs text-white hover:bg-neutral-500"
                    >
                      âœ•
                    </button>
                  </div>
                </div>
              )}

              {/* Show message when no existing streets */}
              {filteredStreets.length === 0 && !searchTerm && (
                <div className="p-3 text-center text-neutral-500">
                  <div className="text-sm">No streets found in this area</div>
                  <div className="mt-1 text-xs text-green-600">
                    âœ“ Create the first street above
                  </div>
                </div>
              )}

              {/* Show "no search results" when searching */}
              {filteredStreets.length === 0 && searchTerm && !showCreateForm && (
                <div className="p-3 text-center text-neutral-500">
                  <div className="text-sm">No streets match your search</div>
                </div>
              )}

              {/* Existing streets */}
              {filteredStreets.map(street => (
                <button
                  key={street.id}
                  type="button"
                  onClick={() => {
                    onSelect(street.id);
                    setSearchTerm('');
                    setIsOpen(false);
                    setShowCreateForm(false);
                  }}
                  className="w-full border-b border-neutral-100 p-3 text-left last:border-b-0 hover:bg-neutral-50"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="font-medium text-neutral-900">{street.name}</div>
                      {street.subdivision_name && (
                        <div className="text-sm text-neutral-600">
                          in {street.subdivision_name}
                        </div>
                      )}
                      {street.description && (
                        <div className="text-xs text-neutral-500">{street.description}</div>
                      )}
                    </div>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Click outside to close */}
      {isOpen && <div className="fixed inset-0 z-0" onClick={() => {
        setIsOpen(false);
        setShowCreateForm(false);
      }} />}

      {/* Error message */}
      {error && (
        <p className="mt-2 text-sm text-red-600" role="alert">
          {error}
        </p>
      )}
    </div>
  );
}

export default function SubdivisionSelector({
  value,
  onSelect,
  error,
  placeholder = 'ðŸ˜ï¸ Search subdivisions or create new',
}: SubdivisionSelectorProps) {
  const { userProfile } = useAuth();
  const [subdivisions, setSubdivisions] = useState<Subdivision[]>([]);
  const [loading, setLoading] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [newSubdivisionData, setNewSubdivisionData] = useState({
    name: '',
    type: 'Subdivision' as 'Subdivision' | 'Zone' | 'Sitio' | 'Purok'
  });
  const [isCreating, setIsCreating] = useState(false);

  // Load subdivisions for the user's barangay
  const loadSubdivisions = useCallback(async () => {
    if (!userProfile?.barangay_code) {
      logger.debug('No barangay_code available, cannot load subdivisions');
      return;
    }

    logger.debug('Loading subdivisions for barangay', { barangayCode: userProfile.barangay_code });
    setLoading(true);
    try {
      const { data: subdivisionsData, error } = await supabase
        .from('geo_subdivisions')
        .select('id, name, type, barangay_code, description')
        .eq('barangay_code', userProfile.barangay_code)
        .eq('is_active', true)
        .order('name', { ascending: true });

      if (error) {
        logger.error('Error loading subdivisions', { error });
        return;
      }

      logger.debug('Loaded subdivisions', { count: subdivisionsData?.length || 0 });
      setSubdivisions(subdivisionsData || []);
    } catch (error) {
      logError(error as Error, 'SUBDIVISION_LOAD_ERROR');
    } finally {
      setLoading(false);
    }
  }, [userProfile?.barangay_code]);

  // Load subdivisions when barangay changes
  useEffect(() => {
    loadSubdivisions();
  }, [loadSubdivisions]);

  // Create new subdivision
  const createSubdivision = async () => {
    if (!newSubdivisionData.name.trim() || !userProfile?.barangay_code) return;

    setIsCreating(true);
    try {
      logger.info('Creating new subdivision', { 
        name: newSubdivisionData.name.trim(),
        type: newSubdivisionData.type 
      });

      // Derive geographic codes from barangay code
      const barangayCode = userProfile.barangay_code;
      const regionCode = barangayCode.substring(0, 2);
      const provinceCode = barangayCode.substring(0, 4);
      const cityMunicipalityCode = barangayCode.substring(0, 6);

      const { data: newSubdivision, error } = await supabase
        .from('geo_subdivisions')
        .insert([{
          name: newSubdivisionData.name.trim(),
          type: newSubdivisionData.type,
          barangay_code: barangayCode,
          city_municipality_code: cityMunicipalityCode,
          province_code: provinceCode,
          region_code: regionCode,
          created_by: userProfile.id,
        }])
        .select('id, name, type')
        .single();

      if (error) {
        logger.error('Error creating subdivision', { error });
        alert(`Failed to create subdivision: ${error.message}`);
        return;
      }

      logger.info('Created new subdivision', { 
        subdivisionId: newSubdivision.id, 
        name: newSubdivision.name,
        type: newSubdivision.type 
      });

      // Select the newly created subdivision
      onSelect(newSubdivision.id);
      setNewSubdivisionData({ name: '', type: 'Subdivision' });
      setShowCreateForm(false);
      setIsOpen(false);

      // Refresh subdivisions list
      setTimeout(() => {
        loadSubdivisions();
      }, 500);

    } catch (error) {
      logError(error as Error, 'SUBDIVISION_CREATION_ERROR');
      alert('An unexpected error occurred while creating the subdivision.');
    } finally {
      setIsCreating(false);
    }
  };

  // Filter subdivisions based on search term
  const filteredSubdivisions = subdivisions.filter(subdivision => {
    const searchLower = searchTerm.toLowerCase();
    return (
      subdivision.name.toLowerCase().includes(searchLower) ||
      subdivision.type.toLowerCase().includes(searchLower)
    );
  });

  const selectedSubdivision = subdivisions.find(s => s.id === value);

  // Helper function to format display name
  const formatDisplayName = (subdivision: Subdivision) => {
    return `${subdivision.name} (${subdivision.type})`;
  };

  const subdivisionTypes = ['Subdivision', 'Zone', 'Sitio', 'Purok'] as const;

  return (
    <div className="relative">
      <div
        className={`font-montserrat relative rounded border text-base focus-within:border-transparent focus-within:ring-2 ${
          error
            ? 'border-red-500 focus-within:ring-red-500'
            : 'border-neutral-300 focus-within:ring-blue-500'
        }`}
      >
        <input
          type="text"
          value={
            selectedSubdivision
              ? formatDisplayName(selectedSubdivision)
              : searchTerm
          }
          onChange={e => {
            setSearchTerm(e.target.value);
            setIsOpen(true);
            if (!e.target.value) {
              onSelect(null);
            }
          }}
          onFocus={() => setIsOpen(true)}
          className="w-full bg-transparent px-3 py-2 outline-none"
          placeholder={placeholder}
        />

        <button
          type="button"
          onClick={() => setIsOpen(!isOpen)}
          className="absolute right-2 top-1/2 -translate-y-1/2 text-neutral-500 hover:text-neutral-700"
        >
          <svg className="size-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d={isOpen ? 'M5 15l7-7 7 7' : 'M19 9l-7 7-7-7'}
            />
          </svg>
        </button>
      </div>

      {/* Dropdown */}
      {isOpen && (
        <div className="absolute z-10 mt-1 max-h-60 w-full overflow-y-auto rounded-lg border border-neutral-300 bg-white shadow-lg">
          {loading ? (
            <div className="p-3 text-center text-neutral-500">
              <div className="animate-pulse">Loading subdivisions...</div>
            </div>
          ) : (
            <div>
              {/* Create new subdivision option */}
              {!showCreateForm ? (
                <button
                  type="button"
                  onClick={() => {
                    setShowCreateForm(true);
                    setNewSubdivisionData(prev => ({ ...prev, name: searchTerm }));
                  }}
                  className="w-full border-b border-neutral-100 p-3 text-left hover:bg-blue-50"
                >
                  <div className="font-medium text-blue-600">+ Create New Subdivision</div>
                  <div className="text-xs text-blue-500">
                    {searchTerm ? `Create "${searchTerm}"` : 'Add a new subdivision/zone/sitio/purok'}
                  </div>
                </button>
              ) : (
                <div className="border-b border-neutral-100 p-3">
                  <div className="font-medium text-blue-600 mb-2">Create New Subdivision</div>
                  <div className="space-y-2">
                    <input
                      type="text"
                      value={newSubdivisionData.name}
                      onChange={e => setNewSubdivisionData(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter subdivision name"
                      className="w-full rounded border border-neutral-300 px-2 py-1 text-sm focus:border-blue-500 focus:outline-none"
                      onKeyDown={e => {
                        if (e.key === 'Enter') {
                          e.preventDefault();
                          createSubdivision();
                        } else if (e.key === 'Escape') {
                          setShowCreateForm(false);
                          setNewSubdivisionData({ name: '', type: 'Subdivision' });
                        }
                      }}
                      autoFocus
                    />
                    <div className="flex gap-2 items-center">
                      <select
                        value={newSubdivisionData.type}
                        onChange={e => setNewSubdivisionData(prev => ({ 
                          ...prev, 
                          type: e.target.value as typeof prev.type 
                        }))}
                        className="flex-1 rounded border border-neutral-300 px-2 py-1 text-sm focus:border-blue-500 focus:outline-none"
                      >
                        {subdivisionTypes.map(type => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                      </select>
                      <button
                        type="button"
                        onClick={createSubdivision}
                        disabled={!newSubdivisionData.name.trim() || isCreating}
                        className="rounded bg-blue-600 px-3 py-1 text-xs text-white hover:bg-blue-700 disabled:bg-neutral-400"
                      >
                        {isCreating ? '...' : 'Add'}
                      </button>
                      <button
                        type="button"
                        onClick={() => {
                          setShowCreateForm(false);
                          setNewSubdivisionData({ name: '', type: 'Subdivision' });
                        }}
                        className="rounded bg-neutral-400 px-2 py-1 text-xs text-white hover:bg-neutral-500"
                      >
                        âœ•
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {/* Show message when no existing subdivisions */}
              {filteredSubdivisions.length === 0 && !searchTerm && (
                <div className="p-3 text-center text-neutral-500">
                  <div className="text-sm">No subdivisions found in this barangay</div>
                  <div className="mt-1 text-xs text-green-600">
                    âœ“ Create the first subdivision above
                  </div>
                </div>
              )}

              {/* Show "no search results" when searching */}
              {filteredSubdivisions.length === 0 && searchTerm && !showCreateForm && (
                <div className="p-3 text-center text-neutral-500">
                  <div className="text-sm">No subdivisions match your search</div>
                </div>
              )}

              {/* Existing subdivisions */}
              {filteredSubdivisions.map(subdivision => (
                <button
                  key={subdivision.id}
                  type="button"
                  onClick={() => {
                    onSelect(subdivision.id);
                    setSearchTerm('');
                    setIsOpen(false);
                    setShowCreateForm(false);
                  }}
                  className="w-full border-b border-neutral-100 p-3 text-left last:border-b-0 hover:bg-neutral-50"
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="font-medium text-neutral-900">{subdivision.name}</div>
                      <div className="text-sm text-neutral-600">Type: {subdivision.type}</div>
                      {subdivision.description && (
                        <div className="text-xs text-neutral-500">{subdivision.description}</div>
                      )}
                    </div>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Click outside to close */}
      {isOpen && <div className="fixed inset-0 z-0" onClick={() => {
        setIsOpen(false);
        setShowCreateForm(false);
      }} />}

      {/* Error message */}
      {error && (
        <p className="mt-2 text-sm text-red-600" role="alert">
          {error}
        </p>
      )}
    </div>
  );
}

