const getVisualization = () => {
  switch (type) {
    case 'width':
      return (
        <div className="w-full rounded bg-blue-100">
          <div
            className="h-4 rounded bg-blue-500 transition-all"
            style={{ width: value.includes('%') ? value : '100px', maxWidth: '200px' }}
          />
        </div>
      );
    case 'height':
      return (
        <div className="flex h-16 items-end">
          <div
            className="w-16 rounded bg-green-500 transition-all"
            style={{
              height: value.includes('px') ? Math.min(parseInt(value), 64) + 'px' : '32px',
            }}
          />
        </div>
      );
    case 'percentage':
      const percentage = parseFloat(value);
      return (
        <div className="w-full rounded bg-purple-100">
          <div
            className="h-4 rounded bg-purple-500 transition-all"
            style={{ width: `${percentage}%` }}
          />
        </div>
      );
    case 'index':
      return (
        <div className="relative h-16 w-16 rounded bg-gray-100">
          <div
            className="absolute inset-2 flex items-center justify-center rounded bg-red-500 font-mono text-xs text-white"
            style={{ zIndex: parseInt(value) || 0 }}
          >
            {value}
          </div>
        </div>
      );
    default:
      return <div className="h-4 w-16 rounded bg-neutral-300" />;
  }
};

const getVisualization = () => {
  switch (type) {
    case 'boxShadow':
      return (
        <div
          className="h-16 w-16 rounded-lg bg-white transition-all"
          style={{ boxShadow: value === 'none' ? 'none' : value }}
        />
      );
    case 'dropShadow':
      return (
        <div
          className="flex h-16 w-16 items-center justify-center rounded-lg bg-blue-500 text-xl font-bold text-white transition-all"
          style={{ filter: value === 'none' ? 'none' : `drop-shadow(${value})` }}
        >
          A
        </div>
      );
    case 'radius':
      return (
        <div className="h-16 w-16 bg-green-500 transition-all" style={{ borderRadius: value }} />
      );
    case 'opacity':
      return (
        <div className="relative h-16 w-16">
          <div className="absolute inset-0 rounded bg-gradient-to-r from-blue-500 to-purple-500" />
          <div
            className="absolute inset-0 rounded bg-red-500 transition-all"
            style={{ opacity: value }}
          />
        </div>
      );
    case 'blur':
      return (
        <div className="relative h-16 w-16 overflow-hidden rounded">
          <div
            className="absolute inset-0 bg-gradient-to-br from-blue-400 to-purple-500 transition-all"
            style={{ filter: value === '0' ? 'none' : `blur(${value})` }}
          />
          <div className="absolute inset-0 flex items-center justify-center font-bold text-white">
            {value === '0' ? 'SHARP' : 'BLUR'}
          </div>
        </div>
      );
    default:
      return <div className="h-16 w-16 rounded bg-neutral-300" />;
  }
};

export function AuthProvider({ children }: { readonly children: React.ReactNode }) {
  // Core auth state
  const [session, setSession] = useState<Session | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  // Profile state
  const [userProfile, setUserProfile] = useState<UserProfile | null>(null);
  const [role, setRole] = useState<Role | null>(null);
  const [profileLoading, setProfileLoading] = useState(false);
  const [profileError, setProfileError] = useState<string | null>(null);
  const [profileCache, setProfileCache] = useState<
    Map<string, { profile: UserProfile; role: Role; timestamp: number }>
  >(new Map());
  const [lastProfileLoad, setLastProfileLoad] = useState<number>(0);

  // Retry helper with exponential backoff
  const retryWithBackoff = async <T,>(
    operation: () => Promise<T>,
    maxRetries = 3,
    baseDelay = 1000
  ): Promise<T> => {
    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        return await operation();
      } catch (error) {
        if (attempt === maxRetries) throw error;

        const delay = baseDelay * Math.pow(2, attempt);
        console.log(`Retry attempt ${attempt + 1}/${maxRetries + 1} in ${delay}ms`);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
    throw new Error('Max retries exceeded');
  };

  // Load user profile and related data (simplified for original schema)
  const loadUserProfile = useCallback(async (userId: string, force = false) => {
    try {
      // Check cache first (cache for 5 minutes)
      const cacheKey = userId;
      const cached = profileCache.get(cacheKey);
      const now = Date.now();
      const cacheTimeout = 5 * 60 * 1000; // 5 minutes

      if (!force && cached && now - cached.timestamp < cacheTimeout) {
        console.log('Using cached profile data');
        setUserProfile(cached.profile);
        setRole(cached.role);
        return;
      }

      // Prevent multiple simultaneous requests for the same user
      if (profileLoading && now - lastProfileLoad < 1000) {
        console.log('Profile already loading, skipping duplicate request');
        return;
      }

      setProfileLoading(true);
      setProfileError(null);
      setLastProfileLoad(now);
      console.log('Loading user profile for:', userId);

      // Try real database query first
      console.log('Attempting real database query...');
      const startTime = Date.now();

      // Set a shorter timeout for this specific query
      const timeoutPromise = new Promise((_, reject) => {
        setTimeout(() => reject(new Error('Profile query timeout')), 15000); // 15 second timeout
      });

      try {
        // Get the current session to pass the auth token
        const {
          data: { session },
        } = await supabase.auth.getSession();

        if (!session?.access_token) {
          throw new Error('No valid session found');
        }

        // Use server-side API to fetch profile data (bypasses RLS issues)
        const response = (await retryWithBackoff(async () => {
          const result = await Promise.race([
            fetch('/api/auth/profile', {
              method: 'GET',
              headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${session.access_token}`,
              },
            }),
            timeoutPromise,
          ]);
          return result;
        })) as Response;

        const queryTime = Date.now() - startTime;
        console.log(`Profile query completed successfully in ${queryTime}ms`);

        if (!response.ok) {
          const errorData = await response.json().catch(() => ({}));
          console.error('Profile API error:', {
            status: response.status,
            statusText: response.statusText,
            error: errorData.error || 'Unknown error',
            fullError: JSON.stringify(errorData, null, 2),
          });
          throw new Error(errorData.error || `HTTP ${response.status}: ${response.statusText}`);
        }

        const data = await response.json();
        const { profile: profileData, role } = data;

        if (!profileData) {
          console.error('No profile found for user:', userId);
          throw new Error('Profile not found');
        }

        // Log the actual data structure to understand what fields exist
        console.log('Raw profile data from API:', profileData);
        console.log('Profile barangay_code:', profileData?.barangay_code);
        console.log('Profile role_id:', profileData?.role_id);

        // Map the database fields to our interface, using defaults for missing fields
        const profile: UserProfile = {
          id: profileData.id || userId,
          email: profileData.email || '',
          first_name: profileData.first_name || '',
          last_name: profileData.last_name || '',
          barangay_code: profileData.barangay_code || '',
          role_id: profileData.role_id || '',
          is_active: profileData.is_active !== undefined ? profileData.is_active : true,
          created_at: profileData.created_at || new Date().toISOString(),
          updated_at: profileData.updated_at || new Date().toISOString(),
        };

        console.log('Profile loaded successfully:', profile);
        console.log('Role loaded:', role);

        const finalRole = role || {
          id: 'default-role',
          name: 'User',
          permissions: { residents_view: true },
        };

        // Cache the results
        const newCache = new Map(profileCache);
        newCache.set(cacheKey, {
          profile,
          role: finalRole,
          timestamp: Date.now(),
        });
        setProfileCache(newCache);

        setUserProfile(profile);
        setRole(finalRole);
      } catch (dbError) {
        console.error('Database query failed and no fallback available:', {
          message: (dbError as any)?.message || 'Unknown error',
          code: (dbError as any)?.code,
          details: (dbError as any)?.details,
          hint: (dbError as any)?.hint,
          fullError: JSON.stringify(dbError, null, 2),
        });
        console.error('User must have a valid profile in the database to use the system');

        // Don't use mock data - require real database profile
        setUserProfile(null);
        setRole(null);
        throw new Error('Failed to load user profile from database');
      }
    } catch (error) {
      console.error('Error in loadUserProfile:', error);
      setProfileError(error instanceof Error ? error.message : 'Failed to load profile');
      setUserProfile(null);
      setRole(null);
    } finally {
      setProfileLoading(false);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Initialize auth state
  useEffect(() => {
    // Get initial session with timeout
    const initAuth = async () => {
      try {
        console.log('Starting auth initialization...');
        const {
          data: { session },
          error,
        } = await supabase.auth.getSession();

        if (error) {
          console.error('Session error:', {
            message: error.message || 'Unknown error',
            code: error.code,
            details: (error as any).details,
            hint: (error as any).hint,
            fullError: JSON.stringify(error, null, 2),
          });
          setLoading(false);
          return;
        }

        console.log('Session retrieved:', session?.user?.id ? 'User found' : 'No user');
        setSession(session);
        setUser(session?.user ?? null);

        console.log('Auth initialization complete');
        setLoading(false);
      } catch (error) {
        console.error('Auth initialization error:', {
          message: (error as any)?.message || 'Unknown error',
          code: (error as any)?.code,
          details: (error as any)?.details,
          hint: (error as any)?.hint,
          fullError: JSON.stringify(error, null, 2),
        });
        setLoading(false);
      }
    };

    // Add timeout to prevent infinite loading - only if page is visible
    let timeoutId: NodeJS.Timeout | null = null;

    if (!document.hidden) {
      timeoutId = setTimeout(() => {
        console.warn('Auth initialization timeout - forcing completion');
        setLoading(false);
        // Don't clear the session if user was found
        if (user) {
          console.log('User was authenticated but initialization timed out - continuing');
        }
      }, 10000); // 10 seconds timeout - shorter for incognito mode
    }

    initAuth().then(() => {
      if (timeoutId) clearTimeout(timeoutId);
    });

    // Listen for auth changes
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange(async (event, session) => {
      console.log('Auth state change:', event, session?.user?.id);
      setSession(session);
      setUser(session?.user ?? null);

      if (event === 'SIGNED_OUT') {
        setUserProfile(null);
        setRole(null);
        setProfileError(null);
      }

      // Always ensure loading is false after auth state change
      setLoading(false);
    });

    return () => {
      if (timeoutId) clearTimeout(timeoutId);
      subscription.unsubscribe();
    };
  }, []);

  // Sign in method
  const signIn = useCallback(
    async (email: string, password: string) => {
      const { error, data } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      // Preload profile data immediately after successful sign-in (but skip on public routes)
      if (!error && data.user) {
        const publicRoutes = ['/signup', '/login', '/'];
        const currentPath = typeof window !== 'undefined' ? window.location.pathname : '';
        const isPublicRoute = publicRoutes.includes(currentPath);

        if (!isPublicRoute) {
          loadUserProfile(data.user.id).catch(err => {
            console.error('Profile preloading failed:', err);
          });
        }
      }

      return { error };
    },
    [loadUserProfile]
  );

  // Sign out method
  const signOut = useCallback(async () => {
    const { error } = await supabase.auth.signOut();
    if (error) {
      console.error('Error signing out:', error);
    }
  }, []);

  // Load profile method (separate from auth)
  const loadProfile = useCallback(async () => {
    if (user?.id) {
      await loadUserProfile(user.id);
    }
  }, [user?.id, loadUserProfile]);

  // Refresh profile method
  const refreshProfile = useCallback(async () => {
    if (user) {
      await loadUserProfile(user.id);
    }
  }, [user, loadUserProfile]);

  // Auto-load profile when user is authenticated (but skip on public routes)
  useEffect(() => {
    // Skip profile loading on public routes
    const publicRoutes = ['/signup', '/login', '/'];
    const currentPath = typeof window !== 'undefined' ? window.location.pathname : '';
    const isPublicRoute = publicRoutes.includes(currentPath);

    if (user?.id && !userProfile && !profileLoading && !isPublicRoute) {
      loadProfile();
    }
  }, [user?.id, userProfile, profileLoading, loadProfile]);

  // Permission helpers
  const checkCrudPermission = useCallback((action: string, permissionValue: string): boolean => {
    if (permissionValue === 'crud' || permissionValue === 'manage') return true;
    if (action === 'view' && (permissionValue === 'read' || permissionValue === 'crud'))
      return true;
    if (action === 'create' && (permissionValue === 'write' || permissionValue === 'crud'))
      return true;
    if (action === 'update' && (permissionValue === 'write' || permissionValue === 'crud'))
      return true;
    if (action === 'delete' && (permissionValue === 'write' || permissionValue === 'crud'))
      return true;
    return false;
  }, []);

  const hasPermission = useCallback(
    (permission: string): boolean => {
      if (!role?.permissions) return false;

      // Super admin has all permissions
      if (role.permissions.all === true) return true;

      // Check specific permission (boolean style)
      if (role.permissions[permission] === true) return true;

      // Check CRUD-style permissions (e.g., "residents_view")
      const [resource, action] = permission.split('_');
      if (resource && action && role.permissions[resource]) {
        const resourcePermission = role.permissions[resource];
        // If permission is boolean true, user has full access
        if (resourcePermission === true) return true;
        // If permission is a string, check CRUD permissions
        if (typeof resourcePermission === 'string') {
          return checkCrudPermission(action, resourcePermission);
        }
      }

      return false;
    },
    [role, checkCrudPermission]
  );

  const isInRole = useCallback(
    (roleName: string): boolean => {
      return role?.name === roleName;
    },
    [role]
  );

  const canAccessBarangay = useCallback(
    (barangayCode: string): boolean => {
      return userProfile?.barangay_code === barangayCode;
    },
    [userProfile]
  );

  const isBarangayAdmin = useCallback((): boolean => {
    return role?.name === 'barangay_admin';
  }, [role]);

  const value: AuthContextType = useMemo(
    () => ({
      // State
      session,
      user,
      userProfile,
      role,
      loading,
      profileLoading,
      profileError,

      // Methods
      signIn,
      signOut,
      loadProfile,
      refreshProfile,

      // Helpers
      hasPermission,
      isInRole,
      canAccessBarangay,
      isBarangayAdmin,
    }),
    [
      session,
      user,
      userProfile,
      role,
      loading,
      profileLoading,
      profileError,
      signIn,
      signOut,
      loadProfile,
      refreshProfile,
      hasPermission,
      isInRole,
      canAccessBarangay,
      isBarangayAdmin,
    ]
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    // Provide a fallback during SSR or if used outside provider
    return {
      user: null,
      session: null,
      userProfile: null,
      role: null,
      loading: true,
      profileLoading: true,
      profileError: null,
      signIn: async () => ({ error: new Error('AuthProvider not available') }),
      signOut: async () => {},
      loadProfile: async () => {},
      refreshProfile: async () => {},
      hasPermission: () => false,
      isInRole: () => false,
      canAccessBarangay: () => false,
      isBarangayAdmin: () => false,
    };
  }
  return context;
}

export function useRequireAuth() {
  const auth = useAuth();

  useEffect(() => {
    if (!auth.loading && !auth.user) {
      // Redirect to login - but give more time for auth to settle during navigation
      const timer = setTimeout(() => {
        window.location.href = '/login';
      }, 500); // Small delay to prevent race conditions during navigation

      return () => clearTimeout(timer);
    }
  }, [auth.loading, auth.user]);

  return auth;
}

export function useRequireRole(requiredRole: string) {
  const auth = useRequireAuth();

  useEffect(() => {
    if (!auth.loading && !auth.profileLoading && auth.user && !auth.isInRole(requiredRole)) {
      // Redirect to unauthorized page
      window.location.href = '/unauthorized';
    }
  }, [auth, requiredRole]);

  return auth;
}

export function useRequirePermission(permission: string) {
  const auth = useRequireAuth();

  useEffect(() => {
    if (!auth.loading && !auth.profileLoading && auth.user && !auth.hasPermission(permission)) {
      // Redirect to unauthorized page
      window.location.href = '/unauthorized';
    }
  }, [auth, permission]);

  return auth;
}

export function DarkModeProvider({ children }: { children: React.ReactNode }) {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    // Check local storage for saved preference
    const savedMode = localStorage.getItem('darkMode');
    if (savedMode !== null) {
      setIsDarkMode(savedMode === 'true');
    } else {
      // Check system preference
      const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setIsDarkMode(prefersDark);
    }
  }, []);

  useEffect(() => {
    // Update document class and local storage when dark mode changes
    if (isDarkMode) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('darkMode', 'true');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('darkMode', 'false');
    }
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <DarkModeContext.Provider value={{ isDarkMode, toggleDarkMode }}>
      {children}
    </DarkModeContext.Provider>
  );
}

export function useDarkMode() {
  const context = useContext(DarkModeContext);
  if (context === undefined) {
    throw new Error('useDarkMode must be used within a DarkModeProvider');
  }
  return context;
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    // Provide a fallback during SSR or if used outside provider
    return {
      theme: 'system' as Theme,
      actualTheme: 'light' as 'light' | 'dark',
      setTheme: () => {},
      toggleTheme: () => {},
    };
  }
  return context;
}

export function ThemeProvider({
  children,
  defaultTheme = 'system',
  storageKey = 'rbi-theme',
}: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(defaultTheme);
  const [actualTheme, setActualTheme] = useState<'light' | 'dark'>('light');
  const [mounted, setMounted] = useState(false);

  // Set mounted flag after hydration
  useEffect(() => {
    setMounted(true);
  }, []);

  // Initialize theme from localStorage or system preference
  useEffect(() => {
    if (!mounted) return;

    const storedTheme = localStorage.getItem(storageKey) as Theme;
    if (storedTheme && ['light', 'dark', 'system'].includes(storedTheme)) {
      setTheme(storedTheme);
    } else {
      // Default to system preference if no stored preference
      setTheme('system');
    }
  }, [storageKey, mounted]);

  // Update actual theme when theme changes
  useEffect(() => {
    if (!mounted) return;

    let newActualTheme: 'light' | 'dark';

    if (theme === 'system') {
      newActualTheme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
    } else {
      newActualTheme = theme;
    }

    setActualTheme(newActualTheme);

    // Apply theme to document
    const root = document.documentElement;
    root.classList.remove('light', 'dark');
    root.classList.add(newActualTheme);

    // Update data attribute for CSS selectors
    root.setAttribute('data-theme', newActualTheme);

    // Store theme preference
    localStorage.setItem(storageKey, theme);
  }, [theme, storageKey, mounted]);

  // Listen to system theme changes
  useEffect(() => {
    if (!mounted || theme !== 'system') return;

    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
    const handleChange = (e: MediaQueryListEvent) => {
      const newTheme = e.matches ? 'dark' : 'light';
      setActualTheme(newTheme);

      // Apply theme change to document immediately
      const root = document.documentElement;
      root.classList.remove('light', 'dark');
      root.classList.add(newTheme);
      root.setAttribute('data-theme', newTheme);
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, [theme, mounted]);

  const toggleTheme = () => {
    if (theme === 'light') {
      setTheme('dark');
    } else if (theme === 'dark') {
      setTheme('system');
    } else {
      setTheme('light');
    }
  };

  const value: ThemeContextType = {
    theme,
    actualTheme,
    setTheme,
    toggleTheme,
  };

  return <ThemeContext.Provider value={value}>{children}</ThemeContext.Provider>;
}

export function getColor(colorPath: string): string {
  const keys = colorPath.split('.');
  let current: unknown = designTokens.colors;

  for (const key of keys) {
    if (!current || typeof current !== 'object' || !(key in current)) {
      console.warn(`Color token '${colorPath}' not found. Using fallback.`);
      return designTokens.colors.neutral[500];
    }
    current = (current as Record<string, unknown>)[key];
  }

  return current as string;
}

export function getSpacing(size: keyof typeof designTokens.spacing): string {
