export function useSelector<T extends { value: string; label: string }>({
  value,
  onChange,
  searchFn,
  loadSelectedFn,
  debounceMs = 300,
  minSearchLength = 2,
  formatDisplayValue,
}: UseSelectorOptions<T>) {
  const [searchTerm, setSearchTerm] = useState('');
  const [options, setOptions] = useState<T[]>([]);
  const [loading, setLoading] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState<T | null>(null);
  const debounceRef = useRef<NodeJS.Timeout | null>(null);

  const loadOptions = useCallback(
    async (search: string) => {
      if (search.length < minSearchLength) {
        setOptions([]);
        return;
      }

      try {
        setLoading(true);
        const results = await searchFn(search);
        setOptions(results);
      } catch (error) {
        logger.error('Selector search error', { error });
        setOptions([]);
      } finally {
        setLoading(false);
      }
    },
    [searchFn, minSearchLength]
  );

  useEffect(() => {
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
    }

    debounceRef.current = setTimeout(() => {
      loadOptions(searchTerm);
    }, debounceMs);

    return () => {
      if (debounceRef.current) {
        clearTimeout(debounceRef.current);
      }
    };
  }, [searchTerm, loadOptions, debounceMs]);

  useEffect(() => {
    if (value && !selectedOption && loadSelectedFn) {
      loadSelectedFn(value).then(option => {
        if (option) {
          setSelectedOption(option);
          setSearchTerm(formatDisplayValue ? formatDisplayValue(option) : option.label);
        }
      });
    }
  }, [value, selectedOption, loadSelectedFn, formatDisplayValue]);

  const handleSearchChange = useCallback((term: string) => {
    setSearchTerm(term);

    if (selectedOption && term !== (formatDisplayValue ? formatDisplayValue(selectedOption) : selectedOption.label)) {
      setSelectedOption(null);
      onChange('');
    }
  }, [selectedOption, onChange, formatDisplayValue]);

  const handleOpenChange = useCallback((open: boolean) => {
    setIsOpen(open);
  }, []);

  return {
    searchTerm,
    options,
    loading,
    isOpen,
    selectedOption,
    onSearchChange: handleSearchChange,
    onOpenChange: handleOpenChange,
    setSearchTerm,
    setOptions,
    setSelectedOption,
  };
}

export function useUserBarangay(): UserBarangayInfo {
  const { userProfile, loading: authLoading, user } = useAuth();
  const [address, setAddress] = useState<AddressHierarchy | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadBarangayAddress = async () => {
      // Wait for auth to load
      if (authLoading) {
        logDebug('waiting for auth to load', { component: 'useUserBarangay' });
        return;
      }

      // User must be authenticated
      if (!user) {
        logDebug('user not authenticated, skipping barangay load', { component: 'useUserBarangay' });
        setError(null); // Don't show error for unauthenticated users - let ProtectedRoute handle it
        setLoading(false);
        return;
      }

      // User must have a barangay assignment
      if (!userProfile?.barangay_code) {
        logDebug('no barangay assignment found', { component: 'useUserBarangay' });
        setError('No barangay assignment found. Please contact your administrator.');
        setLoading(false);
        setAddress(null);
        return;
      }

      try {
        setLoading(true);
        setError(null);

        // Get complete address hierarchy for the user's barangay
        logDebug('Loading address for barangay code', { 
          component: 'useUserBarangay', 
          data: { barangayCode: userProfile.barangay_code } 
        });
        const addressData = await getCompleteAddress(userProfile.barangay_code);

        if (addressData) {
          logDataLoad('barangay address', 1);
          setAddress(addressData);
        } else {
          logError('No address data returned', { 
            component: 'useUserBarangay', 
            data: { barangayCode: userProfile.barangay_code } 
          });
          setError('Unable to load barangay address information');
        }
      } catch (err) {
        logError('Error loading barangay address', { 
          component: 'useUserBarangay', 
          error: err as Error 
        });
        setError('Failed to load barangay information');
      } finally {
        setLoading(false);
      }
    };

    loadBarangayAddress();
  }, [authLoading, user, userProfile]);

  return {
    barangayCode: userProfile?.barangay_code || null,
    address,
    loading: authLoading || loading,
    error,
  };
}

export function useBarangayAccess(barangayCode: string): boolean {
  const { canAccessBarangay } = useAuth();
  return canAccessBarangay(barangayCode);
}

export function useUserBarangays() {
  const { userProfile, loading } = useAuth();

  // For now, return the user's single barangay assignment
  // In the future, this could support multiple barangay access
  const barangayAccounts = userProfile?.barangay_code
    ? [
        {
          barangay_code: userProfile.barangay_code,
          is_primary: true,
        },
      ]
    : [];

  return {
    barangayAccounts,
    barangayCodes: barangayAccounts.map(account => account.barangay_code),
    loading,
  };
}

export function useFocusTrap(isActive: boolean = false) {
  const containerRef = useRef<HTMLDivElement>(null);
  const previouslyFocusedElement = useRef<HTMLElement | null>(null);

  useEffect(() => {
    if (!isActive || !containerRef.current) return;

    // Store the currently focused element
    previouslyFocusedElement.current = document.activeElement as HTMLElement;

    // Get all focusable elements within the container
    const getFocusableElements = () => {
      if (!containerRef.current) return [];

      const focusableSelectors = [
        'a[href]',
        'button:not([disabled])',
        'textarea:not([disabled])',
        'input:not([disabled])',
        'select:not([disabled])',
        '[tabindex]:not([tabindex="-1"])',
      ].join(',');

      return Array.from(
        containerRef.current.querySelectorAll<HTMLElement>(focusableSelectors)
      ).filter(el => el.offsetParent !== null); // Filter out hidden elements
    };

    // Focus the first focusable element
    const focusableElements = getFocusableElements();
    if (focusableElements.length > 0) {
      focusableElements[0].focus();
    }

    // Handle Tab key navigation
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key !== 'Tab') return;

      const focusableElements = getFocusableElements();
      if (focusableElements.length === 0) return;

      const firstElement = focusableElements[0];
      const lastElement = focusableElements[focusableElements.length - 1];

      // Shift+Tab on first element -> focus last element
      if (event.shiftKey && document.activeElement === firstElement) {
        event.preventDefault();
        lastElement.focus();
      }
      // Tab on last element -> focus first element
      else if (!event.shiftKey && document.activeElement === lastElement) {
        event.preventDefault();
        firstElement.focus();
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);

      // Restore focus to the previously focused element
      if (previouslyFocusedElement.current) {
        previouslyFocusedElement.current.focus();
      }
    };
  }, [isActive]);

  return containerRef;
}

export function useArrowKeyNavigation(itemsCount: number, onSelect?: (index: number) => void) {
  const currentIndex = useRef(0);
  const containerRef = useRef<HTMLElement>(null);

  const handleKeyDown = useCallback(
    (event: KeyboardEvent) => {
      if (!containerRef.current) return;

      const items = Array.from(
        containerRef.current.querySelectorAll('[role="option"], [role="menuitem"], [role="tab"]')
      ) as HTMLElement[];

      if (items.length === 0) return;

      let nextIndex = currentIndex.current;

      switch (event.key) {
        case 'ArrowDown':
          event.preventDefault();
          nextIndex = Math.min(currentIndex.current + 1, items.length - 1);
          break;
        case 'ArrowUp':
          event.preventDefault();
          nextIndex = Math.max(currentIndex.current - 1, 0);
          break;
        case 'Home':
          event.preventDefault();
          nextIndex = 0;
          break;
        case 'End':
          event.preventDefault();
          nextIndex = items.length - 1;
          break;
        case 'Enter':
        case ' ':
          event.preventDefault();
          if (onSelect) {
            onSelect(currentIndex.current);
          }
          break;
        default:
          return;
      }

      if (nextIndex !== currentIndex.current) {
        currentIndex.current = nextIndex;
        items[nextIndex]?.focus();
      }
    },
    [onSelect]
  );

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    container.addEventListener('keydown', handleKeyDown);
    return () => container.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  return containerRef;
}

