export function handleValidationError(error: z.ZodError): {
  code: ErrorCode;
  message: string;
  details: Array<{ field: string; message: string }>;
} {
  const details = error.errors.map(err => ({
    field: err.path.join('.'),
    message: err.message
  }));

  return {
    code: ErrorCode.VALIDATION_ERROR,
    message: 'Invalid input data',
    details
  };
}

export function validatePagination(params: URLSearchParams): {
  page: number;
  limit: number;
  offset: number;
} {
  const result = paginationSchema.parse({
    page: params.get('page'),
    limit: params.get('limit')
  });

  return {
    page: result.page,
    limit: result.limit,
    offset: (result.page - 1) * result.limit
  };
}

export function validateSort(params: URLSearchParams, allowedFields: string[]): {
  field: string;
  order: 'asc' | 'desc';
} | null {
  const sort = params.get('sort');
  const order = params.get('order') as 'asc' | 'desc' || 'desc';

  if (!sort || !allowedFields.includes(sort)) {
    return null;
  }

  return { field: sort, order };
}

  handler: (request: Request, validatedData: T) => Promise<Response>
) {
  return async (request: Request): Promise<Response> => {
    try {
      const body = await request.json();
      const validatedData = schema.parse(body);
      return handler(request, validatedData);
    } catch (error) {
      if (error instanceof z.ZodError) {
        const validationError = handleValidationError(error);
        return new Response(
          JSON.stringify({
            error: validationError,
            timestamp: new Date().toISOString(),
            path: new URL(request.url).pathname
          }),
          {
            status: 422,
            headers: { 'Content-Type': 'application/json' }
          }
        );
      }

      // Re-throw non-validation errors
      throw error;
    }
  };
}

export function getErrorMessage(error: string): string {
  // First try exact match
  const exactMatch = AUTH_ERROR_MESSAGES[error as keyof typeof AUTH_ERROR_MESSAGES];
  if (exactMatch) {
    return exactMatch;
  }

  // Try pattern matching for dynamic error messages
  if (error.startsWith('User not found in authentication system with ID:')) {
    return 'Account setup is taking longer than expected. Please try again in a few moments.';
  }

  if (error.includes('Role "') && error.includes('" not found in system')) {
    return 'System configuration error. Please contact technical support.';
  }

  if (error.startsWith('Invalid or inactive barangay code:')) {
    return 'The selected barangay is not valid or is currently inactive. Please choose a different barangay.';
  }

  // Default fallback
  return error;
}

export function getStatusCodeForError(errorCode?: string): number {
  const errorMap: Record<string, number> = {
    'DUPLICATE_PROFILE': 409, // Conflict
    'INVALID_REFERENCE': 400, // Bad Request
    'USER_NOT_FOUND': 404,    // Not Found
  };

  return errorMap[errorCode || ''] || 500; // Default to 500
}

export const registerUser = async (data: RegistrationData) => {
  try {
    logger.debug('Starting user registration for:', data.email);

    // 1. Create auth user
    const { data: authData, error: authError } = await supabase.auth.signUp({
      email: data.email,
      password: data.password,
      options: {
        data: {
          first_name: data.first_name,
          last_name: data.last_name,
          mobile_number: data.mobile_number,
          barangay_code: data.barangay_code,
        },
      },
    });

    if (authError) {
      logger.error('Auth registration failed:', authError.message);
      throw authError;
    }

    if (!authData.user) {
      throw new Error('User creation failed - no user returned');
    }

    // 2. The user profile will be automatically created by the database trigger
    logger.info('User registered successfully:', data.email);

    return {
      user: authData.user,
      session: authData.session,
      needsEmailConfirmation: !authData.session,
    };

  } catch (error) {
    logger.error('Registration error:', error);
    throw error;
  }
};

export const signInUser = async (email: string, password: string) => {
  try {
    logger.debug('Attempting sign in for:', email);

    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      logger.error('Sign in failed:', error.message);
      throw error;
    }

    // Get user profile
    const profile = await getUserProfile(data.user.id);

    if (!profile?.is_active) {
      throw new Error('Account is deactivated. Please contact your administrator.');
    }

    logger.info('User signed in successfully:', email);
    return { user: data.user, session: data.session, profile };

  } catch (error) {
    logger.error('Sign in error:', error);
    throw error;
  }
};

