export function extractPhilSysLast4(philsysNumber: string): string {
  if (!philsysNumber) {
    throw new Error('PhilSys card number is required');
  }

  // Remove all non-digit characters and get last 4 digits
  const digitsOnly = philsysNumber.replace(/\D/g, '');

  if (digitsOnly.length < 4) {
    throw new Error('PhilSys card number must contain at least 4 digits');
  }

  return digitsOnly.slice(-4);
}

export function maskPhilSysNumber(philsysNumber: string): string {
  if (!philsysNumber) {
    return '';
  }

  const last4 = extractPhilSysLast4(philsysNumber);
  return `****-****-****-${last4}`;
}

export function validatePhilSysFormat(philsysNumber: string): boolean {
  if (!philsysNumber) {
    return false;
  }

  // PhilSys format: 1234-5678-9012-3456 (16 digits with dashes)
  const philsysPattern = /^\d{4}-\d{4}-\d{4}-\d{4}$/;
  return philsysPattern.test(philsysNumber);
}

function validateCSRFSecret() {
  if (!CSRF_SECRET && process.env.NODE_ENV === 'production') {
    throw new Error(
      'CSRF_SECRET environment variable must be set in production. Generate a secure random string of at least 32 characters.'
    );
  }
}

function toBase64Url(str: string): string {
  return str.replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}

export function generateCSRFToken(): CSRFToken {
  validateCSRFSecret();
  const timestamp = Date.now();
  const randomToken = toBase64Url(randomBytes(32).toString('base64'));

  // Create signature using HMAC
  const data = `${randomToken}:${timestamp}`;
  const signature = toBase64Url(
    createHash('sha256')
      .update(data + SECRET)
      .digest('base64')
  );

  return {
    token: randomToken,
    timestamp,
    signature,
  };
}

export function verifyCSRFToken(token: string, timestamp: number, signature: string): boolean {
  try {
    validateCSRFSecret();
    // Check if token has expired
    if (Date.now() - timestamp > TOKEN_EXPIRY) {
      console.warn('[CSRF] Token expired');
      return false;
    }

    // Recreate the signature
    const data = `${token}:${timestamp}`;
    const expectedSignature = toBase64Url(
      createHash('sha256')
        .update(data + SECRET)
        .digest('base64')
    );

    // Use timing-safe comparison to prevent timing attacks
    const expectedBuffer = Buffer.from(expectedSignature, 'base64');
    const actualBuffer = Buffer.from(signature, 'base64');

    if (expectedBuffer.length !== actualBuffer.length) {
      return false;
    }

    return timingSafeEqual(expectedBuffer, actualBuffer);
  } catch (error) {
    console.error('[CSRF] Token verification error:', error);
    return false;
  }
}

export function createCSRFTokenString(): string {
  const csrfData = generateCSRFToken();
  return `${csrfData.token}:${csrfData.timestamp}:${csrfData.signature}`;
}

export function verifyCSRFTokenString(tokenString: string): boolean {
  try {
    const parts = tokenString.split(':');
    if (parts.length !== 3) {
      return false;
    }

    const [token, timestampStr, signature] = parts;
    const timestamp = parseInt(timestampStr, 10);

    if (isNaN(timestamp)) {
      return false;
    }

    return verifyCSRFToken(token, timestamp, signature);
  } catch (error) {
    console.error('[CSRF] Token parsing error:', error);
    return false;
  }
}

export function useCSRFToken() {
  // In a real implementation, this would be stored in session or secure cookie
  const getToken = (): string => {
    if (typeof window !== 'undefined') {
      let token = sessionStorage.getItem('csrf_token');
      if (!token) {
        token = createCSRFTokenString();
        sessionStorage.setItem('csrf_token', token);
      }
      return token;
    }
    return createCSRFTokenString();
  };

  const validateToken = (token: string): boolean => {
    return verifyCSRFTokenString(token);
  };

  const refreshToken = (): string => {
    const newToken = createCSRFTokenString();
    if (typeof window !== 'undefined') {
      sessionStorage.setItem('csrf_token', newToken);
    }
    return newToken;
  };

  return {
    getToken,
    validateToken,
    refreshToken,
  };
}

export function validateCSRFMiddleware(token: string | undefined, method: string): boolean {
  // Only validate POST, PUT, DELETE, PATCH requests
  const protectedMethods = ['POST', 'PUT', 'DELETE', 'PATCH'];

  if (!protectedMethods.includes(method.toUpperCase())) {
    return true; // GET requests don't need CSRF protection
  }

  if (!token) {
    console.warn('[CSRF] Missing CSRF token for protected request');
    return false;
  }

  return verifyCSRFTokenString(token);
}

export function getCSRFMetaTags() {
