function validateFileSize(file: File): string[] {
  const errors: string[] = [];

  if (file.size > MAX_FILE_SIZE) {
    errors.push(
      `File size (${(file.size / 1024 / 1024).toFixed(2)}MB) exceeds maximum allowed size (${MAX_FILE_SIZE / 1024 / 1024}MB)`
    );
  }

  if (file.size === 0) {
    errors.push('File is empty');
  }

  return errors;
}

function validateFileType(file: File): string[] {
  const errors: string[] = [];

  // Check MIME type
  if (!ALLOWED_MIME_TYPES.includes(file.type)) {
    errors.push(
      `File type "${file.type}" is not allowed. Allowed types: ${ALLOWED_MIME_TYPES.join(', ')}`
    );
  }

  // Check file extension
  const extension = '.' + file.name.split('.').pop()?.toLowerCase();
  if (!ALLOWED_EXTENSIONS.includes(extension)) {
    errors.push(
      `File extension "${extension}" is not allowed. Allowed extensions: ${ALLOWED_EXTENSIONS.join(', ')}`
    );
  }

  return errors;
}

function validateFileName(fileName: string): string[] {
  const errors: string[] = [];

  // Check for directory traversal
  if (fileName.includes('..') || fileName.includes('/') || fileName.includes('\\')) {
    errors.push('File name contains invalid characters (directory traversal)');
  }

  // Check for null bytes
  if (fileName.includes('\0')) {
    errors.push('File name contains null bytes');
  }

  // Check for excessively long names
  if (fileName.length > 255) {
    errors.push('File name is too long (max 255 characters)');
  }

  // Check for reserved names (Windows)
  const reservedNames = [
    'CON',
    'PRN',
    'AUX',
    'NUL',
    'COM1',
    'COM2',
    'COM3',
    'COM4',
    'COM5',
    'COM6',
    'COM7',
    'COM8',
    'COM9',
    'LPT1',
    'LPT2',
    'LPT3',
    'LPT4',
    'LPT5',
    'LPT6',
    'LPT7',
    'LPT8',
    'LPT9',
  ];
  const baseName = fileName.split('.')[0].toUpperCase();
  if (reservedNames.includes(baseName)) {
    errors.push(`File name "${baseName}" is reserved and not allowed`);
  }

  return errors;
}

export function sanitizeFileName(fileName: string): string {
  return fileName
    .replace(/[^a-zA-Z0-9._-]/g, '_') // Replace unsafe characters
    .replace(/_{2,}/g, '_') // Replace multiple underscores
    .substring(0, 100) // Limit length
    .toLowerCase();
}

export function generateUniqueFileName(originalName: string): string {
  const timestamp = Date.now();
  const random = Math.random().toString(36).substring(2, 8);
  const extension = '.' + originalName.split('.').pop()?.toLowerCase();
  const baseName = sanitizeFileName(originalName.replace(/\.[^/.]+$/, ''));

  return `${baseName}_${timestamp}_${random}${extension}`;
}

export function useVirtualScroll({
  itemCount,
  itemHeight,
  containerHeight,
  overscan = 5
}: {
  itemCount: number;
  itemHeight: number;
  containerHeight: number;
  overscan?: number;
}) {
  const [scrollTop, setScrollTop] = useState(0);

  const visibleRange = useMemo(() => {
    const startIndex = Math.floor(scrollTop / itemHeight);
    const endIndex = Math.min(
      itemCount - 1,
      Math.floor((scrollTop + containerHeight) / itemHeight)
    );

    return {
      start: Math.max(0, startIndex - overscan),
      end: Math.min(itemCount - 1, endIndex + overscan)
    };
  }, [scrollTop, itemHeight, containerHeight, itemCount, overscan]);

  const totalHeight = itemCount * itemHeight;
  const offsetY = visibleRange.start * itemHeight;

  return {
    visibleRange,
    totalHeight,
    offsetY,
    setScrollTop
  };
}

export function usePerformanceMonitor(componentName: string) {
  const renderStartTime = useRef<number>();
  const renderCount = useRef(0);

  useEffect(() => {
    renderStartTime.current = performance.now();
    renderCount.current += 1;
  });

  useEffect(() => {
    const endTime = performance.now();
    const renderTime = endTime - (renderStartTime.current || endTime);

    if (renderTime > 16) { // > 16ms is concerning for 60fps
      logInfo(`Performance warning: ${componentName} render took ${renderTime.toFixed(2)}ms`, {
        component: componentName,
        action: 'performance_warning',
        data: { renderTime, renderCount: renderCount.current }
      });
    }
  });

  return {
    renderCount: renderCount.current
  };
}

async timeAsync<T>(name: string, operation: () => Promise<T>): Promise<T> {
  if (!this.isEnabled) return operation();

  const endTiming = this.startTiming(name);
  try {
    const result = await operation();
    endTiming();
    return result;
  } catch (error) {
    endTiming();
    throw error;
  }
}

export function timed(name?: string) {
  return function (target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;
    const timerName = name || `${target.constructor.name}.${propertyKey}`;

    descriptor.value = function (...args: any[]) {
      const endTiming = performanceMonitor.startTiming(timerName);
      try {
        const result = originalMethod.apply(this, args);

        // Handle async methods
        if (result instanceof Promise) {
          return result.finally(() => endTiming());
        }

        endTiming();
        return result;
      } catch (error) {
        endTiming();
        throw error;
      }
    };

    return descriptor;
  };
}

export function usePerformanceTracking(componentName: string) {
  const renderStart = performance.now();

  return {
    onRenderComplete: (propsSize?: number, isRerender = false) => {
      const renderTime = performance.now() - renderStart;
      performanceMonitor.recordComponentRender(componentName, renderTime, propsSize, isRerender);
    },
  };
}

