export function sanitizeSearchQuery(query: string): string {
  return query
    .trim()
    .replace(/[<>\"'&%]/g, '') // Remove XSS and SQL injection characters
    .substring(0, 100); // Limit length
}

export function validateUserPermission(userRole: string, requiredPermission: string): boolean {
  const rolePermissions: Record<string, string[]> = {
    super_admin: ['all'],
    barangay_admin: ['residents:crud', 'households:crud', 'settings:manage'],
    clerk: ['residents:crud', 'households:crud'],
    resident: ['residents:read_own'],
  };

  const permissions = rolePermissions[userRole] || [];
  return permissions.includes('all') || permissions.includes(requiredPermission);
}

export function middleware(_request: NextRequest) {
  const response = NextResponse.next();

  // Content Security Policy
  const cspHeader = `
    default-src 'self';
    script-src 'self' 'unsafe-eval' 'unsafe-inline' https://*.supabase.co https://vercel.live https://va.vercel-scripts.com;
    style-src 'self' 'unsafe-inline';
    img-src 'self' blob: data: https://*.supabase.co;
    font-src 'self';
    object-src 'none';
    base-uri 'self';
    form-action 'self';
    frame-ancestors 'none';
    block-all-mixed-content;
    upgrade-insecure-requests;
    connect-src 'self' https://*.supabase.co wss://*.supabase.co https://vercel.live;
  `
    .replace(/\s{2,}/g, ' ')
    .trim();

  // Security headers
  response.headers.set('Content-Security-Policy', cspHeader);
  response.headers.set('X-Frame-Options', 'DENY');
  response.headers.set('X-Content-Type-Options', 'nosniff');
  response.headers.set('Referrer-Policy', 'strict-origin-when-cross-origin');
  response.headers.set('Permissions-Policy', 'camera=(), microphone=(), geolocation=()');

  // Remove X-Powered-By header
  response.headers.delete('X-Powered-By');

  // Strict Transport Security (only for production)
  if (process.env.NODE_ENV === 'production') {
    response.headers.set(
      'Strict-Transport-Security',
      'max-age=63072000; includeSubDomains; preload'
    );
  }

  return response;
}

function appReducer(state: AppState, action: AppAction): AppState {
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.payload,
        isAuthenticated: !!action.payload,
      };

    case 'SET_LOADING':
      return {
        ...state,
        isLoading: action.payload,
      };

    case 'SET_THEME':
      return {
        ...state,
        theme: action.payload,
      };

    case 'SET_PRIMARY_COLOR':
      return {
        ...state,
        primaryColor: action.payload,
      };

    case 'TOGGLE_SIDEBAR':
      return {
        ...state,
        sidebarOpen: !state.sidebarOpen,
      };

    case 'TOGGLE_MOBILE_MENU':
      return {
        ...state,
        mobileMenuOpen: !state.mobileMenuOpen,
      };

    case 'ADD_NOTIFICATION':
      return {
        ...state,
        notifications: [
          ...state.notifications,
          {
            ...action.payload,
            id: `notif-${Date.now()}-${Math.random()}`,
            timestamp: Date.now(),
          },
        ].slice(-10), // Keep only last 10 notifications
      };

    case 'REMOVE_NOTIFICATION':
      return {
        ...state,
        notifications: state.notifications.filter(n => n.id !== action.payload),
      };

    case 'SET_LOCALE':
      return {
        ...state,
        locale: action.payload,
      };

    case 'SET_TIMEZONE':
      return {
        ...state,
        timezone: action.payload,
      };

    case 'RESET_STATE':
      return initialState;

    default:
      return state;
  }
}

export function AppProvider({ children, initialUser = null }: AppProviderProps) {
  const [state, dispatch] = useReducer(appReducer, {
    ...initialState,
    user: initialUser,
    isAuthenticated: !!initialUser,
    isLoading: false,
  });

  // Apply theme
  useEffect(() => {
    const root = document.documentElement;

    if (state.theme === 'system') {
      const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      root.classList.toggle('dark', prefersDark);
    } else {
      root.classList.toggle('dark', state.theme === 'dark');
    }

    // Set primary color CSS variable
    root.style.setProperty('--color-primary', state.primaryColor);
  }, [state.theme, state.primaryColor]);

  // Persist settings to localStorage
  useEffect(() => {
    try {
      const settings = {
        theme: state.theme,
        primaryColor: state.primaryColor,
        locale: state.locale,
        timezone: state.timezone,
      };
      localStorage.setItem('app-settings', JSON.stringify(settings));
    } catch (error) {
      logger.error('Failed to save settings', { error });
    }
  }, [state.theme, state.primaryColor, state.locale, state.timezone]);

  // Load settings from localStorage on mount
  useEffect(() => {
    try {
      const savedSettings = localStorage.getItem('app-settings');
      if (savedSettings) {
        const settings = JSON.parse(savedSettings);
        if (settings.theme) dispatch({ type: 'SET_THEME', payload: settings.theme });
        if (settings.primaryColor) dispatch({ type: 'SET_PRIMARY_COLOR', payload: settings.primaryColor });
        if (settings.locale) dispatch({ type: 'SET_LOCALE', payload: settings.locale });
        if (settings.timezone) dispatch({ type: 'SET_TIMEZONE', payload: settings.timezone });
      }
    } catch (error) {
      logger.error('Failed to load settings', { error });
    }
  }, []);

  // Auto-dismiss old notifications
  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      const oldNotifications = state.notifications.filter(
        n => now - n.timestamp > 10000 // 10 seconds
      );
      oldNotifications.forEach(n => {
        dispatch({ type: 'REMOVE_NOTIFICATION', payload: n.id });
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [state.notifications]);

  // Context value
  const contextValue: AppContextValue = {
    ...state,

    // Auth actions
    login: (user: User) => {
      dispatch({ type: 'SET_USER', payload: user });
      logger.info('User logged in', { userId: user.id });
    },

    logout: () => {
      dispatch({ type: 'SET_USER', payload: null });
      dispatch({ type: 'RESET_STATE' });
      logger.info('User logged out');
    },

    // Theme actions
    setTheme: (theme) => dispatch({ type: 'SET_THEME', payload: theme }),
    setPrimaryColor: (color) => dispatch({ type: 'SET_PRIMARY_COLOR', payload: color }),

    // UI actions
    toggleSidebar: () => dispatch({ type: 'TOGGLE_SIDEBAR' }),
    toggleMobileMenu: () => dispatch({ type: 'TOGGLE_MOBILE_MENU' }),

    // Notification actions
    notify: (type, message) => {
      dispatch({ type: 'ADD_NOTIFICATION', payload: { type, message } });
    },

    dismissNotification: (id) => {
      dispatch({ type: 'REMOVE_NOTIFICATION', payload: id });
    },

    // Settings actions
    setLocale: (locale) => dispatch({ type: 'SET_LOCALE', payload: locale }),
    setTimezone: (timezone) => dispatch({ type: 'SET_TIMEZONE', payload: timezone }),
  };

  return (
    <AppContext.Provider value={contextValue}>
      <QueryClientProvider client={queryClient}>
        {children}
        {process.env.NODE_ENV === 'development' && <ReactQueryDevtools />}
      </QueryClientProvider>
    </AppContext.Provider>
  );
}

export function useApp() {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within AppProvider');
  }
  return context;
}

export function useAuth() {
  const { user, isAuthenticated, isLoading, login, logout } = useApp();
  return { user, isAuthenticated, isLoading, login, logout };
}

export function useTheme() {
  const { theme, primaryColor, setTheme, setPrimaryColor } = useApp();
  return { theme, primaryColor, setTheme, setPrimaryColor };
}

export function useNotifications() {
  const { notifications, notify, dismissNotification } = useApp();
  return { notifications, notify, dismissNotification };
}

export function useUI() {
  const { sidebarOpen, mobileMenuOpen, toggleSidebar, toggleMobileMenu } = useApp();
  return { sidebarOpen, mobileMenuOpen, toggleSidebar, toggleMobileMenu };
}

export function RootProvider({ children, initialUser }: RootProviderProps) {
  return (
    <ErrorBoundary level="page" resetOnPropsChange>
      <AppProvider initialUser={initialUser}>
        <ErrorBoundary level="section">
          {children}
        </ErrorBoundary>
      </AppProvider>
    </ErrorBoundary>
  );
}

