              href="/residents"
              className="inline-flex items-center rounded-md border border-gray-200 bg-white px-3 py-2 text-sm font-medium text-gray-600 shadow-xs hover:bg-gray-50"
              style={{
                display: 'inline-flex',
                alignItems: 'center',
                borderRadius: '6px',
