      MAX_ATTEMPTS: 5,
      WINDOW_MS: 300000,
    },
  },
}));

const mockCreateResident = jest.fn();

describe('Security Tests - Philippine Regulatory Compliance', () => {
  const mockRouter = { push: jest.fn() };
  const mockUser = { id: 'user123', role: 'barangay_official' };
  const mockUserProfile = { barangay_code: '042114014' };

  beforeEach(() => {
    jest.clearAllMocks();

    (useRouter as jest.Mock).mockReturnValue(mockRouter);

    (require('@/contexts/AuthContext') as any).useAuth.mockReturnValue({
      user: mockUser,
      userProfile: mockUserProfile,
      session: { access_token: 'token' },
    });

    (require('@/hooks/crud/useResidentOperations') as any).useResidentOperations.mockReturnValue({
      createResident: mockCreateResident,
      isSubmitting: false,
      validationErrors: {},
    });

    mockValidateFormData.mockReturnValue({ isValid: true, errors: {} });
    mockPrepareFormSubmission.mockReturnValue({
      transformedData: {},
      auditInfo: {
        userId: 'user123',
        sessionId: 'session123',
        timestamp: new Date().toISOString(),
      },
    });
    mockCheckRateLimit.mockReturnValue(true);
  });

  describe('URL Parameter Injection Prevention', () => {
    test('should sanitize XSS attempts in suggested_name parameter', () => {
      const xssPayload = '<script>alert("XSS")</script>';
      const mockSearchParams = {
        get: jest.fn(key => {
          if (key === 'suggested_name') return xssPayload;
          return null;
        }),
      };

      mockSanitizeNameInput.mockReturnValue(''); // Sanitized to empty string
      (useSearchParams as jest.Mock).mockReturnValue(mockSearchParams);

      render(<CreateResidentPage />);

      // Verify sanitization was called
      expect(mockSanitizeNameInput).toHaveBeenCalledWith(xssPayload);

      // Should not display script content
      expect(screen.queryByText('script')).not.toBeInTheDocument();
      expect(screen.queryByText('alert')).not.toBeInTheDocument();

      // Should log security event
      expect(mockAuditLogger.info).toHaveBeenCalledWith(
        expect.stringContaining('URL parameter processing'),
        expect.objectContaining({
          eventType: 'URL_PARAM_PROCESSING',
          userId: 'user123',
        })
      );
    });

    test('should handle javascript: protocol injection attempts', () => {
      const jsPayload = 'javascript:void(0)';
      const mockSearchParams = {
        get: jest.fn(key => {
          if (key === 'suggested_name') return jsPayload;
          return null;
        }),
      };

      mockSanitizeNameInput.mockReturnValue('void'); // Sanitized
      (useSearchParams as jest.Mock).mockReturnValue(mockSearchParams);

      expect(() => render(<CreateResidentPage />)).not.toThrow();
      expect(mockSanitizeNameInput).toHaveBeenCalledWith(jsPayload);
    });

    test('should limit name length to prevent buffer overflow attacks', () => {
      const longName = 'A'.repeat(1000);
      const mockSearchParams = {
        get: jest.fn(key => {
          if (key === 'suggested_name') return longName;
          return null;
        }),
      };

      mockSanitizeNameInput.mockReturnValue('A'.repeat(100)); // Truncated
      (useSearchParams as jest.Mock).mockReturnValue(mockSearchParams);

      render(<CreateResidentPage />);

      expect(mockSanitizeNameInput).toHaveBeenCalledWith(longName);
      expect(screen.getByTestId('resident-form')).toBeInTheDocument();
    });

    test('should log security validation failures', () => {
      const maliciousName = '<img src=x onerror=alert(1)>';
      const mockSearchParams = {
        get: jest.fn(key => {
          if (key === 'suggested_name') return maliciousName;
          return null;
        }),
      };

      // Mock parseFullName to throw error for invalid input
      (require('@/utils/resident-form-utils') as any).parseFullName.mockImplementation(() => {
        throw new Error('Invalid name format');
      });

      mockSanitizeNameInput.mockReturnValue('img src=x onerror=alert(1)');
      (useSearchParams as jest.Mock).mockReturnValue(mockSearchParams);

      render(<CreateResidentPage />);

      expect(mockAuditLogger.info).toHaveBeenCalledWith(
        expect.stringContaining('URL parameter validation failed'),
        expect.objectContaining({
          eventType: 'URL_PARAM_VALIDATION_FAILED',
          parameterType: 'suggested_name',
        })
      );
    });
  });

  describe('Console Log Security Compliance (RA 10173)', () => {
    let consoleSpy: jest.SpyInstance;

    beforeEach(() => {
      consoleSpy = jest.spyOn(console, 'log').mockImplementation();
    });

    afterEach(() => {
      consoleSpy.mockRestore();
    });

    test('should not log sensitive form data to console', async () => {
      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Check that console.log was never called with sensitive data patterns
      const logCalls = consoleSpy.mock.calls.flat();

      expect(logCalls).not.toEqual(
        expect.arrayContaining([
          expect.stringMatching(/Raw form data/i),
          expect.stringMatching(/philsys.*number/i),
          expect.stringMatching(/voter.*value/i),
        ])
      );
    });

    test('should use RA 10173 compliant logging instead of console.log', async () => {
      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Verify compliant logging was used
      expect(mockPhilippineCompliantLogger.debug).toHaveBeenCalledWith(
        expect.stringContaining('Form processing initiated'),
        expect.objectContaining({
          complianceNote: 'RA_10173_COMPLIANT_DEV_LOG',
        })
      );

      expect(mockAuditLogger.info).toHaveBeenCalledWith(
        expect.stringContaining('Resident registration attempt'),
        expect.objectContaining({
          complianceFramework: 'RA_10173_BSP_808',
        })
      );

      expect(mockNPCComplianceLogger.info).toHaveBeenCalledWith(
        expect.stringContaining('Data processing event'),
        expect.objectContaining({
          legalBasis: 'PERFORMANCE_OF_TASK_PUBLIC_INTEREST',
        })
      );
    });
  });

  describe('Rate Limiting Security', () => {
    test('should prevent form spam through rate limiting', async () => {
      mockCheckRateLimit.mockReturnValue(false); // Rate limit exceeded

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Should show rate limiting error
      await waitFor(() => {
        expect((require('react-hot-toast') as any).toast.error).toHaveBeenCalledWith(
          'Too many submission attempts. Please wait before trying again.'
        );
      });

      // Should not proceed with form submission
      expect(mockCreateResident).not.toHaveBeenCalled();
    });

    test('should allow submission when rate limit not exceeded', async () => {
      mockCheckRateLimit.mockReturnValue(true); // Rate limit OK
      mockCreateResident.mockResolvedValue({ success: true });

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      expect(mockCheckRateLimit).toHaveBeenCalledWith(
        'user123',
        5, // MAX_ATTEMPTS
        300000 // WINDOW_MS
      );

      await waitFor(() => {
        expect(mockCreateResident).toHaveBeenCalled();
      });
    });
  });

  describe('Input Validation Security', () => {
    test('should validate form data server-side even if client validation passes', async () => {
      mockValidateFormData.mockReturnValue({
        isValid: false,
        errors: { _form: 'Server validation failed' },
      });

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Should log validation failure
      expect(mockAuditLogger.info).toHaveBeenCalledWith(
        'Form validation failed',
        expect.objectContaining({
          eventType: 'VALIDATION_FAILED',
          errorCount: 1,
        })
      );

      // Should not proceed to submission
      expect(mockCreateResident).not.toHaveBeenCalled();
    });

    test('should sanitize all form inputs before processing', async () => {
      mockSanitizeInput.mockReturnValue('sanitized');

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Should call sanitization functions
      expect(mockPrepareFormSubmission).toHaveBeenCalled();
    });
  });

  describe('CSRF Protection', () => {
    test('should include CSRF token in form submissions', async () => {
      mockCreateResident.mockResolvedValue({ success: true });

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      await waitFor(() => {
        expect(mockCreateResident).toHaveBeenCalledWith(
          expect.objectContaining({
            csrfToken: 'csrf-token-123',
          })
        );
      });
    });
  });

  describe('Audit Trail Compliance (BSP Circular 808)', () => {
    test('should create comprehensive audit trail for all form interactions', async () => {
      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Verify all required audit events are logged
      const auditCalls = mockAuditLogger.info.mock.calls;
      const auditEventTypes = auditCalls.map(call => call[1]?.eventType);

      expect(auditEventTypes).toContain('RESIDENT_FORM_PROCESSING');
      expect(
        auditCalls.some(
          call =>
            call[1]?.complianceFramework === 'RA_10173_BSP_808' &&
            call[1]?.retentionPeriod === '7_YEARS'
        )
      ).toBe(true);
    });

    test('should log security events with proper classification', async () => {
      const maliciousInput = '<script>alert("test")</script>';
      const mockSearchParams = {
        get: jest.fn(() => maliciousInput),
      };

      mockSanitizeNameInput.mockReturnValue('');
      (useSearchParams as jest.Mock).mockReturnValue(mockSearchParams);

      render(<CreateResidentPage />);

      expect(mockAuditLogger.info).toHaveBeenCalledWith(
        expect.any(String),
        expect.objectContaining({
          complianceFramework: 'RA_10173_BSP_808',
          retentionPeriod: '7_YEARS',
        })
      );
    });
  });

  describe('Data Privacy Compliance (NPC Requirements)', () => {
    test('should log data processing activities per NPC guidelines', async () => {
      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      expect(mockNPCComplianceLogger.info).toHaveBeenCalledWith(
        'Data processing event',
        expect.objectContaining({
          dataCategory: 'PERSONAL_INFORMATION',
          processingPurpose: 'BARANGAY_RESIDENT_REGISTRATION',
          legalBasis: 'PERFORMANCE_OF_TASK_PUBLIC_INTEREST',
          consentStatus: 'OBTAINED',
        })
      );
    });

    test('should never log actual personal data content', async () => {
      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Check all logger calls to ensure no PII is logged
      const allLogCalls = [
        ...mockPhilippineCompliantLogger.debug.mock.calls,
        ...mockAuditLogger.info.mock.calls,
        ...mockNPCComplianceLogger.info.mock.calls,
      ];

      allLogCalls.forEach(call => {
        const logContent = JSON.stringify(call);

        // Should not contain actual personal data
        expect(logContent).not.toMatch(/John|Doe|1990-01-01/);
        expect(logContent).not.toMatch(/\d{4}-\d{4}-\d{4}/); // PhilSys pattern
        expect(logContent).not.toMatch(/\+639\d{9}/); // Phone pattern
      });
    });
  });

  describe('Error Handling Security', () => {
    test('should not expose sensitive information in error messages', async () => {
      mockValidateFormData.mockReturnValue({
        isValid: false,
        errors: { database: 'Connection failed to server 192.168.1.100' },
      });

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      // Should show generic error, not expose internal details
      await waitFor(() => {
        expect((require('react-hot-toast') as any).toast.error).toHaveBeenCalledWith(
          expect.not.stringMatching(/192\.168\.1\.100|server|database/i)
        );
      });
    });

    test('should log detailed errors for security monitoring', async () => {
      const securityError = new Error('Potential security violation detected');
      mockValidateFormData.mockImplementation(() => {
        throw securityError;
      });

      const user = userEvent.setup();
      render(<CreateResidentPage />);

      const submitButton = screen.getByTestId('submit-button');
      await user.click(submitButton);

      expect(mockAuditLogger.info).toHaveBeenCalledWith(
        'Form submission error',
        expect.objectContaining({
          eventType: 'FORM_SUBMISSION_ERROR',
          errorType: 'Error',
        })
      );
    });
  });
});

'use client';

import Link from 'next/link';
import { useRouter } from 'next/navigation';
import React, { useMemo, useCallback } from 'react';
import { toast } from 'react-hot-toast';

import { ResidentForm } from '@/components';
import { RATE_LIMITS } from '@/constants/resident-form';
import { useAuth } from '@/contexts';
import { useResidentOperations } from '@/hooks/crud/useResidentOperations';
import { useResidentFormURLParameters } from '@/hooks/useURLParameters';
import { useCSRFToken } from '@/lib/auth';
import {
  philippineCompliantLogger,
  auditLogger,
  npcComplianceLogger,
  generateSecureSessionId,
} from '@/lib/security/philippine-logging';
import { checkRateLimit, clearRateLimit, getRateLimitStatus } from '@/utils/input-sanitizer';
import { validateFormData, prepareFormSubmission } from '@/utils/resident-form-utils';

export const dynamic = 'force-dynamic';

const sessionId = generateSecureSessionId();

// Expose rate limit utilities in development mode
if (typeof window !== 'undefined' && process.env.NODE_ENV === 'development') {
  (window as any).clearRateLimit = clearRateLimit;
  (window as any).getRateLimitStatus = getRateLimitStatus;
}

function CreateResidentForm() {
  const router = useRouter();
  const { user, userProfile } = useAuth();
  const { getToken: getCSRFToken } = useCSRFToken();

  const { createResident, validationErrors } = useResidentOperations({
    onSuccess: () => {
      auditLogger.info('Resident creation successful', {
        eventType: 'RESIDENT_CREATE_SUCCESS',
        userId: user?.id || 'anonymous',
        action: 'RESIDENT_CREATED',
        timestamp: new Date().toISOString(),
        sessionId,
        barangayOfficial: user?.role === 'barangay_official',
        complianceFramework: 'RA_10173_BSP_808',
        retentionPeriod: '7_YEARS',
      });

      npcComplianceLogger.info('Data processing completed', {
        dataCategory: 'PERSONAL_INFORMATION',
        processingPurpose: 'BARANGAY_RESIDENT_REGISTRATION',
        legalBasis: 'PERFORMANCE_OF_TASK_PUBLIC_INTEREST',
        dataSubjectCount: 1,
        sensitiveDataProcessed: false, // Never log if sensitive data was processed
        consentStatus: 'OBTAINED',
        timestamp: new Date().toISOString(),
        npcRegistrationRef: process.env.NPC_REGISTRATION_NUMBER,
      });

      toast.success('Resident created successfully!');
      router.push('/residents');
    },
    onError: error => {
      auditLogger.info('Resident creation failed', {
        eventType: 'RESIDENT_CREATE_FAILED',
        userId: user?.id || 'anonymous',
        action: 'RESIDENT_CREATE_ERROR',
        timestamp: new Date().toISOString(),
        sessionId,
        complianceFramework: 'RA_10173_BSP_808',
        retentionPeriod: '7_YEARS',
      });

      toast.error(error || 'Failed to create resident');
    },
  });

  const handleSubmit = useCallback(
    async (formData: any) => {
      try {
        const userIdentifier = user?.id || 'anonymous';

        // Check rate limit and provide helpful feedback
        if (
          !checkRateLimit(
            userIdentifier,
            RATE_LIMITS.FORM_SUBMISSION.MAX_ATTEMPTS,
            RATE_LIMITS.FORM_SUBMISSION.WINDOW_MS
          )
        ) {
          const status = getRateLimitStatus(userIdentifier);
          const waitTime = status.remainingTime ? Math.ceil(status.remainingTime / 1000 / 60) : 5;

          toast.error(
            `Too many submission attempts (${status.count}/5). Please wait ${waitTime} minutes before trying again.` +
              (process.env.NODE_ENV === 'development' ? ' Check console for reset option.' : '')
          );

          // In development, provide a way to reset the rate limit
          if (process.env.NODE_ENV === 'development') {
            philippineCompliantLogger.warn('Rate limit exceeded in development', {
              userIdentifier,
              attempts: status.count,
              waitTimeMinutes: waitTime,
              timestamp: new Date().toISOString(),
              complianceNote: 'DEV_RATE_LIMIT_WARNING',
            });

            // Auto-clear rate limit after 30 seconds in development to help with testing
            setTimeout(() => {
              philippineCompliantLogger.info(
                'Development mode: Auto-clearing rate limit after 30 seconds'
              );
              clearRateLimit(userIdentifier);
              toast.success('Rate limit cleared. You can now try submitting again.');
            }, 30000);
          }

          return;
        }

        philippineCompliantLogger.debug('Form processing initiated', {
          userId: user?.id || 'anonymous',
          timestamp: new Date().toISOString(),
          formFieldCount: Object.keys(formData).length,
          barangayCode: userProfile?.barangay_code?.substring(0, 3) + '***',
          hasPhilSysData: !!formData.philsys_card_number,
          hasVoterData: !!(formData.is_voter || formData.is_resident_voter),
          sessionId,
          complianceNote: 'RA_10173_COMPLIANT_DEV_LOG',
        });

        const validation = validateFormData(formData);
        if (!validation.isValid) {
          auditLogger.info('Form validation failed', {
            eventType: 'VALIDATION_FAILED',
            userId: user?.id || 'anonymous',
            action: 'FORM_VALIDATION',
            timestamp: new Date().toISOString(),
            sessionId,
            complianceFramework: 'RA_10173_BSP_808',
            retentionPeriod: '7_YEARS',
          });

          toast.error(validation.errors._form || 'Please correct the form errors');
          return;
        }

        const { transformedData, auditInfo } = prepareFormSubmission(
          formData,
          user?.id || 'anonymous',
          sessionId,
          userProfile?.barangay_code || ''
        );

        auditLogger.info('Resident registration attempt', {
          eventType: 'RESIDENT_FORM_PROCESSING',
          userId: auditInfo.userId,
          action: 'CREATE_RESIDENT_ATTEMPT',
          timestamp: auditInfo.timestamp,
          sessionId: auditInfo.sessionId,
          barangayOfficial: user?.role === 'barangay_official',
          complianceFramework: 'RA_10173_BSP_808',
          retentionPeriod: '7_YEARS',
        });

        npcComplianceLogger.info('Data processing event', {
          dataCategory: 'PERSONAL_INFORMATION',
          processingPurpose: 'BARANGAY_RESIDENT_REGISTRATION',
          legalBasis: 'PERFORMANCE_OF_TASK_PUBLIC_INTEREST',
          dataSubjectCount: 1,
          sensitiveDataProcessed: auditInfo.hasPhilSys,
          consentStatus: 'OBTAINED',
          timestamp: auditInfo.timestamp,
          npcRegistrationRef: process.env.NPC_REGISTRATION_NUMBER,
        });

        // Get CSRF token separately
        getCSRFToken();
        const result = await createResident(transformedData);

        if (!result?.success) {
          auditLogger.info('Form submission processing completed', {
            eventType: 'FORM_PROCESSING_STATUS',
            userId: user?.id || 'anonymous',
            action: 'PROCESSING_RESULT',
            timestamp: new Date().toISOString(),
            sessionId,
            complianceFramework: 'RA_10173_BSP_808',
            retentionPeriod: '7_YEARS',
          });
        }
      } catch {
        auditLogger.info('Form submission error', {
          eventType: 'FORM_SUBMISSION_ERROR',
          userId: user?.id || 'anonymous',
          action: 'SUBMISSION_EXCEPTION',
          timestamp: new Date().toISOString(),
          sessionId,
          complianceFramework: 'RA_10173_BSP_808',
          retentionPeriod: '7_YEARS',
        });

        toast.error('An unexpected error occurred. Please try again.');
      }
    },
    [user, userProfile, sessionId, createResident, getCSRFToken]
  );

  const { suggestedName, suggestedId, isPreFilled } = useResidentFormURLParameters();

  const initialData = useMemo(() => {
    const data: any = {};

    // Handle suggested ID with validation
    if (suggestedId && suggestedId.length > 0) {
      // Log ID parameter usage (for audit purposes)
      auditLogger.info('URL parameter processing', {
        eventType: 'URL_PARAM_PROCESSING',
        userId: user?.id || 'anonymous',
        action: 'ID_PREFILL',
        timestamp: new Date().toISOString(),
        sessionId,
        complianceFramework: 'RA_10173_BSP_808',
        retentionPeriod: '7_YEARS',
      });
    }

    return Object.keys(data).length > 0 ? data : undefined;
  }, [suggestedName, suggestedId, user?.id, sessionId]);

  return (
    <div className="p-6">
      <div className="mb-8 flex items-center gap-4">
        <Link
          href="/residents"
          className="inline-flex items-center gap-2 rounded-lg border border-gray-200 bg-white px-3 py-2 text-sm/6 font-medium text-gray-600 shadow-xs hover:bg-gray-50 dark:border-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:hover:bg-gray-700"
        >
          <svg
            className="size-4"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth="1.5"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 19.5L8.25 12l7.5-7.5" />
          </svg>
          Back
        </Link>
        <div className="flex-1">
          <h1 className="text-2xl/8 font-semibold text-gray-600 dark:text-gray-400">
            Add New Resident
          </h1>
          <p className="mt-2 text-sm/6 text-gray-600 dark:text-gray-400">
            Complete the form to register a new resident in the system
          </p>

          {isPreFilled && suggestedName && (
            <div className="mt-4 rounded-md bg-blue-50 p-3 dark:bg-blue-900/20">
              <div className="flex">
                <div className="flex-shrink-0">
                  <svg
                    className="h-5 w-5 text-blue-400"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                    />
                  </svg>
                </div>
                <div className="ml-3">
                  <p className="text-sm text-blue-700 dark:text-blue-300">
                    <strong>Form pre-filled:</strong> The name fields have been populated with "
                    {suggestedName}". You can edit these values as needed.
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {Object.keys(validationErrors).length > 0 && (
        <div className="mb-4 rounded-md bg-red-50 p-4 dark:bg-red-900/20">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path
                  fillRule="evenodd"
                  d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z"
                  clipRule="evenodd"
                />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800 dark:text-red-200">
                There were errors with your submission
              </h3>
              <div className="mt-2 text-sm text-red-700 dark:text-red-300">
                <ul className="list-disc space-y-1 pl-5">
                  {Object.entries(validationErrors).map(([field, error]) => (
                    <li key={field}>
                      <strong>{field}:</strong> {error}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      )}

      <ResidentForm
        initialData={initialData}
        onSubmit={handleSubmit}
        onCancel={() => router.push('/residents')}
        hidePhysicalDetails={false}
        hideSectoralInfo={false}
      />
    </div>
  );
}

export default function CreateResidentPage() {
  return <CreateResidentForm />;
}

