import crypto from 'crypto';

import type { SupabaseClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';

import { createAdminSupabaseClient } from '@/lib';
import type { WebhookPayload } from '@/types/api-requests';
import { WebhookUserRecord } from '@/types/auth';

// Webhook secret for verifying Supabase webhook signatures
const WEBHOOK_SECRET = process.env.SUPABASE_WEBHOOK_SECRET || 'dev-webhook-secret';

// WebhookPayload moved to src/types/api-requests.ts for consolidation

function isWebhookUserRecord(v: any): v is WebhookUserRecord {
  return (
    !!v &&
    typeof v.id === 'string' &&
    typeof v.email === 'string' &&
    (v.email_confirmed_at === null || typeof v.email_confirmed_at === 'string') &&
    typeof v.created_at === 'string'
  );
}

export async function POST(request: NextRequest) {
  try {
    const supabaseAdmin = createAdminSupabaseClient();
    const body = await request.text();
    const signature = request.headers.get('x-webhook-signature');

    // Verify webhook signature in production (fail closed)
    if (process.env.NODE_ENV === 'production') {
      if (!signature) {
        console.error('Missing webhook signature');
        return NextResponse.json({ error: 'Missing signature' }, { status: 401 });
      }
      if (!process.env.SUPABASE_WEBHOOK_SECRET || WEBHOOK_SECRET === 'dev-webhook-secret') {
        console.error('Webhook secret not configured in production');
        return NextResponse.json({ error: 'Server misconfiguration' }, { status: 500 });
      }
      const expectedSignature = crypto
        .createHmac('sha256', WEBHOOK_SECRET)
        .update(body)
        .digest('hex');

      // Timing-safe comparison
      const sigBuf = Buffer.from(signature, 'hex');
      const expBuf = Buffer.from(expectedSignature, 'hex');
      if (sigBuf.length !== expBuf.length || !crypto.timingSafeEqual(sigBuf, expBuf)) {
        console.error('Invalid webhook signature');
        return NextResponse.json({ error: 'Invalid signature' }, { status: 401 });
      }
    }

    const payload: WebhookPayload = JSON.parse(body);

    console.log('üîÑ Auth webhook received:', {
      type: payload.type,
      table: payload.table,
      userId: payload.record?.id,
    });

    // Handle different webhook events
    switch (payload.type) {
      case 'UPDATE':
        if (payload.table === 'users' && payload.schema === 'auth') {
          if (
            !isWebhookUserRecord(payload.record) ||
            !isWebhookUserRecord(payload.old_record ?? payload.record)
          ) {
            return NextResponse.json({ error: 'Invalid user record payload' }, { status: 400 });
          }
          await handleUserUpdate(
            supabaseAdmin,
            payload.record as WebhookUserRecord,
            (payload.old_record ?? payload.record) as unknown as WebhookUserRecord
          );
        }
        break;

      case 'INSERT':
        if (payload.table === 'users' && payload.schema === 'auth') {
          if (!isWebhookUserRecord(payload.record)) {
            return NextResponse.json({ error: 'Invalid user record payload' }, { status: 400 });
          }
          await handleUserInsert(supabaseAdmin, payload.record);
        }
        break;

      default:
        console.log(`Unhandled webhook type: ${payload.type}`);
    }

    return NextResponse.json({ message: 'Webhook processed successfully' });
  } catch (error) {
    console.error('Webhook processing error:', error);
    return NextResponse.json({ error: 'Webhook processing failed' }, { status: 500 });
  }
}

async function handleUserUpdate(
  supabaseAdmin: SupabaseClient,
  newRecord: WebhookUserRecord,
  oldRecord: WebhookUserRecord
) {
  const userId = newRecord.id;

  // Check if email was just confirmed
  if (oldRecord.email_confirmed_at === null && newRecord.email_confirmed_at !== null) {
    console.log(`‚úÖ Email confirmed for user: ${userId}`);

    try {
      // Update profile verification status
      const { error: updateError } = await supabaseAdmin
        .from('auth_user_profiles')
        .update({
          email_verified: true,
          email_verified_at: newRecord.email_confirmed_at,
          updated_at: new Date().toISOString(),
        })
        .eq('id', userId);

      if (updateError) {
        console.error('Failed to update profile verification:', updateError);
        return;
      }

      // Complete address hierarchy if needed
      await completeAddressHierarchy(supabaseAdmin, userId);

      // Queue welcome notifications
      await queueWelcomeNotifications(supabaseAdmin, userId);

      console.log(`‚úÖ Post-confirmation processing completed for user: ${userId}`);
    } catch (error) {
      console.error(`‚ùå Post-confirmation processing failed for user ${userId}:`, error);
    }
  }
}

async function handleUserInsert(supabaseAdmin: any, record: WebhookUserRecord) {
  const userId = record.id;
  console.log(`üë§ New user created: ${userId}`);

  // If user is already confirmed (rare but possible), process immediately
  if (record.email_confirmed_at) {
    console.log(`‚úÖ User already confirmed at signup: ${userId}`);
    const mockOldRecord: WebhookUserRecord = {
      ...record,
      email_confirmed_at: null,
    };
    await handleUserUpdate(supabaseAdmin, record, mockOldRecord);
  }
}

async function completeAddressHierarchy(supabaseAdmin: any, userId: string) {
  try {
    // Get user profile with barangay code
    const { data: profile, error: profileError } = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code, city_municipality_code, province_code, region_code')
      .eq('id', userId)
      .single();

    if (profileError || !profile || !profile.barangay_code) {
      console.log(`No address hierarchy to complete for user: ${userId}`);
      return;
    }

    // If hierarchy is already complete, skip
    if (profile.city_municipality_code && profile.province_code && profile.region_code) {
      console.log(`Address hierarchy already complete for user: ${userId}`);
      return;
    }

    // Get complete address hierarchy
    const { data: hierarchy, error: hierarchyError } = await supabaseAdmin
      .from('psgc_barangays')
      .select(
        `
        city_municipality_code,
        psgc_cities_municipalities!inner(
          code,
          province_code,
          psgc_provinces!inner(
            code,
            region_code
          )
        )
      `
      )
      .eq('code', profile.barangay_code)
      .single();

    if (hierarchyError || !hierarchy) {
      console.error(
        `Failed to get address hierarchy for ${profile.barangay_code}:`,
        hierarchyError
      );
      return;
    }

    // Update profile with complete hierarchy
    const { error: updateError } = await supabaseAdmin
      .from('auth_user_profiles')
      .update({
        city_municipality_code: hierarchy.city_municipality_code,
        province_code: (hierarchy as any).psgc_cities_municipalities.province_code,
        region_code: (hierarchy as any).psgc_cities_municipalities.psgc_provinces.region_code,
        updated_at: new Date().toISOString(),
      })
      .eq('id', userId);

    if (updateError) {
      console.error(`Failed to update address hierarchy for user ${userId}:`, updateError);
    } else {
      console.log(`‚úÖ Address hierarchy completed for user: ${userId}`);
    }
  } catch (error) {
    console.error(`Error completing address hierarchy for user ${userId}:`, error);
  }
}

async function queueWelcomeNotifications(supabaseAdmin: any, userId: string) {
  try {
    // Get user profile for notification data
    const { data: profile, error: profileError } = await supabaseAdmin
      .from('auth_user_profiles')
      .select(
        `
        email,
        first_name,
        phone,
        auth_roles!inner(name)
      `
      )
      .eq('id', userId)
      .single();

    if (profileError || !profile) {
      console.error(`Failed to get profile for notifications: ${userId}`, profileError);
      return;
    }

    const notifications = [];

    // Queue welcome email
    notifications.push({
      user_id: userId,
      notification_type: 'welcome_email',
      metadata: {
        email: profile.email,
        first_name: profile.first_name,
        role_name: (profile as any).auth_roles.name,
      },
    });

    // Queue SMS if phone provided
    if (profile.phone) {
      notifications.push({
        user_id: userId,
        notification_type: 'sms_welcome',
        metadata: {
          phone: profile.phone,
          first_name: profile.first_name,
        },
      });
    }

    // Insert notifications
    const { error: notifError } = await supabaseAdmin
      .from('user_notifications')
      .insert(notifications);

    if (notifError) {
      console.error(`Failed to queue notifications for user ${userId}:`, notifError);
    } else {
      console.log(`‚úÖ Welcome notifications queued for user: ${userId}`);
    }
  } catch (error) {
    console.error(`Error queuing notifications for user ${userId}:`, error);
  }
}

/**
 * Cache Statistics API Endpoint
 * Monitor cache performance and metrics
 */

import { NextRequest, NextResponse } from 'next/server';

import { createErrorResponseObject } from '@/lib';
import { cacheManager } from '@/lib/caching/redis-client';
import { responseCache } from '@/lib/caching/response-cache';
import { isProduction } from '@/lib/config/environment';
import { getConnectionPoolStats } from '@/lib/database/connection-pool';
import { queryOptimizer } from '@/lib/database/query-optimizer';

export const dynamic = 'force-dynamic';

/**
 * GET /api/cache/stats - Get cache and performance statistics
 */
export async function GET(request: NextRequest): Promise<NextResponse> {
  try {
    // Simple authentication check for admin endpoints
    const authHeader = request.headers.get('Authorization');
    const isAuthorized =
      authHeader &&
      (authHeader.includes('Bearer') || (!isProduction() && authHeader === 'dev-token'));

    if (!isAuthorized) {
      return NextResponse.json(
        createErrorResponseObject('AUTH_001', 'Unauthorized access to cache statistics'),
        { status: 401 }
      );
    }

    // Gather all cache and performance statistics
    const [cacheStats, responseCacheStats, queryStats, connectionPoolStats] =
      await Promise.allSettled([
        cacheManager.getStats(),
        responseCache.getStats(),
        Promise.resolve(queryOptimizer.getMetrics()),
        Promise.resolve(getConnectionPoolStats()),
      ]);

    // Helper to safely get stats from settled promises
    const getStatsValue = (
      result: PromiseSettledResult<unknown>,
      defaultValue: Record<string, unknown> = {}
    ) => {
      return result.status === 'fulfilled' ? result.value : defaultValue;
    };

    const stats: Record<string, unknown> = {
      timestamp: new Date().toISOString(),
      environment: process.env.NODE_ENV,

      // Cache statistics
      cache: {
        general: getStatsValue(cacheStats, {
          hits: 0,
          misses: 0,
          keys: 0,
          memoryUsage: 0,
        }),

        responseCache: getStatsValue(responseCacheStats, {
          hits: 0,
          misses: 0,
          keys: 0,
          hitRate: '0%',
        }),
      },

      // Database performance
      database: {
        queryOptimizer: getStatsValue(queryStats, {
          totalQueries: 0,
          averageExecutionTime: 0,
          cacheHitRate: 0,
          slowQueries: [],
          recentQueries: [],
        }),

        connectionPool: getStatsValue(connectionPoolStats, {
          activeConnections: 0,
          totalConnections: 0,
          availableConnections: 0,
          maxConnections: 0,
          utilizationPercentage: 0,
        }),
      },

      // Health indicators
      health: {
        cacheHealthy: cacheStats.status === 'fulfilled',
        databaseHealthy: connectionPoolStats.status === 'fulfilled',
        overallHealthy: [cacheStats, connectionPoolStats].every(s => s.status === 'fulfilled'),
      },
    };

    // Add performance warnings
    const warnings: string[] = [];

    const dbStats = getStatsValue(connectionPoolStats) as {
      utilizationPercentage?: number;
      [key: string]: unknown;
    };
    if ((dbStats.utilizationPercentage ?? 0) > 80) {
      warnings.push('Database connection pool utilization is high (>80%)');
    }

    const queryMetrics = getStatsValue(queryStats) as {
      averageExecutionTime?: number;
      slowQueries?: unknown[];
      [key: string]: unknown;
    };
    if ((queryMetrics.averageExecutionTime ?? 0) > 1000) {
      warnings.push('Average query execution time is high (>1000ms)');
    }

    const slowCount = queryMetrics.slowQueries?.length ?? 0;
    if (slowCount > 5) {
      warnings.push(`${slowCount} slow queries detected`);
    }

    if (warnings.length > 0) {
      stats.warnings = warnings;
    }

    return NextResponse.json(stats, {
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        Pragma: 'no-cache',
        Expires: '0',
      },
    });
  } catch (error) {
    return NextResponse.json(
      createErrorResponseObject('SERVER_001', 'Failed to retrieve cache statistics'),
      { status: 500 }
    );
  }
}

/**
 * POST /api/cache/stats - Cache management operations
 */
export async function POST(request: NextRequest): Promise<NextResponse> {
  try {
    // Simple authentication check for admin endpoints
    const authHeader = request.headers.get('Authorization');
    const isAuthorized =
      authHeader &&
      (authHeader.includes('Bearer') || (!isProduction() && authHeader === 'dev-token'));

    if (!isAuthorized) {
      return NextResponse.json(
        createErrorResponseObject('AUTH_001', 'Unauthorized cache management access'),
        { status: 401 }
      );
    }

    const { action, pattern, tags } = await request.json();

    let result: Record<string, unknown> = { success: false };

    switch (action) {
      case 'clear':
        await cacheManager.clear();
        queryOptimizer.clearCache();
        result = { success: true, message: 'All caches cleared' };
        break;

      case 'invalidate':
        if (!pattern) {
          return NextResponse.json(
            createErrorResponseObject('DATA_001', 'Pattern required for invalidation'),
            { status: 400 }
          );
        }
        const count = await responseCache.invalidate(pattern, tags);
        result = {
          success: true,
          message: `Invalidated ${count} cache entries matching pattern: ${pattern}`,
        };
        break;

      case 'clear-query-metrics':
        queryOptimizer.clearMetrics();
        result = { success: true, message: 'Query metrics cleared' };
        break;

      default:
        return NextResponse.json(createErrorResponseObject('DATA_001', 'Invalid cache action'), {
          status: 400,
        });
    }

    return NextResponse.json(result);
  } catch (error) {
    return NextResponse.json(
      createErrorResponseObject('SERVER_001', 'Cache management operation failed'),
      { status: 500 }
    );
  }
}

import { createClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';

import { logger, createErrorResponseObject, type ErrorCode } from '@/lib';
import { withResponseCache, CachePresets } from '@/lib/caching/response-cache';
import { getEnvironmentConfig, isProduction } from '@/lib/config/environment';
import { getPooledConnection, releasePooledConnection } from '@/lib/database/connection-pool';
import { queryOptimizer } from '@/lib/database/query-optimizer';

// Rate limiting configuration
const RATE_LIMIT_WINDOW = 60 * 1000; // 1 minute
const RATE_LIMIT_MAX_REQUESTS = 30; // 30 requests per minute
const requestCounts = new Map<string, { count: number; resetTime: number }>();

// Rate limiting function
function checkRateLimit(userAgent: string, ip: string): boolean {
  const key = `${ip}-${userAgent?.substring(0, 50) || 'unknown'}`;
  const now = Date.now();
  const current = requestCounts.get(key);

  if (!current || now > current.resetTime) {
    requestCounts.set(key, { count: 1, resetTime: now + RATE_LIMIT_WINDOW });
    return true;
  }

  if (current.count >= RATE_LIMIT_MAX_REQUESTS) {
    return false;
  }

  current.count++;
  return true;
}

// Pagination configuration
const MAX_RESIDENTS_PER_PAGE = 1000;
const DEFAULT_PAGE_SIZE = 500;

async function dashboardStatsHandler(request: NextRequest): Promise<NextResponse> {
  try {
    // Environment validation for production
    if (isProduction()) {
      if (!process.env.SUPABASE_SERVICE_ROLE_KEY) {
        return NextResponse.json(
          createErrorResponseObject('SERVER_001', 'Service configuration error'),
          { status: 500 }
        );
      }
    }

    // Rate limiting
    const userAgent = request.headers.get('user-agent') || '';
    const ip =
      request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip') || 'unknown';

    if (!checkRateLimit(userAgent, ip)) {
      return NextResponse.json(
        createErrorResponseObject('RATE_001', 'Rate limit exceeded. Please try again later.'),
        { status: 429 }
      );
    }

    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json(
        createErrorResponseObject('AUTH_001', 'No authentication token provided'),
        { status: 401 }
      );
    }

    const token = authHeader.split(' ')[1];

    // Get optimized connection for user verification
    const supabase = await getPooledConnection('anon');

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json(
        createErrorResponseObject('AUTH_002', 'Invalid or expired authentication token'),
        { status: 401 }
      );
    }

    // Get optimized service role connection
    const supabaseAdmin = await getPooledConnection('service');

    // Get user profile to get barangay_code
    const { data: userProfile, error: profileError } = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    if (profileError) {
      logger.error('Profile query error:', profileError);
      return NextResponse.json(
        createErrorResponseObject('USER_001', 'User profile could not be retrieved'),
        { status: 400 }
      );
    }

    if (!userProfile?.barangay_code) {
      return NextResponse.json(
        createErrorResponseObject('USER_002', 'No barangay assignment found for user'),
        { status: 400 }
      );
    }

    const barangayCode = userProfile.barangay_code;

    // Get dashboard stats using query optimizer
    const {
      data: dashboardStats,
      error: statsError,
      fromCache,
    } = await queryOptimizer.getDashboardStats(
      supabaseAdmin,
      barangayCode,
      { cacheTTL: 2 * 60 * 1000 } // 2 minutes cache
    );

    if (statsError) {
      logger.error('Dashboard stats query error:', statsError);
      return NextResponse.json(
        createErrorResponseObject('DATA_002', 'Unable to retrieve dashboard statistics'),
        { status: 500 }
      );
    }

    // If no stats data exists for this barangay, provide defaults
    const statsData = dashboardStats || {
      total_residents: 0,
      total_households: 0,
      seniors: 0,
      employed: 0,
    };

    // Parse pagination parameters
    const url = new URL(request.url);
    const page = Math.max(1, parseInt(url.searchParams.get('page') || '1'));
    const limit = Math.min(
      MAX_RESIDENTS_PER_PAGE,
      parseInt(url.searchParams.get('limit') || DEFAULT_PAGE_SIZE.toString())
    );
    const offset = (page - 1) * limit;

    // Get individual residents data using optimized query
    const {
      data: residentsData,
      error: residentsError,
      fromCache: residentsFromCache,
    } = await queryOptimizer.executeQuery(
      supabaseAdmin,
      `residents_sectoral_${barangayCode}_${page}_${limit}`,
      async () => {
        return await supabaseAdmin
          .from('residents')
          .select(
            `
            birthdate, 
            sex, 
            civil_status, 
            employment_status,
            household_code,
            households!inner(barangay_code),
            resident_sectoral_info(
              is_labor_force_employed,
              is_unemployed,
              is_overseas_filipino_worker,
              is_person_with_disability,
              is_out_of_school_children,
              is_out_of_school_youth,
              is_senior_citizen,
              is_registered_senior_citizen,
              is_solo_parent,
              is_indigenous_people,
              is_migrant
            )
          `
          )
          .eq('households.barangay_code', barangayCode)
          .eq('is_active', true)
          .range(offset, offset + limit - 1);
      },
      {
        cacheTTL: 1 * 60 * 1000, // 1 minute cache for residents
        enableCache: true,
      }
    );

    if (residentsError) {
      logger.error('Residents query error:', residentsError);
      return NextResponse.json(
        createErrorResponseObject('DATA_002', 'Unable to retrieve resident data'),
        { status: 500 }
      );
    }

    // Calculate real sectoral statistics from residents data
    const sectoralStats = {
      laborForce: 0,
      employed: 0,
      unemployed: 0,
      ofw: 0,
      pwd: 0,
      outOfSchoolChildren: 0,
      outOfSchoolYouth: 0,
      seniorCitizens: 0,
      registeredSeniorCitizens: 0,
      soloParents: 0,
      indigenous: 0,
      migrants: 0,
    };

    if (residentsData && residentsData.length > 0) {
      residentsData.forEach(resident => {
        const sectoral = resident.resident_sectoral_info?.[0];
        if (sectoral) {
          // Labor force includes employed and unemployed
          if (sectoral.is_labor_force_employed || sectoral.is_unemployed)
            sectoralStats.laborForce++;
          if (sectoral.is_labor_force_employed) sectoralStats.employed++;
          if (sectoral.is_unemployed) sectoralStats.unemployed++;
          if (sectoral.is_overseas_filipino_worker) sectoralStats.ofw++;
          if (sectoral.is_person_with_disability) sectoralStats.pwd++;
          if (sectoral.is_out_of_school_children) sectoralStats.outOfSchoolChildren++;
          if (sectoral.is_out_of_school_youth) sectoralStats.outOfSchoolYouth++;
          if (sectoral.is_senior_citizen) sectoralStats.seniorCitizens++;
          if (sectoral.is_registered_senior_citizen) sectoralStats.registeredSeniorCitizens++;
          if (sectoral.is_solo_parent) sectoralStats.soloParents++;
          if (sectoral.is_indigenous_people) sectoralStats.indigenous++;
          if (sectoral.is_migrant) sectoralStats.migrants++;
        }
      });
    }

    // Calculate actual counts from the data we retrieved
    const actualResidentCount = residentsData?.length || 0;
    const uniqueHouseholds = new Set(
      residentsData?.map(r => r.household_code).filter(Boolean) || []
    );
    const actualHouseholdCount = uniqueHouseholds.size;

    const response = {
      stats: {
        residents: actualResidentCount,
        households: actualHouseholdCount,
        businesses: 0, // TODO: Add when businesses table exists
        certifications: 0, // TODO: Add when certifications table exists
        seniorCitizens: sectoralStats.seniorCitizens || statsData?.age_65_plus || 0,
        employedResidents: sectoralStats.employed || statsData?.employed_count || 0,
      },
      // Additional demographic data for charts
      demographics: {
        ageGroups: {
          youngDependents: statsData?.age_0_14 || 0,
          workingAge: statsData?.age_15_64 || 0,
          oldDependents: statsData?.age_65_plus || 0,
        },
        sexDistribution: {
          male: statsData?.male_count || 0,
          female: statsData?.female_count || 0,
        },
        civilStatus: {
          single: statsData?.single_count || 0,
          married: statsData?.married_count || 0,
          widowed: statsData?.widowed_count || 0,
          divorced: statsData?.divorced_separated_count || 0,
        },
        employment: {
          laborForce:
            sectoralStats.laborForce ||
            (statsData?.employed_count || 0) + (statsData?.unemployed_count || 0),
          employed: sectoralStats.employed || statsData?.employed_count || 0,
          unemployed: sectoralStats.unemployed || statsData?.unemployed_count || 0,
        },
        specialCategories: {
          pwd: sectoralStats.pwd,
          soloParents: sectoralStats.soloParents,
          ofw: sectoralStats.ofw,
          indigenous: sectoralStats.indigenous,
          outOfSchoolChildren: sectoralStats.outOfSchoolChildren,
          outOfSchoolYouth: sectoralStats.outOfSchoolYouth,
          registeredSeniorCitizens: sectoralStats.registeredSeniorCitizens,
          migrants: sectoralStats.migrants,
        },
      },
      residentsData: residentsData || [],
      pagination: {
        page,
        limit,
        total: actualResidentCount,
        hasNextPage: residentsData?.length === limit,
      },
      // Performance metadata
      performance: {
        dashboardStatsFromCache: fromCache,
        residentsDataFromCache: residentsFromCache,
        queryOptimizationEnabled: true,
      },
    };

    // Release pooled connections
    releasePooledConnection(supabase);
    releasePooledConnection(supabaseAdmin);

    return NextResponse.json(response);
  } catch (error) {
    logger.error('Dashboard stats API error:', error);
    return NextResponse.json(
      createErrorResponseObject('SERVER_001', 'An unexpected error occurred'),
      { status: 500 }
    );
  }
}

// Wrap the handler with response caching
export async function GET(request: NextRequest): Promise<NextResponse> {
  return withResponseCache(CachePresets.dashboard)(request, () => dashboardStatsHandler(request));
}

import { createClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';

export async function GET(request: NextRequest) {
  try {
    // Get auth header
    const authHeader = request.headers.get('Authorization');
    if (!authHeader?.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create Supabase client for user verification
    const supabase = createClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
    );

    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);
    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Use service role for checks
    const supabaseAdmin = createClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.SUPABASE_SERVICE_ROLE_KEY!
    );

    console.log('[RBI Preflight] Checking system state for user:', user.id);

    // Check 1: Active encryption key
    const { data: encryptionKey, error: keyError } = await supabaseAdmin
      .from('system_encryption_keys')
      .select('key_name, is_active')
      .eq('key_name', 'pii_master_key')
      .eq('is_active', true)
      .single();

    // Check 2: User has barangay assignment via profile (simpler and consistent with API)
    const { data: profile, error: profileError } = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    // Check 3: Function existence (attempt simple no-op call with bogus params to test availability)
    let funcExists = true;
    let funcError: any = null;
    try {
      // Minimal safe call that should fail on validation but confirm function exists in schema
      await supabaseAdmin.rpc('insert_resident_encrypted', {
        p_first_name: 'X',
        p_last_name: 'Y',
        p_birthdate: '2000-01-01',
        p_sex: 'male',
        p_barangay_code: profile?.barangay_code || null,
      });
    } catch (e: any) {
      const message = (e?.message || '').toLowerCase();
      if (message.includes('function') && message.includes('does not exist')) {
        funcExists = false;
        funcError = e?.message;
      } else {
        // Function exists; we ignore expected validation/rls errors
        funcExists = true;
        funcError = null;
      }
    }

    return NextResponse.json({
      user_id: user.id,
      preflight_checks: {
        encryption_key: {
          exists: !!encryptionKey,
          error: keyError?.message || null,
          data: encryptionKey,
        },
        profile_barangay: {
          code: profile?.barangay_code || null,
          error: profileError?.message || null,
        },
        function_check: {
          exists: funcExists,
          error: funcError,
        },
      },
      diagnosis: {
        ready_for_rbi: !!encryptionKey && !!profile?.barangay_code && funcExists,
        issues: [
          !encryptionKey ? 'Missing active pii_master_key' : null,
          !profile?.barangay_code ? 'No barangay assigned on profile' : null,
          !funcExists ? 'insert_resident_encrypted function not available' : null,
        ].filter(Boolean),
      },
    });
  } catch (error) {
    console.error('[RBI Preflight] Error:', error);
    return NextResponse.json({ error: 'Internal server error', details: error }, { status: 500 });
  }
}

import { createClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';

export async function GET(request: NextRequest) {
  try {
    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized - No auth token' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create regular client to verify user
    const supabase = createClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
    );

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized - Invalid token' }, { status: 401 });
    }

    // Use service role client to check data directly
    const supabaseAdmin = createClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.SUPABASE_SERVICE_ROLE_KEY!
    );

    // Get user profile first
    const { data: userProfile, error: profileError } = await supabaseAdmin
      .from('auth_user_profiles')
      .select('*')
      .eq('id', user.id)
      .single();

    if (profileError) {
      return NextResponse.json(
        {
          error: 'Failed to get user profile',
          details: profileError,
        },
        { status: 500 }
      );
    }

    // Check total residents in database
    const { count: totalResidents } = await supabaseAdmin
      .from('residents')
      .select('*', { count: 'exact', head: true });

    // Check residents for user's barangay (via households join)
    let residentsInBarangay = 0;
    if (userProfile?.barangay_code) {
      const { count } = await supabaseAdmin
        .from('residents')
        .select('*, households!inner(barangay_code)', { count: 'exact', head: true })
        .eq('households.barangay_code', userProfile.barangay_code)
        .eq('is_active', true);
      residentsInBarangay = count || 0;
    }

    // Check what barangay codes exist in households table (residents don't have geographic fields)
    const { data: barangayCodes } = await supabaseAdmin
      .from('households')
      .select('barangay_code')
      .limit(10);

    // Check with user RLS policies (using user token) - need to join via households
    let rls_error = null;
    let rls_count = 0;
    try {
      if (userProfile?.barangay_code) {
        const { count, error } = await supabase
          .from('residents')
          .select('*, households!inner(barangay_code)', { count: 'exact', head: true })
          .eq('households.barangay_code', userProfile.barangay_code)
          .eq('is_active', true);

        if (error) {
          rls_error = error;
        } else {
          rls_count = count || 0;
        }
      }
    } catch (error) {
      rls_error = error;
    }

    return NextResponse.json({
      user_id: user.id,
      user_profile: userProfile,
      total_residents_in_db: totalResidents || 0,
      residents_in_user_barangay: residentsInBarangay,
      barangay_codes_sample: barangayCodes?.map(b => b.barangay_code).filter(Boolean) || [],
      rls_test: {
        count: rls_count,
        error: rls_error,
      },
    });
  } catch (error) {
    console.error('Debug API error:', error);
    return NextResponse.json({ error: 'Internal server error', details: error }, { status: 500 });
  }
}

/**
 * Health Check Endpoint
 * Production monitoring and system status checks
 */

import { createClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';

import {
  performRuntimeHealthCheck,
  validateEnvironment,
  getEnvironmentConfig,
} from '@/lib/config/environment';
import type { HealthCheckResult } from '@/types/api-requests';

export const dynamic = 'force-dynamic';

// HealthCheckResult moved to src/types/api-requests.ts

/**
 * Basic health check endpoint
 * GET /api/health - Returns system health status
 */
export async function GET(request: NextRequest): Promise<NextResponse> {
  const startTime = Date.now();

  try {
    // Get environment configuration
    const envConfig = getEnvironmentConfig();
    const envValidation = validateEnvironment();

    // Perform comprehensive health checks
    const healthCheck = await performRuntimeHealthCheck();

    // Additional system information (server-side only)
    let systemInfo = {};
    if (typeof process !== 'undefined') {
      const memory = process.memoryUsage();
      systemInfo = {
        memory: {
          used: Math.round(memory.heapUsed / 1024 / 1024), // MB
          total: Math.round(memory.heapTotal / 1024 / 1024), // MB
          percentage: Math.round((memory.heapUsed / memory.heapTotal) * 100),
        },
        node: {
          version: process.version,
          platform: process.platform,
        },
      };
    }

    // Calculate uptime
    const uptime = typeof process !== 'undefined' && process.uptime ? process.uptime() : 0;

    // Determine overall status
    const hasFailures = Object.values(healthCheck.checks).some(check => check.status === 'fail');
    const hasWarnings = Object.values(healthCheck.checks).some(check => check.status === 'warn');
    const environmentValid = envValidation.isValid;

    let status: 'healthy' | 'unhealthy' | 'degraded' = 'healthy';
    if (!environmentValid || hasFailures) {
      status = 'unhealthy';
    } else if (hasWarnings || envValidation.warnings.length > 0) {
      status = 'degraded';
    }

    const result: HealthCheckResult = {
      status,
      timestamp: new Date().toISOString(),
      version: envConfig.appVersion || '1.0.0',
      environment: envConfig.environment,
      uptime: Math.round(uptime),
      checks: {
        ...healthCheck.checks,
        environment: {
          status: environmentValid ? 'pass' : 'fail',
          message: environmentValid
            ? 'Environment configuration valid'
            : `Environment issues: ${envValidation.errors.join(', ')}`,
          responseTime: Date.now() - startTime,
        },
      },
      system: systemInfo,
    };

    // Add environment warnings to checks if any
    if (envValidation.warnings.length > 0) {
      result.checks.environment_warnings = {
        status: 'warn',
        message: `Warnings: ${envValidation.warnings.join(', ')}`,
      };
    }

    // Return appropriate HTTP status code
    const httpStatus = status === 'healthy' ? 200 : status === 'degraded' ? 200 : 503;

    return NextResponse.json(result, {
      status: httpStatus,
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        Pragma: 'no-cache',
        Expires: '0',
      },
    });
  } catch (error) {
    // Fallback error response
    const result: HealthCheckResult = {
      status: 'unhealthy',
      timestamp: new Date().toISOString(),
      version: '1.0.0',
      environment: 'unknown',
      uptime: 0,
      checks: {
        system: {
          status: 'fail',
          message: `Health check failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
          responseTime: Date.now() - startTime,
        },
      },
    };

    return NextResponse.json(result, {
      status: 503,
      headers: {
        'Cache-Control': 'no-cache, no-store, must-revalidate',
        Pragma: 'no-cache',
        Expires: '0',
      },
    });
  }
}

/**
 * Detailed health check endpoint
 * POST /api/health - Returns detailed system diagnostics
 */
export async function POST(request: NextRequest): Promise<NextResponse> {
  try {
    // Check if request has proper authorization for detailed checks
    const authHeader = request.headers.get('Authorization');
    const isAuthorized = authHeader && authHeader.includes('Bearer');

    if (!isAuthorized) {
      return NextResponse.json(
        {
          error: 'Unauthorized access to detailed health checks',
        },
        { status: 401 }
      );
    }

    // Perform detailed checks
    const startTime = Date.now();
    const envValidation = validateEnvironment();
    const healthCheck = await performRuntimeHealthCheck();

    // Database connectivity test
    let dbCheck: { status: 'pass' | 'fail' | 'skip'; message: string; responseTime?: number } = {
      status: 'skip',
      message: 'Database check skipped',
    };

    if (process.env.NEXT_PUBLIC_SUPABASE_URL && process.env.SUPABASE_SERVICE_ROLE_KEY) {
      try {
        const supabase = createClient(
          process.env.NEXT_PUBLIC_SUPABASE_URL,
          process.env.SUPABASE_SERVICE_ROLE_KEY
        );

        const dbStartTime = Date.now();
        const { error } = await supabase.from('auth_user_profiles').select('id').limit(1);
        const dbResponseTime = Date.now() - dbStartTime;

        dbCheck = {
          status: error ? 'fail' : 'pass',
          message: error
            ? `Database error: ${error.message}`
            : `Database accessible (${dbResponseTime}ms)`,
          responseTime: dbResponseTime,
        };
      } catch (error) {
        dbCheck = {
          status: 'fail',
          message: `Database connection failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        };
      }
    }

    const result = {
      status: 'detailed-check',
      timestamp: new Date().toISOString(),
      environment: envValidation,
      runtime: healthCheck,
      database: dbCheck,
      performance: {
        checkDuration: Date.now() - startTime,
        memoryUsage: typeof process !== 'undefined' ? process.memoryUsage() : null,
      },
    };

    return NextResponse.json(result);
  } catch (error) {
    return NextResponse.json(
      {
        error: 'Detailed health check failed',
        message: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';

import { createPublicSupabaseClient, createAdminSupabaseClient } from '@/lib/data/client-factory';
import type { Database } from '@/lib/data/supabase';
import type { HouseholdRecord } from '@/types/api';
import type { AuthUserProfile } from '@/types/auth';

type DbHouseholdUpdate = Database['public']['Tables']['households']['Update'];

export async function GET(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const resolvedParams = await params;
    const householdCode = resolvedParams.id;

    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized - No auth token' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create regular client to verify user
    const supabase = createPublicSupabaseClient();

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized - Invalid token' }, { status: 401 });
    }

    // Use service role client to bypass RLS
    const supabaseAdmin = createAdminSupabaseClient() as any;

    // Get user profile to verify barangay access
    const profileResult = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    const userProfile = profileResult.data as AuthUserProfile | null;
    const profileError = profileResult.error;

    if (profileError || !userProfile?.barangay_code) {
      return NextResponse.json(
        { error: 'User profile not found or no barangay assigned' },
        { status: 400 }
      );
    }

    // Get household data with head resident info using correct foreign key reference
    const householdResult = await supabaseAdmin
      .from('households')
      .select(
        `
        *,
        head_resident:residents!household_head_id(
          id,
          first_name,
          middle_name,
          last_name,
          email,
          mobile_number,
          sex,
          birthdate,
          civil_status
        )
        `
      )
      .eq('code', householdCode)
      .eq('barangay_code', userProfile.barangay_code)
      .single();

    const household = householdResult.data as HouseholdRecord | null;
    const householdError = householdResult.error;

    if (householdError || !household) {
      return NextResponse.json({ error: 'Household not found or access denied' }, { status: 404 });
    }

    // Get all household members
    const { data: members, error: membersError } = await supabaseAdmin
      .from('residents')
      .select('*')
      .eq('household_code', householdCode)
      .eq('is_active', true)
      .order('created_at', { ascending: true });

    if (membersError) {
      console.warn('Failed to fetch household members:', membersError);
    }

    // Get geographic information
    let geoInfo = {};
    try {
      const { data: barangayData } = await supabaseAdmin
        .from('psgc_barangays')
        .select(
          `
          code,
          name,
          psgc_cities_municipalities!inner(
            code,
            name,
            type,
            psgc_provinces!inner(
              code,
              name,
              psgc_regions!inner(
                code,
                name
              )
            )
          )
          `
        )
        .eq('code', household.barangay_code)
        .single();

      if (barangayData) {
        const cityMunData = (barangayData as any)?.psgc_cities_municipalities ?? null;
        const province = (cityMunData as any)?.psgc_provinces ?? null;
        const region = (province as any)?.psgc_regions ?? null;

        geoInfo = {
          barangay_info: {
            code: (barangayData as any).code,
            name: (barangayData as any).name,
          },
          city_municipality_info: {
            code: (cityMunData as any)?.code ?? null,
            name: (cityMunData as any)?.name ?? null,
            type: (cityMunData as any)?.type ?? null,
          },
          province_info: {
            code: (province as any)?.code ?? null,
            name: (province as any)?.name ?? null,
          },
          region_info: {
            code: (region as any)?.code ?? null,
            name: (region as any)?.name ?? null,
          },
        };
      }
    } catch (geoError) {
      console.warn('Geographic info load failed:', geoError);
    }

    return NextResponse.json({
      household: {
        ...household,
        ...geoInfo,
        member_count: members?.length || 0,
      },
      members: members || [],
    });
  } catch (error) {
    console.error('Household detail API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

export async function PUT(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const resolvedParams = await params;
    const householdCode = resolvedParams.id;
    const updateData = await request.json();

    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized - No auth token' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create regular client to verify user
    const supabase = createPublicSupabaseClient();

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized - Invalid token' }, { status: 401 });
    }

    // Use service role client to bypass RLS
    const supabaseAdmin = createAdminSupabaseClient() as any;

    // Get user profile to verify barangay access
    const profileResult = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    const userProfile = profileResult.data as AuthUserProfile | null;
    const profileError = profileResult.error;

    if (profileError || !userProfile?.barangay_code) {
      return NextResponse.json(
        { error: 'User profile not found or no barangay assigned' },
        { status: 400 }
      );
    }

    // Prepare a typed, sanitized payload (block immutable fields)
    const {
      code: _code,
      barangay_code: _brgy,
      created_at: _created,
      updated_at: _prevUpdated,
      ...rest
    } = (updateData as Record<string, any>) ?? {};
    const updateResult = await supabaseAdmin
      .from('households')
      .update({
        ...(rest as DbHouseholdUpdate),
        updated_at: new Date().toISOString(),
      })
      .eq('code', householdCode)
      .eq('barangay_code', userProfile.barangay_code)
      .select()
      .single();

    const updatedHousehold = updateResult.data;
    const updateError = updateResult.error;

    if (updateError) {
      console.error('Household update error:', updateError);
      return NextResponse.json({ error: 'Failed to update household' }, { status: 500 });
    }

    if (!updatedHousehold) {
      return NextResponse.json({ error: 'Household not found or access denied' }, { status: 404 });
    }

    return NextResponse.json({
      household: updatedHousehold,
      message: 'Household updated successfully',
    });
  } catch (error) {
    console.error('Household update API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

