import { createClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';
// Force rebuild - fixed switch case scoping issue (v2)

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const code = searchParams.get('code');

    if (!code || code.trim().length < 6) {
      return NextResponse.json({ error: 'Valid PSGC code is required' }, { status: 400 });
    }

    // Use service role client to bypass RLS for geographic data
    const supabase = createClient(
      process.env.NEXT_PUBLIC_SUPABASE_URL!,
      process.env.SUPABASE_SERVICE_ROLE_KEY!
    );

    const lookupCode = code.trim();

    // Determine level based on code length and structure
    let level: string;
    let result: any = null;

    if (lookupCode.length === 2) {
      level = 'region';
    } else if (lookupCode.length === 4) {
      level = 'province';
    } else if (lookupCode.length === 6) {
      level = 'city';
    } else if (lookupCode.length >= 9) {
      level = 'barangay';
    } else {
      return NextResponse.json({ error: 'Invalid PSGC code format' }, { status: 400 });
    }

    // Look up based on determined level
    switch (level) {
      case 'region': {
        const { data: regionData, error: regionError } = await supabase
          .from('psgc_regions')
          .select('code, name')
          .eq('code', lookupCode)
          .single();

        if (regionError || !regionData) {
          return NextResponse.json({ error: 'Region not found' }, { status: 404 });
        }

        result = {
          code: regionData.code,
          name: regionData.name,
          level: 'region',
          region_code: regionData.code,
          region_name: regionData.name,
          full_address: regionData.name,
        };
        break;
      }

      case 'province': {
        const { data: provinceData, error: provinceError } = await supabase
          .from('psgc_provinces')
          .select(
            `
            code,
            name,
            region_code,
            psgc_regions (code, name)
          `
          )
          .eq('code', lookupCode)
          .single();

        if (provinceError || !provinceData) {
          return NextResponse.json({ error: 'Province not found' }, { status: 404 });
        }

        const province = provinceData as {
          code: string;
          name: string;
          region_code: string;
          psgc_regions: { code: string; name: string }[] | null;
        };
        const region = Array.isArray(province.psgc_regions)
          ? province.psgc_regions[0]
          : province.psgc_regions;
        result = {
          code: province.code,
          name: province.name,
          level: 'province',
          province_code: province.code,
          province_name: province.name,
          region_code: region?.code,
          region_name: region?.name,
          full_address: [province.name, region?.name].filter(Boolean).join(', '),
        };
        break;
      }

      case 'city': {
        const { data: cityData, error: cityError } = await supabase
          .from('psgc_cities_municipalities')
          .select(
            `
            code,
            name,
            type,
            is_independent,
            province_code,
            psgc_provinces (
              code,
              name,
              region_code,
              psgc_regions (code, name)
            )
          `
          )
          .eq('code', lookupCode)
          .single();

        if (cityError || !cityData) {
          return NextResponse.json({ error: 'City/Municipality not found' }, { status: 404 });
        }

        const city = cityData as {
          code: string;
          name: string;
          type: string;
          is_independent: boolean;
          province_code: string;
          psgc_provinces:
            | {
                code: string;
                name: string;
                region_code: string;
                psgc_regions: { code: string; name: string }[] | null;
              }[]
            | null;
        };
        const cityProvince = Array.isArray(city.psgc_provinces)
          ? city.psgc_provinces[0]
          : city.psgc_provinces;
        const cityRegion = cityProvince?.psgc_regions
          ? Array.isArray(cityProvince.psgc_regions)
            ? cityProvince.psgc_regions[0]
            : cityProvince.psgc_regions
          : null;
        result = {
          code: city.code,
          name: city.name,
          level: 'city',
          type: city.type,
          city_code: city.code,
          city_name: city.name,
          city_type: city.type,
          province_code: cityProvince?.code,
          province_name: cityProvince?.name,
          region_code: cityRegion?.code,
          region_name: cityRegion?.name,
          full_address: [city.name, cityProvince?.name, cityRegion?.name]
            .filter(Boolean)
            .join(', '),
        };
        break;
      }

      case 'barangay': {
        // First get the barangay data
        const { data: barangayData, error: barangayError } = await supabase
          .from('psgc_barangays')
          .select('code, name, city_municipality_code')
          .eq('code', lookupCode)
          .single();

        if (barangayError || !barangayData) {
          return NextResponse.json({ error: 'Barangay not found' }, { status: 404 });
        }

        // Then get the city data separately
        const { data: cityData, error: cityError } = await supabase
          .from('psgc_cities_municipalities')
          .select('code, name, type, province_code')
          .eq('code', barangayData.city_municipality_code)
          .single();

        // Then get the province data separately
        let provinceData = null;
        let regionData = null;

        if (cityData && !cityError) {
          const { data: provData, error: provError } = await supabase
            .from('psgc_provinces')
            .select('code, name, region_code')
            .eq('code', cityData.province_code)
            .single();

          provinceData = provData;

          // Finally get the region data
          if (provData && !provError) {
            const { data: regData, error: regError } = await supabase
              .from('psgc_regions')
              .select('code, name')
              .eq('code', provData.region_code)
              .single();

            regionData = regData;
          }
        }

        result = {
          code: barangayData.code,
          name: barangayData.name,
          level: 'barangay',
          barangay_code: barangayData.code,
          barangay_name: barangayData.name,
          city_code: cityData?.code,
          city_name: cityData?.name,
          city_type: cityData?.type,
          province_code: provinceData?.code,
          province_name: provinceData?.name,
          region_code: regionData?.code,
          region_name: regionData?.name,
          full_address: [barangayData.name, cityData?.name, provinceData?.name, regionData?.name]
            .filter(Boolean)
            .join(', '),
        };
        break;
      }

      default:
        return NextResponse.json({ error: 'Unsupported PSGC level' }, { status: 400 });
    }

    return NextResponse.json({
      data: result,
      level: level,
    });
  } catch (error) {
    console.error('PSGC lookup API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { createClient } from '@supabase/supabase-js';
import { NextRequest, NextResponse } from 'next/server';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const query = searchParams.get('q');
    const limit = parseInt(searchParams.get('limit') || '20', 10);
    const offset = parseInt(searchParams.get('offset') || '0', 10);
    const rawLevels = searchParams.get('levels') || 'city';
    // Handle 'all' levels parameter by including all geographic levels
    const levels =
      rawLevels === 'all' ? ['region', 'province', 'city', 'barangay'] : rawLevels.split(',');
    const maxLevel = searchParams.get('maxLevel') || 'city';
    const minLevel = searchParams.get('minLevel') || 'region';

    if (!query || query.trim().length < 2) {
      // Return empty results instead of error for graceful handling
      return NextResponse.json({ data: [], count: 0 });
    }

    // Use anon key with proper RLS for geographic data
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
    const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

    if (!supabaseUrl || !supabaseAnonKey) {
      return NextResponse.json({ error: 'Missing Supabase configuration' }, { status: 500 });
    }

    const supabase = createClient(supabaseUrl, supabaseAnonKey);

    const rawQuery = query.trim().toLowerCase();

    // Create comprehensive search variations for fuzzy matching
    const variations = [
      `%${rawQuery}%`, // Basic contains search
      `${rawQuery}%`, // Starts with
      `%${rawQuery}`, // Ends with
    ];

    // Split query into words for multi-word fuzzy matching
    const words = rawQuery.split(/\s+/).filter(w => w.length > 0);
    if (words.length > 1) {
      // Add individual word searches
      words.forEach(word => {
        if (word.length >= 2) {
          variations.push(`%${word}%`);
        }
      });

      // Add reversed word order
      const reversed = words.reverse().join(' ');
      variations.push(`%${reversed}%`);
    }

    // Handle common abbreviations and variations
    const commonVariations: Record<string, string[]> = {
      cav: ['cavite'],
      bgc: ['bonifacio global city', 'taguig'],
      qc: ['quezon city'],
      mm: ['metro manila', 'manila'],
      ncr: ['national capital region', 'metro manila'],
      mla: ['manila'],
      makat: ['makati'],
      pasig: ['pasig city'],
      taguig: ['taguig city'],
    };

    // Add abbreviation expansions
    Object.entries(commonVariations).forEach(([abbr, expansions]) => {
      if (rawQuery.includes(abbr)) {
        expansions.forEach(expansion => {
          variations.push(`%${expansion}%`);
          variations.push(`%${expansion.replace(/\s+/g, '')}%`); // Without spaces
        });
      }
    });

    // Handle common prefixes and suffixes
    if (rawQuery.includes('city')) {
      const cityName = rawQuery.replace(/\s*city\s*/g, '').trim();
      if (cityName) {
        variations.push(`%city of ${cityName}%`);
        variations.push(`%${cityName}%`);
        variations.push(`%${cityName} city%`);
      }
    }

    if (rawQuery.includes('municipality')) {
      const munName = rawQuery.replace(/\s*municipality\s*/g, '').trim();
      if (munName) {
        variations.push(`%municipality of ${munName}%`);
        variations.push(`%${munName}%`);
        variations.push(`%${munName} municipality%`);
      }
    }

    // Remove duplicates and empty variations
    const uniqueVariations = Array.from(new Set(variations)).filter(v => v.length > 2);

    const allResults: any[] = [];

    // Search regions if requested
    if (levels.includes('region')) {
      const regionSearchPromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_regions')
          .select('code, name')
          .ilike('name', term)
          .limit(Math.min(limit, 10))
      );

      const regionResults = await Promise.all(regionSearchPromises);
      regionResults.forEach(result => {
        if (result.data) {
          result.data.forEach((region: any) => {
            allResults.push({
              code: region.code,
              name: region.name,
              level: 'region',
              region_code: region.code,
              region_name: region.name,
              full_address: region.name,
            });
          });
        }
      });
    }

    // Search provinces if requested
    if (levels.includes('province')) {
      const provinceSearchPromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_provinces')
          .select(
            `
            code,
            name,
            region_code,
            psgc_regions (code, name)
          `
          )
          .ilike('name', term)
          .limit(Math.min(limit, 15))
      );

      const provinceResults = await Promise.all(provinceSearchPromises);
      provinceResults.forEach(result => {
        if (result.data) {
          result.data.forEach((province: any) => {
            const region = province.psgc_regions;
            allResults.push({
              code: province.code,
              name: province.name,
              level: 'province',
              province_code: province.code,
              province_name: province.name,
              region_code: region?.code,
              region_name: region?.name,
              full_address: [province.name, region?.name].filter(Boolean).join(', '),
            });
          });
        }
      });
    }

    // Search cities/municipalities if requested
    if (levels.includes('city')) {
      // First search cities by name
      const citySearchPromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_cities_municipalities')
          .select(
            `
            code,
            name,
            type,
            is_independent,
            province_code,
            psgc_provinces (
              code,
              name,
              region_code,
              psgc_regions (code, name)
            )
          `
          )
          .ilike('name', term)
          .limit(Math.min(limit, 20))
      );

      // Also search cities by province name (hierarchical search)
      const provinceMatchPromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_cities_municipalities')
          .select(
            `
            code,
            name,
            type,
            is_independent,
            province_code,
            psgc_provinces!inner (
              code,
              name,
              region_code,
              psgc_regions (code, name)
            )
          `
          )
          .filter('psgc_provinces.name', 'ilike', term)
          .limit(Math.min(limit, 25))
      );

      const cityResults = await Promise.all(citySearchPromises);
      const provinceMatchResults = await Promise.all(provinceMatchPromises);

      // Process direct city name matches
      cityResults.forEach(result => {
        if (result.data) {
          result.data.forEach((city: any) => {
            const province = city.psgc_provinces;
            const region = province?.psgc_regions;
            allResults.push({
              code: city.code,
              name: city.name,
              level: 'city',
              type: city.type,
              city_code: city.code,
              city_name: city.name,
              city_type: city.type,
              province_code: province?.code,
              province_name: province?.name,
              region_code: region?.code,
              region_name: region?.name,
              full_address: [city.name, province?.name, region?.name].filter(Boolean).join(', '),
            });
          });
        }
      });

      // Process cities found by province name (hierarchical matches)
      provinceMatchResults.forEach(result => {
        if (result.data) {
          result.data.forEach((city: any) => {
            const province = city.psgc_provinces;
            const region = province?.psgc_regions;
            allResults.push({
              code: city.code,
              name: city.name,
              level: 'city',
              type: city.type,
              city_code: city.code,
              city_name: city.name,
              city_type: city.type,
              province_code: province?.code,
              province_name: province?.name,
              region_code: region?.code,
              region_name: region?.name,
              full_address: [city.name, province?.name, region?.name].filter(Boolean).join(', '),
            });
          });
        }
      });
    }

    // Search barangays if requested
    if (levels.includes('barangay')) {
      // Direct barangay name matches
      const barangaySearchPromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_barangays')
          .select(
            `
            code,
            name,
            city_municipality_code,
            psgc_cities_municipalities (
              code,
              name,
              type,
              province_code,
              psgc_provinces (
                code,
                name,
                region_code,
                psgc_regions (code, name)
              )
            )
          `
          )
          .ilike('name', term)
          .limit(Math.min(limit, 25))
      );

      // Hierarchical search: barangays within matching provinces
      const barangayByProvincePromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_barangays')
          .select(
            `
            code,
            name,
            city_municipality_code,
            psgc_cities_municipalities!inner (
              code,
              name,
              type,
              province_code,
              psgc_provinces!inner (
                code,
                name,
                region_code,
                psgc_regions (code, name)
              )
            )
          `
          )
          .filter('psgc_cities_municipalities.psgc_provinces.name', 'ilike', term)
          .limit(Math.min(limit, 30))
      );

      // Hierarchical search: barangays within matching cities
      const barangayByCityPromises = uniqueVariations.map(term =>
        supabase
          .from('psgc_barangays')
          .select(
            `
            code,
            name,
            city_municipality_code,
            psgc_cities_municipalities!inner (
              code,
              name,
              type,
              province_code,
              psgc_provinces (
                code,
                name,
                region_code,
                psgc_regions (code, name)
              )
            )
          `
          )
          .filter('psgc_cities_municipalities.name', 'ilike', term)
          .limit(Math.min(limit, 20))
      );

      const barangayByProvinceResults = await Promise.all(barangayByProvincePromises);
      const barangayByCityResults = await Promise.all(barangayByCityPromises);

      const barangayResults = await Promise.all(barangaySearchPromises);

      // Process direct barangay name matches
      barangayResults.forEach(result => {
        if (result.data) {
          result.data.forEach((barangay: any) => {
            const city = barangay.psgc_cities_municipalities;
            const province = city?.psgc_provinces;
            const region = province?.psgc_regions;
            allResults.push({
              code: barangay.code,
              name: barangay.name,
              level: 'barangay',
              barangay_code: barangay.code,
              barangay_name: barangay.name,
              city_code: city?.code,
              city_name: city?.name,
              city_type: city?.type,
              province_code: province?.code,
              province_name: province?.name,
              region_code: region?.code,
              region_name: region?.name,
              full_address: [barangay.name, city?.name, province?.name, region?.name]
                .filter(Boolean)
                .join(', '),
            });
          });
        }
      });

      // Process hierarchical matches (barangays within provinces)
      barangayByProvinceResults.forEach(result => {
        if (result.data) {
          result.data.forEach((barangay: any) => {
            const city = barangay.psgc_cities_municipalities;
            const province = city?.psgc_provinces;
            const region = province?.psgc_regions;
            allResults.push({
              code: barangay.code,
              name: barangay.name,
              level: 'barangay',
              barangay_code: barangay.code,
              barangay_name: barangay.name,
              city_code: city?.code,
              city_name: city?.name,
              city_type: city?.type,
              province_code: province?.code,
              province_name: province?.name,
              region_code: region?.code,
              region_name: region?.name,
              full_address: [barangay.name, city?.name, province?.name, region?.name]
                .filter(Boolean)
                .join(', '),
            });
          });
        }
      });

      // Process hierarchical matches (barangays within cities)
      barangayByCityResults.forEach(result => {
        if (result.data) {
          result.data.forEach((barangay: any) => {
            const city = barangay.psgc_cities_municipalities;
            const province = city?.psgc_provinces;
            const region = province?.psgc_regions;
            allResults.push({
              code: barangay.code,
              name: barangay.name,
              level: 'barangay',
              barangay_code: barangay.code,
              barangay_name: barangay.name,
              city_code: city?.code,
              city_name: city?.name,
              city_type: city?.type,
              province_code: province?.code,
              province_name: province?.name,
              region_code: region?.code,
              region_name: region?.name,
              full_address: [barangay.name, city?.name, province?.name, region?.name]
                .filter(Boolean)
                .join(', '),
            });
          });
        }
      });
    }

    // Remove duplicates based on code
    const uniqueResults = allResults.reduce((acc, current) => {
      const exists = acc.find((item: any) => item.code === current.code);
      if (!exists) {
        acc.push(current);
      }
      return acc;
    }, [] as any[]);

    // Enhanced sorting by relevance and geographic hierarchy
    uniqueResults.sort((a: any, b: any) => {
      // First priority: Direct name matches (exact or starts with query)
      const aDirectMatch = a.name.toLowerCase().startsWith(rawQuery) ? 1 : 0;
      const bDirectMatch = b.name.toLowerCase().startsWith(rawQuery) ? 1 : 0;
      if (aDirectMatch !== bDirectMatch) return bDirectMatch - aDirectMatch;

      // Second priority: Exact matches within direct matches
      if (aDirectMatch && bDirectMatch) {
        const aExactMatch = a.name.toLowerCase() === rawQuery ? 1 : 0;
        const bExactMatch = b.name.toLowerCase() === rawQuery ? 1 : 0;
        if (aExactMatch !== bExactMatch) return bExactMatch - aExactMatch;
      }

      // Third priority: Geographic hierarchy grouping
      // Group by province first, then by city within province, then barangays within city
      if (a.province_name !== b.province_name) {
        return (a.province_name || '').localeCompare(b.province_name || '');
      }

      // Within same province: create city groups (city followed by its barangays)
      if (a.city_name !== b.city_name) {
        return (a.city_name || '').localeCompare(b.city_name || '');
      }

      // Within same city: city comes first, then its barangays alphabetically
      const levelOrder = { region: 1, province: 2, city: 3, barangay: 4 };
      const levelDiff =
        levelOrder[a.level as keyof typeof levelOrder] -
        levelOrder[b.level as keyof typeof levelOrder];
      if (levelDiff !== 0) return levelDiff;

      // Final priority: Alphabetical by name
      return a.name.localeCompare(b.name);
    });

    // Apply pagination
    const totalCount = uniqueResults.length;
    const startIndex = Math.max(0, offset);
    const endIndex = Math.min(startIndex + limit, totalCount, startIndex + 100); // Cap at 100 per request
    const data = uniqueResults.slice(startIndex, endIndex);

    return NextResponse.json({
      data: data,
      count: data.length,
      totalCount: totalCount,
      offset: startIndex,
      hasMore: endIndex < totalCount,
    });
  } catch (error) {
    console.error('Address search API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';

import { databaseService } from '@/services/database-service';
import { PSocRecord } from '@/types/api';

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const query = searchParams.get('q');
    const limit = parseInt(searchParams.get('limit') || '20', 10);
    const levels = searchParams.get('levels')?.split(',') || ['occupation']; // Default to occupations only

    if (!query || query.trim().length < 2) {
      // Return empty results instead of error for graceful handling
      return NextResponse.json({ data: [], count: 0 });
    }

    // Use public client for PSOC data search
    const supabase = databaseService.getPublicClient();

    const searchTerm = `%${query.trim()}%`;
    const allResults: any[] = [];

    // Search major groups if requested
    if (levels.includes('major_group')) {
      const { data: majorGroups } = await supabase
        .from('psoc_major_groups')
        .select('*')
        .ilike('title', searchTerm)
        .limit(Math.min(limit, 5));

      if (majorGroups) {
        majorGroups.forEach((item: PSocRecord) => {
          allResults.push({
            code: item.code,
            title: item.title,
            level: 'major_group',
            hierarchy: `Major Group: ${item.title}`,
            match_score: 1,
          });
        });
      }
    }

    // Search sub major groups if requested
    if (levels.includes('sub_major_group')) {
      const { data: subMajorGroups } = await supabase
        .from('psoc_sub_major_groups')
        .select('*')
        .ilike('title', searchTerm)
        .limit(Math.min(limit, 10));

      if (subMajorGroups) {
        subMajorGroups.forEach((item: PSocRecord) => {
          allResults.push({
            code: item.code,
            title: item.title,
            level: 'sub_major_group',
            hierarchy: `Sub Major Group: ${item.title}`,
            match_score: 2,
          });
        });
      }
    }

    // Search unit groups if requested
    if (levels.includes('unit_group')) {
      const { data: unitGroups } = await supabase
        .from('psoc_unit_groups')
        .select('*')
        .ilike('title', searchTerm)
        .limit(Math.min(limit, 10));

      if (unitGroups) {
        unitGroups.forEach((item: PSocRecord) => {
          allResults.push({
            code: item.code,
            title: item.title,
            level: 'unit_group',
            hierarchy: `Unit Group: ${item.title}`,
            match_score: 3,
          });
        });
      }
    }

    // Search unit sub groups if requested
    if (levels.includes('unit_sub_group')) {
      const { data: unitSubGroups } = await supabase
        .from('psoc_unit_sub_groups')
        .select('*')
        .ilike('title', searchTerm)
        .limit(Math.min(limit, 10));

      if (unitSubGroups) {
        unitSubGroups.forEach((item: PSocRecord) => {
          allResults.push({
            code: item.code,
            title: item.title,
            level: 'unit_sub_group',
            hierarchy: `Unit Sub Group: ${item.title}`,
            match_score: 4,
          });
        });
      }
    }

    // Search occupations if requested (most specific)
    if (levels.includes('occupation')) {
      const { data: occupations } = await supabase
        .from('psoc_occupation_search')
        .select('*')
        .ilike('occupation_title', searchTerm)
        .limit(Math.min(limit, 15));

      if (occupations) {
        occupations.forEach((item: PSocRecord) => {
          allResults.push({
            code: item.occupation_code,
            title: item.occupation_title,
            level: 'occupation',
            hierarchy: item.full_hierarchy || item.occupation_title,
            match_score: 5, // Highest priority for specific occupations
          });
        });
      }
    }

    // Remove duplicates based on code
    const uniqueResults = allResults.reduce((acc, current) => {
      const exists = acc.find((item: any) => item.code === current.code);
      if (!exists) {
        acc.push(current);
      }
      return acc;
    }, [] as any[]);

    // Sort by level hierarchy (major_group -> occupation) and then by match score
    const levelOrder: Record<string, number> = {
      major_group: 1,
      sub_major_group: 2,
      unit_group: 3,
      unit_sub_group: 4,
      occupation: 5,
    };

    const sortedResults = uniqueResults
      .sort((a: any, b: any) => {
        const levelDiff = (levelOrder[a.level] || 0) - (levelOrder[b.level] || 0);
        if (levelDiff !== 0) return levelDiff;
        return (b.match_score || 0) - (a.match_score || 0);
      })
      .slice(0, limit);

    return NextResponse.json({
      data: sortedResults,
      count: sortedResults.length,
      total_found: uniqueResults.length,
    });
  } catch (error) {
    console.error('PSOC search error:', error);
    return NextResponse.json(
      {
        error: 'Failed to search PSOC data',
        data: [],
        count: 0,
      },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';

import { createPublicSupabaseClient, createAdminSupabaseClient } from '@/lib/data/client-factory';
import type { ResidentWithHousehold } from '@/types/api';
import type { AuthUserProfile } from '@/types/auth';

// Migration information validation schema
const migrationInfoSchema = z.object({
  previous_barangay_code: z.string().max(10).optional().or(z.literal('')),
  previous_city_municipality_code: z.string().max(10).optional().or(z.literal('')),
  previous_province_code: z.string().max(10).optional().or(z.literal('')),
  previous_region_code: z.string().max(10).optional().or(z.literal('')),
  date_of_transfer: z.string().optional().or(z.literal('')),
  reason_for_leaving: z.string().max(500).optional().or(z.literal('')),
  reason_for_transferring: z.string().max(500).optional().or(z.literal('')),
  length_of_stay_previous_months: z.number().int().min(0).optional(),
  duration_of_stay_current_months: z.number().int().min(0).optional(),
  is_intending_to_return: z.boolean().optional(),
});

// GET /api/residents/[id]/migration - Get migration information
export async function GET(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const resolvedParams = await params;
    const residentId = resolvedParams.id;

    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized - No auth token' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create regular client to verify user
    const supabase = createPublicSupabaseClient();

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized - Invalid token' }, { status: 401 });
    }

    // Use service role client to bypass RLS
    const supabaseAdmin = createAdminSupabaseClient() as any;

    // Get user profile to verify barangay access
    const profileResult = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    const userProfile = profileResult.data as AuthUserProfile | null;
    const profileError = profileResult.error;

    if (profileError || !userProfile?.barangay_code) {
      return NextResponse.json(
        { error: 'User profile not found or no barangay assigned' },
        { status: 400 }
      );
    }

    // Verify resident access through household
    const residentResult = await supabaseAdmin
      .from('residents')
      .select(
        `
        id,
        households!inner(barangay_code)
      `
      )
      .eq('id', residentId)
      .eq('households.barangay_code', userProfile.barangay_code)
      .single();

    const resident = residentResult.data as ResidentWithHousehold | null;
    const residentError = residentResult.error;

    if (residentError || !resident) {
      return NextResponse.json({ error: 'Resident not found or access denied' }, { status: 404 });
    }

    // Get migration information
    const { data: migrationInfo, error: migrationError } = await supabaseAdmin
      .from('resident_migrant_info')
      .select('*')
      .eq('resident_id', residentId)
      .maybeSingle();

    if (migrationError) {
      console.error('Migration info query error:', migrationError);
      return NextResponse.json({ error: 'Failed to fetch migration information' }, { status: 500 });
    }

    return NextResponse.json({
      migrationInfo: migrationInfo || null,
      message: 'Migration information retrieved successfully',
    });
  } catch (error) {
    console.error('Migration info API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

// PUT /api/residents/[id]/migration - Update migration information
export async function PUT(request: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const resolvedParams = await params;
    const residentId = resolvedParams.id;
    const migrationData = await request.json();

    // Validate migration data
    const validationResult = migrationInfoSchema.safeParse(migrationData);
    if (!validationResult.success) {
      return NextResponse.json(
        {
          error: 'Validation failed',
          details: validationResult.error.issues.map(issue => ({
            field: issue.path.join('.'),
            message: issue.message,
          })),
        },
        { status: 400 }
      );
    }

    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized - No auth token' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create regular client to verify user
    const supabase = createPublicSupabaseClient();

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized - Invalid token' }, { status: 401 });
    }

    // Use service role client to bypass RLS
    const supabaseAdmin = createAdminSupabaseClient() as any;

    // Get user profile to verify barangay access
    const profileResult = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    const userProfile = profileResult.data as AuthUserProfile | null;
    const profileError = profileResult.error;

    if (profileError || !userProfile?.barangay_code) {
      return NextResponse.json(
        { error: 'User profile not found or no barangay assigned' },
        { status: 400 }
      );
    }

    // Verify resident access through household
    const residentResult = await supabaseAdmin
      .from('residents')
      .select(
        `
        id,
        households!inner(barangay_code)
      `
      )
      .eq('id', residentId)
      .eq('households.barangay_code', userProfile.barangay_code)
      .single();

    const resident = residentResult.data as ResidentWithHousehold | null;
    const residentError = residentResult.error;

    if (residentError || !resident) {
      return NextResponse.json({ error: 'Resident not found or access denied' }, { status: 404 });
    }

    // Check if migration record exists
    const { data: existingMigration, error: checkError } = await supabaseAdmin
      .from('resident_migrant_info')
      .select('id')
      .eq('resident_id', residentId)
      .maybeSingle();

    if (checkError) {
      console.error('Migration check error:', checkError);
      return NextResponse.json({ error: 'Failed to check migration information' }, { status: 500 });
    }

    const updateData = {
      ...validationResult.data,
      updated_at: new Date().toISOString(),
    };

    if (existingMigration) {
      // Update existing record
      const { data: updatedMigration, error: updateError } = await supabaseAdmin
        .from('resident_migrant_info')
        .update(updateData)
        .eq('resident_id', residentId)
        .select()
        .single();

      if (updateError) {
        console.error('Migration update error:', updateError);
        return NextResponse.json(
          { error: 'Failed to update migration information' },
          { status: 500 }
        );
      }

      return NextResponse.json({
        migrationInfo: updatedMigration,
        message: 'Migration information updated successfully',
      });
    } else {
      // Create new record
      const insertData = {
        resident_id: residentId,
        ...updateData,
        created_at: new Date().toISOString(),
      };

      const { data: newMigration, error: insertError } = await supabaseAdmin
        .from('resident_migrant_info')
        .insert(insertData as any)
        .select()
        .single();

      if (insertError) {
        console.error('Migration insert error:', insertError);
        return NextResponse.json(
          { error: 'Failed to create migration information' },
          { status: 500 }
        );
      }

      return NextResponse.json({
        migrationInfo: newMigration,
        message: 'Migration information created successfully',
      });
    }
  } catch (error) {
    console.error('Migration info API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

// DELETE /api/residents/[id]/migration - Delete migration information
export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const resolvedParams = await params;
    const residentId = resolvedParams.id;

    // Get auth header from the request
    const authHeader = request.headers.get('Authorization') || request.headers.get('authorization');

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized - No auth token' }, { status: 401 });
    }

    const token = authHeader.split(' ')[1];

    // Create regular client to verify user
    const supabase = createPublicSupabaseClient();

    // Verify the user token
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser(token);

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized - Invalid token' }, { status: 401 });
    }

    // Use service role client to bypass RLS
    const supabaseAdmin = createAdminSupabaseClient() as any;

    // Get user profile to verify barangay access
    const profileResult = await supabaseAdmin
      .from('auth_user_profiles')
      .select('barangay_code')
      .eq('id', user.id)
      .single();

    const userProfile = profileResult.data as AuthUserProfile | null;
    const profileError = profileResult.error;

    if (profileError || !userProfile?.barangay_code) {
      return NextResponse.json(
        { error: 'User profile not found or no barangay assigned' },
        { status: 400 }
      );
    }

    // Verify resident access through household
    const residentResult = await supabaseAdmin
      .from('residents')
      .select(
        `
        id,
        households!inner(barangay_code)
      `
      )
      .eq('id', residentId)
      .eq('households.barangay_code', userProfile.barangay_code)
      .single();

    const resident = residentResult.data as ResidentWithHousehold | null;
    const residentError = residentResult.error;

    if (residentError || !resident) {
      return NextResponse.json({ error: 'Resident not found or access denied' }, { status: 404 });
    }

    // Delete migration information
    const { error: deleteError } = await supabaseAdmin
      .from('resident_migrant_info')
      .delete()
      .eq('resident_id', residentId);

    if (deleteError) {
      console.error('Migration delete error:', deleteError);
      return NextResponse.json(
        { error: 'Failed to delete migration information' },
        { status: 500 }
      );
    }

    return NextResponse.json({
      message: 'Migration information deleted successfully',
    });
  } catch (error) {
    console.error('Migration delete API error:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';

import {
  withAuth,
  createAdminSupabaseClient,
  getAccessLevel,
  logger,
  logError,
  auditDataOperation,
  createSuccessResponse,
  createValidationErrorResponse,
  withNextRequestErrorHandling,
  withSecurityHeaders,
  createResidentSchema,
} from '@/lib';
import { RequestContext, Role } from '@/lib/authentication/types';
import { createRateLimitHandler } from '@/lib/security/rate-limit';
import { ResidentFormData } from '@/types';
import type { AuthenticatedUser } from '@/types/auth';

export const GET = withSecurityHeaders(
  withAuth(
    {
      requiredPermissions: [
        'residents.manage.barangay',
        'residents.manage.city',
        'residents.manage.province',
        'residents.manage.region',
        'residents.manage.all',
      ],
    },
    withNextRequestErrorHandling(
      async (request: NextRequest, context: RequestContext, user: AuthenticatedUser) => {
        // Extract params from the URL path since this is a dynamic route
        const url = new URL(request.url);
        const pathSegments = url.pathname.split('/');
        const residentId = pathSegments[pathSegments.length - 1];

        // Apply rate limiting
        const rateLimitResponse = await createRateLimitHandler('SEARCH_RESIDENTS')(
          request,
          user.id
        );
        if (rateLimitResponse) return rateLimitResponse;

        const supabaseAdmin = createAdminSupabaseClient() as any;

        // Get access level for geographic filtering
        const accessLevel = getAccessLevel(user.role);

        // Build query with geographic filtering
        let query = supabaseAdmin
          .from('residents')
          .select(
            `
            *,
            households!inner(
              code,
              barangay_code,
              name,
              address,
              house_number,
              street_id,
              subdivision_id,
              city_municipality_code,
              province_code,
              region_code,
              zip_code,
              no_of_families,
              no_of_household_members,
              no_of_migrants,
              household_type,
              tenure_status,
              tenure_others_specify,
              household_unit,
              monthly_income,
              income_class,
              household_head_id,
              household_head_position,
              is_active,
              created_by,
              updated_by,
              created_at,
              updated_at
            )
          `
          )
          .eq('id', residentId)
          .eq('is_active', true);

        // Apply geographic filtering based on user's access level
        switch (accessLevel) {
          case 'barangay':
            if (user.barangayCode) {
              query = query.eq('households.barangay_code', user.barangayCode);
            }
            break;
          case 'city':
            if (user.cityCode) {
              query = query.eq('households.city_municipality_code', user.cityCode);
            }
            break;
          case 'province':
            if (user.provinceCode) {
              query = query.eq('households.province_code', user.provinceCode);
            }
            break;
          case 'region':
            if (user.regionCode) {
              query = query.eq('households.region_code', user.regionCode);
            }
            break;
          case 'national':
            // No filtering for national access
            break;
        }

        const { data: residentWithHousehold, error: residentError } = await query.single();

        if (residentError || !residentWithHousehold) {
          logError(new Error('Resident not found'), `ID: ${residentId}`);
          throw new Error('Resident not found or access denied');
        }

        // Get sectoral info separately to avoid join issues
        const { data: sectoralInfoArray, error: sectoralError } = await supabaseAdmin
          .from('resident_sectoral_info')
          .select('*')
          .eq('resident_id', residentId);

        // Handle the array result - take first record if exists
        const sectoralInfo =
          sectoralInfoArray && sectoralInfoArray.length > 0 ? sectoralInfoArray[0] : null;

        // Extract household info and merge with resident data
        const { households, ...residentData } = residentWithHousehold;

        const resident = {
          ...residentData,
          ...(sectoralInfo || {}),
        };
        const household = households;

        // Get geographic information
        let geoInfo = {};
        try {
          const { data: barangayData } = await supabaseAdmin
            .from('psgc_barangays')
            .select(
              `
          code,
          name,
          psgc_cities_municipalities!inner(
            code,
            name,
            type,
            psgc_provinces!inner(
              code,
              name,
              psgc_regions!inner(
                code,
                name
              )
            )
          )
          `
            )
            .eq('code', household.barangay_code)
            .single();

          if (barangayData) {
            const cityMunData = barangayData.psgc_cities_municipalities as any;
            const province = cityMunData.psgc_provinces;
            const region = province.psgc_regions;

            geoInfo = {
              barangay_info: {
                code: barangayData.code,
                name: barangayData.name,
              },
              city_municipality_info: {
                code: cityMunData.code,
                name: cityMunData.name,
                type: cityMunData.type,
              },
              province_info: {
                code: province.code,
                name: province.name,
              },
              region_info: {
                code: region.code,
                name: region.name,
              },
            };
          }
        } catch (geoError) {
          console.warn('Geographic info load failed:', geoError);
        }

        // Get birth place information if birth_place_code exists
        let birthPlaceInfo = {};
        if (resident.birth_place_code) {
          try {
            // Resolve from PSGC tables directly with hierarchical formatting
            // Try barangay first (most specific)
            const { data: barangayData } = await supabaseAdmin
              .from('psgc_barangays')
              .select(
                `
            code,
            name,
            psgc_cities_municipalities!inner(
              name,
              type,
              psgc_provinces!inner(name)
            )
          `
              )
              .eq('code', resident.birth_place_code)
              .maybeSingle();

            if (barangayData) {
              const city = barangayData.psgc_cities_municipalities as any;
              const province = city.psgc_provinces;
              birthPlaceInfo = {
                birth_place_info: {
                  code: barangayData.code,
                  name: `${city.name}, ${province.name}`,
                  level: 'barangay',
                },
              };
            } else {
              // Try city/municipality
              const { data: cityData } = await supabaseAdmin
                .from('psgc_cities_municipalities')
                .select(
                  `
              code,
              name,
              type,
              psgc_provinces!inner(name)
            `
                )
                .eq('code', resident.birth_place_code)
                .maybeSingle();

              if (cityData) {
                const province = cityData.psgc_provinces as any;
                birthPlaceInfo = {
                  birth_place_info: {
                    code: cityData.code,
                    name: `${cityData.name}, ${province.name}`,
                    level: 'city_municipality',
                    type: cityData.type, // Include the actual type (city or municipality)
                  },
                };
              } else {
                // Try province
                const { data: provinceData } = await supabaseAdmin
                  .from('psgc_provinces')
                  .select('code, name')
                  .eq('code', resident.birth_place_code)
                  .maybeSingle();

                if (provinceData) {
                  birthPlaceInfo = {
                    birth_place_info: {
                      code: provinceData.code,
                      name: provinceData.name,
                      level: 'province',
                    },
                  };
                } else {
                  // Try region (least specific)
                  const { data: regionData } = await supabaseAdmin
                    .from('psgc_regions')
                    .select('code, name')
                    .eq('code', resident.birth_place_code)
                    .maybeSingle();

                  if (regionData) {
                    birthPlaceInfo = {
                      birth_place_info: {
                        code: regionData.code,
                        name: regionData.name,
                        level: 'region',
                      },
                    };
                  } else {
                    // All fallbacks failed
                    console.warn('Could not resolve birth place:', resident.birth_place_code);
                  }
                }
              }
            }
          } catch (birthPlaceError) {
            console.warn('Birth place info load failed:', birthPlaceError);
          }
        }

        // Get occupation title and hierarchy if occupation_code exists
        let occupationInfo = {};
        if (resident.occupation_code) {
          try {
            const { data: psocData } = await supabaseAdmin
              .from('psoc_unified_search')
              .select(
                'psoc_code, occupation_title, display_text, psoc_level, level_name, parent_code, parent_title'
              )
              .eq('psoc_code', resident.occupation_code)
              .maybeSingle();

            if (psocData) {
              // Build the complete hierarchy from bottom to top
              const hierarchyParts = [];

              // Start with the occupation title
              hierarchyParts.push(psocData.occupation_title);

              // Add parent if it exists (Level 3)
              if (psocData.parent_title) {
                hierarchyParts.push(psocData.parent_title);
              }

              // For a complete hierarchy, we could traverse up further, but
              // showing occupation + immediate parent is usually sufficient
              // Full format: "Graphic And Multimedia Designers ‚Ä∫ Architects, Planners, Surveyors And Designers"
              const hierarchy = hierarchyParts.join(' ‚Ä∫ ');

              occupationInfo = {
                occupation_title: hierarchy,
                occupation_code_display: psocData.psoc_code, // Keep the code for reference
                occupation_level: psocData.level_name, // e.g., "Unit Group"
              };
            }
          } catch (occupationError) {
            console.warn('Occupation info load failed:', occupationError);
          }
        }

        // Audit the data access
        await auditDataOperation('view', 'resident', residentId, context, {
          fullName: `${resident.first_name || ''} ${resident.last_name || ''}`,
        });

        return createSuccessResponse(
          {
            resident: {
              ...resident,
              ...geoInfo,
              ...birthPlaceInfo,
              ...occupationInfo,
            },
            household,
          },
          'Resident retrieved successfully',
          context
        );
      }
    )
  )
);

export const PUT = withSecurityHeaders(
  withAuth(
    {
      requiredPermissions: [
        'residents.manage.barangay',
        'residents.manage.city',
        'residents.manage.province',
        'residents.manage.region',
        'residents.manage.all',
      ],
    },
    withNextRequestErrorHandling(
      async (request: NextRequest, context: RequestContext, user: AuthenticatedUser) => {
        // Extract params from the URL path since this is a dynamic route
        const url = new URL(request.url);
        const pathSegments = url.pathname.split('/');
        const residentId = pathSegments[pathSegments.length - 1];
        console.log('üîß PUT /api/residents/[id] - Received request for resident ID:', residentId);

        // Apply rate limiting
        const rateLimitResponse = await createRateLimitHandler('RESIDENT_CREATE')(request, user.id);
        if (rateLimitResponse) return rateLimitResponse;

        // Parse and validate request body
        const body = await request.json();
        console.log('üîß PUT /api/residents/[id] - Update data keys:', Object.keys(body));

        const validationResult = createResidentSchema.safeParse(body);

        if (!validationResult.success) {
          logger.error('Resident update validation failed', {
            issueCount: validationResult.error.issues.length,
            allIssues: validationResult.error.issues.map(i => ({
              path: i.path,
              message: i.message,
              code: (i as any).code,
            })),
          });
          return createValidationErrorResponse(
            validationResult.error.issues.map((err: z.ZodIssue) => ({
              field: err.path.join('.'),
              message: err.message,
            })),
            context
          );
        }

        const updateData = validationResult.data as ResidentFormData;
        const supabaseAdmin = createAdminSupabaseClient() as any;

        // Get access level for geographic filtering
        const accessLevel = getAccessLevel(user.role);

        // Check if resident exists and user has access through household
        let checkQuery = supabaseAdmin
          .from('residents')
          .select(
            `
            id,
            household_code,
            households!inner(barangay_code, city_municipality_code, province_code, region_code)
          `
          )
          .eq('id', residentId)
          .eq('is_active', true);

        // Apply geographic filtering based on user's access level
        switch (accessLevel) {
          case 'barangay':
            if (user.barangayCode) {
              checkQuery = checkQuery.eq('households.barangay_code', user.barangayCode);
            }
            break;
          case 'city':
            if (user.cityCode) {
              checkQuery = checkQuery.eq('households.city_municipality_code', user.cityCode);
            }
            break;
          case 'province':
            if (user.provinceCode) {
              checkQuery = checkQuery.eq('households.province_code', user.provinceCode);
            }
            break;
          case 'region':
            if (user.regionCode) {
              checkQuery = checkQuery.eq('households.region_code', user.regionCode);
            }
            break;
          case 'national':
            // No filtering for national access
            break;
        }

        const { data: existingResident, error: checkError } = await checkQuery.single();

        if (checkError || !existingResident) {
          logError(new Error('Resident not found for update'), `ID: ${residentId}`);
          throw new Error('Resident not found or access denied');
        }

        // Separate sectoral data from main resident data
        const {
          is_labor_force_employed,
          is_unemployed,
          is_overseas_filipino_worker,
          is_person_with_disability,
          is_out_of_school_children,
          is_out_of_school_youth,
          is_senior_citizen,
          is_registered_senior_citizen,
          is_solo_parent,
          is_indigenous_people,
          is_migrant,
          ...mainResidentData
        } = updateData as ResidentFormData & {
          is_labor_force_employed?: boolean;
          is_unemployed?: boolean;
          is_overseas_filipino_worker?: boolean;
          is_person_with_disability?: boolean;
          is_out_of_school_children?: boolean;
          is_out_of_school_youth?: boolean;
          is_senior_citizen?: boolean;
          is_registered_senior_citizen?: boolean;
          is_solo_parent?: boolean;
          is_indigenous_people?: boolean;
          is_migrant?: boolean;
        };

        // Only include sectoral fields that are explicitly set (not undefined)
        const sectoralData: Record<string, boolean> = {};

        if (is_labor_force_employed !== undefined)
          sectoralData.is_labor_force_employed = is_labor_force_employed;
        if (is_unemployed !== undefined) sectoralData.is_unemployed = is_unemployed;
        if (is_overseas_filipino_worker !== undefined)
          sectoralData.is_overseas_filipino_worker = is_overseas_filipino_worker;
        if (is_person_with_disability !== undefined)
          sectoralData.is_person_with_disability = is_person_with_disability;
        if (is_out_of_school_children !== undefined)
          sectoralData.is_out_of_school_children = is_out_of_school_children;
        if (is_out_of_school_youth !== undefined)
          sectoralData.is_out_of_school_youth = is_out_of_school_youth;
        if (is_senior_citizen !== undefined) sectoralData.is_senior_citizen = is_senior_citizen;
        if (is_registered_senior_citizen !== undefined)
          sectoralData.is_registered_senior_citizen = is_registered_senior_citizen;
        if (is_solo_parent !== undefined) sectoralData.is_solo_parent = is_solo_parent;
        if (is_indigenous_people !== undefined)
          sectoralData.is_indigenous_people = is_indigenous_people;
        if (is_migrant !== undefined) sectoralData.is_migrant = is_migrant;

        // Prepare data for update with exact database field names
        const insertData = {
          // Required fields
          first_name: mainResidentData.first_name,
          last_name: mainResidentData.last_name,
          birthdate: mainResidentData.birthdate,
          sex: mainResidentData.sex,

          // Optional fields (using exact database field names)
          middle_name: mainResidentData.middle_name || null,
          extension_name: mainResidentData.extension_name || null,
          mobile_number: mainResidentData.mobile_number || null,
          telephone_number: mainResidentData.telephone_number || null,
          email: mainResidentData.email || null,
          mother_maiden_first: mainResidentData.mother_maiden_first || null,
          mother_maiden_middle: mainResidentData.mother_maiden_middle || null,
          mother_maiden_last: mainResidentData.mother_maiden_last || null,
          birth_place_code: mainResidentData.birth_place_code || null,
          household_code: mainResidentData.household_code,

          // Additional fields with defaults
          civil_status: mainResidentData.civil_status || 'single',
          civil_status_others_specify: mainResidentData.civil_status_others_specify || null,
          citizenship: mainResidentData.citizenship || 'filipino',
          blood_type: mainResidentData.blood_type || null,
          ethnicity: mainResidentData.ethnicity || null,
          religion: mainResidentData.religion || 'roman_catholic',
          religion_others_specify: mainResidentData.religion_others_specify || null,
          employment_status: mainResidentData.employment_status || null,
          education_attainment: mainResidentData.education_attainment || null,
          is_graduate: mainResidentData.is_graduate || false,
          occupation_code: mainResidentData.occupation_code || null,
          height: mainResidentData.height || null,
          weight: mainResidentData.weight || null,
          complexion: mainResidentData.complexion || null,
          philsys_card_number: mainResidentData.philsys_card_number || null,
          is_voter: mainResidentData.is_voter || null,
          is_resident_voter: mainResidentData.is_resident_voter || null,
          last_voted_date:
            mainResidentData.last_voted_date && mainResidentData.last_voted_date !== ''
              ? mainResidentData.last_voted_date
              : null,
          updated_by: user.id,
          updated_at: new Date().toISOString(),
        };

        // Update resident data (access already verified)
        const { data: updatedResident, error: updateError } = await supabaseAdmin
          .from('residents')
          .update(insertData)
          .eq('id', residentId)
          .select()
          .single();

        if (updateError) {
          logError(new Error('Resident update error'), JSON.stringify(updateError));
          throw updateError;
        }

        // Update sectoral information if any sectoral fields were provided
        if (Object.keys(sectoralData).length > 0) {
          // Check if sectoral record exists
          const { data: existingSectoral, error: checkError } = await supabaseAdmin
            .from('resident_sectoral_info')
            .select('resident_id')
            .eq('resident_id', residentId)
            .maybeSingle(); // Use maybeSingle instead of single to avoid error when no record exists

          // Log the check result for debugging
          console.log('Sectoral record check:', {
            exists: !!existingSectoral,
            residentId,
            checkError: checkError?.message,
          });

          if (existingSectoral) {
            // Update existing sectoral record
            const { data: updatedData, error: sectoralUpdateError } = await supabaseAdmin
              .from('resident_sectoral_info')
              .update({
                ...sectoralData,
                updated_at: new Date().toISOString(),
              })
              .eq('resident_id', residentId)
              .select();

            if (sectoralUpdateError) {
              console.error('Sectoral update error:', {
                errorMessage: sectoralUpdateError.message,
                errorCode: sectoralUpdateError.code,
                data: sectoralData,
                residentId,
              });
              throw new Error(
                `Failed to update sectoral information: ${sectoralUpdateError.message || 'Unknown error'}`
              );
            }

            console.log('Successfully updated sectoral data:', updatedData);
          } else {
            // Create new sectoral record (table doesn't have created_by/updated_by columns)
            const sectoralInsertData = {
              resident_id: residentId,
              ...sectoralData,
              created_at: new Date().toISOString(),
              updated_at: new Date().toISOString(),
            };

            console.log(
              'Attempting to insert sectoral data:',
              JSON.stringify(sectoralInsertData, null, 2)
            );

            const { data: insertedData, error: sectoralInsertError } = await supabaseAdmin
              .from('resident_sectoral_info')
              .insert(sectoralInsertData)
              .select();

            if (sectoralInsertError) {
              console.error('Sectoral insert error details:', {
                errorMessage: sectoralInsertError.message,
                errorCode: sectoralInsertError.code,
                errorDetails: sectoralInsertError.details,
                errorHint: sectoralInsertError.hint,
                data: sectoralInsertData,
                residentId,
              });
              throw new Error(
                `Failed to create sectoral information: ${sectoralInsertError.message || 'Unknown error'}`
              );
            }

            console.log('Successfully inserted sectoral data:', insertedData);
          }
        }

        if (!updatedResident) {
          throw new Error('Resident not found or access denied');
        }

        // Audit the update
        await auditDataOperation('update', 'resident', residentId, context, {
          fullName: `${mainResidentData.first_name || ''} ${mainResidentData.last_name || ''}`,
        });

        // Fetch the complete resident record with sectoral information for the response
        const { data: completeResident, error: fetchError } = await supabaseAdmin
          .from('residents')
          .select(
            `
            *,
            resident_sectoral_info (
              is_labor_force_employed,
              is_unemployed,
              is_overseas_filipino_worker,
              is_person_with_disability,
              is_out_of_school_children,
              is_out_of_school_youth,
              is_senior_citizen,
              is_registered_senior_citizen,
              is_solo_parent,
              is_indigenous_people,
              is_migrant
            ),
            resident_migrant_info (
              previous_barangay_code,
              previous_city_municipality_code,
              previous_province_code,
              previous_region_code,
              length_of_stay_previous_months,
              reason_for_migration,
              date_of_transfer,
              duration_of_stay_current_months,
              is_intending_to_return
            )
          `
          )
          .eq('id', residentId)
          .eq('is_active', true)
          .maybeSingle();

        if (fetchError) {
          logger.warn('Error fetching complete resident record', fetchError);
          // Still return the basic record if fetch fails
          return createSuccessResponse(
            { resident: updatedResident },
            'Resident updated successfully',
            context
          );
        }

        return createSuccessResponse(
          { resident: completeResident || updatedResident },
          'Resident updated successfully',
          context
        );
      }
    )
  )
);

export const DELETE = withSecurityHeaders(
  withAuth(
    {
      requiredPermissions: [
        'residents.manage.barangay',
        'residents.manage.city',
        'residents.manage.province',
        'residents.manage.region',
        'residents.manage.all',
      ],
    },
    withNextRequestErrorHandling(
      async (request: NextRequest, context: RequestContext, user: AuthenticatedUser) => {
        // Extract params from the URL path since this is a dynamic route
        const url = new URL(request.url);
        const pathSegments = url.pathname.split('/');
        const residentId = pathSegments[pathSegments.length - 1];

        // Apply rate limiting
        const rateLimitResponse = await createRateLimitHandler('RESIDENT_CREATE')(request, user.id);
        if (rateLimitResponse) return rateLimitResponse;

        const supabaseAdmin = createAdminSupabaseClient() as any;
        const accessLevel = getAccessLevel(user.role);

        // Check if resident exists and user has access through household
        let checkQuery = supabaseAdmin
          .from('residents')
          .select(
            `
            id,
            household_code,
            first_name,
            last_name,
            households!inner(barangay_code, city_municipality_code, province_code, region_code)
          `
          )
          .eq('id', residentId)
          .eq('is_active', true);

        // Apply geographic filtering based on user's access level
        switch (accessLevel) {
          case 'barangay':
            if (user.barangayCode) {
              checkQuery = checkQuery.eq('households.barangay_code', user.barangayCode);
            }
            break;
          case 'city':
            if (user.cityCode) {
              checkQuery = checkQuery.eq('households.city_municipality_code', user.cityCode);
            }
            break;
          case 'province':
            if (user.provinceCode) {
              checkQuery = checkQuery.eq('households.province_code', user.provinceCode);
            }
            break;
          case 'region':
            if (user.regionCode) {
              checkQuery = checkQuery.eq('households.region_code', user.regionCode);
            }
            break;
          case 'national':
            // No filtering for national access
            break;
        }

        const { data: existingResident, error: checkError } = await checkQuery.single();

        if (checkError || !existingResident) {
          logError(new Error('Resident not found for deletion'), `ID: ${residentId}`);
          throw new Error('Resident not found or access denied');
        }

        // Audit the deletion before performing it
        await auditDataOperation('delete', 'resident', residentId, context, {
          fullName: `${existingResident.first_name} ${existingResident.last_name}`,
        });

        // Soft delete: Update is_active to false instead of hard delete
        const { error: softDeleteError } = await supabaseAdmin
          .from('residents')
          .update({
            is_active: false,
            updated_at: new Date().toISOString(),
            updated_by: user.id,
          })
          .eq('id', residentId);

        if (softDeleteError) {
          logError(new Error('Resident soft delete error'), JSON.stringify(softDeleteError));
          // If soft delete fails, try hard delete (CASCADE will handle related records)
          const { error: hardDeleteError } = await supabaseAdmin
            .from('residents')
            .delete()
            .eq('id', residentId);

          if (hardDeleteError) {
            logError(new Error('Resident hard delete error'), JSON.stringify(hardDeleteError));
            throw new Error('Failed to delete resident');
          }
        }

        return createSuccessResponse(
          {
            id: residentId,
            name: `${existingResident.first_name} ${existingResident.last_name}`,
          },
          'Resident deleted successfully',
          context
        );
      }
    )
  )
);

