'use client';

import { cva, type VariantProps } from 'class-variance-authority';
import React, { forwardRef, InputHTMLAttributes } from 'react';

import { cn } from '@/lib';

import { TitleDescription } from './TitleDescription';

const controlVariants = cva(
  'relative inline-flex items-center cursor-pointer disabled:cursor-not-allowed',
  {
    variants: {
      size: {
        sm: 'text-sm',
        md: 'text-base',
        lg: 'text-lg',
      },
    },
    defaultVariants: {
      size: 'md',
    },
  }
);

// Common input variants for checkbox and radio
const inputVariants = cva(
  'relative border transition-colors focus:outline-hidden focus:ring-2 focus:ring-offset-1',
  {
    variants: {
      variant: {
        default: 'border-[#d4d4d4] bg-white focus:ring-[#7c3aed]/20',
        primary: 'border-[#d4d4d4] bg-white focus:ring-[#2563eb]/20',
        error: 'border-[#dc2626] bg-white focus:ring-[#dc2626]/20',
        disabled: 'border-[#d4d4d4] bg-[#fafafa] cursor-not-allowed',
      },
      size: {
        sm: 'w-4 h-4',
        md: 'w-5 h-5',
        lg: 'w-6 h-6',
      },
      type: {
        checkbox: 'rounded-sm',
        radio: 'rounded-full',
        toggle: 'rounded-full', // Will be overridden with specific toggle styles
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'md',
      type: 'checkbox',
    },
  }
);

// Toggle-specific variants
const toggleSwitchVariants = cva(
  'relative inline-flex items-center rounded-full border-2 transition-colors duration-200 ease-in-out focus:outline-hidden focus:ring-2 focus:ring-offset-2',
  {
    variants: {
      variant: {
        default: 'border-transparent focus:ring-[#7c3aed]/20',
        primary: 'border-transparent focus:ring-[#2563eb]/20',
        error: 'border-transparent focus:ring-[#dc2626]/20',
        disabled: 'border-transparent cursor-not-allowed',
      },
      size: {
        sm: 'h-5 w-9',
        md: 'h-6 w-11',
        lg: 'h-7 w-13',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'md',
    },
  }
);

export interface ControlProps
  extends Omit<InputHTMLAttributes<HTMLInputElement>, 'size' | 'onToggle'>,
    VariantProps<typeof controlVariants> {
  /** Option type: checkbox, radio, or toggle */
  type: 'checkbox' | 'radio' | 'toggle';
  /** Label text displayed next to the option */
  label?: string;
  /** Description text displayed below the label */
  description?: string;
  /** Error message to display */
  errorMessage?: string;
  /** Visual variant */
  variant?: 'default' | 'primary' | 'error' | 'disabled';
  /** Indeterminate state (checkbox only) */
  indeterminate?: boolean;
  /** Toggle change handler (toggle only) */
  onToggle?: (checked: boolean) => void;
}

const Control = forwardRef<HTMLInputElement, ControlProps>(
  (
    {
      className,
      size = 'md',
      variant = 'default',
      type,
      label,
      description,
      errorMessage,
      indeterminate = false,
      disabled,
      checked = false,
      onToggle,
      onChange,
      ...props
    },
    ref
  ) => {
    const actualVariant = disabled ? 'disabled' : errorMessage ? 'error' : variant;
    const checkboxRef = React.useRef<HTMLInputElement>(null);

    // Handle indeterminate state for checkboxes
    React.useEffect(() => {
      if (type === 'checkbox' && checkboxRef.current) {
        checkboxRef.current.indeterminate = indeterminate;
      }
    }, [indeterminate, type]);

    // Use either the forwarded ref or our internal ref
    const inputRef = ref || checkboxRef;

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const newChecked = e.target.checked;
      onChange?.(e);
      if (type === 'toggle') {
        onToggle?.(newChecked);
      }
    };

    // Render toggle
    const renderToggle = () => {
      const getBackgroundColor = () => {
        if (disabled) {
          return checked ? 'bg-[#d4d4d4]' : 'bg-[#fafafa]';
        }
        if (!checked) {
          return 'bg-[#d4d4d4]';
        }
        switch (actualVariant) {
          case 'primary':
            return 'bg-[#2563eb]';
          case 'error':
            return 'bg-[#dc2626]';
          default:
            return 'bg-[#7c3aed]';
        }
      };

      const getThumbClasses = () => {
        const baseClasses =
          'inline-block rounded-full bg-white shadow transform transition-transform duration-200 ease-in-out';
        const sizeClasses = {
          sm: 'w-3 h-3',
          md: 'w-4 h-4',
          lg: 'w-5 h-5',
        };
        const translateClasses = checked
          ? {
              sm: 'translate-x-4',
              md: 'translate-x-5',
              lg: 'translate-x-6',
            }
          : 'translate-x-0';

        const translateClass =
          typeof translateClasses === 'string' ? translateClasses : translateClasses[size!];
        return cn(baseClasses, sizeClasses[size!], translateClass);
      };

      return (
        <div className="relative flex items-start">
          <button
            type="button"
            role="switch"
            aria-checked={checked}
            aria-disabled={disabled}
            className={cn(
              toggleSwitchVariants({ variant: actualVariant, size }),
              getBackgroundColor()
            )}
            onClick={() => {
              if (!disabled) {
                const newChecked = !checked;
                onToggle?.(newChecked);
                // Create synthetic event for compatibility
                const syntheticEvent = {
                  target: { checked: newChecked },
                  currentTarget: { checked: newChecked },
                } as React.ChangeEvent<HTMLInputElement>;
                onChange?.(syntheticEvent);
              }
            }}
          >
            <span className={getThumbClasses()} />
          </button>

          {/* Hidden input for form compatibility */}
          <input
            ref={inputRef}
            type="checkbox"
            className="sr-only"
            checked={checked}
            disabled={disabled}
            onChange={handleChange}
            {...props}
          />
        </div>
      );
    };

    // Render checkbox or radio
    const renderInput = () => {
      return (
        <div className="relative flex items-start">
          <input
            ref={inputRef}
            type={type}
            className={cn(
              inputVariants({ variant: actualVariant, size, type }),
              'shrink-0 appearance-none',
              // Checked styles
              type === 'checkbox' && 'checked:border-[#7c3aed] checked:bg-[#7c3aed]',
              type === 'checkbox' &&
                actualVariant === 'primary' &&
                'checked:border-[#2563eb] checked:bg-[#2563eb]',
              type === 'checkbox' &&
                actualVariant === 'error' &&
                'checked:border-[#dc2626] checked:bg-[#dc2626]',
              type === 'checkbox' && actualVariant === 'disabled' && 'checked:bg-[#d4d4d4]',
              type === 'radio' && 'checked:border-[#7c3aed]',
              type === 'radio' && actualVariant === 'primary' && 'checked:border-[#2563eb]',
              type === 'radio' && actualVariant === 'error' && 'checked:border-[#dc2626]'
            )}
            disabled={disabled}
            checked={checked}
            onChange={handleChange}
            {...props}
          />

          {/* Custom indicators */}
          {type === 'checkbox' && (
            <div
              className={cn(
                'pointer-events-none absolute inset-0 flex items-center justify-center',
                size === 'sm' && 'h-4 w-4',
                size === 'md' && 'h-5 w-5',
                size === 'lg' && 'h-6 w-6'
              )}
            >
              {indeterminate ? (
                <div
                  className={cn(
                    'bg-white',
                    size === 'sm' && 'h-0.5 w-2',
                    size === 'md' && 'h-0.5 w-2.5',
                    size === 'lg' && 'h-1 w-3'
                  )}
                />
              ) : (
                checked && (
                  <svg
                    className={cn(
                      'text-white',
                      size === 'sm' && 'h-3 w-3',
                      size === 'md' && 'h-3 w-3',
                      size === 'lg' && 'h-4 w-4'
                    )}
                    fill="currentColor"
                    viewBox="0 0 20 20"
                  >
                    <path
                      fillRule="evenodd"
                      d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                      clipRule="evenodd"
                    />
                  </svg>
                )
              )}
            </div>
          )}

          {type === 'radio' && checked && (
            <div
              className={cn(
                'pointer-events-none absolute inset-0 flex items-center justify-center',
                size === 'sm' && 'h-4 w-4',
                size === 'md' && 'h-5 w-5',
                size === 'lg' && 'h-6 w-6'
              )}
            >
              <div
                className={cn(
                  'rounded-full',
                  actualVariant === 'primary' && 'bg-[#2563eb]',
                  actualVariant === 'error' && 'bg-[#dc2626]',
                  actualVariant === 'disabled' && 'bg-[#d4d4d4]',
                  actualVariant === 'default' && 'bg-[#7c3aed]',
                  size === 'sm' && 'h-1.5 w-1.5',
                  size === 'md' && 'h-2 w-2',
                  size === 'lg' && 'h-2.5 w-2.5'
                )}
              />
            </div>
          )}
        </div>
      );
    };

    return (
      <div className="w-full">
        <label className={cn(controlVariants({ size }), className)}>
          {type === 'toggle' ? renderToggle() : renderInput()}

          <TitleDescription
            title={label}
            description={description}
            errorMessage={errorMessage}
            variant={actualVariant}
            disabled={disabled}
            size={size}
          />
        </label>
      </div>
    );
  }
);

Control.displayName = 'Control';

export { Control };
export default Control;

'use client';

import React, { forwardRef, useState } from 'react';

export interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  /** Error message to display */
  error?: string;
  /** Custom class name */
  className?: string;
  /** Whether to show a clear button when input has content */
  clearable?: boolean;
  /** Callback when clear button is clicked */
  onClear?: () => void;
  /** Whether to show password visibility toggle for password inputs */
  showPasswordToggle?: boolean;
  /** Left icon element */
  leftIcon?: React.ReactNode;
  /** Right icon element */
  rightIcon?: React.ReactNode;
  /** Whether the input is dismissible/clearable */
  dismissible?: boolean;
  /** Loading state for async operations */
  loading?: boolean;
  /** Whether to suppress clear/dismiss buttons (for Select usage) */
  suppressActions?: boolean;
}

export const Input = forwardRef<HTMLInputElement, InputProps>(
  (
    {
      error,
      className = '',
      clearable = false,
      dismissible = false,
      onClear,
      value,
      showPasswordToggle = false,
      type,
      leftIcon,
      rightIcon,
      name,
      id,
      disabled = false,
      loading = false,
      suppressActions = false,
      ...props
    },
    ref
  ) => {
    const [showPassword, setShowPassword] = useState(false);

    // Generate unique IDs for the form elements
    const inputId = id || `input-${Math.random().toString(36).substr(2, 9)}`;

    const hasValue = value !== undefined && value !== '';
    const showClearButton = !suppressActions && (clearable || dismissible) && hasValue;
    const isPasswordInput = type === 'password';
    const showPasswordButton = !suppressActions && showPasswordToggle && isPasswordInput;
    const effectiveType = isPasswordInput && showPassword ? 'text' : type;

    // Calculate left padding for left icon
    const leftPadding = leftIcon ? 'pl-10' : 'pl-3';

    // Calculate right padding for right elements
    const rightElements = [rightIcon, showClearButton, showPasswordButton].filter(Boolean);
    const rightPadding =
      rightElements.length > 0
        ? rightElements.length === 1
          ? 'pr-10'
          : rightElements.length === 2
            ? 'pr-16'
            : 'pr-20'
        : 'pr-3';

    const handleClear = () => {
      if (onClear) {
        onClear();
      }
    };

    const togglePasswordVisibility = () => {
      setShowPassword(!showPassword);
    };

    return (
      <div className="relative">
        <div
          className={`font-system relative flex w-full items-center transition-colors focus-within:outline-hidden ${
            error
              ? 'rounded-sm border border-red-600 bg-white focus-within:border-red-600 focus-within:shadow-[0px_0px_0px_4px_rgba(220,38,38,0.32)] dark:bg-gray-800'
              : 'rounded-sm border border-gray-300 bg-white focus-within:border-blue-600 focus-within:shadow-[0px_0px_0px_4px_rgba(59,130,246,0.32)] dark:border-gray-600 dark:bg-gray-800'
          } min-h-10 p-2 text-base ${className}`}
        >
          {/* Left Icon */}
          {leftIcon && (
            <div className="mr-2 flex size-5 shrink-0 items-center justify-center text-gray-400 dark:text-gray-500">
              {leftIcon}
            </div>
          )}

          <div className="flex min-h-0 min-w-0 grow basis-0 flex-col items-center justify-center gap-0.5 px-1 py-0">
            <div className="font-montserrat flex w-full flex-col justify-center overflow-hidden leading-5 font-normal text-nowrap text-ellipsis">
              <input
                ref={ref}
                id={inputId}
                name={name}
                type={effectiveType}
                value={value || ''}
                {...props}
                className="font-montserrat w-full border-0 bg-transparent text-base leading-5 font-normal text-gray-600 shadow-none ring-0 outline-0 placeholder:text-gray-500 focus:border-0 focus:shadow-none focus:ring-0 focus:outline-0 dark:text-gray-300 dark:placeholder:text-gray-400"
                style={{
                  border: 'none',
                  outline: 'none',
                  boxShadow: 'none',
                  appearance: 'none',
                }}
                disabled={disabled}
                aria-invalid={error ? 'true' : 'false'}
              />
            </div>
          </div>

          {/* Right Icons and Controls */}
          <div className="flex items-center space-x-2">
            {/* Loading Indicator */}
            {loading && (
              <div className="flex size-5 shrink-0 items-center justify-center text-gray-600 dark:text-gray-400">
                <svg className="size-4 animate-spin" fill="none" viewBox="0 0 24 24">
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  />
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  />
                </svg>
              </div>
            )}

            {/* Right Icon */}
            {rightIcon && (
              <div className="flex size-5 shrink-0 items-center justify-center text-gray-400 dark:text-gray-500">
                {rightIcon}
              </div>
            )}

            {/* Clear/Dismiss Button */}
            {showClearButton && (
              <button
                type="button"
                className="focus:ring-opacity-50 flex size-5 shrink-0 items-center justify-center rounded text-gray-400 transition-colors duration-200 hover:text-gray-600 focus:ring-2 focus:ring-blue-500 focus:outline-none dark:text-gray-500 dark:hover:text-gray-300"
                onClick={handleClear}
                aria-label="Clear input"
                tabIndex={-1}
              >
                <svg className="size-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </button>
            )}

            {/* Password Toggle */}
            {showPasswordButton && (
              <button
                type="button"
                className="focus:ring-opacity-50 flex size-5 shrink-0 items-center justify-center rounded text-gray-400 transition-colors duration-200 hover:text-gray-600 focus:ring-2 focus:ring-blue-500 focus:outline-none dark:text-gray-500 dark:hover:text-gray-300"
                onClick={togglePasswordVisibility}
                aria-label={showPassword ? 'Hide password' : 'Show password'}
                tabIndex={-1}
              >
                {showPassword ? (
                  <svg className="size-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.878 9.878L3 3m6.878 6.878L21 21"
                    />
                  </svg>
                ) : (
                  <svg className="size-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
                    />
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"
                    />
                  </svg>
                )}
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }
);

Input.displayName = 'Input';

export default Input;

'use client';

import React, { forwardRef } from 'react';

export interface OptionProps {
  /** Whether this option is currently selected */
  selected?: boolean;
  /** Whether this option is currently focused/highlighted */
  focused?: boolean;
  /** Whether this option is disabled */
  disabled?: boolean;
  /** Main option label */
  label: string;
  /** Optional description text */
  description?: string;
  /** Optional value for the option */
  value?: string;
  /** Optional badge text to display on the right */
  badge?: string;
  /** Click handler */
  onClick?: () => void;
  /** Mouse enter handler for focus management */
  onMouseEnter?: () => void;
  /** Custom class name */
  className?: string;
  /** Custom children to override default rendering */
  children?: React.ReactNode;
}

export const Option = forwardRef<HTMLDivElement, OptionProps>(
  (
    {
      selected = false,
      focused = false,
      disabled = false,
      label,
      description,
      value,
      badge,
      onClick,
      onMouseEnter,
      className = '',
      children,
    },
    ref
  ) => {
    const handleClick = () => {
      if (!disabled && onClick) {
        onClick();
      }
    };

    const handleMouseEnter = () => {
      if (!disabled && onMouseEnter) {
        onMouseEnter();
      }
    };

    return (
      <div
        ref={ref}
        role="option"
        aria-selected={selected}
        aria-disabled={disabled}
        className={`cursor-pointer px-3 py-3 transition-colors duration-150 sm:px-4 ${
          focused
            ? 'bg-blue-50 text-blue-900 dark:bg-blue-900/30 dark:text-blue-100'
            : 'text-gray-900 hover:bg-gray-50 dark:text-gray-100 dark:hover:bg-gray-700'
        } ${selected ? 'bg-blue-100 font-medium dark:bg-blue-900/20' : ''} ${
          disabled ? 'cursor-not-allowed opacity-50' : ''
        } ${className}`}
        onClick={handleClick}
        onMouseEnter={handleMouseEnter}
      >
        {children ? (
          children
        ) : (
          <div className="flex items-center justify-between gap-3">
            <div className="min-w-0 flex-1">
              <div className="truncate text-sm font-medium">
                {label.includes(',') ? (
                  <>
                    {label.split(',')[0]}
                    <span className="font-normal text-gray-500 dark:text-gray-400">
                      , {label.split(',').slice(1).join(',')}
                    </span>
                  </>
                ) : (
                  label
                )}
              </div>
            </div>
            <div className="flex flex-shrink-0 items-center gap-2">
              {badge && (
                <span className="inline-flex items-center rounded bg-gray-100 px-2 py-0.5 text-xs font-medium text-gray-800 dark:bg-gray-700 dark:text-gray-200">
                  {badge}
                </span>
              )}
              {selected && (
                <svg className="h-4 w-4 text-blue-600" fill="currentColor" viewBox="0 0 20 20">
                  <path
                    fillRule="evenodd"
                    d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                    clipRule="evenodd"
                  />
                </svg>
              )}
            </div>
          </div>
        )}
      </div>
    );
  }
);

Option.displayName = 'Option';

export default Option;

/**
 * Enhanced Select Component
 * Follows PSGCSelector patterns for consistency across the application
 * Supports both static enums and API-driven data
 */

'use client';

import { useState, useEffect, useRef, useCallback } from 'react';

import { createDropdownKeyHandler } from '@/lib/keyboardUtils';

import { Input } from '../Input';

import { Option } from './Option';

export type SelectOption = {
  value: string;
  label: string;
  disabled?: boolean;
  description?: string;
  category?: string;
  badge?: string;
};

interface SelectProps {
  value?: string;
  onSelect: (option: SelectOption | null) => void;
  placeholder?: string;
  className?: string;
  error?: string;
  // Data sources
  options?: SelectOption[];
  enumData?: Record<string, string> | SelectOption[];
  // Configuration options
  name?: string;
  id?: string;
  disabled?: boolean;
  searchable?: boolean;
  allowCustom?: boolean;
  // API integration (similar to PSGCSelector)
  onSearch?: (query: string) => void;
  loading?: boolean;
  // Lazy loading support
  hasMore?: boolean;
  onLoadMore?: () => void;
  loadingMore?: boolean;
  infiniteScroll?: boolean; // Enable infinite scroll vs manual button
}

// Utility function to convert enum/constant data to SelectOption array
const normalizeOptions = (
  data: SelectOption[] | Record<string, string> | undefined
): SelectOption[] => {
  if (!data) return [];

  if (Array.isArray(data)) {
    return data;
  }

  // Convert enum or object to options
  return Object.entries(data).map(([key, value]) => ({
    value: key,
    label: typeof value === 'string' ? value : String(value),
  }));
};

export default function Select({
  value,
  onSelect,
  placeholder = 'Select an option...',
  className = '',
  error,
  options = [],
  enumData,
  name,
  id,
  disabled = false,
  searchable = true,
  allowCustom = false,
  onSearch, // For API-driven searches
  loading = false,
  hasMore = false,
  onLoadMore,
  loadingMore = false,
  infiniteScroll = true, // Default to infinite scroll
}: SelectProps) {
  const [inputValue, setInputValue] = useState('');
  const [normalizedOptions, setNormalizedOptions] = useState<SelectOption[]>([]);
  const [filteredOptions, setFilteredOptions] = useState<SelectOption[]>([]);
  const [selectedOption, setSelectedOption] = useState<SelectOption | null>(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [focusedIndex, setFocusedIndex] = useState(-1);
  const [dropdownPosition, setDropdownPosition] = useState<'below' | 'above'>('below');
  const [justSelected, setJustSelected] = useState(false);

  const debounceTimerRef = useRef<NodeJS.Timeout | null>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const optionRefs = useRef<(HTMLDivElement | null)[]>([]);
  const lastSearchRef = useRef<string>('');

  // Generate unique IDs for the form elements
  const inputId = id || `select-input-${Math.random().toString(36).substr(2, 9)}`;

  // Normalize and set options (for static data)
  useEffect(() => {
    if (enumData || options.length > 0) {
      const normalized = normalizeOptions(enumData || options);
      setNormalizedOptions(normalized);
      // Don't automatically set filteredOptions here - let explicit interactions control this
      // setFilteredOptions(normalized);
    }
  }, [options, enumData]);

  // Debounced search (handles both static and API)
  useEffect(() => {
    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current);
    }

    if (onSearch) {
      // API-driven search with debounce
      const timer = setTimeout(() => {
        if (inputValue.trim() && inputValue.length >= 2 && lastSearchRef.current !== inputValue) {
          lastSearchRef.current = inputValue;
          onSearch(inputValue);
        } else if (!enumData && inputValue.trim().length < 2) {
          setFilteredOptions([]);
        }
      }, 300);
      debounceTimerRef.current = timer;
    } else {
      // Static data filtering - immediate for better UX
      if (!inputValue.trim() || !searchable) {
        // Don't automatically set filteredOptions when input is empty
        // This prevents unwanted dropdown showing
        // setFilteredOptions(normalizedOptions);
        setFocusedIndex(-1);
      } else {
        const filtered = normalizedOptions.filter(
          option =>
            option.label.toLowerCase().includes(inputValue.toLowerCase()) ||
            option.value.toLowerCase().includes(inputValue.toLowerCase()) ||
            option.description?.toLowerCase().includes(inputValue.toLowerCase())
        );
        setFilteredOptions(filtered);
        setFocusedIndex(filtered.length > 0 ? 0 : -1);
      }
    }

    return () => {
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current);
      }
    };
  }, [inputValue, onSearch, normalizedOptions, searchable, enumData]);

  // Calculate dropdown position based on available space
  const calculateDropdownPosition = useCallback(() => {
    if (!inputRef.current) return 'below';

    const inputRect = inputRef.current.getBoundingClientRect();
    const viewportHeight = window.innerHeight;
    const dropdownHeight = 320; // max-h-80 = 320px

    const spaceBelow = viewportHeight - inputRect.bottom;
    const spaceAbove = inputRect.top;

    // If there's not enough space below but enough space above, show above
    if (spaceBelow < dropdownHeight && spaceAbove > dropdownHeight) {
      return 'above';
    }

    return 'below';
  }, []);

  // Show dropdown only on explicit user interaction (focus, click, typing, keyboard)
  // By default, hide dropdown - only show when user actively interacts
  useEffect(() => {
    // Always hide dropdown by default after selection
    if (justSelected) {
      setShowDropdown(false);
      return;
    }

    // For typing behavior: only show when user has typed enough characters
    // BUT don't show if the inputValue exactly matches the selectedOption (means it's not user typing)
    if (inputValue.trim().length > 0 && !(selectedOption && inputValue === selectedOption.label)) {
      const shouldShow = onSearch
        ? true // For API search: always show when user is typing, let dropdown content handle the messaging
        : filteredOptions.length > 0; // For static data: only show if there are options

      if (shouldShow) {
        setShowDropdown(true);
        setDropdownPosition(calculateDropdownPosition());
        optionRefs.current = optionRefs.current.slice(0, Math.max(filteredOptions.length, 1));
      } else {
        setShowDropdown(false);
      }
    }
    // Note: Focus and keyboard interactions are handled separately in their respective handlers
  }, [
    filteredOptions,
    inputValue,
    calculateDropdownPosition,
    onSearch,
    justSelected,
    selectedOption,
  ]);

  // Scroll focused option into view
  useEffect(() => {
    if (focusedIndex >= 0 && optionRefs.current[focusedIndex]) {
      optionRefs.current[focusedIndex]?.scrollIntoView({
        behavior: 'smooth',
        block: 'nearest',
        inline: 'nearest',
      });
    }
  }, [focusedIndex]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as Node;
      const isOutsideDropdown = dropdownRef.current && !dropdownRef.current.contains(target);
      const isOutsideInput = inputRef.current && !inputRef.current.contains(target);

      if (isOutsideDropdown && isOutsideInput) {
        setShowDropdown(false);
        setFocusedIndex(-1);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Handle input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation(); // Prevent event bubbling to parent components
    const query = e.target.value;
    setInputValue(query);

    // Always show dropdown when user starts typing (especially important for API selects and error states)
    setShowDropdown(true);
    setDropdownPosition(calculateDropdownPosition());
    setFocusedIndex(-1);
    setJustSelected(false); // Reset the flag when user starts typing

    // Clear selection when user starts typing (unless it matches exactly)
    if (!onSearch) {
      // Only clear selection if the input doesn't match the current selection
      if (selectedOption && query !== selectedOption.label) {
        setSelectedOption(null);
        onSelect(null);
      }

      // Check for exact matches
      const matchingOption = normalizedOptions.find(
        option => option.label === query || option.value === query
      );

      if (matchingOption && query === matchingOption.label) {
        setSelectedOption(matchingOption);
        onSelect(matchingOption);
      } else if (!query.trim()) {
        setSelectedOption(null);
        onSelect(null);
        setShowDropdown(false);
      } else if (allowCustom && query.trim()) {
        // Create custom option for allowCustom mode
        const customOption: SelectOption = { value: query, label: query };
        setSelectedOption(customOption);
        onSelect(customOption);
      }
    } else {
      // For API-driven search, clear selection when user modifies input
      if (selectedOption && query !== selectedOption.label) {
        setSelectedOption(null);
        onSelect(null);
      }
    }
  };

  // Handle input focus - show options with selected value highlighted
  const handleInputFocus = (e: React.FocusEvent<HTMLInputElement>) => {
    e.stopPropagation(); // Prevent event bubbling to parent components
    if (!disabled) {
      // Reset just selected flag to allow dropdown to show
      setJustSelected(false);

      // For static data, show all options on focus
      if (!onSearch && normalizedOptions.length > 0) {
        setFilteredOptions(normalizedOptions);
        setShowDropdown(true);
        setDropdownPosition(calculateDropdownPosition());

        // Highlight and scroll to the currently selected item
        if (selectedOption) {
          const selectedIndex = normalizedOptions.findIndex(
            opt => opt.value === selectedOption.value
          );
          if (selectedIndex >= 0) {
            setFocusedIndex(selectedIndex);
            // Scroll will be handled by the focusedIndex useEffect
          } else {
            setFocusedIndex(0);
          }
        } else {
          setFocusedIndex(0);
        }
      }

      // For API-driven selects, show dropdown immediately to display empty state
      else if (onSearch) {
        setShowDropdown(true);
        setDropdownPosition(calculateDropdownPosition());
        setFocusedIndex(-1);
        // Don't trigger onSearch with empty string to avoid API errors
      }
    }
  };

  // Handle input blur
  const handleInputBlur = () => {
    // Delay hiding dropdown to allow clicks on options
    setTimeout(() => {
      // More specific check: only hide if the active element is not within this component
      const activeElement = document.activeElement;
      const isWithinThisComponent =
        dropdownRef.current?.contains(activeElement) ||
        inputRef.current?.contains(activeElement) ||
        inputRef.current === activeElement;

      if (!isWithinThisComponent) {
        setShowDropdown(false);
        setFocusedIndex(-1);
      }
    }, 300);
  };

  // Handle keyboard navigation using consolidated utility
  const handleKeyDown = createDropdownKeyHandler({
    isOpen: showDropdown,
    selectedIndex: focusedIndex,
    itemCount: filteredOptions.length,
    onOpen: () => {
      // Reset just selected flag to allow dropdown to show
      setJustSelected(false);

      if (onSearch) {
        // For API-driven selects, show dropdown immediately without triggering API call
        setShowDropdown(true);
        setDropdownPosition(calculateDropdownPosition());
      } else {
        // Show all options when opening dropdown via keyboard for static data
        setFilteredOptions(normalizedOptions);
        setShowDropdown(true);
        setDropdownPosition(calculateDropdownPosition());

        // Highlight and scroll to the currently selected item
        if (selectedOption) {
          const selectedIndex = normalizedOptions.findIndex(
            opt => opt.value === selectedOption.value
          );
          setFocusedIndex(selectedIndex >= 0 ? selectedIndex : 0);
        } else {
          setFocusedIndex(0);
        }
      }
    },
    onClose: () => {
      setShowDropdown(false);
      setFocusedIndex(-1);
      inputRef.current?.blur();
    },
    onSelect: (index: number) => {
      if (index >= 0 && index < filteredOptions.length) {
        handleOptionSelect(filteredOptions[index]);
      } else if (showDropdown) {
        // If dropdown is open but no option focused, just close it
        setShowDropdown(false);
        setFocusedIndex(-1);
      }
    },
    onNavigate: (index: number) => {
      setFocusedIndex(index);
    }
  });

  // Handle option selection from dropdown
  const handleOptionSelect = (option: SelectOption) => {
    if (option.disabled) return;

    setSelectedOption(option);
    setInputValue(option.label);
    setShowDropdown(false);
    setFocusedIndex(-1);
    setJustSelected(true);

    // Always reset filtering for static data after selection to show all options next time
    if (!onSearch && normalizedOptions.length > 0) {
      setFilteredOptions(normalizedOptions);
    }

    onSelect(option);

    // Blur the input after selection to prevent API handler from reopening
    inputRef.current?.blur();

    // Reset the justSelected flag after a longer delay to prevent reopening
    setTimeout(() => setJustSelected(false), 300);
  };

  // Load initial value if provided
  useEffect(() => {
    if (value && !selectedOption && normalizedOptions.length > 0) {
      const matchingOption = normalizedOptions.find(opt => opt.value === value);
      if (matchingOption) {
        setSelectedOption(matchingOption);
        setInputValue(matchingOption.label);
      }
    }
  }, [value, selectedOption, normalizedOptions]);

  // Handle external option updates (for API-driven data)
  useEffect(() => {
    if (onSearch && options.length > 0) {
      // Only update if options have actually changed
      setFilteredOptions(prev => {
        if (JSON.stringify(prev) !== JSON.stringify(options)) {
          return options;
        }
        return prev;
      });
    }
  }, [options, onSearch]);

  // Special handler for API-driven selects: show dropdown after focus loads options
  useEffect(() => {
    // Only show dropdown if:
    // 1. It's an API-driven select
    // 2. We have options from API
    // 3. We haven't just selected something
    // 4. Input is currently focused (user is actively interacting)
    // 5. Input is not showing a selected value (avoid reopening after selection)
    const shouldShowAPI =
      onSearch &&
      options.length > 0 &&
      !justSelected &&
      document.activeElement === inputRef.current &&
      !showDropdown && // Don't reopen if already showing
      !(selectedOption && inputValue === selectedOption.label); // Don't reopen if showing selected value

    if (shouldShowAPI) {
      setShowDropdown(true);
      setDropdownPosition(calculateDropdownPosition());

      // Highlight and scroll to the currently selected item
      if (selectedOption) {
        const selectedIndex = options.findIndex(opt => opt.value === selectedOption.value);
        if (selectedIndex >= 0) {
          setFocusedIndex(selectedIndex);
        } else {
          setFocusedIndex(0);
        }
      } else {
        setFocusedIndex(0);
      }
    }
  }, [
    options,
    onSearch,
    justSelected,
    selectedOption,
    calculateDropdownPosition,
    showDropdown,
    inputValue,
  ]);

  // Handle clear button click
  const handleClearClick = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    setSelectedOption(null);
    setInputValue('');
    setShowDropdown(false);
    setFocusedIndex(-1);
    onSelect(null);

    // Focus back to input for better UX
    inputRef.current?.focus();
  };

  // Handle dropdown icon click
  const handleDropdownIconClick = () => {
    if (!disabled) {
      if (showDropdown) {
        setShowDropdown(false);
        setFocusedIndex(-1);
      } else {
        // Reset just selected flag to allow dropdown to show
        setJustSelected(false);

        if (onSearch) {
          // For API-driven selects, show dropdown immediately without triggering API call
          setShowDropdown(true);
          setDropdownPosition(calculateDropdownPosition());
          inputRef.current?.focus();
        } else {
          // Show all options when clicking the dropdown arrow for static data
          setFilteredOptions(normalizedOptions);
          setShowDropdown(true);
          setDropdownPosition(calculateDropdownPosition());

          // Highlight and scroll to the currently selected item
          if (selectedOption) {
            const selectedIndex = normalizedOptions.findIndex(
              opt => opt.value === selectedOption.value
            );
            setFocusedIndex(selectedIndex >= 0 ? selectedIndex : 0);
          } else {
            setFocusedIndex(0);
          }

          inputRef.current?.focus();
        }
      }
    }
  };

  // Create the clear button icon
  const clearIcon = (
    <svg
      className="size-4 cursor-pointer transition-colors duration-200 hover:text-red-600 dark:hover:text-red-400"
      fill="none"
      viewBox="0 0 24 24"
      stroke="currentColor"
      onClick={handleClearClick}
    >
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
    </svg>
  );

  // Create the dropdown chevron icon
  const dropdownIcon = (
    <svg
      className={`size-4 cursor-pointer transition-transform duration-200 ${showDropdown ? 'rotate-180' : ''}`}
      fill="none"
      viewBox="0 0 24 24"
      stroke="currentColor"
      onClick={handleDropdownIconClick}
    >
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
    </svg>
  );

  return (
    <div className="relative">
      <Input
        ref={inputRef}
        id={inputId}
        name={name ? `${name}_display` : undefined}
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        onFocus={handleInputFocus}
        onBlur={handleInputBlur}
        onKeyDown={handleKeyDown}
        placeholder={placeholder}
        disabled={disabled}
        error={error}
        loading={loading}
        rightIcon={selectedOption && inputValue ? clearIcon : dropdownIcon}
        suppressActions={true}
        autoComplete="off"
        role="combobox"
        aria-expanded={showDropdown}
        aria-haspopup="listbox"
        className={className}
      />

      {/* Custom dropdown */}
      {showDropdown && (
        <div
          ref={dropdownRef}
          className={`absolute right-0 left-0 z-50 max-h-60 overflow-y-auto rounded-md border border-gray-300 bg-white shadow-lg sm:max-h-80 dark:border-gray-600 dark:bg-gray-800 ${
            dropdownPosition === 'above' ? 'bottom-full mb-1' : 'top-full mt-1'
          }`}
          onMouseDown={e => {
            // Prevent the dropdown from closing when clicking inside it
            e.preventDefault();
          }}
        >
          <div
            role="listbox"
            className="py-1"
            onScroll={e => {
              // Enhanced infinite scroll debugging
              const target = e.currentTarget;
              const scrollTop = target.scrollTop;
              const scrollHeight = target.scrollHeight;
              const clientHeight = target.clientHeight;
              const threshold = 50;
              const isScrollable = scrollHeight > clientHeight;
              const isNearBottom = scrollTop + clientHeight >= scrollHeight - threshold;

              console.log('üìä Scroll Debug:', {
                infiniteScroll,
                hasMore,
                hasOnLoadMore: !!onLoadMore,
                loadingMore,
                scrollTop,
                scrollHeight,
                clientHeight,
                isScrollable,
                isNearBottom,
                distanceFromBottom: scrollHeight - (scrollTop + clientHeight),
              });

              // Infinite scroll: trigger when scrolled near bottom
              if (infiniteScroll && hasMore && onLoadMore && !loadingMore) {
                if (isScrollable && isNearBottom) {
                  console.log('üöÄ Infinite scroll triggered');
                  onLoadMore();
                }
              }
            }}
          >
            {filteredOptions.length > 0 ? (
              <>
                {filteredOptions.map((option, index) => (
                  <Option
                    key={`${option.value}-${index}`}
                    ref={el => {
                      optionRefs.current[index] = el;
                    }}
                    selected={selectedOption?.value === option.value}
                    focused={focusedIndex === index}
                    disabled={option.disabled}
                    label={option.label}
                    description={option.description}
                    value={option.value}
                    badge={option.badge}
                    onClick={() => handleOptionSelect(option)}
                    onMouseEnter={() => setFocusedIndex(index)}
                  />
                ))}

                {/* Load More Indicator */}
                {hasMore && (
                  <div className="border-t border-gray-200 px-4 py-2 text-center dark:border-gray-600">
                    {loadingMore ? (
                      <div className="flex items-center justify-center gap-2 text-sm text-gray-500 dark:text-gray-400">
                        <div className="h-4 w-4 animate-spin rounded-full border-b-2 border-blue-600 dark:border-blue-400"></div>
                        {infiniteScroll ? 'Loading more...' : 'Loading...'}
                      </div>
                    ) : !infiniteScroll ? (
                      <button
                        onClick={onLoadMore}
                        className="text-sm text-blue-600 hover:text-blue-800 focus:outline-none dark:text-blue-400 dark:hover:text-blue-200"
                      >
                        Load more results
                      </button>
                    ) : (
                      <div className="py-1 text-xs text-gray-400">Scroll for more results</div>
                    )}
                  </div>
                )}
              </>
            ) : (
              <div className="px-4 py-8 text-center text-gray-500 dark:text-gray-400">
                {loading ? (
                  <div className="flex flex-col items-center gap-3">
                    <div className="h-6 w-6 animate-spin rounded-full border-b-2 border-blue-600 dark:border-blue-400"></div>
                    <p className="text-sm">Searching...</p>
                  </div>
                ) : onSearch && inputValue.length >= 2 && filteredOptions.length === 0 ? (
                  <div className="flex flex-col items-center gap-3">
                    <div className="h-6 w-6 animate-spin rounded-full border-b-2 border-blue-600 dark:border-blue-400"></div>
                    <p className="text-sm">Searching...</p>
                  </div>
                ) : onSearch && inputValue.length < 2 ? (
                  <div className="flex flex-col items-center gap-3">
                    <svg
                      className="h-8 w-8 text-gray-400"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                      />
                    </svg>
                    <div>
                      <p className="text-sm font-medium">Start typing to search</p>
                      <p className="mt-1 text-xs">Type at least 2 characters to see results</p>
                    </div>
                  </div>
                ) : (
                  <div className="flex flex-col items-center gap-3">
                    <svg
                      className="h-8 w-8 text-gray-400"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M9.172 16.172a4 4 0 015.656 0M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
                      />
                    </svg>
                    <div>
                      <p className="text-sm font-medium">No results found</p>
                      <p className="mt-1 text-xs">Try a different search term</p>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Hidden input for form submission */}
      <input type="hidden" name={name} value={selectedOption?.value || ''} />
    </div>
  );
}

