/**
 * Advanced Filters Component
 * Comprehensive filtering interface for resident data
 */

'use client';

import React, { useState, useCallback, useEffect } from 'react';

import { Button } from '@/components';
import { clientLogger } from '@/lib/logging/client-logger';

interface FilterOption {
  value: string;
  label: string;
  count?: number;
}

interface FilterField {
  key: string;
  label: string;
  type: 'select' | 'multiselect' | 'range' | 'date' | 'text' | 'boolean';
  options?: FilterOption[];
  placeholder?: string;
  min?: number;
  max?: number;
}

interface FilterValue {
  [key: string]: any;
}

interface AdvancedFiltersProps {
  fields: FilterField[];
  values: FilterValue;
  onChange: (values: FilterValue) => void;
  onApply?: () => void;
  onClear?: () => void;
  loading?: boolean;
  className?: string;
  compact?: boolean;
}

export const AdvancedFilters: React.FC<AdvancedFiltersProps> = ({
  fields,
  values,
  onChange,
  onApply,
  onClear,
  loading = false,
  className = '',
  compact = false,
}) => {
  const [isExpanded, setIsExpanded] = useState(!compact);
  const [localValues, setLocalValues] = useState<FilterValue>(values);
  const [hasChanges, setHasChanges] = useState(false);

  // Sync local values with prop changes
  useEffect(() => {
    setLocalValues(values);
    setHasChanges(false);
  }, [values]);

  // Check if there are any changes
  useEffect(() => {
    const hasAnyChanges = Object.keys(localValues).some(key => {
      const localValue = localValues[key];
      const propValue = values[key];

      if (Array.isArray(localValue) && Array.isArray(propValue)) {
        return (
          localValue.length !== propValue.length ||
          localValue.some((val, idx) => val !== propValue[idx])
        );
      }

      return localValue !== propValue;
    });

    setHasChanges(hasAnyChanges);
  }, [localValues, values]);

  const handleFieldChange = useCallback((fieldKey: string, value: any) => {
    setLocalValues(prev => ({
      ...prev,
      [fieldKey]: value,
    }));

    clientLogger.userAction('filter_field_changed', 'AdvancedFilters', {
      field: fieldKey,
      valueType: typeof value,
    });
  }, []);

  const handleApply = useCallback(() => {
    onChange(localValues);
    if (onApply) {
      onApply();
    }

    clientLogger.userAction('filters_applied', 'AdvancedFilters', {
      filterCount: Object.keys(localValues).filter(key => {
        const value = localValues[key];
        return (
          value !== undefined &&
          value !== null &&
          value !== '' &&
          (!Array.isArray(value) || value.length > 0)
        );
      }).length,
    });
  }, [localValues, onChange, onApply]);

  const handleClear = useCallback(() => {
    const clearedValues: FilterValue = {};
    fields.forEach(field => {
      if (field.type === 'multiselect') {
        clearedValues[field.key] = [];
      } else if (field.type === 'boolean') {
        clearedValues[field.key] = undefined;
      } else {
        clearedValues[field.key] = '';
      }
    });

    setLocalValues(clearedValues);
    onChange(clearedValues);

    if (onClear) {
      onClear();
    }

    clientLogger.userAction('filters_cleared', 'AdvancedFilters');
  }, [fields, onChange, onClear]);

  const renderFilterField = (field: FilterField) => {
    const value = localValues[field.key];

    switch (field.type) {
      case 'select':
        return (
          <select
            value={value || ''}
            onChange={e => handleFieldChange(field.key, e.target.value)}
            className="w-full rounded-md border border-gray-300 px-3 py-2 text-sm shadow-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
            disabled={loading}
          >
            <option value="">All {field.label}</option>
            {field.options?.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
                {option.count !== undefined && ` (${option.count})`}
              </option>
            ))}
          </select>
        );

      case 'multiselect':
        const selectedValues = Array.isArray(value) ? value : [];
        return (
          <div className="space-y-2">
            <div className="mb-1 text-xs text-gray-500">{selectedValues.length} selected</div>
            <div className="max-h-32 overflow-y-auto rounded-md border border-gray-300 bg-gray-50 p-2">
              {field.options?.map(option => (
                <label
                  key={option.value}
                  className="flex items-center space-x-2 rounded px-1 py-1 hover:bg-gray-100"
                >
                  <input
                    type="checkbox"
                    checked={selectedValues.includes(option.value)}
                    onChange={e => {
                      const newValues = e.target.checked
                        ? [...selectedValues, option.value]
                        : selectedValues.filter(v => v !== option.value);
                      handleFieldChange(field.key, newValues);
                    }}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                    disabled={loading}
                  />
                  <span className="text-sm text-gray-700">{option.label}</span>
                  {option.count !== undefined && (
                    <span className="text-xs text-gray-500">({option.count})</span>
                  )}
                </label>
              ))}
            </div>
          </div>
        );

      case 'range':
        const rangeValue = value || { min: field.min, max: field.max };
        return (
          <div className="space-y-2">
            <div className="grid grid-cols-2 gap-2">
              <div>
                <label className="mb-1 block text-xs text-gray-500">Min</label>
                <input
                  type="number"
                  value={rangeValue.min || ''}
                  onChange={e =>
                    handleFieldChange(field.key, {
                      ...rangeValue,
                      min: e.target.value ? parseInt(e.target.value) : undefined,
                    })
                  }
                  min={field.min}
                  max={field.max}
                  className="w-full rounded border border-gray-300 px-2 py-1 text-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
                  disabled={loading}
                />
              </div>
              <div>
                <label className="mb-1 block text-xs text-gray-500">Max</label>
                <input
                  type="number"
                  value={rangeValue.max || ''}
                  onChange={e =>
                    handleFieldChange(field.key, {
                      ...rangeValue,
                      max: e.target.value ? parseInt(e.target.value) : undefined,
                    })
                  }
                  min={field.min}
                  max={field.max}
                  className="w-full rounded border border-gray-300 px-2 py-1 text-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
                  disabled={loading}
                />
              </div>
            </div>
          </div>
        );

      case 'date':
        const dateValue = value || { from: '', to: '' };
        return (
          <div className="space-y-2">
            <div className="grid grid-cols-2 gap-2">
              <div>
                <label className="mb-1 block text-xs text-gray-500">From</label>
                <input
                  type="date"
                  value={dateValue.from || ''}
                  onChange={e =>
                    handleFieldChange(field.key, {
                      ...dateValue,
                      from: e.target.value,
                    })
                  }
                  className="w-full rounded border border-gray-300 px-2 py-1 text-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
                  disabled={loading}
                />
              </div>
              <div>
                <label className="mb-1 block text-xs text-gray-500">To</label>
                <input
                  type="date"
                  value={dateValue.to || ''}
                  onChange={e =>
                    handleFieldChange(field.key, {
                      ...dateValue,
                      to: e.target.value,
                    })
                  }
                  className="w-full rounded border border-gray-300 px-2 py-1 text-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
                  disabled={loading}
                />
              </div>
            </div>
          </div>
        );

      case 'text':
        return (
          <input
            type="text"
            value={value || ''}
            onChange={e => handleFieldChange(field.key, e.target.value)}
            placeholder={field.placeholder || `Enter ${field.label.toLowerCase()}...`}
            className="w-full rounded-md border border-gray-300 px-3 py-2 text-sm shadow-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
            disabled={loading}
          />
        );

      case 'boolean':
        return (
          <select
            value={value === undefined ? '' : value.toString()}
            onChange={e => {
              const newValue = e.target.value === '' ? undefined : e.target.value === 'true';
              handleFieldChange(field.key, newValue);
            }}
            className="w-full rounded-md border border-gray-300 px-3 py-2 text-sm shadow-sm focus:border-blue-500 focus:ring-2 focus:ring-blue-500 focus:outline-none"
            disabled={loading}
          >
            <option value="">All</option>
            <option value="true">Yes</option>
            <option value="false">No</option>
          </select>
        );

      default:
        return null;
    }
  };

  const activeFilterCount = Object.keys(localValues).filter(key => {
    const value = localValues[key];
    if (value === undefined || value === null || value === '') return false;
    if (Array.isArray(value)) return value.length > 0;
    if (typeof value === 'object' && value !== null) {
      return Object.values(value).some(v => v !== undefined && v !== null && v !== '');
    }
    return true;
  }).length;

  return (
    <div className={`rounded-lg border border-gray-200 bg-white ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between border-b border-gray-200 p-4">
        <div className="flex items-center space-x-2">
          <h3 className="text-sm font-medium text-gray-900">Advanced Filters</h3>
          {activeFilterCount > 0 && (
            <span className="inline-flex items-center rounded-full bg-blue-100 px-2 py-0.5 text-xs font-medium text-blue-800">
              {activeFilterCount} active
            </span>
          )}
        </div>

        <div className="flex items-center space-x-2">
          {hasChanges && <span className="text-xs text-orange-600">Unsaved changes</span>}

          <button
            onClick={() => setIsExpanded(!isExpanded)}
            className="rounded-md p-1 hover:bg-gray-100 focus:ring-2 focus:ring-blue-500 focus:outline-none"
            disabled={loading}
          >
            <svg
              className={`h-4 w-4 text-gray-500 transition-transform ${isExpanded ? 'rotate-180' : ''}`}
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M19 9l-7 7-7-7"
              />
            </svg>
          </button>
        </div>
      </div>

      {/* Filter Fields */}
      {isExpanded && (
        <div className="p-4">
          <div
            className={`grid gap-4 ${compact ? 'grid-cols-2 lg:grid-cols-3' : 'grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'}`}
          >
            {fields.map(field => (
              <div key={field.key} className="space-y-1">
                <label className="block text-xs font-medium text-gray-700">{field.label}</label>
                {renderFilterField(field)}
              </div>
            ))}
          </div>

          {/* Actions */}
          <div className="mt-4 flex items-center justify-between border-t border-gray-200 pt-4">
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClear}
              disabled={loading || activeFilterCount === 0}
            >
              Clear All
            </Button>

            <div className="flex space-x-2">
              <Button
                variant="secondary"
                size="sm"
                onClick={() => setIsExpanded(false)}
                disabled={loading}
              >
                Close
              </Button>

              <Button
                variant="primary"
                size="sm"
                onClick={handleApply}
                disabled={loading || !hasChanges}
              >
                {loading ? 'Applying...' : 'Apply Filters'}
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdvancedFilters;

'use client';

import React, { useState, useEffect } from 'react';

import { supabase } from '@/lib/data/supabase';

export default function AuthDebug() {
  const [debugInfo, setDebugInfo] = useState<any>({});
  const [showDebug, setShowDebug] = useState(false);

  useEffect(() => {
    const updateDebugInfo = async () => {
      try {
        // Check localStorage for auth tokens
        const authToken = localStorage.getItem('rbi-auth-token');
        const supabaseAuth = localStorage.getItem('sb-cdtcbelaimyftpxmzkjf-auth-token');

        // Also check for any Supabase-related storage keys
        const allStorageKeys = Object.keys(localStorage);
        const supabaseKeys = allStorageKeys.filter(
          key => key.includes('supabase') || key.includes('sb-')
        );
        const authKeys = allStorageKeys.filter(key => key.includes('auth'));

        // Get current session
        const {
          data: { session },
          error,
        } = await supabase.auth.getSession();

        // Get current user
        const {
          data: { user },
          error: userError,
        } = await supabase.auth.getUser();

        setDebugInfo({
          localStorage: {
            hasRbiToken: !!authToken,
            hasSupabaseToken: !!supabaseAuth,
            rbiTokenLength: authToken?.length || 0,
            supabaseTokenLength: supabaseAuth?.length || 0,
            allSupabaseKeys: supabaseKeys,
            allAuthKeys: authKeys,
            totalStorageKeys: allStorageKeys.length,
          },
          session: {
            hasSession: !!session,
            hasUser: !!session?.user,
            userId: session?.user?.id,
            expiresAt: session?.expires_at,
            expiresIn: session?.expires_in,
            sessionError: error?.message,
          },
          user: {
            hasUser: !!user,
            userId: user?.id,
            email: user?.email,
            userError: userError?.message,
          },
          browser: {
            isLocalStorageSupported: typeof Storage !== 'undefined',
            userAgent: navigator.userAgent,
            cookieEnabled: navigator.cookieEnabled,
          },
        });
      } catch (error) {
        setDebugInfo({
          error: (error as any)?.message || 'Debug info error',
        });
      }
    };

    if (showDebug) {
      updateDebugInfo();
      const interval = setInterval(updateDebugInfo, 5000); // Update every 5 seconds
      return () => clearInterval(interval);
    }
  }, [showDebug]);

  if (!showDebug) {
    return (
      <button
        onClick={() => setShowDebug(true)}
        className="fixed bottom-4 left-4 z-40 rounded-full bg-orange-600 p-2 text-xs text-white shadow-lg transition-colors hover:bg-orange-700"
        title="Auth Debug"
      >
        üîê
      </button>
    );
  }

  return (
    <div className="fixed bottom-4 left-4 z-40 max-h-96 max-w-md overflow-y-auto rounded-lg border border-gray-200 bg-white p-4 shadow-lg dark:border-gray-700 dark:bg-gray-800">
      <div className="mb-3 flex items-center justify-between">
        <h3 className="text-sm font-semibold text-gray-900 dark:text-white">Auth Debug Info</h3>
        <button
          onClick={() => setShowDebug(false)}
          className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-200"
        >
          <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        </button>
      </div>

      <div className="space-y-3 text-xs">
        {/* localStorage Status */}
        <div>
          <h4 className="mb-1 font-medium text-gray-700 dark:text-gray-300">Local Storage:</h4>
          <div className="space-y-1 pl-2">
            <div
              className={`flex justify-between ${debugInfo.localStorage?.hasRbiToken ? 'text-green-600' : 'text-red-600'}`}
            >
              <span>RBI Token:</span>
              <span>
                {debugInfo.localStorage?.hasRbiToken ? '‚úì' : '‚úó'} (
                {debugInfo.localStorage?.rbiTokenLength})
              </span>
            </div>
            <div
              className={`flex justify-between ${debugInfo.localStorage?.hasSupabaseToken ? 'text-green-600' : 'text-red-600'}`}
            >
              <span>Supabase Token:</span>
              <span>
                {debugInfo.localStorage?.hasSupabaseToken ? '‚úì' : '‚úó'} (
                {debugInfo.localStorage?.supabaseTokenLength})
              </span>
            </div>
            <div className="text-xs text-gray-600 dark:text-gray-400">
              <div>Total Keys: {debugInfo.localStorage?.totalStorageKeys}</div>
              {debugInfo.localStorage?.allSupabaseKeys?.length > 0 && (
