'use client';

import React, { useState, useEffect } from 'react';

import { useConnectionStatus } from '@/hooks/utilities/useConnectionStatus';
import { syncQueue } from '@/lib/data';

// Custom hook to handle client-side mounting
function useIsClient() {
  const [isClient, setIsClient] = useState(false);

  useEffect(() => {
    setIsClient(true);
  }, []);

  return isClient;
}

interface ConnectionStatusProps {
  className?: string;
}

export default function ConnectionStatus({ className = '' }: ConnectionStatusProps) {
  const isClient = useIsClient();
  const [isOnline, setIsOnline] = useState(true);
  const [syncPending, setSyncPending] = useState(false);
  const [syncStatus, setSyncStatus] = useState({
    isProcessing: false,
    pendingCount: 0,
  });
  const [showBanner, setShowBanner] = useState(false);

  useEffect(() => {
    // Only run on client side
    if (!isClient) return;

    // Set initial connection status
    setIsOnline(navigator.onLine);

    // Setup enhanced sync status monitoring
    const handleOnline = async () => {
      setIsOnline(true);
      setShowBanner(true);

      // Check for pending sync items
      const status = await syncQueue.getStatus();
      setSyncStatus(status);
      setSyncPending(status.pendingCount > 0);

      // Auto-hide banner after sync completes
      setTimeout(() => {
        if (status.pendingCount === 0) {
          setShowBanner(false);
        }
      }, 3000);
    };

    const handleOffline = () => {
      setIsOnline(false);
      setShowBanner(true);
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    // Initial sync status check
    const checkSyncStatus = async () => {
      const status = await syncQueue.getStatus();
      setSyncStatus(status);
      setSyncPending(status.pendingCount > 0);

      // Show banner if there are pending items
      if (status.pendingCount > 0) {
        setShowBanner(true);
      }
    };

    checkSyncStatus();

    // Periodic sync status updates
    const statusInterval = setInterval(checkSyncStatus, 5000);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
      clearInterval(statusInterval);
    };
  }, [isClient]);

  const handleDismiss = () => {
    setShowBanner(false);
  };

  const handleForceSync = async () => {
    if (isOnline) {
      try {
        await syncQueue.forceSync();
        const status = await syncQueue.getStatus();
        setSyncStatus(status);
        setSyncPending(status.pendingCount > 0);
      } catch (error) {
        console.error('Force sync failed:', error);
      }
    }
  };

  const getStatusInfo = () => {
    if (!isOnline) {
      return {
        message: 'You are offline',
        detail: 'Changes will be saved locally and synced when connection is restored',
        color: 'bg-red-500',
        icon: (
          <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M18.364 5.636L5.636 18.364M5.636 5.636l12.728 12.728"
            />
          </svg>
        ),
      };
    }

    if (syncStatus.isProcessing) {
      return {
        message: 'Syncing data...',
        detail: `Uploading ${syncStatus.pendingCount} pending changes`,
        color: 'bg-blue-500',
        icon: (
          <svg
            className="h-4 w-4 animate-spin"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15"
            />
          </svg>
        ),
      };
    }

    if (syncStatus.pendingCount > 0) {
      return {
        message: 'Connection restored',
        detail: `${syncStatus.pendingCount} changes ready to sync`,
        color: 'bg-orange-500',
        icon: (
          <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L4.082 16.5c-.77.833.192 2.5 1.732 2.5z"
            />
          </svg>
        ),
      };
    }

    return {
      message: 'Connection restored',
      detail: 'All data is up to date',
      color: 'bg-green-500',
      icon: (
        <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
        </svg>
      ),
    };
  };

  // Don't render on server to prevent hydration mismatch
  if (!isClient) {
    return null;
  }

  // Don't show if online and no pending sync
  if (!showBanner && isOnline && syncStatus.pendingCount === 0) {
    return null;
  }

  const statusInfo = getStatusInfo();

  return (
    <div className={`fixed top-0 right-0 left-0 z-50 ${statusInfo.color} text-white ${className}`}>
      <div className="mx-auto max-w-7xl px-4 py-2">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="flex-shrink-0">{statusInfo.icon}</div>
            <div className="min-w-0 flex-1">
              <p className="truncate text-sm font-medium">{statusInfo.message}</p>
              <p className="truncate text-xs opacity-90">{statusInfo.detail}</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            {/* Force Sync Button */}
            {isOnline && syncStatus.pendingCount > 0 && !syncStatus.isProcessing && (
              <button
                onClick={handleForceSync}
                className="bg-opacity-20 hover:bg-opacity-30 rounded bg-white px-2 py-1 text-xs transition-colors"
              >
                Sync Now
              </button>
            )}

            {/* Dismiss Button */}
            <button
              onClick={handleDismiss}
              className="text-white transition-colors hover:text-gray-200"
            >
              <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React from 'react';

import type { DependencyData } from '@/types/charts';

import GenericPieChart from '../GenericPieChart/GenericPieChart';

interface DependencyRatioPieChartProps {
  data: DependencyData;
  title: string;
  className?: string;
}

export default function DependencyRatioPieChart({
  data,
  title,
  className = '',
}: DependencyRatioPieChartProps) {
  const { youngDependents, workingAge, oldDependents } = data;
  const total = youngDependents + workingAge + oldDependents;

  console.log('DependencyRatioPieChart received data:', data);
  console.log('Calculated total:', total);

  // Let GenericPieChart generate beautiful colors automatically
  const colors = {
    youngDependents: '',
    workingAge: '',
    oldDependents: '',
  };

  // Prepare chart data with semantic colors
  const chartData = [
    {
      label: 'Young (0-14)',
      value: youngDependents,
      percentage: total > 0 ? (youngDependents / total) * 100 : 0,
      color: colors.youngDependents,
    },
    {
      label: 'Working (15-64)',
      value: workingAge,
      percentage: total > 0 ? (workingAge / total) * 100 : 0,
      color: colors.workingAge,
    },
    {
      label: 'Elderly (65+)',
      value: oldDependents,
      percentage: total > 0 ? (oldDependents / total) * 100 : 0,
      color: colors.oldDependents,
    },
  ]; // Show all categories including zero values

  return <GenericPieChart data={chartData} title={title} className={className} />;
}

'use client';

import React from 'react';

import { cn } from '@/lib';
import type { FormMode } from '@/types';
import {
  getFieldId,
  getFieldIds,
  buildAriaDescribedBy,
  buildAriaLabelledBy,
} from '@/utils/id-generators';

import { Label, HelperText, ReadOnly } from '../../../atoms/Field';
import { Control } from '../../../atoms/Field/Control/Control';

export interface ControlFieldProps {
  children?: React.ReactNode;
  /** Form mode - determines if field is editable or read-only */
  mode?: FormMode;
  label?: string;
  required?: boolean;
  helperText?: string;
  errorMessage?: string;
  className?: string;
  orientation?: 'vertical' | 'horizontal';
  labelWidth?: 'sm' | 'md' | 'lg';
  htmlFor?: string;
  labelSize?: 'xs' | 'sm' | 'md' | 'lg' | 'xl';
  // Control component props (when used directly with Control)
  controlProps?: React.ComponentProps<typeof Control>;
  // Label component props
  labelProps?: Omit<
    React.ComponentProps<typeof Label>,
    'htmlFor' | 'required' | 'children' | 'size'
  >;
  // Toggle-specific props
  toggleText?: {
    checked: string;
    unchecked: string;
  };
}

export const ControlField = ({
  children,
  mode = 'create',
  label,
  required = false,
  helperText,
  errorMessage,
  className,
  orientation = 'vertical',
  labelWidth = 'md',
  htmlFor,
  labelSize = 'sm',
  controlProps,
  labelProps,
  toggleText,
}: ControlFieldProps) => {
  const isHorizontal = orientation === 'horizontal';

  // Generate unique field ID using utility function
  const fieldId = getFieldId(htmlFor, controlProps?.id, 'control-field');
  const { labelId, helperTextId, errorId } = getFieldIds(fieldId);

  // Use errorMessage as the control error if provided (errorMessage takes precedence)
  const controlError = errorMessage || controlProps?.errorMessage;
  const hasHelperText = helperText || errorMessage;

  // Build ARIA attributes for accessibility
  const ariaLabelledBy = buildAriaLabelledBy(label ? labelId : undefined);
  const ariaDescribedBy = buildAriaDescribedBy(
    helperText ? helperTextId : undefined,
    controlError ? errorId : undefined
  );

  const getLabelWidthClass = (width: 'sm' | 'md' | 'lg') => {
    const widthClasses = {
      sm: 'w-32', // 128px
      md: 'w-40', // 160px
      lg: 'w-48', // 192px
    };
    return widthClasses[width];
  };

  return (
    <div className={cn('w-full', isHorizontal && 'flex items-start space-x-4', className)}>
      {/* Label */}
      {label && (
        <div
          className={cn(isHorizontal ? `${getLabelWidthClass(labelWidth)} shrink-0 pt-2` : 'mb-1')}
        >
          <Label htmlFor={fieldId} required={required} size={labelSize} {...labelProps}>
            {label}
          </Label>
        </div>
      )}

      {/* Field Container */}
      <div className={cn('flex-1', isHorizontal ? 'min-w-0' : 'flex flex-col')}>
        {/* Control/Field */}
        <div>
          {mode === 'view' ? (
            controlProps ? (
              <ReadOnly
                id={fieldId}
                value={
                  controlProps.type === 'toggle' && toggleText
                    ? controlProps.checked
                      ? toggleText.checked
                      : toggleText.unchecked
                    : controlProps.checked
                      ? 'Yes'
                      : 'No'
                }
                aria-labelledby={ariaLabelledBy}
                aria-describedby={ariaDescribedBy}
              />
            ) : (
              React.Children.map(children, child => {
                if (React.isValidElement(child)) {
                  return React.cloneElement(child as React.ReactElement<any>, {
                    id: fieldId,
                    'aria-labelledby': ariaLabelledBy,
                    'aria-describedby': ariaDescribedBy,
                    mode: 'view',
                  });
                }
                return child;
              })
            )
          ) : controlProps ? (
            <Control
              id={fieldId}
              errorMessage={controlError}
              aria-labelledby={ariaLabelledBy}
              aria-describedby={
                ariaDescribedBy || (toggleText ? `${fieldId}-description` : undefined)
              }
              description={
                controlProps.type === 'toggle' && toggleText
                  ? controlProps.checked
                    ? toggleText.checked
                    : toggleText.unchecked
                  : controlProps.description || label
              }
              {...controlProps}
            />
          ) : (
            React.Children.map(children, child => {
              if (React.isValidElement(child)) {
                return React.cloneElement(child as React.ReactElement<any>, {
                  id: fieldId,
                  'aria-labelledby': ariaLabelledBy,
                  'aria-describedby': ariaDescribedBy,
                });
              }
              return child;
            })
          )}
        </div>

        {/* Helper Text and Error Messages */}
        {hasHelperText && (
          <div className="mt-1 space-y-1">
            {/* Helper Text */}
            {helperText && <HelperText id={helperTextId}>{helperText}</HelperText>}

            {/* Error Message */}
            {errorMessage && (
              <HelperText id={errorId} error>
                {errorMessage}
              </HelperText>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

// Control Group for organizing multiple controls
export interface ControlGroupProps {
  children: React.ReactNode;
  title?: string;
  description?: string;
  className?: string;
  spacing?: 'sm' | 'md' | 'lg';
  orientation?: 'vertical' | 'horizontal';
}

export const ControlGroup = ({
  children,
  title,
  description,
  className,
  spacing = 'md',
  orientation = 'vertical',
}: ControlGroupProps) => {
  const getSpacingClass = (spacing: 'sm' | 'md' | 'lg') => {
    const spacingMap = {
      sm: '2',
      md: '4',
      lg: '6',
    };
    return spacingMap[spacing];
  };

  const spacingClass = getSpacingClass(spacing);
  const orientationClass = orientation === 'vertical' ? 'flex-col' : 'flex-row flex-wrap';

  return (
    <div className={cn('w-full', className)}>
      {/* Title and Description */}
      {(title || description) && (
        <div className="mb-3">
          {title && (
