'use client';

import Link from 'next/link';
import React, { useState, useMemo } from 'react';

import { Button } from '@/components';

export interface TableColumn<T = any> {
  key: string;
  title: string;
  dataIndex?: keyof T | ((record: T) => any);
  render?: (value: any, record: T, index: number) => React.ReactNode;
  sortable?: boolean;
  filterable?: boolean;
  width?: string | number;
  align?: 'left' | 'center' | 'right';
  fixed?: 'left' | 'right';
}

export interface TableAction<T = any> {
  key: string;
  label: string;
  icon?: React.ReactNode;
  onClick?: (record: T, index: number) => void;
  href?: (record: T) => string;
  visible?: (record: T) => boolean;
  disabled?: (record: T) => boolean;
  variant?: 'primary' | 'secondary' | 'danger' | 'success' | 'warning';
}

export interface DataTableProps<T = any> {
  data: T[];
  columns: TableColumn<T>[];
  actions?: TableAction<T>[];
  loading?: boolean;
  pagination?: {
    current: number;
    pageSize: number;
    total: number;
    onChange: (page: number, pageSize: number) => void;
    showSizeChanger?: boolean;
    pageSizeOptions?: string[];
  };
  selection?: {
    selectedRowKeys: string[];
    onChange: (selectedRowKeys: string[], selectedRows: T[]) => void;
    getCheckboxProps?: (record: T) => { disabled?: boolean };
  };
  rowKey?: keyof T | ((record: T) => string);
  onRow?: (
    record: T,
    index: number
  ) => {
    onClick?: () => void;
    onDoubleClick?: () => void;
    className?: string;
  };
  emptyText?: React.ReactNode;
  className?: string;
  size?: 'small' | 'middle' | 'large';
}

export default function DataTable<T extends Record<string, any>>({
  data,
  columns,
  actions = [],
  loading = false,
  pagination,
  selection,
  rowKey = 'id',
  onRow,
  emptyText = 'No data available',
  className = '',
  size = 'middle',
}: DataTableProps<T>) {
  const [sortField, setSortField] = useState<string>('');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

  // Get row key
  const getRowKey = (record: T, index: number): string => {
    if (typeof rowKey === 'function') {
      return rowKey(record);
    }
    return record[rowKey]?.toString() || index.toString();
  };

  // Handle sorting
  const handleSort = (columnKey: string) => {
    if (sortField === columnKey) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(columnKey);
      setSortOrder('asc');
    }
  };

  // Sort data
  const sortedData = useMemo(() => {
    if (!sortField) return data;

    return [...data].sort((a, b) => {
      const column = columns.find(col => col.key === sortField);
      let aValue = a[sortField];
      let bValue = b[sortField];

      if (column?.dataIndex && typeof column.dataIndex === 'function') {
        aValue = column.dataIndex(a);
        bValue = column.dataIndex(b);
      } else if (column?.dataIndex && typeof column.dataIndex === 'string') {
        aValue = a[column.dataIndex];
        bValue = b[column.dataIndex];
      }

      // Handle null/undefined values
      if (aValue == null && bValue == null) return 0;
      if (aValue == null) return sortOrder === 'asc' ? 1 : -1;
      if (bValue == null) return sortOrder === 'asc' ? -1 : 1;

      // Convert to strings for comparison
      const aStr = aValue.toString().toLowerCase();
      const bStr = bValue.toString().toLowerCase();

      if (aStr < bStr) return sortOrder === 'asc' ? -1 : 1;
      if (aStr > bStr) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });
  }, [data, sortField, sortOrder, columns]);

  // Handle select all
  const handleSelectAll = (checked: boolean) => {
    if (!selection) return;

    if (checked) {
      const allKeys = sortedData.map((record, index) => getRowKey(record, index));
      selection.onChange(allKeys, sortedData);
    } else {
      selection.onChange([], []);
    }
  };

  // Handle single row selection
  const handleRowSelect = (record: T, index: number, checked: boolean) => {
    if (!selection) return;

    const rowKeyValue = getRowKey(record, index);
    let newSelectedKeys = [...selection.selectedRowKeys];
    let newSelectedRows = sortedData.filter((item, idx) =>
      selection.selectedRowKeys.includes(getRowKey(item, idx))
    );

    if (checked) {
      newSelectedKeys.push(rowKeyValue);
      newSelectedRows.push(record);
    } else {
      newSelectedKeys = newSelectedKeys.filter(key => key !== rowKeyValue);
      newSelectedRows = newSelectedRows.filter(item => getRowKey(item, 0) !== rowKeyValue);
    }

    selection.onChange(newSelectedKeys, newSelectedRows);
  };

  // Get cell value
  const getCellValue = (record: T, column: TableColumn<T>, index: number) => {
    let value: any;

    if (column.dataIndex) {
      if (typeof column.dataIndex === 'function') {
        value = column.dataIndex(record);
      } else {
        value = record[column.dataIndex];
      }
    } else {
      value = record[column.key];
    }

    if (column.render) {
      return column.render(value, record, index);
    }

    return value;
  };

  const sizeClasses = {
    small: 'text-sm',
    middle: 'text-base',
    large: 'text-lg',
  };

  const paddingClasses = {
    small: 'px-3 py-2',
    middle: 'px-4 py-3',
    large: 'px-6 py-4',
  };

  if (loading) {
    return (
      <div
        className={`rounded-lg border border-gray-300 bg-white dark:border-gray-600 dark:bg-gray-800 ${className}`}
      >
        <div className="flex items-center justify-center py-12">
          <div className="flex items-center space-x-2">
            <div className="size-6 animate-spin rounded-full border-b-2 border-blue-600"></div>
            <span className="text-sm text-gray-600 dark:text-gray-400 dark:text-gray-600">
              Loading...
            </span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div
      className={`overflow-hidden rounded-lg border border-gray-300 bg-white dark:border-gray-600 dark:bg-gray-800 ${className}`}
    >
      {/* Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y border-gray-300 dark:border-gray-600">
          {/* Header */}
          <thead className="bg-gray-100 dark:bg-gray-700">
            <tr>
              {/* Selection column */}
              {selection && (
                <th className={`${paddingClasses[size]} w-12`}>
                  <input
                    type="checkbox"
                    className="size-4 rounded-sm border-gray-300 bg-white text-gray-600 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-800 dark:text-gray-400 dark:text-gray-600"
                    checked={
                      selection.selectedRowKeys.length === sortedData.length &&
                      sortedData.length > 0
                    }
                    onChange={e => handleSelectAll(e.target.checked)}
                  />
                </th>
              )}

              {/* Data columns */}
              {columns.map(column => (
                <th
                  key={column.key}
                  className={`${paddingClasses[size]} text-left ${sizeClasses[size]} font-medium text-gray-600 dark:text-gray-400 ${
                    column.sortable ? 'cursor-pointer hover:bg-gray-50 dark:bg-gray-700' : ''
                  }`}
                  style={{ width: column.width }}
                  onClick={column.sortable ? () => handleSort(column.key) : undefined}
                >
                  <div className="flex items-center space-x-1">
                    <span>{column.title}</span>
                    {column.sortable && (
                      <div className="flex flex-col">
                        <svg
                          className={`size-3 ${
                            sortField === column.key && sortOrder === 'asc'
                              ? 'text-gray-600 dark:text-gray-400'
                              : 'text-gray-500 dark:text-gray-400 dark:text-gray-600'
                          }`}
                          fill="currentColor"
                          viewBox="0 0 20 20"
                        >
                          <path d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" />
                        </svg>
                      </div>
                    )}
                  </div>
                </th>
              ))}

              {/* Actions column */}
              {actions.length > 0 && (
                <th
                  className={`${paddingClasses[size]} text-right ${sizeClasses[size]} font-medium text-gray-600 dark:text-gray-400`}
                >
                  Actions
                </th>
              )}
            </tr>
          </thead>

          {/* Body */}
          <tbody className="divide-y border-gray-300 bg-white dark:border-gray-600 dark:bg-gray-800">
            {sortedData.length === 0 ? (
              <tr>
                <td
                  colSpan={columns.length + (selection ? 1 : 0) + (actions.length > 0 ? 1 : 0)}
                  className="px-6 py-12 text-center text-gray-600 dark:text-gray-400 dark:text-gray-600"
                >
                  {emptyText}
                </td>
              </tr>
            ) : (
              sortedData.map((record, index) => {
                const rowKeyValue = getRowKey(record, index);
                const rowProps = onRow?.(record, index) || {};
                const isSelected = selection?.selectedRowKeys.includes(rowKeyValue) || false;

                return (
                  <tr
                    key={rowKeyValue}
                    className={`hover:bg-gray-50 dark:bg-gray-700 ${isSelected ? 'bg-blue-50 dark:bg-blue-900/20' : ''} ${rowProps.className || ''}`}
                    onClick={rowProps.onClick}
                    onDoubleClick={rowProps.onDoubleClick}
                  >
                    {/* Selection column */}
                    {selection && (
                      <td className={paddingClasses[size]}>
                        <input
                          type="checkbox"
                          className="size-4 rounded-sm border-gray-300 bg-white text-gray-600 focus:ring-blue-500 dark:border-gray-600 dark:bg-gray-800 dark:text-gray-400 dark:text-gray-600"
                          checked={isSelected}
                          onChange={e => handleRowSelect(record, index, e.target.checked)}
                          disabled={selection.getCheckboxProps?.(record)?.disabled}
                        />
                      </td>
                    )}

                    {/* Data columns */}
                    {columns.map(column => (
                      <td
                        key={column.key}
                        className={`${paddingClasses[size]} ${sizeClasses[size]} text-gray-600 dark:text-gray-400`}
                        style={{ textAlign: column.align || 'left' }}
                      >
                        {getCellValue(record, column, index)}
                      </td>
                    ))}

                    {/* Actions column */}
                    {actions.length > 0 && (
                      <td className={`${paddingClasses[size]} text-right`}>
                        <div className="flex items-center justify-end space-x-2">
                          {actions
                            .filter(action => action.visible?.(record) !== false)
                            .map(action => {
                              const isDisabled = action.disabled?.(record) || false;

                              if (action.href) {
                                return (
                                  <Link
                                    key={action.key}
                                    href={action.href(record)}
                                    className="inline-flex items-center px-3 py-1 text-sm font-medium text-gray-600 hover:text-gray-800 dark:text-gray-200 dark:text-gray-400"
                                  >
                                    {action.icon && <span className="mr-1">{action.icon}</span>}
                                    {action.label}
                                  </Link>
                                );
                              }

                              return (
                                <Button
                                  key={action.key}
                                  size="sm"
                                  variant={action.variant || 'secondary-outline'}
                                  onClick={
                                    action.onClick
                                      ? () => action.onClick!(record, index)
                                      : undefined
                                  }
                                  disabled={isDisabled}
                                  leftIcon={action.icon}
                                >
                                  {action.label}
                                </Button>
                              );
                            })}
                        </div>
                      </td>
                    )}
                  </tr>
                );
              })
            )}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      {pagination && (
        <div className="flex items-center justify-between border-t border-gray-300 bg-white px-4 py-3 dark:border-gray-600 dark:bg-gray-800">
          <div className="flex items-center text-sm text-gray-600 dark:text-gray-400">
            <span>
              Showing{' '}
              {Math.min((pagination.current - 1) * pagination.pageSize + 1, pagination.total)} to{' '}
              {Math.min(pagination.current * pagination.pageSize, pagination.total)} of{' '}
              {pagination.total} results
            </span>
          </div>

          <div className="flex items-center space-x-2">
            <Button
              size="sm"
              variant="secondary-outline"
              onClick={() => pagination.onChange(pagination.current - 1, pagination.pageSize)}
              disabled={pagination.current <= 1}
            >
              Previous
            </Button>

            <span className="text-sm text-gray-600 dark:text-gray-400">
              Page {pagination.current} of {Math.ceil(pagination.total / pagination.pageSize)}
            </span>

            <Button
              size="sm"
              variant="secondary-outline"
              onClick={() => pagination.onChange(pagination.current + 1, pagination.pageSize)}
              disabled={pagination.current >= Math.ceil(pagination.total / pagination.pageSize)}
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

import React, { useEffect } from 'react';

import { SelectField } from '@/components/molecules/FieldSet';
import { useGeographicData } from '@/hooks/api/useGeographicData';

import { HouseholdDetailsData } from '../../types';

export interface GeographicLocationProps {
  formData: HouseholdDetailsData;
  onChange: (field: string, value: string | number | boolean | null) => void;
  errors?: Record<string, string>;
  mode?: 'create' | 'view' | 'edit';
  className?: string;
}

/**
 * Geographic Location Form Fields Component
 *
 * Handles the administrative geographic hierarchy for households:
 * - Region
 * - Province
 * - City/Municipality
 * - Barangay
 */
export function GeographicLocation({
  formData,
  onChange,
  errors,
  mode = 'edit',
  className = '',
}: GeographicLocationProps) {
  const isReadOnly = mode === 'view';

  const {
    regions,
    provinces,
    cities,
    barangays,
    loading,
    error,
    loadProvinces,
    loadCities,
    loadBarangays,
    loadIndependentCities,
  } = useGeographicData();

  const handleFieldChange = (field: string) => (value: string | number | boolean | null) => {
    onChange(field, value);

    // Handle cascading selections
    if (field === 'region_code') {
      // Clear dependent fields
      onChange('province_code', '');
      onChange('city_municipality_code', '');
      onChange('barangay_code', '');

      if (value) {
        // Load provinces or independent cities based on region
        // NCR (code: '13') has independent cities, not provinces
        if (value === '13') {
          loadIndependentCities(value as string);
        } else {
          loadProvinces(value as string);
        }
      }
    } else if (field === 'province_code') {
      // Clear dependent fields
      onChange('city_municipality_code', '');
      onChange('barangay_code', '');

      if (value) {
        loadCities(value as string);
      }
    } else if (field === 'city_municipality_code') {
      // Clear dependent fields
      onChange('barangay_code', '');

      if (value) {
        loadBarangays(value as string);
      }
    }
  };

  // Load initial data based on existing form values
  useEffect(() => {
    if (formData.region_code) {
      if (formData.region_code === '13') {
        loadIndependentCities(formData.region_code);
      } else {
        loadProvinces(formData.region_code);
      }

      if (formData.province_code) {
        loadCities(formData.province_code);

        if (formData.city_municipality_code) {
          loadBarangays(formData.city_municipality_code);
        }
      } else if (formData.city_municipality_code && formData.region_code === '13') {
        // For NCR, load barangays directly from city
        loadBarangays(formData.city_municipality_code);
      }
    }
  }, [
    formData.region_code,
    formData.province_code,
    formData.city_municipality_code,
    loadProvinces,
    loadCities,
    loadBarangays,
    loadIndependentCities,
  ]);

  return (
    <div className={`space-y-4 ${className}`}>
      <div className="grid grid-cols-1 gap-3 md:grid-cols-2">
        {/* Region */}
        <SelectField
          label="Region"
          required
          helperText="Choose the administrative region"
          errorMessage={errors?.region_code}
          selectProps={{
            name: 'region_code',
            value: formData.region_code || '',
            onSelect: option => handleFieldChange('region_code')(option?.value || ''),
            placeholder: 'Select region...',
            disabled: isReadOnly,
            loading: loading.regions,
            options: [{ value: '', label: 'Select region...' }, ...regions],
          }}
        />

        {/* Province */}
        <SelectField
          label="Province"
          helperText="Choose the province (if applicable)"
          errorMessage={errors?.province_code}
          selectProps={{
            name: 'province_code',
            value: formData.province_code || '',
            onSelect: option => handleFieldChange('province_code')(option?.value || ''),
            placeholder: 'Select province...',
            disabled: isReadOnly || !formData.region_code || formData.region_code === '13',
            loading: loading.provinces,
            options: [
              {
                value: '',
                label:
                  formData.region_code === '13' ? 'N/A (Independent Cities)' : 'Select province...',
              },
              ...provinces,
            ],
          }}
        />
      </div>

      <div className="grid grid-cols-1 gap-3 md:grid-cols-2">
        {/* City/Municipality */}
        <SelectField
          label="City/Municipality"
          required
          helperText="Choose the city or municipality"
          errorMessage={errors?.city_municipality_code}
          selectProps={{
            name: 'city_municipality_code',
            value: formData.city_municipality_code || '',
            onSelect: option => handleFieldChange('city_municipality_code')(option?.value || ''),
            placeholder: 'Select city/municipality...',
            disabled:
              isReadOnly ||
              !formData.region_code ||
              (!formData.province_code && formData.region_code !== '13'),
            loading: loading.cities,
            options: [{ value: '', label: 'Select city/municipality...' }, ...cities],
          }}
        />

        {/* Barangay */}
        <SelectField
          label="Barangay"
          required
          helperText="Choose the barangay"
          errorMessage={errors?.barangay_code}
          selectProps={{
            name: 'barangay_code',
            value: formData.barangay_code || '',
            onSelect: option => handleFieldChange('barangay_code')(option?.value || ''),
            placeholder: 'Select barangay...',
            disabled: isReadOnly || !formData.city_municipality_code,
            loading: loading.barangays,
            options: [{ value: '', label: 'Select barangay...' }, ...barangays],
          }}
        />
      </div>

      {/* Helper Text for Geographic Hierarchy */}
      <div className="bg-info/5 border-info/20 rounded-md border p-3">
        <div>
          <p className="mb-1.5 font-medium text-zinc-900 dark:text-zinc-100">
            Geographic Hierarchy Information:
          </p>
          <p className="text-sm text-zinc-500 dark:text-zinc-400">
            Select the geographic location from the highest level (Region) down to the specific
            Barangay. Each selection will filter the options for the next level.
          </p>
        </div>
      </div>
    </div>
  );
}

import React from 'react';

import { InputField } from '@/components/molecules/FieldSet';

import { HouseholdDetailsData } from '../../types';

export interface HouseholdStatisticsProps {
  formData: HouseholdDetailsData;
  onChange: (field: string, value: string | number | boolean | null) => void;
  errors?: Record<string, string>;
  mode?: 'create' | 'view' | 'edit';
  className?: string;
}

/**
 * Household Statistics Form Fields Component
 *
 * Handles the numerical statistics for households:
 * - Number of families
 * - Number of household members
 * - Number of migrants
 */
export function HouseholdStatistics({
  formData,
  onChange,
  errors,
  mode = 'edit',
  className = '',
}: HouseholdStatisticsProps) {
  const isReadOnly = mode === 'view';

  const handleFieldChange = (field: string) => (value: string | number | boolean | null) => {
    onChange(field, value);
  };

  const handleNumberChange = (field: string) => (value: string | number | boolean | null) => {
    // Convert string to number for numeric fields
    const numericValue = typeof value === 'string' ? parseInt(value, 10) || 0 : value;
    onChange(field, numericValue);
  };

  return (
    <div className={`space-y-4 ${className}`}>
      <div className="grid grid-cols-1 gap-3 md:grid-cols-3">
        {/* Number of Families */}
        <InputField
          label="Number of Families"
          helperText="Total families in this household"
          errorMessage={errors?.no_of_families}
          inputProps={{
            name: 'no_of_families',
            type: 'number',
            value: formData.no_of_families?.toString() || '1',
            onChange: (e: React.ChangeEvent<HTMLInputElement>) =>
              handleNumberChange('no_of_families')(e.target.value),
            readOnly: isReadOnly,
            placeholder: '1',
            min: 1,
            max: 50,
          }}
        />

        {/* Number of Household Members */}
        <InputField
          label="Household Members"
          helperText="Total people living in household"
          errorMessage={errors?.no_of_household_members}
          inputProps={{
            name: 'no_of_household_members',
            type: 'number',
            value: formData.no_of_household_members?.toString() || '0',
            onChange: (e: React.ChangeEvent<HTMLInputElement>) =>
              handleNumberChange('no_of_household_members')(e.target.value),
            readOnly: isReadOnly,
            placeholder: '0',
            min: 0,
            max: 100,
          }}
        />

        {/* Number of Migrants */}
        <InputField
          label="Number of Migrants"
          helperText="Household members who are migrants"
          errorMessage={errors?.no_of_migrants}
          inputProps={{
            name: 'no_of_migrants',
            type: 'number',
            value: formData.no_of_migrants?.toString() || '0',
            onChange: (e: React.ChangeEvent<HTMLInputElement>) =>
              handleNumberChange('no_of_migrants')(e.target.value),
            readOnly: isReadOnly,
            placeholder: '0',
            min: 0,
            max: 100,
          }}
        />
      </div>

      {/* Statistics Information Panel */}
      <div className="bg-info/5 border-info/20 rounded-md border p-3">
        <div>
          <p className="mb-2 font-medium text-zinc-900 dark:text-zinc-100">
            Household Statistics Guidelines:
          </p>
          <ul className="space-y-2 text-xs">
            <li className="flex items-start">
              <span className="mr-2">‚Ä¢</span>
              <span>
                <strong>Families:</strong> Count each family unit living in the household (minimum
                1)
              </span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">‚Ä¢</span>
              <span>
                <strong>Members:</strong> Total number of people who regularly live in this
                household
              </span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">‚Ä¢</span>
              <span>
                <strong>Migrants:</strong> Household members who moved from another location
              </span>
            </li>
          </ul>
        </div>
      </div>

      {/* Validation Summary */}
      {(formData.no_of_migrants || 0) > (formData.no_of_household_members || 0) && (
        <div className="bg-warning/5 border-warning/20 rounded-md border p-3">
          <div>
            <p className="font-medium text-yellow-600 dark:text-yellow-400">
              ‚ö†Ô∏è Data Validation Warning
            </p>
            <p className="mt-1.5 text-sm text-zinc-500 dark:text-zinc-400">
              Number of migrants ({formData.no_of_migrants}) cannot exceed the total household
              members ({formData.no_of_household_members}).
            </p>
          </div>
        </div>
      )}
    </div>
  );
}

import React from 'react';

import { InputField, SelectField } from '@/components';
import type { FormMode } from '@/types';

export interface HouseholdTypeInformationData {
  householdType: string;
  tenureStatus: string;
  tenureOthersSpecify: string;
  householdUnit: string;
  householdName: string;
}

export interface HouseholdTypeInformationProps {
  /** Form mode - determines if field is editable or read-only */
  mode?: FormMode;
  value: HouseholdTypeInformationData;
  onChange: (value: HouseholdTypeInformationData) => void;
  errors: Record<string, string>;
  className?: string;
}

// Household Type Options
const HOUSEHOLD_TYPE_OPTIONS = [
  { value: '', label: 'Select household type' },
  { value: 'nuclear', label: 'Nuclear Family' },
  { value: 'single_parent', label: 'Single Parent' },
  { value: 'extended', label: 'Extended Family' },
  { value: 'childless', label: 'Childless' },
  { value: 'one_person', label: 'One Person' },
  { value: 'non_family', label: 'Non-Family' },
  { value: 'other', label: 'Other' },
];

