// Memoized validation summary
export const MemoizedValidationSummary = memo<{
  errors: Record<string, string>;
  onErrorClick?: (fieldName: string) => void;
}>(({ errors, onErrorClick }) => {
  const errorEntries = useMemo(
    () => Object.entries(errors).filter(([field, error]) => error && field !== '_form'),
    [errors]
  );

  const handleErrorClick = useCallback(
    (fieldName: string) => {
      onErrorClick?.(fieldName);

      // Focus the field with error
      const fieldElement = document.querySelector(
        `[name="${fieldName}"], #${fieldName}`
      ) as HTMLElement;
      if (fieldElement) {
        fieldElement.focus();
        fieldElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    },
    [onErrorClick]
  );

  if (errorEntries.length === 0 && !errors._form) {
    return null;
  }

  return (
    <div className="rounded-md bg-red-50 p-4" role="alert">
      <div className="flex">
        <div className="flex-shrink-0">
          <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
            <path
              fillRule="evenodd"
              d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z"
              clipRule="evenodd"
            />
          </svg>
        </div>
        <div className="ml-3">
          <h3 className="text-sm font-medium text-red-800">
            {errors._form ? 'Form Validation Error' : 'Please fix the following errors:'}
          </h3>
          {errors._form ? (
            <div className="mt-2 text-sm text-red-700">{errors._form}</div>
          ) : (
            <div className="mt-2 text-sm text-red-700">
              <ul className="list-disc space-y-1 pl-5">
                {errorEntries.map(([field, error]) => (
                  <li key={field}>
                    <button
                      type="button"
                      className="text-left text-red-700 underline hover:text-red-900"
                      onClick={() => handleErrorClick(field)}
                    >
                      <strong>{FIELD_LABELS[field] || field}:</strong> {error}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
});

MemoizedValidationSummary.displayName = 'MemoizedValidationSummary';

// Memoized form actions (submit/cancel buttons)
export const MemoizedFormActions = memo<{
  onSubmit: () => void;
  onCancel: () => void;
  isSubmitting: boolean;
  submitText?: string;
  cancelText?: string;
  isValid?: boolean;
}>(
  ({
    onSubmit,
    onCancel,
    isSubmitting,
    submitText = 'Submit',
    cancelText = 'Cancel',
    isValid = true,
  }) => {
    const handleSubmit = useCallback(
      (e: React.FormEvent) => {
        e.preventDefault();
        if (!isSubmitting && isValid) {
          onSubmit();
        }
      },
      [onSubmit, isSubmitting, isValid]
    );

    const handleCancel = useCallback(
      (e: React.FormEvent) => {
        e.preventDefault();
        if (!isSubmitting) {
          onCancel();
        }
      },
      [onCancel, isSubmitting]
    );

    return (
      <div className="flex justify-end space-x-3 border-t border-gray-200 pt-6">
        <button
          type="button"
          onClick={handleCancel}
          disabled={isSubmitting}
          className="inline-flex justify-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-50 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:outline-none disabled:cursor-not-allowed disabled:opacity-50"
        >
          {cancelText}
        </button>
        <button
          type="submit"
          onClick={handleSubmit}
          disabled={isSubmitting || !isValid}
          className="inline-flex justify-center rounded-md border border-transparent bg-blue-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-blue-700 focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:outline-none disabled:cursor-not-allowed disabled:opacity-50"
        >
          {isSubmitting ? (
            <>
              <svg
                className="mr-3 -ml-1 h-5 w-5 animate-spin text-white"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                ></circle>
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                ></path>
              </svg>
              Processing...
            </>
          ) : (
            submitText
          )}
        </button>
      </div>
    );
  }
);

MemoizedFormActions.displayName = 'MemoizedFormActions';

