import React, { useMemo } from 'react';

import { InputField, SelectField } from '@/components';
import { usePsgcSearch } from '@/hooks/search/usePsgcSearch';
import type { FormMode } from '@/types';

export interface BirthInformationData {
  birthdate: string;
  birth_place_name: string;
  birth_place_code: string;
}

export interface BirthInformationProps {
  /** Form mode - determines if field is editable or read-only */
  mode?: FormMode;
  value: BirthInformationData;
  onChange: (value: BirthInformationData) => void;
  errors: Record<string, string>;
  className?: string;
}

export function BirthInformation({
  mode = 'create',
  value,
  onChange,
  errors,
  className = '',
}: Readonly<BirthInformationProps>) {
  // PSGC search hook for birth place
  const {
    options: psgcOptions,
    isLoading: psgcLoading,
    setQuery: handlePsgcSearch,
  } = usePsgcSearch({
    levels: 'all', // Allow province, city, and barangay selection
    limit: 20, // Smaller initial load for better performance
    debounceMs: 300,
  });

  const handleChange = (field: keyof BirthInformationData, fieldValue: any) => {
    onChange({
      ...value,
      [field]: fieldValue,
    });
  };

  // Memoize options to prevent infinite re-renders
  const birthPlaceOptions = useMemo(() => {
    const allOptions = psgcOptions.map(place => {
      // Format hierarchical display based on level
      let displayLabel = place.name;
      let description = '';
      let badge = place.level;

      if (place.level === 'barangay') {
        // For barangay: "Barangay Name, City, Province"
        if ((place as any).city_name && (place as any).province_name) {
          displayLabel = `${place.name}, ${(place as any).city_name}, ${(place as any).province_name}`;
        } else if ((place as any).city_name) {
          displayLabel = `${place.name}, ${(place as any).city_name}`;
        }
        badge = 'barangay';
      } else if (place.level === 'city') {
        // For city/municipality: "City Name, Province"
        if ((place as any).province_name) {
          displayLabel = `${place.name}, ${(place as any).province_name}`;
        }
        badge = (place as any).type || 'city';
      } else if (place.level === 'province') {
        // For province: just "Province Name"
        displayLabel = place.name;
        badge = 'province';
      }

      // Format description for subtext
      if ((place as any).full_address) {
        // Use full address but trim to province level (remove region)
        const parts = (place as any).full_address.split(', ');
        if (parts.length >= 3) {
          description = parts.slice(0, 3).join(', '); // Up to Province
        } else {
          description = (place as any).full_address;
        }
      }

      return {
        value: place.code,
        label: displayLabel,
        description: description,
        badge: badge,
      };
    });

    // Ensure current selected value is always in options for proper display in view mode
    if (value.birth_place_code && value.birth_place_name) {
      const exists = allOptions.some(opt => opt.value === value.birth_place_code);
      if (!exists) {
        allOptions.unshift({
          value: value.birth_place_code,
          label: value.birth_place_name,
          description: 'Current selection',
          badge: 'barangay',
        });
      }
    }

    return allOptions;
  }, [psgcOptions, value.birth_place_code, value.birth_place_name]);

  return (
    <div className={`space-y-4 ${className}`}>
      <div>
        <h4 className="text-lg font-medium text-gray-800 dark:text-gray-200">Birth Information</h4>
        <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
          Birth date and place information.
        </p>
      </div>

      <div className="grid grid-cols-1 gap-x-4 gap-y-6 sm:grid-cols-2">
        <InputField
          mode={mode}
          label="Birth Date"
          required
          labelSize="sm"
          errorMessage={errors.birthdate}
          inputProps={{
            type: 'date',
            value: value.birthdate,
            onChange: e => handleChange('birthdate', e.target.value),
            required: true,
          }}
        />

        <SelectField
          mode={mode}
          label="Birth Place"
          required
          labelSize="sm"
          errorMessage={errors.birth_place_name}
          helperText="Search for the place of birth"
          selectProps={{
            placeholder: 'Search for birth place...',
            options: birthPlaceOptions,
            value: value.birth_place_code,
            onSelect: option => {
              handleChange('birth_place_code', option?.value || '');
              handleChange('birth_place_name', option?.label || '');
            },
            onSearch: handlePsgcSearch,
            loading: psgcLoading,
          }}
        />
      </div>
    </div>
  );
}

export default BirthInformation;

import React from 'react';

import { SelectField, ControlFieldSet } from '@/components';
import { Radio } from '@/components/atoms/Field/Control/Radio/Radio';
import { EDUCATION_LEVEL_OPTIONS_WITH_EMPTY } from '@/lib/constants/resident-enums';
import type { FormMode } from '@/types';

// Graduate status options
const GRADUATE_STATUS_OPTIONS = [
  { value: 'yes', label: 'Yes, graduated' },
  { value: 'no', label: 'No, not graduated' },
];

export interface EducationInformationData {
  education_attainment: string;
  is_graduate: string; // 'yes' | 'no' (defaults to 'yes')
}

export interface EducationInformationProps {
  value: EducationInformationData;
  onChange: (value: EducationInformationData) => void;
  errors: Record<string, string>;
  /** Form mode - determines if field is editable or read-only */
  mode?: FormMode;
  className?: string;
}

export function EducationInformation({
  value,
  onChange,
  errors,
  mode = 'create',
  className = '',
}: EducationInformationProps) {
  const handleChange = (field: keyof EducationInformationData, fieldValue: any) => {
    const updatedValue = {
      ...value,
      [field]: fieldValue,
    };

    // Business logic: Auto-set graduate status based on education level
    if (field === 'education_attainment') {
      // Post-graduate education implies college graduation
      if (fieldValue === 'post_graduate') {
        updatedValue.is_graduate = 'yes';
      }
      // For other levels, keep the current graduate status or default to 'no' if empty
      else if (!value.is_graduate) {
        updatedValue.is_graduate = 'no';
      }
    }

    onChange(updatedValue);
  };

  return (
    <div className={`space-y-4 ${className}`}>
      <div>
        <h4 className="text-lg font-medium text-gray-800 dark:text-gray-200">
          Education Information
        </h4>
        <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
          Educational attainment and graduation status.
        </p>
      </div>

      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
        <SelectField
          label="Highest Educational Attainment"
          required
          labelSize="sm"
          errorMessage={errors.education_attainment}
          mode={mode}
          selectProps={{
            placeholder: 'Select education level...',
            options: EDUCATION_LEVEL_OPTIONS_WITH_EMPTY,
            value: value.education_attainment,
            onSelect: option => handleChange('education_attainment', option?.value || ''),
          }}
        />

        <ControlFieldSet
          type="radio"
          label="Graduate Status"
          labelSize="sm"
          radioName="is_graduate"
          radioValue={value.is_graduate}
          onRadioChange={(selectedValue: string) => handleChange('is_graduate', selectedValue)}
          errorMessage={errors.is_graduate}
          orientation="horizontal"
          spacing="sm"
          mode={value.education_attainment === 'post_graduate' ? 'view' : mode}
          // helperText={
          //   value.education_attainment === 'post_graduate'
          //     ? "Automatically set to 'Yes' for post-graduate education"
          //     : "Whether the current education level has been completed"
          // }
        >
          {GRADUATE_STATUS_OPTIONS.map(option => (
            <Radio
              key={option.value}
              value={option.value}
              label={option.label}
              style="button"
              buttonProps={{
                variant: 'neutral-outline',
                size: 'lg',
              }}
            />
          ))}
        </ControlFieldSet>
      </div>
    </div>
  );
}

export default EducationInformation;

import React from 'react';

import { SelectField } from '@/components';
import { EMPLOYMENT_STATUS_OPTIONS_WITH_EMPTY } from '@/lib/constants/resident-enums';
import type { FormMode } from '@/types';
import { SelectOption } from '@/types';

export interface EmploymentInformationData {
  employment_status: string;
  occupation_code: string;
  occupation_title: string;
}

export interface EmploymentInformationProps {
  value: EmploymentInformationData;
  onChange: (value: EmploymentInformationData) => void;
  errors: Record<string, string>;
  // PSOC search functionality
  onPsocSearch?: (query: string) => void;
  psocOptions?: SelectOption[];
  psocLoading?: boolean;
  /** Form mode - determines if field is editable or read-only */
  mode?: FormMode;
  className?: string;
}

export function EmploymentInformation({
  value,
  onChange,
  errors,
  onPsocSearch,
  psocOptions = [],
  psocLoading = false,
  mode = 'create',
  className = '',
}: EmploymentInformationProps) {
  const handleChange = (field: keyof EmploymentInformationData, fieldValue: string) => {
    onChange({
      ...value,
      [field]: fieldValue,
    });
  };

  return (
    <div className={`space-y-4 ${className}`}>
      <div>
        <h4 className="text-lg font-medium text-gray-800 dark:text-gray-200">
          Employment Information
        </h4>
        <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
          Employment status and occupation details.
        </p>
      </div>

      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
        <SelectField
          label="Employment Status"
          labelSize="sm"
          errorMessage={errors.employment_status}
          mode={mode}
          selectProps={{
            placeholder: 'Select employment status...',
            options: EMPLOYMENT_STATUS_OPTIONS_WITH_EMPTY,
            value: value.employment_status,
            onSelect: option => handleChange('employment_status', option?.value || ''),
          }}
        />

        <SelectField
          label="Occupation Name"
          labelSize="sm"
          errorMessage={errors.occupation_title || errors.occupation_code}
          mode={mode}
          selectProps={{
            placeholder: 'Search occupation from level 1-5...',
            options: (() => {
              // Ensure the current selected value is in options
              const currentOptions = [...psocOptions];
              if (value.occupation_code && value.occupation_title) {
                // Check if current selection is already in options
                const hasCurrentOption = currentOptions.some(
                  opt => opt.value === value.occupation_code
                );
                if (!hasCurrentOption) {
                  // Add the current value as an option so it displays correctly
                  currentOptions.unshift({
                    value: value.occupation_code,
                    label: value.occupation_title, // Use full hierarchy as label
                    description: `PSOC Code: ${value.occupation_code}`,
                  });
                }
              }
              return currentOptions;
            })(),
            value: value.occupation_code,
            loading: psocLoading,
            onSearch: onPsocSearch,
            onSelect: option => {
              if (option) {
                // Only allow specific occupation (level 5) as final result
                if ((option as any).level_type === 'occupation') {
                  handleChange('occupation_code', (option as any).occupation_code);
                  handleChange('occupation_title', (option as any).occupation_title);
                } else {
                  // If higher level is selected, clear the fields
                  handleChange('occupation_code', '');
                  handleChange('occupation_title', '');
                }
              } else {
                handleChange('occupation_code', '');
                handleChange('occupation_title', '');
              }
            },
          }}
        />
      </div>
    </div>
  );
}

export default EmploymentInformation;

import React from 'react';

import type { FormMode } from '@/types';

import { BasicInformation, BasicInformationData } from './FormField/BasicInformation';
import { BirthInformation, BirthInformationData } from './FormField/BirthInformation';
import { EducationInformation, EducationInformationData } from './FormField/EducationInformation';
import {
  EmploymentInformation,
  EmploymentInformationData,
} from './FormField/EmploymentInformation';
import { PhilSysCardField } from './FormField/PhilSysCardField';

export interface PersonalInformationFormProps {
  /** Form mode - determines if field is editable or read-only */
  mode?: FormMode;
  formData: {
    // PhilSys and Personal Info
    philsys_card_number?: string;
    first_name?: string;
    middle_name?: string;
    last_name?: string;
    extension_name?: string;
    sex?: string;
    civil_status?: string;
    civil_status_others_specify?: string;
    citizenship?: string;
    // Birth Information
    birthdate?: string;
    birth_place_name?: string;
    birth_place_code?: string;
    // Education
    education_attainment?: string;
    is_graduate?: boolean;
    // Employment
    employment_status?: string;
    occupation_code?: string;
    occupation_title?: string;
  };
  onChange: (field: string, value: string | number | boolean | null) => void;
  errors: Record<string, string>;
  // API handlers for search functionality
  onPsgcSearch?: (query: string) => void;
  onPsocSearch?: (query: string) => void;
  psgcOptions?: any[];
  psocOptions?: any[];
  psgcLoading?: boolean;
  psocLoading?: boolean;
}

export function PersonalInformationForm({
  mode = 'create',
  formData,
  onChange,
  errors,
  onPsgcSearch,
  onPsocSearch,
  psgcOptions = [],
  psocOptions = [],
  psgcLoading = false,
  psocLoading = false,
}: PersonalInformationFormProps) {
  // Map form data to BasicInformation component props
  const basicInfoValue: BasicInformationData = React.useMemo(
    () => ({
      first_name: formData.first_name || '',
      middle_name: formData.middle_name || '',
      last_name: formData.last_name || '',
      extension_name: formData.extension_name || '',
      sex: (formData.sex || '') as '' | 'male' | 'female',
      civil_status: formData.civil_status || '',
    }),
    [
      formData.first_name,
      formData.middle_name,
      formData.last_name,
      formData.extension_name,
      formData.sex,
      formData.civil_status,
    ]
  );

  // Handle changes from BasicInformation component
  const handleBasicInfoChange = React.useCallback(
    (value: BasicInformationData) => {
      // Only update fields that have actually changed from current form data
      const currentBasicInfo: BasicInformationData = {
        first_name: formData.first_name || '',
        middle_name: formData.middle_name || '',
        last_name: formData.last_name || '',
        extension_name: formData.extension_name || '',
        sex: (formData.sex || '') as '' | 'male' | 'female',
        civil_status: formData.civil_status || '',
        civil_status_others_specify: formData.civil_status_others_specify || '',
      };

      Object.entries(value).forEach(([field, fieldValue]) => {
        if (currentBasicInfo[field as keyof BasicInformationData] !== fieldValue) {
          onChange(field, fieldValue);
        }
      });
    },
    [
      onChange,
      formData.first_name,
      formData.middle_name,
      formData.last_name,
      formData.extension_name,
      formData.sex,
      formData.civil_status,
    ]
  );

  // Handle changes from BirthInformation component
  const handleBirthInfoChange = React.useCallback(
    (value: BirthInformationData) => {
      // Only update fields that have actually changed from current form data
      const currentBirthInfo: BirthInformationData = {
        birthdate: formData.birthdate || '',
        birth_place_name: formData.birth_place_name || '',
        birth_place_code: formData.birth_place_code || '',
      };

      Object.entries(value).forEach(([field, fieldValue]) => {
        if (currentBirthInfo[field as keyof BirthInformationData] !== fieldValue) {
          onChange(field, fieldValue);
        }
      });
    },
    [onChange, formData.birthdate, formData.birth_place_name, formData.birth_place_code]
  );

  // Handle changes from EducationInformation component
  const handleEducationInfoChange = React.useCallback(
    (value: EducationInformationData) => {
      // Only update fields that have actually changed from current form data
      const currentEducationInfo = {
        education_attainment: formData.education_attainment || '',
        is_graduate: formData.is_graduate ? 'yes' : 'no',
      };

      Object.entries(value).forEach(([field, fieldValue]) => {
        if (currentEducationInfo[field as keyof typeof currentEducationInfo] !== fieldValue) {
          // Convert is_graduate string back to boolean for form state
          if (field === 'is_graduate') {
            onChange(field, fieldValue === 'yes');
          } else {
            onChange(field, fieldValue);
          }
        }
      });
    },
    [onChange, formData.education_attainment, formData.is_graduate]
  );

  // Handle changes from EmploymentInformation component
  const handleEmploymentInfoChange = React.useCallback(
    (value: EmploymentInformationData) => {
      // Only update fields that have actually changed from current form data
      const currentEmploymentInfo: EmploymentInformationData = {
        employment_status: formData.employment_status || '',
        occupation_code: formData.occupation_code || '',
        occupation_title: formData.occupation_title || '',
      };

      Object.entries(value).forEach(([field, fieldValue]) => {
        if (currentEmploymentInfo[field as keyof EmploymentInformationData] !== fieldValue) {
          onChange(field, fieldValue);
        }
      });
    },
    [onChange, formData.employment_status, formData.occupation_code, formData.occupation_title]
  );

  return (
    <div className="rounded-lg border border-gray-300 bg-white p-6 shadow-xs dark:border-gray-600 dark:bg-gray-800">
      <div className="space-y-6">
        <div>
          <h2 className="text-xl font-semibold text-gray-800 dark:text-gray-200">
            Personal Information
          </h2>
          <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
            Basic personal details, birth information, and educational/employment background.
          </p>
        </div>

        <div className="space-y-8">
          {/* PhilSys Card Number */}
          <PhilSysCardField
            mode={mode}
            value={formData.philsys_card_number || ''}
            onChange={value => onChange('philsys_card_number', value)}
            error={errors.philsys_card_number}
          />

          {/* Basic Information Component - Names, Sex, Civil Status, Citizenship */}
          <BasicInformation
            mode={mode}
            value={basicInfoValue}
            onChange={handleBasicInfoChange}
            errors={errors}
          />

          {/* Birth Information */}
          <BirthInformation
            mode={mode}
            value={React.useMemo(
              () => ({
                birthdate: formData.birthdate || '',
                birth_place_name: formData.birth_place_name || '',
                birth_place_code: formData.birth_place_code || '',
              }),
              [formData.birthdate, formData.birth_place_name, formData.birth_place_code]
            )}
            onChange={handleBirthInfoChange}
            errors={errors}
          />

          {/* Education Information */}
          <EducationInformation
            mode={mode}
            value={React.useMemo(
              () => ({
                education_attainment: formData.education_attainment || '',
                is_graduate: formData.is_graduate ? 'yes' : 'no',
              }),
              [formData.education_attainment, formData.is_graduate]
            )}
            onChange={handleEducationInfoChange}
            errors={errors}
          />

          {/* Employment Information */}
          <EmploymentInformation
            mode={mode}
            value={React.useMemo(
              () => ({
                employment_status: formData.employment_status || '',
                occupation_code: formData.occupation_code || '',
                occupation_title: formData.occupation_title || '',
              }),
              [formData.employment_status, formData.occupation_code, formData.occupation_title]
            )}
            onChange={handleEmploymentInfoChange}
            errors={errors}
            onPsocSearch={onPsocSearch}
            psocOptions={psocOptions}
            psocLoading={psocLoading}
          />
        </div>
      </div>
    </div>
  );
}

import React from 'react';

