'use client';

import { useQueryClient, useQuery } from '@tanstack/react-query';
import { useRouter } from 'next/navigation';
import React, { useState, useEffect } from 'react';

import { Button } from '@/components';
import {
  LocationAndDemographicsForm,
  HouseholdDetailsForm,
} from '@/components/organisms/FormSection/Household';
import { useAuth } from '@/contexts';
import { useUserBarangay } from '@/hooks/utilities';
import { supabase, logger, logError } from '@/lib';
import { useCSRFToken } from '@/lib/auth/csrf';
import { geographicService } from '@/services/geographic.service';

// Import our new Form/Household components

// Import molecules and atoms

import { HouseholdFormData } from '@/types/forms';

export type { HouseholdFormData };
export type HouseholdFormMode = 'create' | 'view' | 'edit';

interface HouseholdFormProps {
  mode?: HouseholdFormMode;
  onSubmit?: (data: HouseholdFormData) => Promise<void>;
  onCancel?: () => void;
  onModeChange?: (mode: HouseholdFormMode) => void;
  initialData?: Partial<HouseholdFormData>;
  householdId?: string;
  // View mode options for displaying labels
  viewModeOptions?: {
    addressLabels?: any;
    household_typeLabels?: any;
    householdHeadLabel?: string;
  };
}

export default function HouseholdForm({
  mode = 'create',
  onSubmit,
  onCancel: _onCancel,
  onModeChange,
  initialData,
  householdId,
  viewModeOptions,
}: HouseholdFormProps) {
  const router = useRouter();
  const queryClient = useQueryClient();
  const { getToken: getCSRFToken } = useCSRFToken();
  const { user, userProfile, session } = useAuth();

  const [errors, setErrors] = useState<Partial<Record<keyof HouseholdFormData, string>>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isAutoFilling, setIsAutoFilling] = useState(false);

  // Production-ready auto-fill state management
  const [autoFillAttempted, setAutoFillAttempted] = useState(false);
  const [autoFillStatus, setAutoFillStatus] = useState<'idle' | 'loading' | 'success' | 'failed'>(
    'idle'
  );

  // Test function to manually trigger auto-fill with sample data
  const testAutoFill = () => {
    console.log('üß™ TEST AUTO-FILL triggered');
    console.log('üß™ Current form data codes:', {
      region_code: formData.region_code,
      province_code: formData.province_code,
      cityCode: formData.city_municipality_code,
      barangay_code: formData.barangay_code,
    });
    console.log('üß™ Available options:', {
      regions: regionOptions.length,
      provinces: provinceOptions.length,
      cities: cityOptions.length,
      barangays: barangayOptions.length,
    });
    console.log('üß™ Cities loading state:', citiesLoading);
    console.log('üß™ Should load cities:', shouldLoadCities);

    if (regionOptions.length === 0) {
      alert(
        '‚ùå No regions loaded. Please wait for the page to load completely or check your connection.'
      );
      return;
    }

    // Find region 04 (CALABARZON)
    const testRegion = regionOptions.find((r: any) => r.value === '04');
    if (!testRegion) {
      console.log(
        '‚ùå Region 04 not found. Available regions:',
        regionOptions.map((r: any) => `${r.value}:${r.label}`).slice(0, 5)
      );
      alert('‚ùå Region 04 (CALABARZON) not found in options');
      return;
    }

    console.log('‚úÖ Found region 04:', testRegion);
    console.log('üß™ Setting form data...');

    setFormData(prev => ({
      ...prev,
      region_code: '04', // CALABARZON
      province_code: '0421', // Cavite
      city_municipality_code: '042114', // Dasmari√±as
      barangay_code: '042114014', // Sample barangay
    }));

    setAutoFillStatus('success');
    console.log('‚úÖ Test auto-fill completed');
    alert('‚úÖ Test auto-fill completed! Check the form fields.');
  };

  const [formData, setFormData] = useState<HouseholdFormData>(() => ({
    // Required fields with defaults
    code: '',
    barangay_code: '',
    city_municipality_code: '',
    region_code: '',
    house_number: '',
    street_id: '',
    is_active: true,
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),

    // Location and Demographics
    subdivision_id: null,
    province_code: null,
    zip_code: null,
    no_of_families: 1,
    no_of_household_members: 0,
    no_of_migrants: 0,

    // Household Details
    name: null,
    address: null,
    household_type: null,
    tenure_status: null,
    tenure_others_specify: null,
    household_unit: null,
    monthly_income: null,
    income_class: null,
    household_head_id: null,
    household_head_position: null,
    created_by: null,
    updated_by: null,

    // Merge initial data if provided
    ...initialData,
  }));

  // User's assigned barangay address (auto-populated)
  const { address: userAddress, loading: loadingAddress } = useUserBarangay();

  // Load location options using React Query directly - always load regions
  const { data: regionOptions = [], isLoading: regionsLoading } = useQuery({
    queryKey: ['addresses', 'regions'],
    queryFn: async () => {
      return await geographicService.getRegions();
    },
    staleTime: 10 * 60 * 1000, // 10 minutes
  });

  // Load provinces when we have a region (either from form data or user selection)
  const shouldLoadProvinces = formData.region_code || userAddress?.region_code;

  const { data: provinceOptions = [], isLoading: provincesLoading } = useQuery({
    queryKey: [
      'addresses',
      'provinces',
      shouldLoadProvinces ? formData.region_code || userAddress?.region_code : '',
    ],
    queryFn: async () => {
      const region_code = formData.region_code || userAddress?.region_code;
      if (!region_code) return [];
      return await geographicService.getProvincesByRegion(region_code);
    },
    enabled: !!shouldLoadProvinces,
    staleTime: 10 * 60 * 1000,
  });

  // Load cities when we have a province
  const shouldLoadCities = formData.province_code || userAddress?.province_code;

  const { data: cityOptions = [], isLoading: citiesLoading } = useQuery({
    queryKey: [
      'addresses',
      'cities',
      shouldLoadCities ? formData.province_code || userAddress?.province_code : '',
    ],
    queryFn: async () => {
      const province_code = formData.province_code || userAddress?.province_code;
      if (!province_code) return [];
      const cityOptions = await geographicService.getCitiesByProvince(province_code);
      console.log('üèôÔ∏è Cities loaded:', cityOptions.length, 'cities for province:', province_code);
      return cityOptions;
    },
    enabled: !!shouldLoadCities,
    staleTime: 10 * 60 * 1000,
  });

  // Load barangays when we have a city
  const shouldLoadBarangays =
    formData.city_municipality_code || userAddress?.city_municipality_code;

  const { data: barangayOptions = [], isLoading: barangaysLoading } = useQuery({
    queryKey: [
      'addresses',
      'barangays',
      shouldLoadBarangays
        ? formData.city_municipality_code || userAddress?.city_municipality_code
        : '',
    ],
    queryFn: async () => {
      const cityCode = formData.city_municipality_code || userAddress?.city_municipality_code;
      if (!cityCode) return [];
      return await geographicService.getBarangaysByCity(cityCode);
    },
    enabled: !!shouldLoadBarangays,
    staleTime: 10 * 60 * 1000,
  });

  // Enhanced auto-fill with multiple strategies and better debugging
  useEffect(() => {
    const performAutoFill = async () => {
      console.log('üîÑ Auto-fill check started');

      // Only attempt once per form session (unless it was unsuccessful)
      if (autoFillAttempted && formData.region_code && formData.barangay_code) {
        console.log('‚è≠Ô∏è Auto-fill already completed successfully, skipping');
        return;
      }

      // Skip if form already has location data
      if (
        formData.region_code ||
        formData.province_code ||
        formData.city_municipality_code ||
        formData.barangay_code
      ) {
        console.log('‚è≠Ô∏è Form already has location data, skipping auto-fill');
        setAutoFillAttempted(true);
        return;
      }

      // Wait for regions to be available
      if (regionsLoading || regionOptions.length === 0) {
        console.log('‚è≥ Waiting for regions to load...', {
          regionsLoading,
          regionCount: regionOptions.length,
        });
        return;
      }

      // Mark as attempted to prevent multiple calls
      setAutoFillAttempted(true);
      setAutoFillStatus('loading');
      setIsAutoFilling(true);
      console.log('üöÄ Starting auto-fill process...');

      try {
        let addressData = null;

        // Strategy 1: Use userAddress from context (preferred)
        if (userAddress?.region_code) {
          console.log('‚úÖ Using userAddress from context');
          addressData = userAddress;
        }
        // Strategy 1B: Fetch from API if authenticated but no userAddress
        else if (user && userProfile?.barangay_code) {
          console.log('üåê Fetching address data from API for barangay:', userProfile.barangay_code);

          try {
            const result = await geographicService.getGeographicHierarchy(
              userProfile.barangay_code
            );
            addressData = result;
            console.log('‚úÖ Address data retrieved successfully');
          } catch (error) {
            console.error('‚ùå API fetch failed:', error);
          }
        } else {
          console.warn('üö´ Auto-fill failed: No authenticated user data available');
          setAutoFillStatus('failed');
          return;
        }

        // Apply auto-fill if we have valid address data
        if (addressData && 'region_code' in addressData && addressData.region_code) {
          console.log('üîç Validating and applying address data...');

          // Validate the region exists in our options
          const regionExists = regionOptions.some((r: any) => r.value === addressData.region_code);

          if (regionExists) {
            console.log('‚úÖ Auto-filling location fields...');

            // Step 1: Set region and trigger province loading
            handleRegionChange(addressData.region_code);

            // Step 2: Wait for provinces to load, then set province and trigger city loading
            setTimeout(() => {
              if ('province_code' in addressData && addressData.province_code) {
                handleProvinceChange(addressData.province_code);

                // Step 3: Wait for cities to load, then set city and trigger barangay loading
                setTimeout(() => {
                  let cityCode: string | undefined;
                  if (
                    'city_municipality_code' in addressData &&
                    typeof addressData.city_municipality_code === 'string' &&
                    addressData.city_municipality_code
                  ) {
                    cityCode = addressData.city_municipality_code;
                  } else if (
                    'city_code' in addressData &&
                    typeof addressData.city_code === 'string' &&
                    addressData.city_code
                  ) {
                    cityCode = addressData.city_code;
                  }

                  if (cityCode) {
                    handleCityChange(cityCode);

                    // Step 4: Wait for barangays to load, then set barangay
                    setTimeout(() => {
                      if ('barangay_code' in addressData && addressData.barangay_code) {
                        handleBarangayChange(addressData.barangay_code);
                      }
                    }, 300);
                  }
                }, 300);
              }
            }, 300);

            setAutoFillStatus('success');
            console.log('‚úÖ Auto-fill completed successfully!');
          } else {
            console.warn('‚ö†Ô∏è Region validation failed - region not found in options');
            setAutoFillStatus('failed');
          }
        } else {
          console.warn('‚ö†Ô∏è No valid address data available');
          setAutoFillStatus('failed');
        }
      } catch (error) {
        console.error('‚ùå Auto-fill error:', error);
        setAutoFillStatus('failed');
      } finally {
        setIsAutoFilling(false);
      }
    };

    performAutoFill();
  }, [
    user,
    userProfile?.barangay_code,
    userAddress,
    regionsLoading,
    regionOptions,
    autoFillAttempted,
    formData.region_code,
    formData.province_code,
    formData.city_municipality_code,
    formData.barangay_code,
  ]);

  // Household code will be generated by the database
  // The generate_hierarchical_household_id() function creates codes in format: RRPPMMBBB-SSSS-TTTT-HHHH
  // Example: "137404001-0001-0002-0123" based on PSGC barangay code and address components

  const handleInputChange = (field: string, value: string | number | boolean | null) => {
    setFormData(prev => ({
      ...prev,
      [field]: value,
    }));

    // Clear error when user starts typing
    if (errors[field as keyof HouseholdFormData]) {
      setErrors(prev => ({
        ...prev,
        [field as keyof HouseholdFormData]: undefined,
      }));
    }
  };

  // Location change handlers for cascade and auto-fill
  const handleRegionChange = (region_code: string) => {
    setFormData(prev => ({
      ...prev,
      region_code,
      province_code: '',
      city_municipality_code: '',
      barangay_code: '',
      subdivision_id: '',
      street_id: '',
    }));
  };

  const handleProvinceChange = (province_code: string) => {
    setFormData(prev => ({
      ...prev,
      province_code,
      city_municipality_code: '',
      barangay_code: '',
      subdivision_id: '',
      street_id: '',
    }));
  };

  const handleCityChange = (cityCode: string) => {
    setFormData(prev => ({
      ...prev,
      city_municipality_code: cityCode,
      barangay_code: '',
      subdivision_id: '',
      street_id: '',
    }));
  };

  const handleBarangayChange = (barangay_code: string) => {
    setFormData(prev => ({
      ...prev,
      barangay_code,
      subdivision_id: '',
      street_id: '',
    }));
  };

  const validateForm = (): boolean => {
    const newErrors: Partial<Record<keyof HouseholdFormData, string>> = {};

    try {
      // Location and Demographics validation
      if (!formData.house_number?.trim()) newErrors.house_number = 'House number is required';
      if (!formData.street_id?.trim()) newErrors.street_id = 'Street name is required';
      if (!formData.barangay_code) newErrors.barangay_code = 'Barangay is required';
      if (!formData.city_municipality_code)
        newErrors.city_municipality_code = 'City/Municipality is required';
      if (!formData.province_code) newErrors.province_code = 'Province is required';
      if (!formData.region_code) newErrors.region_code = 'Region is required';
      if ((formData.no_of_families ?? 0) < 1)
        newErrors.no_of_families = 'Number of families must be at least 1';
      if ((formData.no_of_household_members ?? 0) < 0)
        newErrors.no_of_household_members = 'Number of household members cannot be negative';

      // Household Details validation
      if (!formData.household_type) newErrors.household_type = 'Household type is required';
      if (!formData.tenure_status) newErrors.tenure_status = 'Tenure status is required';
      if (formData.tenure_status === 'others' && !formData.tenure_others_specify?.trim()) {
        newErrors.tenure_others_specify = 'Please specify tenure status';
      }
      if (!formData.household_unit) newErrors.household_unit = 'Household unit is required';
      if (!formData.name?.trim()) newErrors.name = 'Household name is required';
      if (!formData.household_head_position)
        newErrors.household_head_position = 'Head position is required';

      setErrors(newErrors);
      return Object.keys(newErrors).length === 0;
    } catch (error) {
      logError(error as Error, 'VALIDATION_ERROR');
      return false;
    }
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      // Find the first error field and scroll to it
      const firstErrorField = document.querySelector('[aria-invalid="true"]');
      if (firstErrorField) {
        firstErrorField.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
      return;
    }

    setIsSubmitting(true);

    try {
      const action = mode === 'create' ? 'Creating' : 'Updating';
      logger.info(`${action} household`, {
        name: formData.name,
        barangay_code: formData.barangay_code,
        householdId,
      });

      let household;

      if (mode === 'create') {
        // Create household in database (code will be auto-generated by database function)
        const { data, error } = await supabase
          .from('households')
          .insert({
            name: formData.name,
            house_number: formData.house_number,
            street_id: formData.street_id || null,
            subdivision_id: formData.subdivision_id || null,
            barangay_code: formData.barangay_code,
            city_municipality_code: formData.city_municipality_code,
            province_code: formData.province_code,
            region_code: formData.region_code,
            no_of_families: formData.no_of_families,
            no_of_household_members: formData.no_of_household_members,
            no_of_migrants: formData.no_of_migrants,
            household_type: formData.household_type,
            tenure_status: formData.tenure_status,
            tenure_others_specify: formData.tenure_others_specify || null,
            household_unit: formData.household_unit,
            monthly_income: formData.monthly_income || null,
            household_head_id: formData.household_head_id || null,
            household_head_position: formData.household_head_position,
          })
          .select()
          .single();

        if (error) throw error;
        household = data;
      } else {
        // Update existing household
        if (!householdId) throw new Error('Household ID is required for updates');

        const { data, error } = await supabase
          .from('households')
          .update({
            name: formData.name,
            house_number: formData.house_number,
            street_id: formData.street_id || null,
            subdivision_id: formData.subdivision_id || null,
            barangay_code: formData.barangay_code,
            city_municipality_code: formData.city_municipality_code,
            province_code: formData.province_code,
            region_code: formData.region_code,
            no_of_families: formData.no_of_families,
            no_of_household_members: formData.no_of_household_members,
            no_of_migrants: formData.no_of_migrants,
            household_type: formData.household_type,
            tenure_status: formData.tenure_status,
            tenure_others_specify: formData.tenure_others_specify || null,
            household_unit: formData.household_unit,
            monthly_income: formData.monthly_income || null,
            household_head_id: formData.household_head_id || null,
            household_head_position: formData.household_head_position,
            updated_at: new Date().toISOString(),
          })
          .eq('id', householdId)
          .select()
          .single();

        if (error) throw error;
        household = data;
      }

      logger.info(`Household ${mode === 'create' ? 'created' : 'updated'} successfully`, {
        id: household.id,
        code: household.code,
      });

      // Invalidate relevant queries
      await queryClient.invalidateQueries({ queryKey: ['households'] });

      if (onSubmit) {
        await onSubmit(formData);
      } else if (mode === 'create') {
        router.push(`/households/${household.id}`);
      } else {
        // Switch to view mode after successful edit
        onModeChange?.('view');
      }
    } catch (error) {
      logError(error as Error, `HOUSEHOLD_${mode.toUpperCase()}_ERROR`);
      setErrors({
        code: `Failed to ${mode === 'create' ? 'create' : 'update'} household. Please try again.`,
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    if (_onCancel) {
      _onCancel();
    } else {
      router.push('/households');
    }
  };

  if (loadingAddress || (isAutoFilling && autoFillStatus === 'loading')) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="text-center">
          <div className="mb-4 size-8 animate-spin rounded-full border-b-2 border-zinc-950"></div>
          <p className="text-sm text-zinc-600">
            {loadingAddress ? 'Loading address information...' : 'Auto-filling location fields...'}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="mx-auto max-w-4xl">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-zinc-950 dark:text-white">
              {mode === 'create'
                ? 'Create New Household'
                : mode === 'edit'
                  ? 'Edit Household'
                  : 'Household Details'}
            </h2>
            <p className="mt-2 text-sm text-zinc-600 dark:text-zinc-400">
              {mode === 'create'
                ? 'Fill in all required information to register a new household in the barangay.'
                : mode === 'edit'
                  ? 'Update the household information as needed.'
                  : 'View the household information and details.'}
            </p>
          </div>
          {mode !== 'create' && onModeChange && (
            <div className="flex gap-2">
              {mode === 'view' && (
                <Button variant="primary" onClick={() => onModeChange('edit')}>
                  Edit Household
                </Button>
              )}
              {mode === 'edit' && (
                <Button variant="secondary-outline" onClick={() => onModeChange('view')}>
                  Cancel Edit
                </Button>
              )}
            </div>
          )}
        </div>

        {autoFillStatus === 'failed' && (
          <div className="mt-4 rounded-lg bg-yellow-50 p-4 ring-1 ring-yellow-900/10 dark:bg-yellow-400/10 dark:ring-yellow-400/20">
            <div className="flex">
              <div className="shrink-0">
                <svg className="size-5 text-yellow-400" viewBox="0 0 20 20" fill="currentColor">
                  <path
                    fillRule="evenodd"
                    d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z"
                    clipRule="evenodd"
                  />
                </svg>
              </div>
              <div className="ml-3">
                <h4 className="text-sm font-medium text-yellow-800 dark:text-yellow-200">
                  Auto-fill Not Available
                </h4>
                <div className="mt-1 text-sm text-yellow-700 dark:text-yellow-300">
                  <p>
                    Please manually select the location information.{' '}
                    {!user
                      ? 'Sign in for automatic population.'
                      : 'Location data could not be loaded automatically.'}
                  </p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Form Sections */}
      <div className="space-y-8">
        {/* Section 1: Location & Demographics */}
        <LocationAndDemographicsForm
          mode={mode}
          formData={{
            // Map new snake_case to old camelCase for compatibility
            houseNumber: formData.house_number || '',
            streetId: formData.street_id || '',
            subdivisionId: formData.subdivision_id || '',
            barangayCode: formData.barangay_code || '',
            cityMunicipalityCode: formData.city_municipality_code || '',
            provinceCode: formData.province_code || '',
            regionCode: formData.region_code || '',
            noOfFamilies: formData.no_of_families || 1,
            noOfHouseholdMembers: formData.no_of_household_members || 0,
            noOfMigrants: formData.no_of_migrants || 0,
          }}
          onChange={handleInputChange}
          errors={errors}
          regionOptions={
            mode === 'view' && viewModeOptions?.addressLabels
              ? formData.region_code
                ? [
                    {
                      value: formData.region_code,
                      label: viewModeOptions.addressLabels.regionLabel || formData.region_code,
                    },
                  ]
                : []
              : regionOptions
          }
          provinceOptions={
            mode === 'view' && viewModeOptions?.addressLabels
              ? formData.province_code
                ? [
                    {
                      value: formData.province_code,
                      label: viewModeOptions.addressLabels.provinceLabel || formData.province_code,
                    },
                  ]
                : []
              : provinceOptions
          }
          cityOptions={
            mode === 'view' && viewModeOptions?.addressLabels
              ? formData.city_municipality_code
                ? [
                    {
                      value: formData.city_municipality_code,
                      label:
                        viewModeOptions.addressLabels.cityLabel || formData.city_municipality_code,
                    },
                  ]
                : []
              : cityOptions
          }
          barangayOptions={
            mode === 'view' && viewModeOptions?.addressLabels
              ? formData.barangay_code
                ? [
                    {
                      value: formData.barangay_code,
                      label: viewModeOptions.addressLabels.barangayLabel || formData.barangay_code,
                    },
                  ]
                : []
              : barangayOptions
          }
          regionsLoading={regionsLoading}
          provincesLoading={provincesLoading}
          citiesLoading={citiesLoading}
          barangaysLoading={barangaysLoading}
          onRegionChange={handleRegionChange}
          onProvinceChange={handleProvinceChange}
          onCityChange={handleCityChange}
          onBarangayChange={handleBarangayChange}
        />

        {/* Section 2: Household Details */}
        <HouseholdDetailsForm
          mode={mode}
          formData={{
            // Map new snake_case to old camelCase for compatibility
            householdType: formData.household_type || '',
            tenureStatus: formData.tenure_status || '',
            tenureOthersSpecify: formData.tenure_others_specify || '',
            householdUnit: formData.household_unit || '',
            householdName: formData.name || '',
            monthlyIncome: formData.monthly_income || 0,
            householdHeadId: formData.household_head_id || '',
            householdHeadPosition: formData.household_head_position || '',
          }}
          onChange={handleInputChange}
          errors={errors}
          // Head of family options would be populated from residents
          householdHeadOptions={
            mode === 'view' && viewModeOptions?.householdHeadLabel
              ? formData.household_head_id
                ? [{ value: formData.household_head_id, label: viewModeOptions.householdHeadLabel }]
                : []
              : []
          }
          householdHeadsLoading={false}
        />

        {/* Form Actions */}
        {mode !== 'view' && (
          <div className="flex justify-end gap-3 border-t pt-6">
            <Button variant="secondary-outline" onClick={handleCancel}>
              Cancel
            </Button>
            <Button variant="primary" onClick={handleSubmit} disabled={isSubmitting}>
              {isSubmitting
                ? mode === 'create'
                  ? 'Creating Household...'
                  : 'Saving Changes...'
                : mode === 'create'
                  ? 'Create Household'
                  : 'Save Changes'}
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import React, { useState, useCallback, useEffect } from 'react';

import {
  PersonalInformationForm,
  ContactInformationForm,
  PhysicalPersonalDetailsForm,
  SectoralInformationForm,
  MigrationInformation,
} from '@/components/organisms/FormSection';
import { useAuth } from '@/contexts';
import { isIndigenousPeople } from '@/lib/business-rules/sectoral-classification';
import { supabase } from '@/lib/data/supabase';
import type { FormMode } from '@/types';
import { ResidentFormState } from '@/types/residents';

import { FormActions } from './components/FormActions';
import { FormHeader } from './components/FormHeader';

// Use the database-aligned ResidentFormState interface
type ResidentFormData = ResidentFormState;

interface ResidentFormProps {
  mode?: FormMode;
  onSubmit?: (data: ResidentFormData) => void;
  onCancel?: () => void;
  initialData?: Partial<ResidentFormData>;
  onModeChange?: (mode: FormMode) => void;
  hidePhysicalDetails?: boolean;
  hideSectoralInfo?: boolean;
  onChange?: (data: ResidentFormData) => void;
}

// Helper function to get sectoral classifications based on ethnicity
const getSectoralClassificationsByEthnicity = (ethnicity: string): Partial<ResidentFormData> => {
  const updates: Partial<ResidentFormData> = {};

  // Reset all sectoral classifications first (except manually set ones)
  updates.is_indigenous_people = false;

  // Indigenous peoples classification - automatically set to true
  if (isIndigenousPeople(ethnicity)) {
    updates.is_indigenous_people = true;
  }

  // Note: Other sectoral classifications could be auto-suggested based on ethnicity:
  // - is_migrant: Some communities are traditionally mobile (e.g., Badjao)
  // - is_person_with_disability: Cannot be determined by ethnicity
  // - is_solo_parent: Cannot be determined by ethnicity
  // - is_senior_citizen: Cannot be determined by ethnicity
  // - is_out_of_school_children/youth: Cannot be determined by ethnicity
  // - is_overseas_filipino_worker: Cannot be determined by ethnicity

  // For now, we only auto-set is_indigenous_people as it's directly determinable from ethnicity
  // Other classifications should be manually selected by the user

  return updates;
};

// Database default values matching schema.sql + UX defaults for required fields
// ONLY for truly empty/new forms - should NOT override existing data
const DEFAULT_FORM_VALUES: Partial<ResidentFormData> = {
  // Database defaults - ONLY for create mode, not for existing data
  citizenship: 'filipino', // Database default
  is_graduate: false, // Database default

  // UX defaults for required fields without database defaults - ONLY for create mode
  sex: 'male', // No database default but required - provide UX default

  // DO NOT set civil_status default here - let real data take precedence
  // Note: ethnicity, blood_type, religion are nullable - no defaults
};

export function ResidentForm({
  mode = 'create',
  onSubmit,
  onCancel,
  initialData,
  onModeChange,
  hidePhysicalDetails = false,
  hideSectoralInfo = false,
  onChange,
}: ResidentFormProps) {
  // Auth context
  const { userProfile } = useAuth();

  // Search options state - need state for re-renders when options change
  const [searchOptions, setSearchOptions] = useState({
    psgc: [] as any[],
    psoc: [] as any[],
    household: [] as any[],
  });

  const [searchLoading, setSearchLoading] = useState({
    psgc: false,
    psoc: false,
    household: false,
  });

  // Create form data with database defaults, then merge with initialData if provided
  const [formData, setFormData] = useState<ResidentFormData>(() => {
    const baseFormData: ResidentFormData = {
      // Personal Information - database field names
      first_name: '',
      middle_name: '',
      last_name: '',
      extension_name: '',
      sex: '', // Required field - no default
      civil_status: '', // Will be set from defaults
      civil_status_others_specify: '',
      citizenship: '', // Will be set from defaults
      birthdate: '',
      birth_place_name: '',
      birth_place_code: '',
      philsys_card_number: '',
      education_attainment: '', // No database default
      is_graduate: false, // Will be set from defaults
      employment_status: '', // No database default
      occupation_code: '',
      occupation_title: '',

      // Contact Information - database field names
      email: '',
      telephone_number: '',
      mobile_number: '',
      household_code: '',

      // Physical Personal Details - database field names
      blood_type: '', // Nullable field - no default
      complexion: '',
      height: 0, // Will be excluded from submission if section is hidden
      weight: 0, // Will be excluded from submission if section is hidden
      ethnicity: '', // Nullable field - no default
      religion: '', // Nullable field - no default
      religion_others_specify: '',
      is_voter: false, // Default to false for boolean validation
      is_resident_voter: false, // Default to false for boolean validation
      last_voted_date: '',
      mother_maiden_first: '',
      mother_maiden_middle: '',
      mother_maiden_last: '',

      // Sectoral Information - database field names
      is_labor_force_employed: false,
      is_unemployed: false,
      is_overseas_filipino_worker: false,
      is_person_with_disability: false,
      is_out_of_school_children: false,
      is_out_of_school_youth: false,
      is_senior_citizen: false,
      is_registered_senior_citizen: false,
      is_solo_parent: false,
      is_indigenous_people: false,
      is_migrant: false,

      // Migration Information - database field names
      previous_barangay_code: '',
      previous_city_municipality_code: '',
      previous_province_code: '',
      previous_region_code: '',
      length_of_stay_previous_months: 0,
      date_of_transfer: '',
      duration_of_stay_current_months: 0,
      is_intending_to_return: false,
    };

    // Smart merge strategy: only apply defaults for missing/empty values
    const formWithDefaults = { ...baseFormData };

    // Apply defaults ONLY for create mode or when values are truly missing
    if (mode === 'create' || !initialData) {
      Object.assign(formWithDefaults, DEFAULT_FORM_VALUES);
    } else {
      // For existing data, only apply defaults for undefined/null values
      Object.entries(DEFAULT_FORM_VALUES).forEach(([key, defaultValue]) => {
        if (
          formWithDefaults[key as keyof ResidentFormData] === undefined ||
          formWithDefaults[key as keyof ResidentFormData] === null
        ) {
          (formWithDefaults as any)[key] = defaultValue;
        }
      });
    }

    // Merge with initialData - initialData takes precedence over defaults
    const finalFormData = initialData ? { ...formWithDefaults, ...initialData } : formWithDefaults;

    // Notify parent of initial data (use setTimeout to avoid state update during render)
    if (onChange) {
      setTimeout(() => {
        onChange(finalFormData);
      }, 0);
    }

    return finalFormData;
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Production-ready stable search handlers
  const handlePsgcSearch = useCallback(async (query: string) => {
    if (!query || query.trim().length < 2) {
      setSearchOptions(prev => ({ ...prev, psgc: [] }));
      setSearchLoading(prev => ({ ...prev, psgc: false }));
      return;
    }

    setSearchLoading(prev => ({ ...prev, psgc: true }));
    try {
      const params = new URLSearchParams({
        q: query.trim(),
        limit: '50',
        levels: 'province,city',
        maxLevel: 'city',
        minLevel: 'province',
      });

      const response = await fetch(`/api/psgc/search?${params}`);

      if (response.ok) {
        const data = await response.json();
        if (data.data && Array.isArray(data.data)) {
          setSearchOptions(prev => ({
            ...prev,
            psgc: data.data.map((item: any) => ({
              value: item.code || item.city_code || item.province_code,
              label: item.name || item.city_name || item.province_name,
              description: item.full_address || item.full_hierarchy,
              level: item.level,
              code: item.code || item.city_code || item.province_code,
            })),
          }));
        }
      }
    } catch (error) {
      console.error('PSGC search error:', error);
      setSearchOptions(prev => ({ ...prev, psgc: [] }));
    } finally {
      setSearchLoading(prev => ({ ...prev, psgc: false }));
    }
  }, []);

  const handlePsocSearch = useCallback(async (query: string) => {
    if (!query || query.trim().length < 2) {
      setSearchOptions(prev => ({ ...prev, psoc: [] }));
      setSearchLoading(prev => ({ ...prev, psoc: false }));
      return;
    }

    setSearchLoading(prev => ({ ...prev, psoc: true }));
    try {
      const params = new URLSearchParams({
        q: query.trim(),
        limit: '20',
        levels: 'major_group,sub_major_group,unit_group,unit_sub_group,occupation',
        maxLevel: 'occupation',
        minLevel: 'major_group',
      });

      const response = await fetch(`/api/psoc/search?${params}`);

      if (response.ok) {
        const data = await response.json();
        if (data.data && Array.isArray(data.data)) {
          setSearchOptions(prev => ({
            ...prev,
            psoc: data.data.map((item: any) => ({
              value: item.code,
              label: item.title,
              description: item.hierarchy,
              level_type: item.level,
              occupation_code: item.code,
              occupation_title: item.title,
            })),
          }));
        }
      }
    } catch (error) {
      console.error('PSOC search error:', error);
      setSearchOptions(prev => ({ ...prev, psoc: [] }));
    } finally {
      setSearchLoading(prev => ({ ...prev, psoc: false }));
    }
  }, []);

  const handleHouseholdSearch = useCallback(
    async (query: string) => {
      if (!userProfile?.barangay_code) {
        setSearchOptions(prev => ({ ...prev, household: [] }));
        return;
      }

      setSearchLoading(prev => ({ ...prev, household: true }));
      try {
        // Get current session to pass auth token
        const {
          data: { session },
        } = await supabase.auth.getSession();

        if (!session?.access_token) {
          console.warn('No valid session found for household search');
          setSearchOptions(prev => ({ ...prev, household: [] }));
          return;
        }

        // Call the actual households API
        const url = `/api/households?${query ? `search=${encodeURIComponent(query)}&` : ''}limit=50`;
        const response = await fetch(url, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${session.access_token}`,
          },
        });

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        const data = await response.json();
        const households = data.data || [];

        // Transform household data for the dropdown
        const householdOptions = households.map((household: any) => ({
          value: household.code,
          label: `${household.code}${household.name ? ` - ${household.name}` : ''}`,
          description: household.address || `House ${household.house_number || 'N/A'}`,
        }));

        setSearchOptions(prev => ({ ...prev, household: householdOptions }));
      } catch (error) {
        console.error('Household search error:', error);
        // Fallback to empty array if API fails
        setSearchOptions(prev => ({ ...prev, household: [] }));
      } finally {
        setSearchLoading(prev => ({ ...prev, household: false }));
      }
    },
    [userProfile?.barangay_code]
  );

  // Load initial household options when component mounts
  useEffect(() => {
    if (userProfile?.barangay_code) {
      handleHouseholdSearch(''); // Load initial household data
    }
  }, [userProfile?.barangay_code, handleHouseholdSearch]);

  // Handle form field changes
  const handleFieldChange = (
    field: string | number | symbol,
    value: string | number | boolean | null
  ) => {
    const fieldKey = String(field);

    // Handle household batch update to avoid race condition
    if (fieldKey === '__household_batch__' && value && typeof value === 'object') {
      const householdData = value as any;

      const updatedData: Partial<ResidentFormData> = {
        household_code: householdData.household_code || '',
        household_name: householdData.household_name || '',
      };

      const newFormData = {
        ...formData,
        ...updatedData,
      };

      setFormData(newFormData);

      // Notify parent component of changes
      if (onChange) {
        onChange(newFormData);
      }

      // Clear errors for both household fields
      if (errors.household_code || errors.household_name) {
        setErrors(prev => ({
          ...prev,
          household_code: '',
          household_name: '',
        }));
      }

      return; // Exit early for batch update
    }

    // Start with the basic field update
    let updatedData: Partial<ResidentFormData> = {
      [fieldKey]: value,
    };

    if (fieldKey === 'ethnicity' && typeof value === 'string') {
      // Auto-update sectoral classifications based on ethnicity
      const sectoralUpdates = getSectoralClassificationsByEthnicity(value);
      updatedData = { ...updatedData, ...sectoralUpdates };
    }

    const newFormData = {
      ...formData,
      ...updatedData,
    };

    setFormData(newFormData);

    // Notify parent component of changes
    if (onChange) {
      onChange(newFormData);
    }

    // Clear error when user starts typing
    if (errors[fieldKey]) {
      setErrors(prev => ({
        ...prev,
        [fieldKey]: '',
      }));
    }
  };

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Basic validation using database field names
      const newErrors: Record<string, string> = {};

      if (!formData.first_name.trim()) newErrors.first_name = 'First name is required';
      if (!formData.last_name.trim()) newErrors.last_name = 'Last name is required';
      if (!formData.sex) newErrors.sex = 'Sex is required';
      if (!formData.birthdate) newErrors.birthdate = 'Birth date is required';
      if (!formData.household_code || !formData.household_code.trim()) {
        newErrors.household_code = 'Household assignment is required';
      }

      if (Object.keys(newErrors).length > 0) {
        setErrors(newErrors);
        setIsSubmitting(false);
        return;
      }

      // Call onSubmit callback
      if (onSubmit) {
        // Create a filtered version of form data that excludes hidden fields
        const filteredFormData = { ...formData };

        // Remove Physical & Personal Details fields if section is hidden
        if (hidePhysicalDetails) {
          const physicalFields = [
            'height',
            'weight',
            'blood_type',
            'complexion',
            'ethnicity',
            'religion',
            'religion_others_specify',
            'is_voter',
            'is_resident_voter',
            'last_voted_date',
            'mother_maiden_first',
            'mother_maiden_middle',
            'mother_maiden_last',
          ];
          physicalFields.forEach(field => {
            if (filteredFormData.hasOwnProperty(field)) {
              delete filteredFormData[field as keyof typeof filteredFormData];
            }
          });
        }

        // Remove Sectoral Information fields if section is hidden
        if (hideSectoralInfo) {
          const sectoralFields = [
            'is_labor_force_employed',
            'is_unemployed',
            'is_overseas_filipino_worker',
            'is_person_with_disability',
            'is_out_of_school_children',
            'is_out_of_school_youth',
            'is_senior_citizen',
            'is_registered_senior_citizen',
            'is_solo_parent',
            'is_indigenous_people',
            'is_migrant',
          ];
          sectoralFields.forEach(field => {
            if (filteredFormData.hasOwnProperty(field)) {
              delete filteredFormData[field as keyof typeof filteredFormData];
            }
          });
        }

        console.log('üöÄ RESIDENTFORM: Calling onSubmit with data:');
        console.log('üöÄ RESIDENTFORM: is_migrant value:', filteredFormData.is_migrant);
        console.log('üöÄ RESIDENTFORM: sectoral fields in filtered data:', {
          is_migrant: filteredFormData.is_migrant,
          is_solo_parent: filteredFormData.is_solo_parent,
          is_person_with_disability: filteredFormData.is_person_with_disability,
        });

        onSubmit(filteredFormData);
      }
    } catch (error) {
      console.error('Form submission error:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="space-y-8">
      {/* Form Header */}
      <FormHeader mode={mode} onModeChange={onModeChange} />

      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Personal Information Section */}
        <PersonalInformationForm
          mode={mode}
          formData={{
            // Direct snake_case properties (matching database schema)
            philsys_card_number: formData.philsys_card_number,
            first_name: formData.first_name,
            middle_name: formData.middle_name,
            last_name: formData.last_name,
            extension_name: formData.extension_name,
            sex: formData.sex,
            civil_status: formData.civil_status,
            citizenship: formData.citizenship,
            birthdate: formData.birthdate,
            birth_place_name: formData.birth_place_name,
            birth_place_code: formData.birth_place_code,
            education_attainment: formData.education_attainment,
            is_graduate: formData.is_graduate,
            employment_status: formData.employment_status,
            occupation_code: formData.occupation_code,
            occupation_title: formData.occupation_title,
          }}
          onChange={(field: string | number | symbol, value: string | number | boolean | null) => {
            // Direct field mapping (no conversion needed as both use snake_case)
            const fieldName = String(field);
            handleFieldChange(fieldName, value);
          }}
          errors={errors}
          onPsgcSearch={handlePsgcSearch}
          onPsocSearch={handlePsocSearch}
          psgcOptions={searchOptions.psgc}
          psocOptions={searchOptions.psoc}
          psgcLoading={searchLoading.psgc}
          psocLoading={searchLoading.psoc}
        />

        {/* Contact Information Section */}
        <ContactInformationForm
          mode={mode}
          formData={{
            // Direct snake_case properties (matching database schema)
            email: formData.email,
            telephone_number: formData.telephone_number,
            mobile_number: formData.mobile_number,
            household_code: formData.household_code,
            household_name: formData.household_name,
          }}
          onChange={(field: string | number | symbol, value: string | number | boolean | null) => {
            // Direct field mapping (no conversion needed as both use snake_case)
            const fieldName = String(field);
            handleFieldChange(fieldName, value);
          }}
          errors={errors}
          onHouseholdSearch={handleHouseholdSearch}
          householdOptions={searchOptions.household}
          householdLoading={searchLoading.household}
        />

        {/* Physical Personal Details Section - Hidden in basic create mode */}
        {!hidePhysicalDetails && (
          <PhysicalPersonalDetailsForm
            mode={mode}
            formData={{
              // Direct snake_case properties (matching database schema)
              blood_type: formData.blood_type,
              complexion: formData.complexion,
              height: formData.height ? formData.height.toString() : '',
              weight: formData.weight ? formData.weight.toString() : '',
              ethnicity: formData.ethnicity,
              religion: formData.religion,
              religion_others_specify: formData.religion_others_specify,
              is_voter: formData.is_voter,
              is_resident_voter: formData.is_resident_voter,
              last_voted_date: formData.last_voted_date,
              mother_maiden_first: formData.mother_maiden_first,
              mother_maiden_middle: formData.mother_maiden_middle,
              mother_maiden_last: formData.mother_maiden_last,
            }}
            onChange={(
              field: string | number | symbol,
              value: string | number | boolean | null
            ) => {
              // Direct field mapping with type conversion for height/weight
              const fieldName = String(field);
              // Convert height/weight strings back to numbers for database
              let convertedValue = value;
              if ((fieldName === 'height' || fieldName === 'weight') && typeof value === 'string') {
                convertedValue = parseFloat(value) || 0;
              }
              handleFieldChange(fieldName, convertedValue);
            }}
            errors={errors}
          />
        )}

        {/* Sectoral Information Section - Hidden in basic create mode */}
        {!hideSectoralInfo && (
          <SectoralInformationForm
            mode={mode}
            formData={{
              // Sectoral classifications (matching database schema)
              is_labor_force_employed: formData.is_labor_force_employed,
              is_unemployed: formData.is_unemployed,
              is_overseas_filipino: formData.is_overseas_filipino_worker,
              is_person_with_disability: formData.is_person_with_disability,
              is_out_of_school_children: formData.is_out_of_school_children,
              is_out_of_school_youth: formData.is_out_of_school_youth,
              is_senior_citizen: formData.is_senior_citizen,
              is_registered_senior_citizen: formData.is_registered_senior_citizen,
              is_solo_parent: formData.is_solo_parent,
              is_indigenous_people: formData.is_indigenous_people,
              is_migrant: formData.is_migrant,
              // Context data for auto-calculation
              birthdate: formData.birthdate,
              employment_status: formData.employment_status,
              education_attainment: formData.education_attainment,
              civil_status: formData.civil_status,
              ethnicity: formData.ethnicity,
            }}
            onChange={handleFieldChange}
            errors={errors}
          />
        )}

        {/* Migration Information Section - Only show if migrant is checked */}
        {formData.is_migrant && (
          <MigrationInformation
            mode={mode}
            value={{
              previous_barangay_code: formData.previous_barangay_code || undefined,
              previous_city_municipality_code:
                formData.previous_city_municipality_code || undefined,
              previous_province_code: formData.previous_province_code || undefined,
              previous_region_code: formData.previous_region_code || undefined,
              length_of_stay_previous_months: formData.length_of_stay_previous_months || undefined,
              reason_for_migration: formData.reason_for_migration || undefined,
              date_of_transfer: formData.date_of_transfer || undefined,
              migration_type: formData.migration_type || undefined,
            }}
            onChange={migrationData => {
              handleFieldChange(
                'previous_barangay_code',
                migrationData.previous_barangay_code || ''
              );
              handleFieldChange(
                'previous_city_municipality_code',
                migrationData.previous_city_municipality_code || ''
              );
              handleFieldChange(
                'previous_province_code',
                migrationData.previous_province_code || ''
              );
              handleFieldChange('previous_region_code', migrationData.previous_region_code || '');
              handleFieldChange(
                'length_of_stay_previous_months',
                migrationData.length_of_stay_previous_months || 0
              );
              handleFieldChange('reason_for_migration', migrationData.reason_for_migration || '');
              handleFieldChange('date_of_transfer', migrationData.date_of_transfer || '');
              handleFieldChange(
                'reason_for_transferring',
                migrationData.reason_for_transferring || ''
              );
              handleFieldChange(
                'duration_of_stay_current_months',
                migrationData.duration_of_stay_current_months || 0
              );
              handleFieldChange(
                'is_intending_to_return',
                migrationData.is_intending_to_return || false
              );
            }}
            errors={errors}
          />
        )}

        {/* Form Actions - Submit/Cancel buttons */}
        <FormActions
          mode={mode}
          isSubmitting={isSubmitting}
          isOptimisticallyUpdated={false}
          onCancel={onCancel}
          errorCount={Object.keys(errors).length}
        />
      </form>
    </div>
  );
}

export default ResidentForm;

import React from 'react';

import type { FormMode } from '@/types';

// Helper function to get button variant
const getButtonVariant = (isOptimisticallyUpdated: boolean): string => {
  return isOptimisticallyUpdated
    ? 'px-4 py-2 bg-success-600 hover:bg-success-700 text-white font-medium rounded-lg transition-colors'
    : 'px-4 py-2 bg-primary-600 hover:bg-primary-700 text-white font-medium rounded-lg transition-colors';
};

// Helper function to get button content
const getButtonContent = (isSubmitting: boolean, isOptimisticallyUpdated: boolean) => {
  if (isSubmitting) {
    return (
      <>
        <span aria-hidden="true">Saving...</span>
        <span className="sr-only">Saving resident data, please wait</span>
      </>
    );
  }

  if (isOptimisticallyUpdated) {
    return (
      <>
        <span aria-hidden="true">‚úì Saved</span>
        <span className="sr-only">Changes saved successfully</span>
      </>
    );
  }

  return 'Save Resident';
};

// Helper function to get help text
const getHelpText = (errorCount: number): string => {
  if (errorCount > 0) {
    const errorText = errorCount > 1 ? 's' : '';
    return `Please fix ${errorCount} validation error${errorText} before submitting`;
  }
  return 'Form is ready to submit';
};

interface FormActionsProps {
  mode: FormMode;
  isSubmitting: boolean;
  isOptimisticallyUpdated: boolean;
  onCancel?: () => void;
  errorCount: number;
}

const FormActionsComponent = ({
  mode,
  isSubmitting,
  isOptimisticallyUpdated,
  onCancel,
  errorCount,
}: FormActionsProps) => {
  console.log(
    'üé¨ FormActions render - mode:',
    mode,
    'errorCount:',
    errorCount,
    'isSubmitting:',
    isSubmitting
  );

  if (mode === 'view') {
    console.log('üëÅÔ∏è FormActions: returning null because mode is "view"');
    return null;
  }

  return (
    <div className="mt-8 flex items-center justify-end gap-3 border-t border-zinc-200 pt-8 dark:border-zinc-800">
      {onCancel && (
        <button
          type="button"
          onClick={onCancel}
          className="rounded-lg border border-zinc-200 bg-zinc-50 px-4 py-2 font-medium text-zinc-900 transition-colors hover:bg-zinc-50 dark:border-zinc-800 dark:bg-zinc-900 dark:text-zinc-100 dark:hover:bg-zinc-800"
        >
          Cancel
        </button>
      )}
      <button
        type="submit"
        disabled={isSubmitting}
        aria-describedby="submit-help"
        aria-label={
          isSubmitting ? 'Saving resident data, please wait' : 'Save resident information'
        }
        className={getButtonVariant(isOptimisticallyUpdated)}
      >
        {getButtonContent(isSubmitting, isOptimisticallyUpdated)}
      </button>
