      // Should use neutral.500 as fallback
      expect(getColor('invalid.path')).toBe('#737373');
    });

    it('should warn for invalid color paths', () => {
      const consoleSpy = jest.spyOn(console, 'warn').mockImplementation();
      getColor('invalid.color');
