/**
 * Accessibility hook for form field components
 * Standardizes ARIA patterns and ID generation for form elements
 */
import { useMemo } from 'react';

import { generateId } from '@/utils/id-generators';

export interface FieldAccessibilityOptions {
  /** Base identifier for the field */
  baseId?: string;
  /** Whether the field has an error */
  hasError?: boolean;
  /** Whether the field has helper text */
  hasHelperText?: boolean;
  /** Whether the field is required */
  required?: boolean;
  /** Custom label */
  label?: string;
}

export interface FieldAccessibilityIds {
  /** ID for the field input element */
  field: string;
  /** ID for the field label */
  label: string;
  /** ID for helper text */
  helper: string;
  /** ID for error message */
  error: string;
}

export interface FieldAccessibilityProps {
  /** Generated IDs for field elements */
  ids: FieldAccessibilityIds;
  /** aria-describedby attribute value */
  ariaDescribedBy?: string;
  /** aria-labelledby attribute value */
  ariaLabelledBy: string;
  /** aria-invalid attribute value */
  ariaInvalid: boolean | 'false' | 'true';
  /** aria-required attribute value */
  ariaRequired: boolean | 'false' | 'true';
}

/**
 * Hook for standardizing field accessibility patterns
 *
 * @example
 * ```tsx
 * function MyInput({ label, error, helperText, required, ...props }) {
 *   const accessibility = useFieldAccessibility({
 *     baseId: props.id || 'my-input',
 *     hasError: !!error,
 *     hasHelperText: !!helperText,
 *     required,
 *     label
 *   });
 *
 *   return (
 *     <div>
 *       <label id={accessibility.ids.label}>
 *         {label}
 *       </label>
 *       <input
 *         id={accessibility.ids.field}
 *         aria-labelledby={accessibility.ariaLabelledBy}
 *         aria-describedby={accessibility.ariaDescribedBy}
 *         aria-invalid={accessibility.ariaInvalid}
 *         aria-required={accessibility.ariaRequired}
 *         {...props}
 *       />
 *       {helperText && (
 *         <div id={accessibility.ids.helper}>{helperText}</div>
 *       )}
 *       {error && (
 *         <div id={accessibility.ids.error}>{error}</div>
 *       )}
 *     </div>
 *   );
 * }
 * ```
 */
export function useFieldAccessibility({
  baseId,
  hasError = false,
  hasHelperText = false,
  required = false,
  label,
}: FieldAccessibilityOptions = {}): FieldAccessibilityProps {
  const ids = useMemo(() => {
    const fieldId = baseId || generateId('field');
    return {
      field: fieldId,
      label: `${fieldId}-label`,
      helper: `${fieldId}-helper`,
      error: `${fieldId}-error`,
    };
  }, [baseId]);

  const ariaDescribedBy = useMemo(() => {
    const describedByIds: string[] = [];

    if (hasHelperText) {
      describedByIds.push(ids.helper);
    }

    if (hasError) {
      describedByIds.push(ids.error);
    }

    return describedByIds.length > 0 ? describedByIds.join(' ') : undefined;
  }, [hasHelperText, hasError, ids.helper, ids.error]);

  return {
    ids,
    ariaDescribedBy,
    ariaLabelledBy: ids.label,
    ariaInvalid: hasError,
    ariaRequired: required,
  };
}

/**
 * Hook for field groups/fieldsets accessibility
 */
export function useFieldGroupAccessibility({
  baseId,
  hasError = false,
  required = false,
}: Pick<FieldAccessibilityOptions, 'baseId' | 'hasError' | 'required'> = {}) {
  const ids = useMemo(() => {
    const groupId = baseId || generateId('fieldgroup');
    return {
      group: groupId,
      legend: `${groupId}-legend`,
      error: `${groupId}-error`,
    };
  }, [baseId]);

  const ariaDescribedBy = useMemo(() => {
    return hasError ? ids.error : undefined;
  }, [hasError, ids.error]);

  return {
    ids,
    ariaDescribedBy,
    ariaInvalid: hasError,
    ariaRequired: required,
    role: 'group' as const,
  };
}

/**
 * Geographic Data Hook
 * Manages PSGC geographic location data with proper caching and state management
 */

import { useState, useEffect, useCallback } from 'react';

import { supabase } from '@/lib/data/supabase';

export interface GeographicOption {
  value: string;
  label: string;
}

interface GeographicData {
  regions: GeographicOption[];
  provinces: GeographicOption[];
  cities: GeographicOption[];
  barangays: GeographicOption[];
  streets: GeographicOption[];
  subdivisions: GeographicOption[];
}

interface GeographicState extends GeographicData {
  loading: {
    regions: boolean;
    provinces: boolean;
    cities: boolean;
    barangays: boolean;
    streets: boolean;
    subdivisions: boolean;
  };
  error: {
    regions: string | null;
    provinces: string | null;
    cities: string | null;
    barangays: string | null;
    streets: string | null;
    subdivisions: string | null;
  };
}

export function useGeographicData() {
  const [state, setState] = useState<GeographicState>({
    regions: [],
    provinces: [],
    cities: [],
    barangays: [],
    streets: [],
    subdivisions: [],
    loading: {
      regions: false,
      provinces: false,
      cities: false,
      barangays: false,
      streets: false,
      subdivisions: false,
    },
    error: {
      regions: null,
      provinces: null,
      cities: null,
      barangays: null,
      streets: null,
      subdivisions: null,
    },
  });

  /**
   * Generic API call function with auth token
   */
  const fetchWithAuth = useCallback(async (url: string) => {
    const {
      data: { session },
    } = await supabase.auth.getSession();

    if (!session?.access_token) {
      throw new Error('No authentication token available');
    }

    const response = await fetch(url, {
      headers: {
        Authorization: `Bearer ${session.access_token}`,
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.error || `HTTP ${response.status}`);
    }

    const result = await response.json();
    return result.data || [];
  }, []);

  /**
   * Load regions
   */
  const loadRegions = useCallback(async () => {
    setState(prev => ({
      ...prev,
      loading: { ...prev.loading, regions: true },
      error: { ...prev.error, regions: null },
    }));

    try {
      const data = await fetchWithAuth('/api/addresses/regions');

      const options: GeographicOption[] = data.map((region: any) => ({
        value: region.code,
        label: region.name,
      }));

      setState(prev => ({
        ...prev,
        regions: options,
        loading: { ...prev.loading, regions: false },
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, regions: false },
        error: {
          ...prev.error,
          regions: error instanceof Error ? error.message : 'Failed to load regions',
        },
      }));
    }
  }, [fetchWithAuth]);

  /**
   * Load provinces by region
   */
  const loadProvinces = useCallback(
    async (regionCode: string) => {
      if (!regionCode) {
        setState(prev => ({ ...prev, provinces: [], cities: [], barangays: [] }));
        return;
      }

      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, provinces: true },
        error: { ...prev.error, provinces: null },
        provinces: [],
        cities: [],
        barangays: [],
      }));

      try {
        const data = await fetchWithAuth(`/api/addresses/provinces?region=${regionCode}`);

        const options: GeographicOption[] = data.map((province: any) => ({
          value: province.code,
          label: province.name,
        }));

        setState(prev => ({
          ...prev,
          provinces: options,
          loading: { ...prev.loading, provinces: false },
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          loading: { ...prev.loading, provinces: false },
          error: {
            ...prev.error,
            provinces: error instanceof Error ? error.message : 'Failed to load provinces',
          },
        }));
      }
    },
    [fetchWithAuth]
  );

  /**
   * Load cities by province
   */
  const loadCities = useCallback(
    async (provinceCode: string) => {
      if (!provinceCode) {
        setState(prev => ({ ...prev, cities: [], barangays: [] }));
        return;
      }

      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, cities: true },
        error: { ...prev.error, cities: null },
        cities: [],
        barangays: [],
      }));

      try {
        const data = await fetchWithAuth(`/api/addresses/cities?province=${provinceCode}`);

        const options: GeographicOption[] = data.map((city: any) => ({
          value: city.code,
          label: `${city.name} (${city.type})`,
        }));

        setState(prev => ({
          ...prev,
          cities: options,
          loading: { ...prev.loading, cities: false },
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          loading: { ...prev.loading, cities: false },
          error: {
            ...prev.error,
            cities: error instanceof Error ? error.message : 'Failed to load cities',
          },
        }));
      }
    },
    [fetchWithAuth]
  );

  /**
   * Load barangays by city
   */
  const loadBarangays = useCallback(
    async (cityCode: string) => {
      if (!cityCode) {
        setState(prev => ({ ...prev, barangays: [] }));
        return;
      }

      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, barangays: true },
        error: { ...prev.error, barangays: null },
        barangays: [],
      }));

      try {
        const data = await fetchWithAuth(`/api/addresses/barangays?city=${cityCode}`);

        const options: GeographicOption[] = data.map((barangay: any) => ({
          value: barangay.code,
          label: barangay.name,
        }));

        setState(prev => ({
          ...prev,
          barangays: options,
          loading: { ...prev.loading, barangays: false },
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          loading: { ...prev.loading, barangays: false },
          error: {
            ...prev.error,
            barangays: error instanceof Error ? error.message : 'Failed to load barangays',
          },
        }));
      }
    },
    [fetchWithAuth]
  );

  /**
   * Load independent cities for regions without provinces (like NCR)
   */
  const loadIndependentCities = useCallback(
    async (regionCode: string) => {
      if (!regionCode) {
        setState(prev => ({ ...prev, cities: [], barangays: [] }));
        return;
      }

      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, cities: true },
        error: { ...prev.error, cities: null },
        cities: [],
        barangays: [],
      }));

      try {
        // For regions like NCR that don't have provinces, get cities directly
        const data = await fetchWithAuth(`/api/addresses/cities`);

        // Filter for independent cities (no province_code)
        const independentCities = data.filter((city: any) => !city.province_code);

        const options: GeographicOption[] = independentCities.map((city: any) => ({
          value: city.code,
          label: `${city.name} (${city.type})`,
        }));

        setState(prev => ({
          ...prev,
          cities: options,
          loading: { ...prev.loading, cities: false },
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          loading: { ...prev.loading, cities: false },
          error: {
            ...prev.error,
            cities: error instanceof Error ? error.message : 'Failed to load cities',
          },
        }));
      }
    },
    [fetchWithAuth]
  );

  /**
   * Load subdivisions by barangay
   */
  const loadSubdivisions = useCallback(
    async (barangayCode: string) => {
      if (!barangayCode) {
        setState(prev => ({ ...prev, subdivisions: [], streets: [] }));
        return;
      }

      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, subdivisions: true },
        error: { ...prev.error, subdivisions: null },
        subdivisions: [],
        streets: [],
      }));

      try {
        const data = await fetchWithAuth(`/api/addresses/subdivisions?barangay=${barangayCode}`);

        const options: GeographicOption[] = data.map((subdivision: any) => ({
          value: subdivision.id,
          label: `${subdivision.name} (${subdivision.type})`,
        }));

        setState(prev => ({
          ...prev,
          subdivisions: options,
          loading: { ...prev.loading, subdivisions: false },
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          loading: { ...prev.loading, subdivisions: false },
          error: {
            ...prev.error,
            subdivisions: error instanceof Error ? error.message : 'Failed to load subdivisions',
          },
        }));
      }
    },
    [fetchWithAuth]
  );

  /**
   * Load streets by barangay (and optionally by subdivision)
   */
  const loadStreets = useCallback(
    async (barangayCode: string, subdivisionId?: string) => {
      if (!barangayCode) {
        setState(prev => ({ ...prev, streets: [] }));
        return;
      }

      setState(prev => ({
        ...prev,
        loading: { ...prev.loading, streets: true },
        error: { ...prev.error, streets: null },
        streets: [],
      }));

      try {
        let url = `/api/addresses/streets?barangay=${barangayCode}`;
        if (subdivisionId) {
          url += `&subdivision=${subdivisionId}`;
        }

        const data = await fetchWithAuth(url);

        const options: GeographicOption[] = data.map((street: any) => ({
          value: street.id,
          label: street.name,
        }));

        setState(prev => ({
          ...prev,
          streets: options,
          loading: { ...prev.loading, streets: false },
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          loading: { ...prev.loading, streets: false },
          error: {
            ...prev.error,
            streets: error instanceof Error ? error.message : 'Failed to load streets',
          },
        }));
      }
    },
    [fetchWithAuth]
  );

  /**
   * Reset all geographic data
   */
  const resetAll = useCallback(() => {
    setState({
      regions: [],
      provinces: [],
      cities: [],
      barangays: [],
      streets: [],
      subdivisions: [],
      loading: {
        regions: false,
        provinces: false,
        cities: false,
        barangays: false,
        streets: false,
        subdivisions: false,
      },
      error: {
        regions: null,
        provinces: null,
        cities: null,
        barangays: null,
        streets: null,
        subdivisions: null,
      },
    });
  }, []);

  /**
   * Auto-load regions on mount
   */
  useEffect(() => {
    loadRegions();
  }, [loadRegions]);

  return {
    // Data
    regions: state.regions,
    provinces: state.provinces,
    cities: state.cities,
    barangays: state.barangays,
    streets: state.streets,
    subdivisions: state.subdivisions,

    // Loading states
    loading: state.loading,

    // Error states
    error: state.error,

    // Actions
    loadRegions,
    loadProvinces,
    loadCities,
    loadBarangays,
    loadIndependentCities,
    loadSubdivisions,
    loadStreets,
    resetAll,

    // Computed states
    isLoading: Object.values(state.loading).some(Boolean),
    hasError: Object.values(state.error).some(Boolean),
  };
}

'use client';

import { useRouter } from 'next/navigation';
import { useState, useEffect, useCallback, useMemo } from 'react';

import type { CommandMenuItemType as CommandMenuItem } from '@/components';
import { useCommandMenuShortcut, createDropdownKeyHandler } from '@/lib/keyboardUtils';

interface UseCommandMenuProps {
  items: CommandMenuItem[];
  maxResults?: number;
}

export function useCommandMenu({ items, maxResults = 10 }: UseCommandMenuProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedIndex, setSelectedIndex] = useState(0);
  const router = useRouter();

  // Filter and search items
  const filteredItems = useMemo(() => {
    if (!searchQuery.trim()) {
      // Show recent items first when no search query
      const recentItems = items.filter(item => item.recent).slice(0, 5);
      const otherItems = items
        .filter(item => !item.recent)
        .slice(0, maxResults - recentItems.length);
      return [...recentItems, ...otherItems];
    }

    const query = searchQuery.toLowerCase();
    const scored = items
      .map(item => {
        let score = 0;
        const label = item.label?.toLowerCase() || '';
        const description = item.description?.toLowerCase() || '';
        const keywords = item.keywords?.join(' ').toLowerCase() || '';

        // Exact match gets highest score
        if (label === query) score += 100;
        else if (label.startsWith(query)) score += 50;
        else if (label.includes(query)) score += 25;

        // Description matches
        if (description.includes(query)) score += 15;

        // Keywords matches
        if (keywords.includes(query)) score += 10;

        return { item, score };
      })
      .filter(({ score }) => score > 0)
      .sort((a, b) => b.score - a.score)
      .slice(0, maxResults)
      .map(({ item }) => item);

    return scored;
  }, [items, searchQuery, maxResults]);

  // Reset selected index when filtered items change
  useEffect(() => {
    setSelectedIndex(0);
  }, [filteredItems]);

  // Global keyboard shortcut for opening command menu
  useCommandMenuShortcut(() => setIsOpen(true), true);

  // Command menu navigation when open
  useEffect(() => {
    if (!isOpen) return;

    const handleMenuKeyDown = createDropdownKeyHandler({
      isOpen: true,
      selectedIndex,
      itemCount: filteredItems.length,
      onClose: close,
      onSelect: (index: number) => {
        if (filteredItems[index]) {
          executeCommand(filteredItems[index]);
        }
      },
      onNavigate: setSelectedIndex
    });

    const handleKeyDown = (event: KeyboardEvent) => {
      handleMenuKeyDown(event);
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, filteredItems, selectedIndex]);

  const open = useCallback(() => {
    setIsOpen(true);
    setSearchQuery('');
    setSelectedIndex(0);
  }, []);

  const close = useCallback(() => {
    setIsOpen(false);
    setSearchQuery('');
    setSelectedIndex(0);
  }, []);

  const toggle = useCallback(() => {
    if (isOpen) {
      close();
    } else {
      open();
    }
  }, [isOpen, open, close]);

  const executeCommand = useCallback(
    (item: CommandMenuItem) => {
      if (item.disabled) return;

      // Close menu first
      close();

      // Execute the command
      if (item.onClick) {
        item.onClick();
      } else if (item.href) {
        router.push(item.href);
      }

      // Mark as recent (this would typically be handled by a context or store)
      // For now, we'll just trigger the action
    },
    [close, router]
  );

  return {
    isOpen,
    open,
    close,
    toggle,
    searchQuery,
    setSearchQuery,
    filteredItems,
    selectedIndex,
    setSelectedIndex,
    executeCommand,
  };
}

/**
 * Command Menu Actions Hook
 *
 * @description Handles command execution and API actions for command menu.
 * Extracted from useCommandMenuWithApi for better maintainability.
 */

'use client';

import { useRouter } from 'next/navigation';
import { useCallback } from 'react';
import { toast } from 'react-hot-toast';

import type { CommandMenuItemType as CommandMenuItem } from '@/components';
import { useAsyncErrorBoundary } from '@/hooks/utilities/useAsyncErrorBoundary';
import {
  exportData,
  backupData,
  createResident,
  createHousehold,
  findSeniorCitizens,
  findPWDs,
  findSoloParents,
  generateCertificate,
  generateReport,
  trackCommandMenuNavigation,
  trackCommandMenuAction,
} from '@/lib/command-menu';
import { trackNavigation, trackAction } from '@/lib/data';

/**
 * Return type for command menu actions hook
 */
export interface UseCommandMenuActionsReturn {
  /** Execute a command */
  executeCommand: (item: CommandMenuItem) => void;
  /** Handle export data action */
  handleExportData: (type: 'residents' | 'households', format: 'csv' | 'xlsx') => Promise<void>;
  /** Handle backup data action */
  handleBackupData: () => Promise<void>;
  /** Handle quick action */
  handleQuickAction: (actionFn: () => Promise<string>) => Promise<void>;
  /** Get enhanced menu items with actions */
  getEnhancedMenuItems: (baseItems: CommandMenuItem[]) => CommandMenuItem[];
}

/**
 * Hook for command menu actions and execution
 *
 * @description Provides command execution with error handling, analytics tracking,
 * and toast notifications.
 */
export function useCommandMenuActions(): UseCommandMenuActionsReturn {
  const router = useRouter();

  // Error boundary for action operations
  const { wrapAsync } = useAsyncErrorBoundary({
    onError: (error, errorInfo) => {
      if (process.env.NODE_ENV === 'development') {
        console.error('Command Menu Action Error:', errorInfo, error);
      }
    },
    enableRecovery: false,
    maxRetries: 0,
  });

  /**
   * Handle export data action
   */
  const handleExportData = useCallback(
    async (type: 'residents' | 'households', format: 'csv' | 'xlsx') => {
      toast.loading('Preparing export...', { id: 'export' });

      try {
        await wrapAsync(() => exportData({ type, format }), 'export data')();
        toast.success('Export completed successfully', { id: 'export' });
      } catch (error) {
        toast.error('Export failed', { id: 'export' });
      }
    },
    [wrapAsync]
  );

  /**
   * Handle backup data action
   */
  const handleBackupData = useCallback(async () => {
    toast.loading('Creating backup...', { id: 'backup' });

    try {
      await wrapAsync(backupData, 'backup data')();
      toast.success('Backup created successfully', { id: 'backup' });
    } catch (error) {
      toast.error('Backup failed', { id: 'backup' });
    }
  }, [wrapAsync]);

  /**
   * Handle quick action
   */
  const handleQuickAction = useCallback(
    async (actionFn: () => Promise<string>) => {
      try {
        const href = await wrapAsync(actionFn, 'quick action')();
        if (href) {
          router.push(href);
        }
      } catch (error) {
        // Error handled by async error boundary
        toast.error('Action failed');
      }
    },
    [router, wrapAsync]
  );

  /**
   * Execute a command
   */
  const executeCommand = useCallback(
    (item: CommandMenuItem) => {
      if (item.disabled) return;

      // Track the interaction based on item type
      if (item.id.startsWith('search-')) {
        // Track navigation to search result
        const originalId = item.id.replace('search-', '');
        const type = item.description?.includes('Resident') ? 'resident' : 'household';
        if (item.href) {
          trackNavigation(
            originalId,
            item.label || item.title || 'Unknown',
            item.description || '',
            type as 'resident' | 'household',
            item.href
          );
          trackCommandMenuNavigation(originalId, type, item.href);
        }
      } else if (item.onClick) {
        // Track action execution
        trackAction(item.id, item.label || item.title || 'Unknown', item.description || '');
        trackCommandMenuAction(item.id, 'click_action');
      } else if (item.href) {
        // Track navigation
        trackAction(
          item.id,
          item.label || item.title || 'Unknown',
          `Navigated to ${item.label || item.title || 'Unknown'}`
        );
        trackCommandMenuNavigation(item.id, 'navigation', item.href);
      }

      // Execute the command
      if (item.onClick) {
        item.onClick();
      } else if (item.href) {
        router.push(item.href);
      }
    },
    [router]
  );

  /**
   * Get enhanced menu items with real API functionality
   */
  const getEnhancedMenuItems = useCallback(
    (baseItems: CommandMenuItem[]): CommandMenuItem[] => {
      return baseItems.map(item => {
        const enhancedItem = { ...item };

        // Add real onClick handlers for specific actions
        switch (item.id) {
          case 'export-residents':
            enhancedItem.onClick = () => handleExportData('residents', 'csv');
            break;
          case 'export-households':
            enhancedItem.onClick = () => handleExportData('households', 'csv');
            break;
          case 'admin-backup':
            enhancedItem.onClick = () => handleBackupData();
            break;
          case 'action-add-resident':
            enhancedItem.onClick = () => handleQuickAction(async () => createResident());
            break;
          case 'action-create-household':
            enhancedItem.onClick = () => handleQuickAction(async () => createHousehold());
            break;
          case 'search-seniors':
            enhancedItem.onClick = () => handleQuickAction(async () => findSeniorCitizens());
            break;
          case 'search-pwd':
            enhancedItem.onClick = () => handleQuickAction(async () => findPWDs());
            break;
          case 'search-solo-parents':
            enhancedItem.onClick = () => handleQuickAction(async () => findSoloParents());
            break;
          case 'cert-barangay-clearance':
            enhancedItem.onClick = () =>
              handleQuickAction(async () => generateCertificate('clearance'));
            break;
          case 'cert-residency':
            enhancedItem.onClick = () =>
              handleQuickAction(async () => generateCertificate('residency'));
            break;
          case 'cert-indigency':
            enhancedItem.onClick = () =>
              handleQuickAction(async () => generateCertificate('indigency'));
            break;
          case 'report-population':
            enhancedItem.onClick = () =>
              handleQuickAction(async () => generateReport('population'));
            break;
          case 'report-households-summary':
            enhancedItem.onClick = () =>
              handleQuickAction(async () => generateReport('households'));
            break;
        }

        return enhancedItem;
      });
    },
    [handleExportData, handleBackupData, handleQuickAction]
  );

  return {
    executeCommand,
    handleExportData,
    handleBackupData,
    handleQuickAction,
    getEnhancedMenuItems,
  };
}

