/**
 * Command Menu Recent Items Hook
 *
 * @description Handles recent items management for command menu.
 * Extracted from useCommandMenuWithApi for better maintainability.
 */

'use client';

import { useState, useCallback } from 'react';
import { toast } from 'react-hot-toast';

import { useAsyncErrorBoundary } from '@/hooks/utilities/useAsyncErrorBoundary';
import {
  getRecentApiItems as getRecentItems,
  clearRecentApiItems as clearRecentItems,
} from '@/lib/command-menu';
import type { CommandMenuSearchResult as CommandMenuItem } from '@/types';

/**
 * Recent item from storage
 */
export interface RecentItem {
  id: string;
  title: string;
  description: string;
  href: string;
}

/**
 * Return type for command menu recent items hook
 */
export interface UseCommandMenuRecentsReturn {
  /** Recent menu items */
  recentItems: CommandMenuItem[];
  /** Loading state */
  isLoading: boolean;
  /** Load recent items */
  loadRecentItems: () => Promise<void>;
  /** Clear all recent items */
  handleClearRecentItems: () => Promise<void>;
  /** Number of recent items */
  recentItemsCount: number;
}

/**
 * Hook for command menu recent items management
 *
 * @description Provides recent items functionality with error handling
 * and toast notifications.
 */
export function useCommandMenuRecents(): UseCommandMenuRecentsReturn {
  const [recentItems, setRecentItems] = useState<CommandMenuItem[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  // Error boundary for recent items operations
  const { wrapAsync } = useAsyncErrorBoundary({
    onError: (error, errorInfo) => {
      if (process.env.NODE_ENV === 'development') {
        console.error('Command Menu Recents Error:', errorInfo, error);
      }
    },
    enableRecovery: false,
    maxRetries: 0,
  });

  /**
   * Load recent items from storage
   */
  const loadRecentItems = useCallback(async () => {
    setIsLoading(true);

    try {
      const recent = await wrapAsync(getRecentItems, 'load recent items')();

      if (recent) {
        const recentMenuItems: CommandMenuItem[] = recent.map(item => ({
          id: `recent-${item.id}`,
          title: item.title,
          subtitle: item.description,
          group: 'Recent',
          data: item.href,
          score: 0,
          type: 'navigation' as const,
          recent: true,
        }));

        setRecentItems(recentMenuItems);
      } else {
        setRecentItems([]);
      }
    } catch (error) {
      // Error handled by async error boundary
      setRecentItems([]);
    } finally {
      setIsLoading(false);
    }
  }, [wrapAsync]);

  /**
   * Clear all recent items
   */
  const handleClearRecentItems = useCallback(async () => {
    try {
      await wrapAsync(clearRecentItems, 'clear recent items')();
      setRecentItems([]); // Clear the UI immediately
      toast.success('Recent items cleared');
    } catch (error) {
      // Error handled by async error boundary
      toast.error('Failed to clear recent items');
    }
  }, [wrapAsync]);

  return {
    recentItems,
    isLoading,
    loadRecentItems,
    handleClearRecentItems,
    recentItemsCount: recentItems.length,
  };
}

// Export for backward compatibility
export default useCommandMenuRecents;

/**
 * Command Menu Search Hook
 *
 * @description Handles search functionality for command menu.
 * Extracted from useCommandMenuWithApi for better maintainability.
 */

'use client';

import { useState, useEffect, useCallback } from 'react';

import { useAsyncErrorBoundary } from '@/hooks/utilities/useAsyncErrorBoundary';
import { searchData, trackCommandMenuSearch, trackCommandMenuError } from '@/lib/command-menu';
import { trackSearch } from '@/lib/data';
import type { CommandMenuSearchResult as CommandMenuItem } from '@/types';

/**
 * Search options
 */
export interface CommandMenuSearchOptions {
  /** Maximum number of results */
  maxResults?: number;
  /** Debounce delay in milliseconds */
  debounceDelay?: number;
}

/**
 * Search result from API
 */
export interface SearchResult {
  id: string;
  title: string;
  description: string;
  href: string;
  type: 'resident' | 'household';
}

/**
 * Return type for command menu search hook
 */
export interface UseCommandMenuSearchReturn {
  /** Current search query */
  searchQuery: string;
  /** Set search query */
  setSearchQuery: (query: string) => void;
  /** Search loading state */
  isLoading: boolean;
  /** Dynamic search results */
  dynamicResults: CommandMenuItem[];
  /** Clear search */
  clearSearch: () => void;
}

/**
 * Hook for command menu search functionality
 *
 * @description Provides search capabilities with debouncing, error handling,
 * and analytics tracking.
 */
export function useCommandMenuSearch(
  options: CommandMenuSearchOptions = {}
): UseCommandMenuSearchReturn {
  const { maxResults = 5, debounceDelay = 300 } = options;

  const [searchQuery, setSearchQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [dynamicResults, setDynamicResults] = useState<CommandMenuItem[]>([]);

  // Error boundary for search operations
  const { wrapAsync } = useAsyncErrorBoundary({
    onError: (error, errorInfo) => {
      // Track search errors for monitoring
      trackCommandMenuError(error, {
        context: 'search',
        query: searchQuery.slice(0, 50),
      });

      if (process.env.NODE_ENV === 'development') {
        console.error('Command Menu Search Error:', errorInfo, error);
      }
    },
    enableRecovery: false,
    maxRetries: 0,
  });

  /**
   * Search with API integration
   */
  useEffect(() => {
    const searchWithApi = async () => {
      if (!searchQuery.trim()) {
        setDynamicResults([]);
        return;
      }

      setIsLoading(true);

      try {
        // Track the search query in recent items
        trackSearch(searchQuery);

        // Wrap search API call with error boundary
        const apiResults = await wrapAsync(
          () => searchData(searchQuery, maxResults),
          'command menu search'
        )();

        if (apiResults) {
          // Track search analytics
          trackCommandMenuSearch(searchQuery, apiResults.length);

          // Convert API results to menu items
          const dynamicMenuItems: CommandMenuItem[] = apiResults.map(result => ({
            id: `search-${result.id}`,
            title: result.title,
            subtitle: result.description,
            group: 'Search Results',
            data: result.href,
            score: 0,
            type: 'navigation' as const,
            keywords: [result.title.toLowerCase(), result.type],
            icon: getIconForType(result.type),
          }));

          setDynamicResults(dynamicMenuItems);
        } else {
          setDynamicResults([]);
        }
      } catch (error) {
        // Error already handled by async error boundary
        setDynamicResults([]);
      } finally {
        setIsLoading(false);
      }
    };

    const debounce = setTimeout(searchWithApi, debounceDelay);
    return () => clearTimeout(debounce);
  }, [searchQuery, maxResults, debounceDelay, wrapAsync]);

  /**
   * Clear search state
   */
  const clearSearch = useCallback(() => {
    setSearchQuery('');
    setDynamicResults([]);
    setIsLoading(false);
  }, []);

  return {
    searchQuery,
    setSearchQuery,
    isLoading,
    dynamicResults,
    clearSearch,
  };
}

/**
 * Get icon for search result type
 */
function getIconForType(type: string): React.ComponentType<{ className?: string }> | undefined {
  // This would typically use your icon mapping logic - returning undefined for now
  // In a real implementation, you'd import and return actual icon components
  switch (type) {
    case 'resident':
    case 'household':
    default:
      return undefined;
  }
}

// Export for backward compatibility
export default useCommandMenuSearch;

/**
 * Command Menu with API Hook (Refactored)
 *
 * @description Lightweight orchestrator for command menu functionality with API integration.
 * Composes specialized hooks for better maintainability.
 *
 * Architecture:
 * - useCommandMenuSearch: Search functionality
 * - useCommandMenuRecents: Recent items management
 * - useCommandMenuActions: Command execution and API actions
 * - useCommandMenu: Base menu functionality
 */

'use client';

import { useState, useEffect, useCallback, useMemo } from 'react';

import type { CommandMenuItemType as CommandMenuItem } from '@/components';
import { getCommandMenuItems } from '@/lib/command-menu';

import { useCommandMenu } from './useCommandMenu';
import { useCommandMenuActions, type UseCommandMenuActionsReturn } from './useCommandMenuActions';
import { useCommandMenuRecents, type UseCommandMenuRecentsReturn } from './useCommandMenuRecents';
import { useCommandMenuSearch, type UseCommandMenuSearchReturn } from './useCommandMenuSearch';

/**
 * Options for command menu with API
 */
export interface UseCommandMenuWithApiProps {
  /** Maximum number of results */
  maxResults?: number;
}

/**
 * Return type for command menu with API hook
 */
export interface UseCommandMenuWithApiReturn
  extends Pick<UseCommandMenuSearchReturn, 'searchQuery' | 'setSearchQuery' | 'isLoading'>,
    Pick<UseCommandMenuRecentsReturn, 'handleClearRecentItems'>,
    Pick<UseCommandMenuActionsReturn, 'executeCommand'> {
  /** Menu open state */
  isOpen: boolean;
  /** Open menu */
  open: () => void;
  /** Close menu */
  close: () => void;
  /** Toggle menu */
  toggle: () => void;
  /** Filtered menu items */
  filteredItems: CommandMenuItem[];
  /** Selected index */
  selectedIndex: number;
  /** Set selected index */
  setSelectedIndex: (index: number) => void;
  /** Number of dynamic results */
  dynamicResults: number;
  /** Number of recent items */
  recentItems: number;
}

/**
 * Command menu with API integration hook (Refactored)
 *
 * @description Orchestrates command menu functionality with API integration.
 * Much smaller and more maintainable than the original implementation.
 */
export function useCommandMenuWithApi({
  maxResults = 10,
}: UseCommandMenuWithApiProps = {}): UseCommandMenuWithApiReturn {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(0);

  // Compose specialized hooks
  const search = useCommandMenuSearch({ maxResults: 5 });
  const recents = useCommandMenuRecents();
  const actions = useCommandMenuActions();

  // Get static menu items with enhanced actions
  const staticMenuItems = useMemo(() => {
    const baseItems = getCommandMenuItems();
    return actions.getEnhancedMenuItems(baseItems);
  }, [actions]);

  // Load recent items when menu opens
  useEffect(() => {
    if (isOpen) {
      recents.loadRecentItems();
    }
  }, [isOpen, recents.loadRecentItems]);

  // Combine all items based on search state
  const allItems = useMemo(() => {
    let items: CommandMenuItem[] = [];

    if (search.searchQuery.trim()) {
      // Show dynamic search results first, then filtered static items
      items = [...search.dynamicResults];

      // Add matching static items
      const query = search.searchQuery.toLowerCase();
      const matchingStaticItems = staticMenuItems.filter(
        item =>
          item.label?.toLowerCase().includes(query) ||
          item.description?.toLowerCase().includes(query) ||
          item.keywords?.some(keyword => keyword.includes(query))
      );

      items.push(...matchingStaticItems);
    } else {
      // Show recent items first, then all static items
      items = [...recents.recentItems, ...staticMenuItems];
    }

    // Ensure we always have at least the static menu items
    if (items.length === 0 && staticMenuItems.length > 0) {
      items = staticMenuItems;
    }

    // Only limit results for search queries - show all static items when browsing
    const limit = search.searchQuery.trim()
      ? maxResults
      : Math.max(maxResults, staticMenuItems.length);
    return items.slice(0, limit);
  }, [search.searchQuery, search.dynamicResults, staticMenuItems, recents.recentItems, maxResults]);

  // Use base command menu for keyboard handling
  const baseMenu = useCommandMenu({
    items: allItems,
    maxResults,
  });

  // Reset selected index when filtered items change
  useEffect(() => {
    setSelectedIndex(0);
  }, [allItems]);

  // Override base menu's open/close to manage our state
  const open = useCallback(() => {
    setIsOpen(true);
    search.setSearchQuery('');
    setSelectedIndex(0);
  }, [search]);

  const close = useCallback(() => {
    setIsOpen(false);
    search.clearSearch();
    setSelectedIndex(0);
  }, [search]);

  const toggle = useCallback(() => {
    if (isOpen) {
      close();
    } else {
      open();
    }
  }, [isOpen, open, close]);

  // Enhanced execute command with close
  const executeCommand = useCallback(
    (item: CommandMenuItem) => {
      close();
      actions.executeCommand(item);
    },
    [close, actions]
  );

  return {
    // Menu state
    isOpen,
    open,
    close,
    toggle,

    // Search functionality
    searchQuery: search.searchQuery,
    setSearchQuery: search.setSearchQuery,
    isLoading: search.isLoading,

    // Items and selection
    filteredItems: allItems,
    selectedIndex,
    setSelectedIndex,

    // Actions
    executeCommand,

    // Statistics
    dynamicResults: search.dynamicResults.length,
    recentItems: recents.recentItemsCount,

    // Recent items management
    handleClearRecentItems: recents.handleClearRecentItems,
  };
}

