export default useCommandMenuWithApi;

'use client';

/**
 * Household CRUD Operations Hook
 *
 * @description Focused hook for basic CRUD operations on households.
 * Extracted from useHouseholdOperations to follow single responsibility principle.
 */

import { useState, useCallback } from 'react';

import { householdService, HouseholdFormData } from '@/services/household.service';

/**
 * CRUD operation result
 */
export interface CrudOperationResult<T = any> {
  success: boolean;
  data?: T;
  error?: string;
}

/**
 * Options for CRUD operations
 */
export interface UseHouseholdCrudOptions {
  onSuccess?: (data: any) => void;
  onError?: (error: string) => void;
}

/**
 * Return type for useHouseholdCrud hook
 */
export interface UseHouseholdCrudReturn {
  /** Whether any operation is in progress */
  isLoading: boolean;
  /** Get household by ID */
  getHousehold: (id: string) => Promise<CrudOperationResult>;
  /** Get household by code */
  getHouseholdByCode: (code: string) => Promise<CrudOperationResult>;
  /** List households with pagination */
  listHouseholds: (page?: number, limit?: number) => Promise<CrudOperationResult>;
  /** Update household */
  updateHousehold: (
    id: string,
    updates: Partial<HouseholdFormData>
  ) => Promise<CrudOperationResult>;
  /** Delete household */
  deleteHousehold: (id: string) => Promise<CrudOperationResult>;
}

/**
 * Custom hook for household CRUD operations
 *
 * @description Provides basic create, read, update, delete operations for households
 * with consistent error handling and loading states.
 */
export function useHouseholdCrud(options: UseHouseholdCrudOptions = {}): UseHouseholdCrudReturn {
  const { onSuccess, onError } = options;
  const [isLoading, setIsLoading] = useState(false);

  /**
   * Generic error handler
   */
  const handleError = useCallback(
    (error: unknown, defaultMessage: string): CrudOperationResult => {
      const errorMessage = error instanceof Error ? error.message : defaultMessage;
      if (onError) {
        onError(errorMessage);
      }
      return { success: false, error: errorMessage };
    },
    [onError]
  );

  /**
   * Generic success handler
   */
  const handleSuccess = useCallback(
    (data: any): CrudOperationResult => {
      if (onSuccess) {
        onSuccess(data);
      }
      return { success: true, data };
    },
    [onSuccess]
  );

  /**
   * Get household by ID
   */
  const getHousehold = useCallback(
    async (id: string): Promise<CrudOperationResult> => {
      setIsLoading(true);
      try {
        const result = await householdService.getHousehold(id);

        if (!result.success) {
          return handleError(new Error(result.error), 'Failed to fetch household');
        }

        return handleSuccess(result.data);
      } catch (error) {
        return handleError(error, 'Failed to fetch household');
      } finally {
        setIsLoading(false);
      }
    },
    [handleError, handleSuccess]
  );

  /**
   * Get household by code
   */
  const getHouseholdByCode = useCallback(
    async (code: string): Promise<CrudOperationResult> => {
      setIsLoading(true);
      try {
        const result = await householdService.getHouseholdByCode(code);

        if (!result.success) {
          return handleError(new Error(result.error), 'Failed to fetch household by code');
        }

        return handleSuccess(result.data);
      } catch (error) {
        return handleError(error, 'Failed to fetch household by code');
      } finally {
        setIsLoading(false);
      }
    },
    [handleError, handleSuccess]
  );

  /**
   * List households with pagination
   */
  const listHouseholds = useCallback(
    async (page = 1, limit = 10): Promise<CrudOperationResult> => {
      setIsLoading(true);
      try {
        const result = await householdService.listHouseholds(page, limit);

        if (!result.success) {
          return handleError(new Error(result.error), 'Failed to list households');
        }

        return handleSuccess(result.data);
      } catch (error) {
        return handleError(error, 'Failed to list households');
      } finally {
        setIsLoading(false);
      }
    },
    [handleError, handleSuccess]
  );

  /**
   * Update household
   */
  const updateHousehold = useCallback(
    async (id: string, updates: Partial<HouseholdFormData>): Promise<CrudOperationResult> => {
      setIsLoading(true);
      try {
        const result = await householdService.updateHousehold(id, updates);

        if (!result.success) {
          return handleError(new Error(result.error), 'Failed to update household');
        }

        return handleSuccess(result.data);
      } catch (error) {
        return handleError(error, 'Failed to update household');
      } finally {
        setIsLoading(false);
      }
    },
    [handleError, handleSuccess]
  );

  /**
   * Delete household
   */
  const deleteHousehold = useCallback(
    async (id: string): Promise<CrudOperationResult> => {
      setIsLoading(true);
      try {
        const result = await householdService.deleteHousehold(id);

        if (!result.success) {
          return handleError(new Error(result.error), 'Failed to delete household');
        }

        return handleSuccess(null);
      } catch (error) {
        return handleError(error, 'Failed to delete household');
      } finally {
        setIsLoading(false);
      }
    },
    [handleError, handleSuccess]
  );

  return {
    isLoading,
    getHousehold,
    getHouseholdByCode,
    listHouseholds,
    updateHousehold,
    deleteHousehold,
  };
}

'use client';

/**
 * useResidentOperations Hook
 *
 * Custom React hook for resident data operations.
 * Provides a clean interface between UI components and business logic.
 */

import { useQueryClient } from '@tanstack/react-query';
import { useState, useCallback } from 'react';

import { useAuth } from '@/contexts';
import { useCSRFToken } from '@/lib/auth';
import { residentService } from '@/services/resident.service';
import { ResidentFormData } from '@/types/forms';

export interface UseResidentOperationsOptions {
  onSuccess?: (data: any) => void;
  onError?: (error: string) => void;
}

/**
 * Custom hook for resident operations
 */
export function useResidentOperations(options: UseResidentOperationsOptions = {}) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});
  const { user, userProfile } = useAuth();
  const { getToken: getCSRFToken } = useCSRFToken();
  const queryClient = useQueryClient();

  // Get user information from auth context
  const barangayCode = userProfile?.barangay_code;

  /**
   * Create a new resident
   */
  const createResident = useCallback(
    async (formData: ResidentFormData) => {
      setIsSubmitting(true);
      setValidationErrors({});

      try {
        // Validate required barangay code
        if (!barangayCode) {
          throw new Error('User barangay code is required to create residents');
        }

        // Get CSRF token for secure form submission
        const csrfToken = getCSRFToken();

        // Call service to create resident
        const result = await residentService.createResident({
          formData,
          barangayCode,
          csrfToken,
        });

        if (!result.success) {
          // Handle validation errors
          if (result.data?.validationErrors) {
            const errorMap: Record<string, string> = {};
            result.data.validationErrors.forEach((error: { field: string; message: string }) => {
              errorMap[error.field] = error.message;
            });
            setValidationErrors(errorMap);
          }

          if (options.onError) {
            options.onError(result.error || 'Failed to create resident');
          }
          return result;
        }

        // Invalidate residents cache to refresh the list
        await queryClient.invalidateQueries({
          queryKey: ['residents'],
        });

        // Success callback
        if (options.onSuccess) {
          options.onSuccess(result.data);
        }

        return result;
      } catch (error) {
        const errorMessage = 'An unexpected error occurred. Please try again.';
        if (options.onError) {
          options.onError(errorMessage);
        }
        return { success: false, error: errorMessage };
      } finally {
        setIsSubmitting(false);
      }
    },
    [barangayCode, options, queryClient]
  );

  /**
   * Validate resident data
   */
  const validateResident = useCallback(async (formData: ResidentFormData) => {
    const result = await residentService.validateResident(formData);

    if (!result.success && result.errors) {
      const errorMap: Record<string, string> = {};
      result.errors.forEach(error => {
        errorMap[error.field] = error.message;
      });
      setValidationErrors(errorMap);
    } else {
      setValidationErrors({});
    }

    return result;
  }, []);

  /**
   * Get resident by ID
   */
  const getResident = useCallback(
    async (id: string) => {
      try {
        const result = await residentService.getResident(id);
        if (!result.success && options.onError) {
          options.onError(result.error || 'Failed to fetch resident');
        }
        return result;
      } catch (error) {
        const errorMessage = 'Failed to fetch resident';
        if (options.onError) {
          options.onError(errorMessage);
        }
        return { success: false, error: errorMessage };
      }
    },
    [options]
  );

  /**
   * List residents with pagination
   */
  const listResidents = useCallback(
    async (page = 1, limit = 10) => {
      try {
        const result = await residentService.listResidents(page, limit);
        if (!result.success && options.onError) {
          options.onError(result.error || 'Failed to list residents');
        }
        return result;
      } catch (error) {
        const errorMessage = 'Failed to list residents';
        if (options.onError) {
          options.onError(errorMessage);
        }
        return { success: false, error: errorMessage };
      }
    },
    [options]
  );

  /**
   * Clear validation errors
   */
  const clearValidationErrors = useCallback(() => {
    setValidationErrors({});
  }, []);

  return {
    // Operations
    createResident,
    validateResident,
    getResident,
    listResidents,
    clearValidationErrors,

    // State
    isSubmitting,
    validationErrors,
  };
}

