    vi.clearAllMocks();
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  it('should return an object with onMouseEnter handler', () => {
    const { result } = renderHook(() => usePreloadOnHover('dataTable'));

    expect(result.current).toEqual({
      onMouseEnter: expect.any(Function),
    });
