/**
 * Resident Form State Management Hook
 *
 * @description Focused hook for managing resident form state and data persistence.
 * Extracted from useResidentEditForm to follow single responsibility principle.
 */

import { useState, useCallback, useEffect } from 'react';

import type { ResidentFormData as ResidentEditFormData } from '@/types';

/**
 * Default form data with proper typing
 */
const DEFAULT_FORM_DATA: Partial<ResidentEditFormData> = {
  first_name: '',
  last_name: '',
  birthdate: '',
  sex: 'male',
};
/**
 * Form state options
 */
export interface UseResidentFormStateOptions {
  initialData?: Partial<ResidentEditFormData>;
  autoSave?: boolean;
  autoSaveKey?: string;
}

/**
 * Return type for useResidentFormState hook
 */
export interface UseResidentFormStateReturn {
  /** Current form data */
  formData: Partial<ResidentEditFormData>;
  /** Whether form has unsaved changes */
  isDirty: boolean;
  /** Update a single field */
  updateField: <K extends keyof ResidentEditFormData>(
    field: K,
    value: ResidentEditFormData[K]
  ) => void;
  /** Update multiple fields at once */
  updateFields: (fields: Partial<ResidentEditFormData>) => void;
  /** Reset form to initial state */
  resetForm: () => void;
  /** Set form data programmatically */
  setFormData: (data: Partial<ResidentEditFormData>) => void;
  /** Clear auto-saved data */
  clearAutoSave: () => void;
}

/**
 * Custom hook for resident form state management
 *
 * @description Manages form data state, auto-save functionality, and data persistence.
 * Provides clean separation between state management and validation/submission logic.
 */
export function useResidentFormState(
  options: UseResidentFormStateOptions = {}
): UseResidentFormStateReturn {
  const { initialData = {}, autoSave = false, autoSaveKey = 'resident-form-draft' } = options;

  /**
   * Initialize form data with auto-save recovery
   */
  const [formData, setFormDataState] = useState<Partial<ResidentEditFormData>>(() => {
    const merged = { ...DEFAULT_FORM_DATA, ...initialData };

    // Load from localStorage if autoSave is enabled and no initial data
    if (autoSave && Object.keys(initialData).length === 0) {
      try {
        const saved = localStorage.getItem(autoSaveKey);
        if (saved) {
          const parsedData = JSON.parse(saved);
          return { ...merged, ...parsedData };
        }
      } catch (error) {
        // Silently handle localStorage errors
      }
    }

    return merged;
  });

  const [isDirty, setIsDirty] = useState(false);

  /**
   * Auto-save to localStorage when form changes
   */
  useEffect(() => {
    if (autoSave && isDirty) {
      try {
        localStorage.setItem(autoSaveKey, JSON.stringify(formData));
      } catch (error) {
        // Silently handle localStorage errors
      }
    }
  }, [formData, autoSave, autoSaveKey, isDirty]);

  /**
   * Update a single field
   */
  const updateField = useCallback(
    <K extends keyof ResidentEditFormData>(field: K, value: ResidentEditFormData[K]) => {
      setFormDataState(prev => ({
        ...prev,
        [field]: value,
      }));
      setIsDirty(true);
    },
    []
  );

  /**
   * Update multiple fields at once
   */
  const updateFields = useCallback((fields: Partial<ResidentEditFormData>) => {
    setFormDataState(prev => ({
      ...prev,
      ...fields,
    }));
    setIsDirty(true);
  }, []);

  /**
   * Reset form to initial state
   */
  const resetForm = useCallback(() => {
    const resetData = { ...DEFAULT_FORM_DATA, ...initialData };
    setFormDataState(resetData);
    setIsDirty(false);

    // Clear auto-save
    if (autoSave) {
      try {
        localStorage.removeItem(autoSaveKey);
      } catch (error) {
        // Silently handle localStorage errors
      }
    }
  }, [initialData, autoSave, autoSaveKey]);

  /**
   * Set form data programmatically
   */
  const setFormData = useCallback((data: Partial<ResidentEditFormData>) => {
    setFormDataState(data);
    setIsDirty(true);
  }, []);

  /**
   * Clear auto-saved data
   */
  const clearAutoSave = useCallback(() => {
    if (autoSave) {
      try {
        localStorage.removeItem(autoSaveKey);
      } catch (error) {
        // Silently handle localStorage errors
      }
    }
  }, [autoSave, autoSaveKey]);

  return {
    formData,
    isDirty,
    updateField,
    updateFields,
    resetForm,
    setFormData,
    clearAutoSave,
  };
}

/**
 * User Behavior Analytics
 * Tracks user engagement patterns for PWA install prompts and general app usage
 */

// Import types from centralized location
import type {
  UserBehaviorMetrics,
  PWAInstallCriteria,
  PersonalizedMessage,
} from '@/types/components';

// Re-export for backwards compatibility
export type { UserBehaviorMetrics, PWAInstallCriteria, PersonalizedMessage };

const STORAGE_KEY = 'pwa-user-metrics';
const DISMISSAL_KEY = 'pwa-prompt-dismissed-date';
const SESSION_DISMISSAL_KEY = 'pwa-prompt-dismissed';

/**
 * Load user behavior metrics from localStorage
 */
export function loadUserMetrics(): UserBehaviorMetrics {
  if (typeof window === 'undefined') {
    return getDefaultMetrics();
  }

  try {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      return JSON.parse(stored);
    }
  } catch (error) {
    console.warn('Failed to load user metrics:', error);
  }
  return getDefaultMetrics();
}

/**
 * Save user behavior metrics to localStorage
 */
export function saveUserMetrics(metrics: UserBehaviorMetrics): void {
  if (typeof window === 'undefined') return;

  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(metrics));
  } catch (error) {
    console.warn('Failed to save user metrics:', error);
  }
}

/**
 * Get default user metrics
 */
export function getDefaultMetrics(): UserBehaviorMetrics {
  return {
    pageViews: 0,
    timeSpent: 0,
    interactions: 0,
    revisits: 0,
    lastVisit: 0,
  };
}

/**
 * Update user behavior metrics for a page view
 */
export function updatePageViewMetrics(): UserBehaviorMetrics {
  const metrics = loadUserMetrics();
  const now = Date.now();
  const isRevisit = metrics.lastVisit > 0 && now - metrics.lastVisit > 24 * 60 * 60 * 1000; // 24 hours

  const updatedMetrics = {
    pageViews: metrics.pageViews + 1,
    timeSpent: metrics.timeSpent,
    interactions: metrics.interactions,
    revisits: isRevisit ? metrics.revisits + 1 : metrics.revisits,
    lastVisit: now,
  };

  saveUserMetrics(updatedMetrics);
  return updatedMetrics;
}

/**
 * Update interaction count
 */
export function trackUserInteraction(): UserBehaviorMetrics {
  const metrics = loadUserMetrics();
  const updated = { ...metrics, interactions: metrics.interactions + 1 };
  saveUserMetrics(updated);
  return updated;
}

/**
 * Track time spent in session
 */
export function trackTimeSpent(sessionStartTime: number): UserBehaviorMetrics {
  const metrics = loadUserMetrics();
  const timeSpent = Date.now() - sessionStartTime;
  const updated = { ...metrics, timeSpent: metrics.timeSpent + timeSpent };
  saveUserMetrics(updated);
  return updated;
}

/**
 * Check if PWA install prompt should be shown based on user behavior
 */
export function shouldShowInstallPrompt(metrics: UserBehaviorMetrics): boolean {
  // Don't show if recently dismissed (within 7 days)
  if (typeof window !== 'undefined') {
    const dismissedDate = localStorage.getItem(DISMISSAL_KEY);
    if (dismissedDate) {
      const daysSinceDismissed = (Date.now() - parseInt(dismissedDate)) / (1000 * 60 * 60 * 24);
      if (daysSinceDismissed < 7) {
        return false;
      }
    }

    // Don't show if dismissed in current session
    if (sessionStorage.getItem(SESSION_DISMISSAL_KEY)) {
      return false;
    }
  }

  // Smart timing criteria
  const criteria = getPWAInstallCriteria(metrics);

  // Must meet at least 2 of the 4 criteria
  const metCriteria = Object.values(criteria).filter(Boolean).length;
  return metCriteria >= 2;
}

/**
 * Get PWA install criteria for a user
 */
export function getPWAInstallCriteria(metrics: UserBehaviorMetrics): PWAInstallCriteria {
  return {
    // User has visited multiple times
    isReturningUser: metrics.revisits >= 2,
    // User has engaged with the app
    hasInteracted: metrics.interactions >= 5,
    // User has spent reasonable time
    hasSpentTime: metrics.timeSpent >= 60000, // 1 minute
    // Multiple page views indicate engagement
    hasExplored: metrics.pageViews >= 3,
  };
}

/**
 * Get personalized install message based on user behavior
 */
export function getPersonalizedInstallMessage(metrics: UserBehaviorMetrics): PersonalizedMessage {
  if (metrics.revisits >= 2) {
    return {
      title: 'Welcome back! Install Citizenly',
      description:
        "You've been using Citizenly regularly. Install it for faster access and offline use.",
    };
  }

  if (metrics.pageViews >= 5) {
    return {
      title: 'Enjoying Citizenly? Install it!',
      description: "You've explored multiple features. Get the full experience with our app.",
    };
  }

  if (metrics.timeSpent >= 120000) {
    // 2 minutes
    return {
      title: 'Install Citizenly for convenience',
      description:
        "You've spent quality time here. Install for quicker access and offline capabilities.",
    };
  }

  return {
    title: 'Install Citizenly',
    description: 'Add to your home screen for quick access and offline use',
  };
}

/**
 * Check if PWA is installed
 */
export function isPWAInstalled(): boolean {
  if (typeof window === 'undefined') return false;

  // Check for standalone mode (Android/iOS)
  if (window.matchMedia('(display-mode: standalone)').matches) {
    return true;
  }

  // Check for iOS Safari standalone
  if ((window.navigator as any).standalone === true) {
    return true;
  }

  return false;
}

/**
 * Record install prompt dismissal
 */
export function recordInstallDismissal(): void {
  if (typeof window === 'undefined') return;

  // Store dismissal date for smart timing (7 days cooldown)
  localStorage.setItem(DISMISSAL_KEY, Date.now().toString());

  // Also set session storage for immediate dismissal
  sessionStorage.setItem(SESSION_DISMISSAL_KEY, 'true');
}

/**
 * Get delay for install prompt based on user behavior
 */
export function getInstallPromptDelay(metrics: UserBehaviorMetrics): number {
  // Shorter delay for returning users
  return metrics.revisits > 0 ? 2000 : 5000;
}

/**
 * Setup user interaction tracking listeners
 */
export function setupUserInteractionTracking(onInteraction: () => void): () => void {
  if (typeof window === 'undefined') return () => {};

  // Track user interactions
  const trackInteraction = () => onInteraction();

  window.addEventListener('click', trackInteraction);
  window.addEventListener('scroll', trackInteraction);
  window.addEventListener('keydown', trackInteraction);

  // Return cleanup function
  return () => {
    window.removeEventListener('click', trackInteraction);
    window.removeEventListener('scroll', trackInteraction);
    window.removeEventListener('keydown', trackInteraction);
  };
}

/**
 * Setup time tracking for a session
 */
export function setupTimeTracking(onTimeUpdate: (sessionTime: number) => void): () => void {
  if (typeof window === 'undefined') return () => {};

  const sessionStart = Date.now();

  const trackTime = () => {
    const sessionTime = Date.now() - sessionStart;
    onTimeUpdate(sessionTime);
  };

  // Track time on beforeunload
  window.addEventListener('beforeunload', trackTime);

  // Return cleanup function that also tracks final time
  return () => {
    window.removeEventListener('beforeunload', trackTime);
    trackTime(); // Track time when cleanup is called
  };
}

/**
 * PSGC Route Handlers - Consolidated Philippine Standard Geographic Code API Utilities
 *
 * @fileoverview Production-ready utilities for handling PSGC address data queries.
 * Eliminates duplicate patterns across regions, provinces, cities, barangays, and streets API routes.
 * Integrates with existing authentication and response utilities.
 *
 * @version 1.0.0
 * @since 2025-08-29
 * @author Citizenly Development Team
 */

import { NextRequest } from 'next/server';

import { createAdminSupabaseClient } from '@/lib';
import { 
  createSuccessResponse, 
  createErrorResponse, 
  withNextRequestErrorHandling 
} from '@/lib/authentication/responseUtils';

// =============================================================================
// TYPES
// =============================================================================

/**
 * PSGC query configuration
 */
export interface PSGCQueryConfig {
  /** Database table name */
  table: string;
  /** Fields to select from the table */
  selectFields: string;
  /** Field to filter by (optional) */
  filterField?: string;
  /** Query parameter name for filtering (optional) */
  filterParam?: string;
  /** Context name for error messages */
  errorContext: string;
  /** Order field (defaults to 'name') */
  orderField?: string;
}

/**
 * Standard PSGC option format for SelectField components
 */
export interface PSGCOption {
  value: string;
  label: string;
  [key: string]: any; // Additional fields from the database
}

// =============================================================================
// CORE PSGC HANDLER
// =============================================================================

/**
 * Creates a standardized PSGC GET route handler
 * Eliminates duplicate patterns across all PSGC endpoints
 */
export function createPSGCHandler(config: PSGCQueryConfig) {
  return withNextRequestErrorHandling(async (request: NextRequest) => {
    const supabase = createAdminSupabaseClient();
    const { searchParams } = new URL(request.url);

    // Build base query
    let query = supabase
      .from(config.table)
      .select(config.selectFields)
      .order(config.orderField || 'name');

    // Apply filter if configured
    if (config.filterField && config.filterParam) {
      const filterValue = searchParams.get(config.filterParam);
      if (filterValue) {
        query = query.eq(config.filterField, filterValue);
      }
    }

    const { data, error } = await query;

    if (error) {
      console.error(`${config.errorContext} query error:`, error);
      throw new Error(`Failed to fetch ${config.errorContext.toLowerCase()}`);
    }

    // Transform data to standard SelectField format
    const options: PSGCOption[] = data?.map((item: any) => {
      const option: PSGCOption = {
        value: item.code,
        label: item.name,
      };

      // Add additional fields dynamically (preserve all database fields)
      Object.keys(item).forEach(key => {
        if (!['code', 'name'].includes(key)) {
          option[key] = item[key];
        }
      });

      return option;
    }) || [];

    return createSuccessResponse(options, `${config.errorContext} retrieved successfully`);
  });
}

// =============================================================================
// PRE-CONFIGURED PSGC HANDLERS
// =============================================================================

/**
 * Pre-configured PSGC route handlers for all address levels
 * These replace the duplicate implementations in individual route files
 */
export const PSGCHandlers = {
  /**
   * Regions handler - No filtering, returns all regions
   * Usage: export const GET = PSGCHandlers.regions;
   */
  regions: createPSGCHandler({
    table: 'psgc_regions',
    selectFields: 'code, name',
    errorContext: 'Regions',
  }),

  /**
   * Provinces handler - Optionally filter by region
   * Usage: export const GET = PSGCHandlers.provinces;
   * Query: ?region=REGION_CODE
   */
  provinces: createPSGCHandler({
    table: 'psgc_provinces',
    selectFields: 'code, name, region_code',
    filterField: 'region_code',
    filterParam: 'region',
    errorContext: 'Provinces',
  }),

  /**
   * Cities/Municipalities handler - Optionally filter by province
   * Usage: export const GET = PSGCHandlers.cities;
   * Query: ?province=PROVINCE_CODE
   */
  cities: createPSGCHandler({
    table: 'psgc_cities_municipalities',
    selectFields: 'code, name, type, province_code',
    filterField: 'province_code',
    filterParam: 'province',
    errorContext: 'Cities',
  }),

  /**
   * Barangays handler - Optionally filter by city
   * Usage: export const GET = PSGCHandlers.barangays;
   * Query: ?city=CITY_CODE
   */
  barangays: createPSGCHandler({
    table: 'psgc_barangays',
    selectFields: 'code, name, city_municipality_code',
    filterField: 'city_municipality_code',
    filterParam: 'city',
    errorContext: 'Barangays',
  }),

  /**
   * Subdivisions handler - Special geo_subdivisions table with search
   * Usage: export const GET = PSGCHandlers.subdivisions;
   * Query: ?barangay_code=BARANGAY_CODE&search=SEARCH_TERM
   */
  subdivisions: withNextRequestErrorHandling(async (request: NextRequest) => {
    const supabase = createAdminSupabaseClient();
    const { searchParams } = new URL(request.url);
    const barangayCode = searchParams.get('barangay_code');
    const search = searchParams.get('search');

    let query = supabase
      .from('geo_subdivisions')
      .select('id, name, type, barangay_code, is_active')
      .eq('is_active', true)
      .order('name');

    // Filter by barangay if provided
    if (barangayCode) {
      query = query.eq('barangay_code', barangayCode);
    }

    // Add search filter if provided
    if (search && search.trim() !== '') {
      query = query.ilike('name', `%${search.trim()}%`);
    }

    const { data: subdivisions, error } = await query.limit(100);

    if (error) {
      console.error('Subdivisions query error:', error);
      throw new Error('Failed to fetch subdivisions');
    }

    // Transform data to SelectField format
    const options = subdivisions?.map((subdivision: any) => ({
      value: subdivision.id,
      label: subdivision.name,
      barangay_code: subdivision.barangay_code,
      type: subdivision.type,
    })) || [];

    return createSuccessResponse(options, 'Subdivisions retrieved successfully');
  }),

  /**
   * Streets handler - Special geo_streets table with search and subdivision filtering
   * Usage: export const GET = PSGCHandlers.streets;
   * Query: ?barangay_code=BARANGAY_CODE&subdivision_id=SUBDIVISION_ID&search=SEARCH_TERM
   */
  streets: withNextRequestErrorHandling(async (request: NextRequest) => {
    const supabase = createAdminSupabaseClient();
    const { searchParams } = new URL(request.url);
    const barangayCode = searchParams.get('barangay_code');
    const subdivisionId = searchParams.get('subdivision_id');
    const search = searchParams.get('search');

    let query = supabase
      .from('geo_streets')
      .select('id, name, subdivision_id, barangay_code, is_active')
      .eq('is_active', true)
      .order('name');

    // Filter by barangay if provided
    if (barangayCode) {
      query = query.eq('barangay_code', barangayCode);
    }

    // Filter by subdivision if provided
    if (subdivisionId) {
      query = query.eq('subdivision_id', subdivisionId);
    }

    // Add search filter if provided
    if (search && search.trim() !== '') {
      query = query.ilike('name', `%${search.trim()}%`);
    }

    const { data: streets, error } = await query.limit(100);

    if (error) {
      console.error('Streets query error:', error);
      throw new Error('Failed to fetch streets');
    }

    // Transform data to SelectField format
    const options = streets?.map((street: any) => ({
      value: street.id,
      label: street.name,
      subdivision_id: street.subdivision_id,
      barangay_code: street.barangay_code,
    })) || [];

    return createSuccessResponse(options, 'Streets retrieved successfully');
  }),
} as const;

