 */
export function setupCacheCleanup(intervalMs: number = 10 * 60 * 1000): () => void {
  const interval = setInterval(() => {
    const cleaned = queryCache.cleanup();
    if (cleaned > 0) {
      logger.debug('Cache cleanup completed', { entriesRemoved: cleaned });
