/**
 * Batch operations utility for large data operations
 */
export const performBatchOperation = async <T>(
  items: T[],
  batchSize: number,
  operation: (batch: T[]) => Promise<void>
): Promise<void> => {
  for (let i = 0; i < items.length; i += batchSize) {
    const batch = items.slice(i, i + batchSize);
    await operation(batch);

    // Small delay to prevent overwhelming the database
    if (i + batchSize < items.length) {
      await new Promise(resolve => setTimeout(resolve, 100));
    }
  }
};

/**
 * Type guard for checking if data is fresh enough
 */
export const isDataFresh = (timestamp: string | Date, maxAgeMinutes: number): boolean => {
  const dataTime = new Date(timestamp);
  const cutoff = new Date();
  cutoff.setMinutes(cutoff.getMinutes() - maxAgeMinutes);

  return dataTime > cutoff;
};

/**
 * Supabase Helper Functions
 * Type-safe wrappers for common Supabase operations to resolve 'never' type issues
 */

import type { SupabaseClient } from '@supabase/supabase-js';

/**
 * Type-safe insert operation
 */
export async function safeInsert<T = any>(
  client: SupabaseClient,
  table: string,
  data: Record<string, any> | Record<string, any>[]
): Promise<{ data: T | null; error: any }> {
  const result = await (client as any).from(table).insert(data).select().single();

  return {
    data: result.data,
    error: result.error,
  };
}

/**
 * Type-safe update operation
 */
export async function safeUpdate<T = any>(
  client: SupabaseClient,
  table: string,
  data: Record<string, any>,
  filters: Record<string, any>
): Promise<{ data: T | null; error: any }> {
  let query = (client as any).from(table).update(data);

  Object.entries(filters).forEach(([key, value]) => {
    query = query.eq(key, value);
  });

  const result = await query.select().single();

  return {
    data: result.data,
    error: result.error,
  };
}

/**
 * Type-safe select operation with filters
 */
export async function safeSelect<T = any>(
  client: SupabaseClient,
  table: string,
  selectFields: string = '*',
  filters: Record<string, any> = {}
): Promise<{ data: T | null; error: any }> {
  let query = (client as any).from(table).select(selectFields);

  Object.entries(filters).forEach(([key, value]) => {
    query = query.eq(key, value);
  });

  const result = await query.single();

  return {
    data: result.data,
    error: result.error,
  };
}

/**
 * Supabase Client Configuration
 * Multi-environment Supabase client for RBI System
 */

import { createClient } from '@supabase/supabase-js';

import {
  getSupabaseConfig,
  validateEnvironment,
  createLogger,
  isProductionLike,
} from '../config/environment';

const logger = createLogger('Supabase');

// Get environment-specific Supabase configuration
const { url, anonKey, options } = getSupabaseConfig();

// Check if we're in a valid Supabase environment
export const isSupabaseAvailable = () => {
  return (
    process.env.NEXT_PUBLIC_SUPABASE_URL &&
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY &&
    !process.env.NEXT_PUBLIC_SUPABASE_URL.includes('placeholder')
  );
};

// Validate environment on startup (only in browser for production-like environments)
if (typeof window !== 'undefined' && isProductionLike()) {
  const validation = validateEnvironment();
  if (!validation.isValid) {
    logger.error('Environment validation failed:', validation.errors);
    throw new Error(`Environment validation failed: ${validation.errors.join(', ')}`);
  }
}

// Create Supabase client with environment-specific configuration
export const supabase = createClient(url, anonKey, options);

// Force schema refresh function
export const refreshSchema = async () => {
  if (!isSupabaseAvailable()) {
    logger.debug('Supabase not available, skipping schema refresh');
    return;
  }

  try {
    // Force a schema refresh by making a simple query
    const { error } = await supabase.rpc('version');
    if (error) {
      logger.debug('Schema refresh attempt:', error.message);
    } else {
      logger.debug('Schema refresh completed successfully');
    }
  } catch (e) {
    logger.debug('Schema refresh completed with exception:', e);
  }
};

