/**
 * Background Sync Queue System
 * Handles offline data synchronization when connection is restored
 */

import { ErrorCode, ErrorSeverity } from '@/types/errors';
import type { SyncResult } from '@/types/utilities';
import { createAppError } from '@/utils/errorUtils';

import { offlineStorage } from './offline-storage';

export class SyncQueue {
  private isProcessing = false;
  private maxRetries = 3;
  private retryDelay = 5000; // 5 seconds

  /**
   * Add action to sync queue
   */
  async addToQueue(
    action: 'CREATE' | 'UPDATE' | 'DELETE',
    type: 'resident' | 'household' | 'user',
    data: any
  ): Promise<void> {
    if (typeof window === 'undefined') return;

    await offlineStorage.addToSyncQueue({
      action,
      type,
      data,
    });

    console.log(`Added ${action} ${type} to sync queue`);

    // Try to process queue if online
    if (navigator.onLine && !this.isProcessing) {
      this.processQueue();
    }
  }

  /**
   * Process pending sync items
   */
  async processQueue(): Promise<void> {
    if (typeof window === 'undefined' || this.isProcessing || !navigator.onLine) {
      return;
    }

    this.isProcessing = true;
    console.log('Processing sync queue...');

    try {
      const pendingItems = await offlineStorage.getPendingSyncItems();

      for (const item of pendingItems) {
        if (item.retryCount >= this.maxRetries) {
          console.warn(`Max retries exceeded for sync item ${item.id}`);
          continue;
        }

        try {
          const result = await this.syncItem(item);

          if (result.success) {
            await offlineStorage.markSyncItemCompleted(item.id!);
            console.log(`Successfully synced ${item.action} ${item.type}`);
          } else {
            await offlineStorage.incrementSyncRetry(item.id!);
            console.error(`Failed to sync ${item.action} ${item.type}:`, result.error);
          }
        } catch (error) {
          await offlineStorage.incrementSyncRetry(item.id!);
          console.error(`Error syncing ${item.action} ${item.type}:`, error);
        }

        // Add delay between sync operations
        await this.delay(1000);
      }
    } catch (error) {
      console.error('Error processing sync queue:', error);
    } finally {
      this.isProcessing = false;
    }
  }

  /**
   * Sync individual item
   */
  private async syncItem(item: any): Promise<SyncResult> {
    const { action, type, data } = item;

    try {
      // Get auth token
      const { supabase } = await import('@/lib/data/supabase');
      const {
        data: { session },
      } = await supabase.auth.getSession();

      if (!session?.access_token) {
        return { success: false, error: 'No authentication token' };
      }

      const headers = {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${session.access_token}`,
      };

      let response: Response;

      switch (action) {
        case 'CREATE':
          response = await this.handleCreate(type, data, headers);
          break;
        case 'UPDATE':
          response = await this.handleUpdate(type, data, headers);
          break;
        case 'DELETE':
          response = await this.handleDelete(type, data, headers);
          break;
        default:
          return { success: false, error: `Unknown action: ${action}` };
      }

      if (response.ok) {
        const responseData = await response.json();
        return { success: true, data: responseData };
      } else {
        const errorData = await response.json().catch(() => ({}));
        return {
          success: false,
          error: errorData.error || `HTTP ${response.status}: ${response.statusText}`,
        };
      }
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  /**
   * Handle CREATE operations
   */
  private async handleCreate(type: string, data: any, headers: HeadersInit): Promise<Response> {
    const endpoints = {
      resident: '/api/residents',
      household: '/api/households',
      user: '/api/auth/create-profile',
    };

    const endpoint = endpoints[type as keyof typeof endpoints];
    if (!endpoint) {
      throw createAppError(`No endpoint defined for type: ${type}`, {
        code: ErrorCode.INVALID_OPERATION,
        severity: ErrorSeverity.HIGH,
        context: { type, action: 'CREATE' },
      });
    }

    return fetch(endpoint, {
      method: 'POST',
      headers,
      body: JSON.stringify(data),
    });
  }

  /**
   * Handle UPDATE operations
   */
  private async handleUpdate(type: string, data: any, headers: HeadersInit): Promise<Response> {
    const endpoints = {
      resident: `/api/residents/${data.id}`,
      household: `/api/households/${data.code || data.id}`,
      user: `/api/auth/profile`,
    };

    const endpoint = endpoints[type as keyof typeof endpoints];
    if (!endpoint) {
      throw createAppError(`No endpoint defined for type: ${type}`, {
        code: ErrorCode.INVALID_OPERATION,
        severity: ErrorSeverity.HIGH,
        context: { type, action: 'UPDATE' },
      });
    }

    return fetch(endpoint, {
      method: 'PUT',
      headers,
      body: JSON.stringify(data),
    });
  }

  /**
   * Handle DELETE operations
   */
  private async handleDelete(type: string, data: any, headers: HeadersInit): Promise<Response> {
    const endpoints = {
      resident: `/api/residents/${data.id}`,
      household: `/api/households/${data.code || data.id}`,
      user: `/api/auth/profile`,
    };

    const endpoint = endpoints[type as keyof typeof endpoints];
    if (!endpoint) {
      throw createAppError(`No endpoint defined for type: ${type}`, {
        code: ErrorCode.INVALID_OPERATION,
        severity: ErrorSeverity.HIGH,
        context: { type, action: 'DELETE' },
      });
    }

    return fetch(endpoint, {
      method: 'DELETE',
      headers,
    });
  }

  /**
   * Force sync all pending items
   */
  async forceSync(): Promise<void> {
    if (typeof window === 'undefined') return;

    if (!navigator.onLine) {
      throw createAppError('Cannot force sync while offline', {
        code: ErrorCode.NETWORK_ERROR,
        severity: ErrorSeverity.MEDIUM,
      });
    }

    await this.processQueue();
  }

  /**
   * Get sync queue status
   */
  async getStatus(): Promise<{
    isProcessing: boolean;
    pendingCount: number;
    isOnline: boolean;
  }> {
    const pendingItems = await offlineStorage.getPendingSyncItems();

    return {
      isProcessing: this.isProcessing,
      pendingCount: pendingItems.length,
      isOnline: navigator.onLine,
    };
  }

  /**
   * Clear all completed sync items
   */
  async clearCompleted(): Promise<void> {
    // This would need to be implemented in offline storage
    // For now, we'll just log it
    console.log('Clearing completed sync items...');
  }

  /**
   * Utility delay function
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Setup event listeners for online/offline events
   */
  setupEventListeners(): void {
    if (typeof window === 'undefined') return;

    window.addEventListener('online', () => {
      console.log('Connection restored, processing sync queue...');
      this.processQueue();
    });

    window.addEventListener('offline', () => {
      console.log('Connection lost, sync queue paused');
    });

    // Process queue on page load if online
    if (navigator.onLine) {
      setTimeout(() => this.processQueue(), 1000);
    }
  }

  /**
   * Cleanup event listeners
   */
  cleanup(): void {
    window.removeEventListener('online', this.processQueue);
    window.removeEventListener('offline', () => {});
  }
}

// Export singleton instance
export const syncQueue = new SyncQueue();

/**
 * Database Connection Pool Management
 * Optimized Supabase client management with connection pooling
 */

import { createClient, SupabaseClient } from '@supabase/supabase-js';

import { getSupabaseConfig, isProduction, createLogger } from '@/lib/config/environment';

const logger = createLogger('ConnectionPool');

interface ConnectionPoolConfig {
  maxConnections: number;
  idleTimeout: number;
  connectionTimeout: number;
  retryAttempts: number;
  healthCheckInterval: number;
}

interface PooledConnection {
  client: SupabaseClient;
  createdAt: number;
  lastUsed: number;
  isActive: boolean;
  connectionId: string;
}

class DatabaseConnectionPool {
  private pool: Map<string, PooledConnection> = new Map();
  private config: ConnectionPoolConfig;
  private healthCheckTimer?: NodeJS.Timeout;
  private isInitialized = false;

  constructor() {
    this.config = {
      maxConnections: isProduction() ? 20 : 10,
      idleTimeout: 300000, // 5 minutes
      connectionTimeout: 10000, // 10 seconds
      retryAttempts: 3,
      healthCheckInterval: 60000, // 1 minute
    };
  }

  /**
   * Initialize the connection pool
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    logger.info('Initializing database connection pool', {
      maxConnections: this.config.maxConnections,
      environment: process.env.NODE_ENV,
    });

    // Start health check timer
    this.startHealthCheck();
    this.isInitialized = true;

    logger.info('Database connection pool initialized successfully');
  }

  /**
   * Get a connection from the pool or create a new one
   */
  async getConnection(type: 'anon' | 'service' = 'anon'): Promise<SupabaseClient> {
    if (!this.isInitialized) {
      await this.initialize();
    }

    const poolKey = `${type}_${Math.floor(this.pool.size / this.config.maxConnections)}`;

    // Try to reuse existing connection
    const existingConnection = this.findAvailableConnection(type);
    if (existingConnection) {
      existingConnection.lastUsed = Date.now();
      existingConnection.isActive = true;
      return existingConnection.client;
    }

    // Create new connection if pool not full
    if (this.pool.size < this.config.maxConnections) {
      return this.createConnection(poolKey, type);
    }

    // Pool is full, wait for available connection or create with retry
    logger.warn('Connection pool at capacity, attempting to create connection with retry');
    return this.createConnectionWithRetry(poolKey, type);
  }

  /**
   * Create a new database connection
   */
  private createConnection(connectionId: string, type: 'anon' | 'service'): SupabaseClient {
    const supabaseConfig = getSupabaseConfig();
    const now = Date.now();

    let client: SupabaseClient<any, 'public', any>;

    if (type === 'service') {
      if (!process.env.SUPABASE_SERVICE_ROLE_KEY) {
        throw new Error('Service role key not configured');
      }

      client = createClient(supabaseConfig.url, process.env.SUPABASE_SERVICE_ROLE_KEY, {
        ...supabaseConfig.options,
        auth: {
          ...supabaseConfig.options.auth,
          autoRefreshToken: false,
          persistSession: false,
        },
      }) as SupabaseClient<any, 'public', any>;
    } else {
      client = createClient(
        supabaseConfig.url,
        supabaseConfig.anonKey,
        supabaseConfig.options
      ) as SupabaseClient<any, 'public', any>;
    }

    const pooledConnection: PooledConnection = {
      client,
      createdAt: now,
      lastUsed: now,
      isActive: true,
      connectionId,
    };

    this.pool.set(connectionId, pooledConnection);

    logger.debug(`Created new ${type} connection`, {
      connectionId,
      poolSize: this.pool.size,
      type,
    });

    return client;
  }

  /**
   * Create connection with retry logic
   */
  private async createConnectionWithRetry(
    connectionId: string,
    type: 'anon' | 'service'
  ): Promise<SupabaseClient> {
    let lastError: Error | null = null;

    for (let attempt = 1; attempt <= this.config.retryAttempts; attempt++) {
      try {
        // Clean up idle connections first
        await this.cleanupIdleConnections();

        // Try to create connection again
        if (this.pool.size < this.config.maxConnections) {
          return this.createConnection(connectionId, type);
        }

        // Wait before retry
        if (attempt < this.config.retryAttempts) {
          await this.wait(1000 * attempt); // Exponential backoff
        }
      } catch (error) {
        lastError = error instanceof Error ? error : new Error(String(error));
        logger.warn(`Connection creation attempt ${attempt} failed`, {
          error: lastError.message,
          attempt,
          connectionId,
        });
      }
    }

    throw new Error(
      `Failed to create database connection after ${this.config.retryAttempts} attempts: ${lastError?.message}`
    );
  }

  /**
   * Find an available connection in the pool
   */
  private findAvailableConnection(type: 'anon' | 'service'): PooledConnection | null {
    let result: PooledConnection | null = null;

    this.pool.forEach((connection, connectionId) => {
      if (result) return; // Already found one

      const isCorrectType =
        type === 'service' ? connectionId.startsWith('service_') : connectionId.startsWith('anon_');

      if (isCorrectType && !connection.isActive) {
        result = connection;
      }
    });

    return result;
  }

  /**
   * Release a connection back to the pool
   */
  releaseConnection(client: SupabaseClient): void {
    this.pool.forEach((connection, connectionId) => {
      if (connection.client === client) {
        connection.isActive = false;
        connection.lastUsed = Date.now();

        logger.debug(`Released connection back to pool`, {
          connectionId,
          poolSize: this.pool.size,
        });
      }
    });
  }

  /**
   * Clean up idle connections
   */
  private async cleanupIdleConnections(): Promise<void> {
    const now = Date.now();
    const connectionsToRemove: string[] = [];

    this.pool.forEach((connection, connectionId) => {
      const idleTime = now - connection.lastUsed;

      if (!connection.isActive && idleTime > this.config.idleTimeout) {
        connectionsToRemove.push(connectionId);
      }
    });

    connectionsToRemove.forEach(connectionId => {
      this.pool.delete(connectionId);
      logger.debug(`Removed idle connection`, { connectionId });
    });

    if (connectionsToRemove.length > 0) {
      logger.info(`Cleaned up ${connectionsToRemove.length} idle connections`, {
        remainingConnections: this.pool.size,
      });
    }
  }

  /**
   * Health check for all connections
   */
  private async performHealthCheck(): Promise<void> {
    const unhealthyConnections: string[] = [];

    // Use forEach to iterate over the Map
    await Promise.all(
      Array.from(this.pool.entries()).map(async ([connectionId, connection]) => {
        try {
          // Simple health check - try to access the API
          const { error } = await connection.client
            .from('auth_user_profiles')
            .select('id')
            .limit(1);

          if (error) {
            logger.warn(`Connection health check failed`, {
              connectionId,
              error: error.message,
            });
            unhealthyConnections.push(connectionId);
          }
        } catch (error) {
          logger.warn(`Connection health check error`, {
            connectionId,
            error: error instanceof Error ? error.message : 'Unknown error',
          });
          unhealthyConnections.push(connectionId);
        }
      })
    );

    // Remove unhealthy connections
    unhealthyConnections.forEach(connectionId => {
      this.pool.delete(connectionId);
    });

    if (unhealthyConnections.length > 0) {
      logger.info(`Removed ${unhealthyConnections.length} unhealthy connections`);
    }
  }

  /**
   * Start periodic health checks
   */
  private startHealthCheck(): void {
    this.healthCheckTimer = setInterval(async () => {
      try {
        await this.cleanupIdleConnections();
        await this.performHealthCheck();
      } catch (error) {
        logger.error('Health check failed', {
          error: error instanceof Error ? error.message : 'Unknown error',
        });
      }
    }, this.config.healthCheckInterval);
  }

  /**
   * Get pool statistics
   */
  getStats() {
    const activeConnections = Array.from(this.pool.values()).filter(conn => conn.isActive).length;
    const totalConnections = this.pool.size;

    return {
      activeConnections,
      totalConnections,
      availableConnections: totalConnections - activeConnections,
      maxConnections: this.config.maxConnections,
      utilizationPercentage: Math.round((totalConnections / this.config.maxConnections) * 100),
    };
  }

  /**
   * Graceful shutdown
   */
  async shutdown(): Promise<void> {
    logger.info('Shutting down database connection pool');

    if (this.healthCheckTimer) {
      clearInterval(this.healthCheckTimer);
    }

    this.pool.clear();
    this.isInitialized = false;

    logger.info('Database connection pool shutdown complete');
  }

  /**
   * Utility function for waiting
   */
  private wait(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }
}

// Export singleton instance
export const connectionPool = new DatabaseConnectionPool();

// Export convenience functions
export const getPooledConnection = (type?: 'anon' | 'service') =>
  connectionPool.getConnection(type);

export const releasePooledConnection = (client: SupabaseClient) =>
  connectionPool.releaseConnection(client);

export const getConnectionPoolStats = () => connectionPool.getStats();

