/**
 * Common validation patterns
 */
export const ValidationPatterns = {
  email: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
  philippineMobile: /^(09|\+639)\d{9}$/,
  philippineLandline: /^(\(?\d{2,3}\)?[-\s]?)?\d{3}[-\s]?\d{4}$/,
  numeric: /^\d+$/,
  decimal: /^\d*\.?\d+$/,
  alphanumeric: /^[a-zA-Z0-9]+$/,
  alphanumericWithSpaces: /^[a-zA-Z0-9\s]+$/,
} as const;

/**
 * Common validation functions
 */
export const ValidationHelpers = {
  required: (value: any, fieldName: string) => 
    !value || (typeof value === 'string' && !value.trim()) 
      ? `${fieldName} is required` 
      : '',

  minLength: (value: string, min: number, fieldName: string) =>
    value && value.length < min 
      ? `${fieldName} must be at least ${min} characters` 
      : '',

  maxLength: (value: string, max: number, fieldName: string) =>
    value && value.length > max 
      ? `${fieldName} must not exceed ${max} characters` 
      : '',

  pattern: (value: string, pattern: RegExp, fieldName: string, message?: string) =>
    value && !pattern.test(value) 
      ? message || `${fieldName} format is invalid` 
      : '',

  email: (value: string) =>
    value && !ValidationPatterns.email.test(value) 
      ? 'Please enter a valid email address' 
      : '',

  philippineMobile: (value: string) =>
    value && !ValidationPatterns.philippineMobile.test(value.replace(/\s+/g, '')) 
      ? 'Please enter a valid Philippine mobile number' 
      : '',
} as const;

/**
 * Form Field Logic Library
 *
 * @description Pure business logic for form fields, separated from UI components.
 * Contains field state logic, validation rules, and data transformation utilities.
 */

import type { FormMode } from '@/types/forms';

/**
 * Helper function to determine if a field should be readonly
 */
export const isFieldReadOnly = (
  fieldName: string,
  mode: FormMode,
  readOnlyFields: string[] = []
): boolean => {
  return mode === 'view' || readOnlyFields.includes(fieldName);
};

/**
 * Format date for display in readonly mode
 */
export const formatDateForDisplay = (dateValue: string | undefined): string => {
  if (!dateValue) return '';

  try {
    return new Date(dateValue).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  } catch {
    return dateValue; // Fallback to original value if parsing fails
  }
};

/**
 * Format boolean value for display in readonly mode
 */
export const formatBooleanForDisplay = (value: boolean | undefined): string => {
  return value ? 'Yes' : 'No';
};

/**
 * Get the display value for a select option
 */
export const getSelectDisplayValue = (
  value: string | undefined,
  options: Array<{ value: string; label: string }>
): string => {
  if (!value) return '';

  const selectedOption = options.find(opt => opt.value === value);
  return selectedOption?.label || value;
};

/**
 * Common field validation utilities
 */
export const fieldLogic = {
  /**
   * Validate required fields
   */
  validateRequired: (value: any, fieldName: string): string | null => {
    if (!value || (typeof value === 'string' && value.trim() === '')) {
      return `${fieldName} is required`;
    }
    return null;
  },

  /**
   * Validate number ranges
   */
  validateNumberRange: (
    value: number | undefined,
    min?: number,
    max?: number,
    fieldName: string = 'Field'
  ): string | null => {
    if (value === undefined || value === null) return null;

    if (min !== undefined && value < min) {
      return `${fieldName} must be at least ${min}`;
    }

    if (max !== undefined && value > max) {
      return `${fieldName} must be no more than ${max}`;
    }

    return null;
  },

  /**
   * Validate date ranges
   */
  validateDateRange: (
    value: string | undefined,
    min?: string,
    max?: string,
    fieldName: string = 'Date'
  ): string | null => {
    if (!value) return null;

    const dateValue = new Date(value);

    if (min && dateValue < new Date(min)) {
      return `${fieldName} must be after ${formatDateForDisplay(min)}`;
    }

    if (max && dateValue > new Date(max)) {
      return `${fieldName} must be before ${formatDateForDisplay(max)}`;
    }

    return null;
  },
};

/**
 * Field state management utilities
 */
export const fieldState = {
  /**
   * Get common field props based on mode and configuration
   */
  getFieldProps: (fieldName: string, mode: FormMode, readOnlyFields: string[] = []) => ({
    isReadOnly: isFieldReadOnly(fieldName, mode, readOnlyFields),
    isDisabled: mode === 'view',
    isRequired: mode !== 'view', // Usually required in edit/create modes
  }),

  /**
   * Get field validation state
   */
  getValidationState: (value: any, validators: Array<(value: any) => string | null>) => {
    for (const validator of validators) {
      const error = validator(value);
      if (error) {
        return { isValid: false, error };
      }
    }
    return { isValid: true, error: null };
  },
};

/**
 * Form Handlers Library
 *
 * @description Reusable form handling utilities for consistent form behavior
 * across the application. Provides type-safe handlers and validation helpers.
 */

import React from 'react';

import { logError } from '@/utils/errorUtils';

/**
 * Generic field change handler factory
 * Creates a type-safe handler for updating nested object fields
 */
export function createFieldChangeHandler<T extends Record<string, any>>(
  currentValue: T,
  onChange: (value: T) => void
) {
  return (field: keyof T, fieldValue: any) => {
    onChange({
      ...currentValue,
      [field]: fieldValue,
    });
  };
}

/**
 * Generic select change handler factory
 * Creates a handler specifically for select field option changes
 */
export function createSelectChangeHandler<T extends Record<string, any>>(
  currentValue: T,
  onChange: (value: T) => void
) {
  return (field: keyof T) => (option: any) => {
    onChange({
      ...currentValue,
      [field]: option?.value || '',
    });
  };
}

/**
 * Boolean field change handler factory
 * Creates a handler specifically for checkbox/boolean fields
 */
export function createBooleanChangeHandler<T extends Record<string, any>>(
  currentValue: T,
  onChange: (value: T) => void
) {
  return (field: keyof T) => (checked: boolean) => {
    onChange({
      ...currentValue,
      [field]: checked,
    });
  };
}

/**
 * Generic form submit handler factory
 * Creates a standardized form submission handler with error handling
 */
export function createFormSubmitHandler<T>(
  onSubmit: (data: T) => void | Promise<void>,
  formData: T,
  options: {
    validate?: (data: T) => boolean;
    onError?: (error: Error) => void;
    onSuccess?: () => void;
  } = {}
) {
  return async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      // Run validation if provided
      if (options.validate && !options.validate(formData)) {
        return;
      }

      await onSubmit(formData);
      options.onSuccess?.();
    } catch (error) {
      logError(error as Error, { component: 'FormSubmission' });
      options.onError?.(error as Error);
    }
  };
}

/**
 * Field validation helper
 * Provides common validation patterns for form fields
 */
export const fieldValidators = {
  required: (value: any, fieldName: string) => {
    if (!value || (typeof value === 'string' && value.trim() === '')) {
      return `${fieldName} is required`;
    }
    return null;
  },

  email: (value: string, fieldName: string) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (value && !emailRegex.test(value)) {
      return `${fieldName} must be a valid email address`;
    }
    return null;
  },

  phone: (value: string, fieldName: string) => {
    const phoneRegex = /^(\+\d{1,3}[- ]?)?\d{10}$/;
    if (value && !phoneRegex.test(value.replace(/[^\d+]/g, ''))) {
      return `${fieldName} must be a valid phone number`;
    }
    return null;
  },

  minLength: (value: string, minLength: number, fieldName: string) => {
    if (value && value.length < minLength) {
      return `${fieldName} must be at least ${minLength} characters`;
    }
    return null;
  },

  maxLength: (value: string, maxLength: number, fieldName: string) => {
    if (value && value.length > maxLength) {
      return `${fieldName} must be no more than ${maxLength} characters`;
    }
    return null;
  },
};

/**
 * Utility to build error object from validation results
 */
export function buildErrorsFromValidation<T extends Record<string, any>>(
  data: T,
  validationRules: Record<keyof T, Array<(value: any) => string | null>>
): Partial<Record<keyof T, string>> {
  const errors: Partial<Record<keyof T, string>> = {};

  for (const [field, rules] of Object.entries(validationRules) as Array<
    [keyof T, Array<(value: any) => string | null>]
  >) {
    for (const rule of rules) {
      const error = rule(data[field]);
      if (error) {
        errors[field] = error;
        break; // Stop at first error for this field
      }
    }
  }

  return errors;
}

/**
 * Higher-Order Component Utilities
 * 
 * @fileoverview Consolidated utilities for creating Higher-Order Components (HOCs).
 * Eliminates duplicate WrappedComponent patterns across the codebase and provides
 * standardized component wrapping with proper displayName handling.
 * 
 * @version 1.0.0
 * @since 2025-08-29
 * @author Citizenly Development Team
 */

'use client';

import React from 'react';

// =============================================================================
// TYPES
// =============================================================================

/**
 * Generic wrapper component props
 */
export interface WrapperComponentProps {
  children: React.ReactNode;
}

/**
 * HOC factory function type
 */
export type HOCFactory<WrapperProps, ComponentProps> = (
  Component: React.ComponentType<ComponentProps>,
  ...args: any[]
) => React.ComponentType<ComponentProps>;

/**
 * Component wrapper function type
 */
export type ComponentWrapper<T extends object> = (props: T) => React.ReactElement;

// =============================================================================
// CONSOLIDATED HOC UTILITY
// =============================================================================

/**
 * Creates a standardized wrapped component with proper displayName handling
 * Eliminates duplicate WrappedComponent patterns across all HOCs
 */
export function createWrappedComponent<P extends object>(
  Component: React.ComponentType<P>,
  WrapperComponent: React.ComponentType<WrapperComponentProps>,
  wrapperDisplayName: string,
  wrapperProps?: Record<string, any>
): React.ComponentType<P> {
  const WrappedComponent = (props: P) => (
    <WrapperComponent {...wrapperProps}>
      <Component {...props} />
    </WrapperComponent>
  );

  // Standardized displayName pattern
  WrappedComponent.displayName = `${wrapperDisplayName}(${
    Component.displayName || Component.name || 'Anonymous'
  })`;

  return WrappedComponent;
}

/**
 * Advanced wrapped component creator with render prop support
 */
export function createWrappedComponentWithRender<P extends object>(
  Component: React.ComponentType<P>,
  renderWrapper: (children: React.ReactNode, props: P) => React.ReactElement,
  wrapperDisplayName: string
): React.ComponentType<P> {
  const WrappedComponent = (props: P) => renderWrapper(<Component {...props} />, props);

  WrappedComponent.displayName = `${wrapperDisplayName}(${
    Component.displayName || Component.name || 'Anonymous'
  })`;

  return WrappedComponent;
}

/**
 * Creates a HOC with standardized WrappedComponent pattern
 */
export function createStandardHOC<WrapperProps, ComponentProps extends object>(
  createWrapper: (wrapperProps: WrapperProps) => React.ComponentType<WrapperComponentProps>,
  hocDisplayName: string
) {
  return function HOC(
    Component: React.ComponentType<ComponentProps>,
    wrapperProps: WrapperProps
  ): React.ComponentType<ComponentProps> {
    const WrapperComponent = createWrapper(wrapperProps);
    return createWrappedComponent(Component, WrapperComponent, hocDisplayName, wrapperProps as any);
  };
}

// =============================================================================
// SPECIALIZED HOC CREATORS
// =============================================================================

/**
 * Creates error boundary HOC with consolidated WrappedComponent pattern
 */
export function createErrorBoundaryHOC<T extends object>(
  ErrorBoundaryComponent: React.ComponentType<any>,
  boundaryProps?: any
) {
  return function withErrorBoundary(
    Component: React.ComponentType<T>,
    additionalProps?: any
  ): React.ComponentType<T> {
    return createWrappedComponent(
      Component,
      ErrorBoundaryComponent,
      'withErrorBoundary',
      { ...boundaryProps, ...additionalProps }
    );
  };
}

/**
 * Creates lazy loading HOC with consolidated WrappedComponent pattern
 */
export function createLazyLoadingHOC(
  SuspenseWrapper: React.ComponentType<WrapperComponentProps>,
  suspenseProps?: any
) {
  return function withLazyLoading<T extends object>(
    Component: React.ComponentType<T>,
    loadingMessage?: string
  ): React.ComponentType<T> {
    return createWrappedComponent(
      Component,
      SuspenseWrapper,
      'withLazyLoading',
      { ...suspenseProps, loadingMessage }
    );
  };
}

/**
 * Creates monitoring HOC with consolidated WrappedComponent pattern
 */
export function createMonitoringHOC(
  MonitoringWrapper: React.ComponentType<WrapperComponentProps>,
  monitoringProps?: any
) {
  return function withMonitoring<T extends object>(
    Component: React.ComponentType<T>,
    componentName?: string
  ): React.ComponentType<T> {
    return createWrappedComponent(
      Component,
      MonitoringWrapper,
      'withMonitoring',
      { ...monitoringProps, componentName: componentName || Component.displayName || Component.name }
    );
  };
}

// =============================================================================
// UTILITY FUNCTIONS
// =============================================================================

/**
 * Extracts component name for display purposes
 */
export function getComponentName(Component: React.ComponentType<any>): string {
  return Component.displayName || Component.name || 'Anonymous';
}

/**
 * Creates standardized HOC displayName
 */
export function createHOCDisplayName(hocName: string, Component: React.ComponentType<any>): string {
  return `${hocName}(${getComponentName(Component)})`;
}

/**
 * Validates that a component is a valid React component
 */
export function validateComponent(Component: any): asserts Component is React.ComponentType<any> {
  if (typeof Component !== 'function' && typeof Component !== 'object') {
    throw new Error(`Invalid component: Expected function or object, received ${typeof Component}`);
  }
}

/**
 * Compose multiple HOCs into a single HOC
 */
export function composeHOCs<T extends object>(
  ...hocs: Array<(Component: React.ComponentType<T>) => React.ComponentType<T>>
) {
  return function composedHOC(Component: React.ComponentType<T>): React.ComponentType<T> {
    return hocs.reduceRight((acc, hoc) => hoc(acc), Component);
  };
}

/**
 * Creates a memo-wrapped component with HOC pattern
 */
export function createMemoWrappedComponent<P extends object>(
  Component: React.ComponentType<P>,
  areEqual?: (prevProps: P, nextProps: P) => boolean
): React.ComponentType<P> {
  const MemoizedComponent = React.memo(Component, areEqual);
  MemoizedComponent.displayName = `memo(${getComponentName(Component)})`;
  return MemoizedComponent;
}

// =============================================================================
// LEGACY COMPATIBILITY
// =============================================================================

/**
 * @deprecated Use createWrappedComponent instead
 * Provided for backward compatibility during migration
 */
export function legacyWrappedComponent<P extends object>(
  Component: React.ComponentType<P>,
  wrapper: (children: React.ReactNode) => React.ReactElement,
  displayName: string
): React.ComponentType<P> {
  console.warn(
    'legacyWrappedComponent is deprecated. Use createWrappedComponent or createWrappedComponentWithRender instead.'
  );

  return createWrappedComponentWithRender(
    Component,
    (children) => wrapper(children),
    displayName
  );
}

/**
 * Consolidated Keyboard Event Handling Utilities
 * 
 * @fileoverview Eliminates duplicate handleKeyDown patterns across UI components.
 * Provides standardized keyboard interaction handlers for dropdowns, search,
 * and global shortcuts with proper event handling and accessibility support.
 * 
 * @version 1.0.0
 * @since 2025-08-29
 * @author Citizenly Development Team
 */

'use client';

import { useEffect, useCallback } from 'react';

// =============================================================================
// TYPES
// =============================================================================

/**
 * Base keyboard event handler type
 */
export type KeyboardEventHandler = (event: KeyboardEvent | React.KeyboardEvent) => void;

/**
 * Key combination configuration
 */
export interface KeyCombination {
  key: string;
  ctrlKey?: boolean;
  metaKey?: boolean;
  shiftKey?: boolean;
  altKey?: boolean;
}

/**
 * Dropdown navigation options
 */
export interface DropdownKeyboardOptions {
  isOpen: boolean;
  selectedIndex: number;
  itemCount: number;
  onOpen?: () => void;
  onClose?: () => void;
  onSelect?: (index: number) => void;
  onNavigate?: (index: number) => void;
  preventDefault?: boolean;
}

/**
 * Search keyboard options
 */
export interface SearchKeyboardOptions {
  onSearch?: (query: string) => void;
  onClear?: () => void;
  onEscape?: () => void;
  currentValue?: string;
  preventDefault?: boolean;
}

/**
 * Global shortcut options
 */
export interface GlobalShortcutOptions {
  enabled?: boolean;
  preventDefault?: boolean;
  stopPropagation?: boolean;
}

// =============================================================================
// KEYBOARD UTILITIES
// =============================================================================

/**
 * Checks if a key combination matches the current event
 */
export function matchesKeyCombination(
  event: KeyboardEvent | React.KeyboardEvent,
  combination: KeyCombination
): boolean {
  return (
    event.key === combination.key &&
    !!event.ctrlKey === !!combination.ctrlKey &&
    !!event.metaKey === !!combination.metaKey &&
    !!event.shiftKey === !!combination.shiftKey &&
    !!event.altKey === !!combination.altKey
  );
}

/**
 * Creates a standardized dropdown navigation handler
 * Consolidates arrow key navigation, Enter selection, and Escape closing
 */
export function createDropdownKeyHandler(options: DropdownKeyboardOptions): KeyboardEventHandler {
  return (event: KeyboardEvent | React.KeyboardEvent) => {
    const {
      isOpen,
      selectedIndex,
      itemCount,
      onOpen,
      onClose,
      onSelect,
      onNavigate,
      preventDefault = true
    } = options;

    switch (event.key) {
      case 'ArrowDown':
        if (preventDefault) event.preventDefault();
        if (!isOpen && onOpen) {
          onOpen();
        } else if (isOpen && onNavigate) {
          const nextIndex = selectedIndex < itemCount - 1 ? selectedIndex + 1 : 0;
          onNavigate(nextIndex);
        }
        break;

      case 'ArrowUp':
        if (preventDefault) event.preventDefault();
        if (isOpen && onNavigate) {
          const prevIndex = selectedIndex > 0 ? selectedIndex - 1 : itemCount - 1;
          onNavigate(prevIndex);
        }
        break;

      case 'Enter':
        if (preventDefault) event.preventDefault();
        if (isOpen && onSelect && selectedIndex >= 0) {
          onSelect(selectedIndex);
        } else if (!isOpen && onOpen) {
          onOpen();
        }
        break;

      case 'Escape':
        if (preventDefault) event.preventDefault();
        if (isOpen && onClose) {
          onClose();
        }
        break;

      case ' ': // Space key
        if (preventDefault) event.preventDefault();
        if (!isOpen && onOpen) {
          onOpen();
        }
        break;
    }
  };
}

/**
 * Creates a standardized search input handler
 * Consolidates Enter search, Escape clear/blur, and input validation
 */
export function createSearchKeyHandler(options: SearchKeyboardOptions): KeyboardEventHandler {
  return (event: KeyboardEvent | React.KeyboardEvent) => {
    const {
      onSearch,
      onClear,
      onEscape,
      currentValue = '',
      preventDefault = false
    } = options;

    switch (event.key) {
      case 'Enter':
        if (preventDefault) event.preventDefault();
        if (onSearch && currentValue.trim()) {
          onSearch(currentValue.trim());
        }
        break;

      case 'Escape':
        if (preventDefault) event.preventDefault();
        if (currentValue && onClear) {
          onClear();
        } else if (onEscape) {
          onEscape();
        }
        // Blur the input if it's focused
        if (event.target instanceof HTMLElement) {
          event.target.blur();
        }
        break;
    }
  };
}

/**
 * Creates a global keyboard shortcut handler
 * Consolidates Cmd/Ctrl+Key combinations with proper platform detection
 */
export function createGlobalShortcutHandler(
  combinations: Array<{ combination: KeyCombination; handler: () => void }>,
  options: GlobalShortcutOptions = {}
): KeyboardEventHandler {
  const { enabled = true, preventDefault = true, stopPropagation = true } = options;

  return (event: KeyboardEvent | React.KeyboardEvent) => {
    if (!enabled) return;

    for (const { combination, handler } of combinations) {
      if (matchesKeyCombination(event, combination)) {
        if (preventDefault) event.preventDefault();
        if (stopPropagation) event.stopPropagation();
        handler();
        break;
      }
    }
  };
}

// =============================================================================
// REACT HOOKS
// =============================================================================

/**
 * Hook for global keyboard shortcuts
 * Consolidates document-level keyboard event handling
 */
export function useGlobalKeyboard(
  combinations: Array<{ combination: KeyCombination; handler: () => void }>,
  options: GlobalShortcutOptions = {}
) {
  const { enabled = true } = options;

  const handleKeyDown = useCallback(
    createGlobalShortcutHandler(combinations, options),
    [combinations, options]
  );

  useEffect(() => {
    if (!enabled) return;

    const handleEvent = (event: KeyboardEvent) => {
      handleKeyDown(event);
    };

    document.addEventListener('keydown', handleEvent);
    return () => document.removeEventListener('keydown', handleEvent);
  }, [handleKeyDown, enabled]);
}

/**
 * Hook for command menu keyboard shortcuts
 * Standardized Cmd/Ctrl+K pattern with platform detection
 */
export function useCommandMenuShortcut(
  onToggle: () => void,
  enabled: boolean = true
) {
  const combinations = [
    {
      combination: { key: 'k', metaKey: true } as KeyCombination, // Mac: Cmd+K
      handler: onToggle
    },
    {
      combination: { key: 'k', ctrlKey: true } as KeyCombination, // Windows/Linux: Ctrl+K
      handler: onToggle
    }
  ];

  useGlobalKeyboard(combinations, { enabled });
}

