  'https://www.citizenly.ph',
].filter(Boolean);

const ALLOWED_METHODS = ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS', 'PATCH'];
const ALLOWED_HEADERS = [
  'Content-Type',
  'Authorization',
  'X-Requested-With',
  'Accept',
  'Origin',
  'User-Agent',
];

/**
 * Add CORS headers to response
 */
export function addCorsHeaders(response: NextResponse, origin?: string | null): NextResponse {
  // Check if origin is allowed
  const isAllowedOrigin = !origin || ALLOWED_ORIGINS.includes(origin);

  if (isAllowedOrigin && origin) {
    response.headers.set('Access-Control-Allow-Origin', origin);
  } else if (!origin) {
    response.headers.set('Access-Control-Allow-Origin', '*');
  }

  response.headers.set('Access-Control-Allow-Methods', ALLOWED_METHODS.join(', '));
  response.headers.set('Access-Control-Allow-Headers', ALLOWED_HEADERS.join(', '));
  response.headers.set('Access-Control-Allow-Credentials', 'true');
  response.headers.set('Access-Control-Max-Age', '86400'); // 24 hours

  return response;
}

/**
 * Handle OPTIONS preflight requests
 */
export function handleCorsPreflightOptions(request: NextRequest): NextResponse {
  const origin = request.headers.get('origin');
  const response = new NextResponse(null, { status: 200 });

  return addCorsHeaders(response, origin);
}

/**
 * Higher-order function to add CORS to API routes
 */
export function withCors(handler: (request: NextRequest, context: any) => Promise<NextResponse>) {
  return async (request: NextRequest, context: any): Promise<NextResponse> => {
    // Handle preflight OPTIONS requests
    if (request.method === 'OPTIONS') {
      return handleCorsPreflightOptions(request);
    }

    // Process the actual request
    const response = await handler(request, context);

    // Add CORS headers to the response
    const origin = request.headers.get('origin');
    return addCorsHeaders(response, origin);
  };
}

/**
 * Validate origin for sensitive operations
 */
export function validateCorsOrigin(request: NextRequest): { valid: boolean; origin?: string } {
  const origin = request.headers.get('origin');

  // Allow same-origin requests (no origin header)
  if (!origin) {
    return { valid: true };
  }

  // Check against allowed origins
  const isAllowed = ALLOWED_ORIGINS.includes(origin);

  return {
    valid: isAllowed,
    origin,
  };
}

/**
 * Performance Monitoring Middleware
 * Track API route performance and add optimization headers
 */

import { NextRequest, NextResponse } from 'next/server';

import type { RequestPerformanceMetrics } from '@/types/performance';

// In-memory performance store (use proper metrics service in production)
const performanceStore: RequestPerformanceMetrics[] = [];
const MAX_STORED_METRICS = 1000;

/**
 * Record performance metrics
 */
function recordMetrics(metrics: RequestPerformanceMetrics): void {
  performanceStore.push(metrics);

  // Keep only recent metrics
  if (performanceStore.length > MAX_STORED_METRICS) {
    performanceStore.shift();
  }

  // Log slow requests
  if (metrics.duration > 1000) {
    console.warn('üêå Slow API request detected:', {
      path: metrics.path,
      method: metrics.method,
      duration: `${metrics.duration}ms`,
      statusCode: metrics.statusCode,
    });
  }
}

/**
 * Get performance statistics
 */
export function getPerformanceStats(): {
  totalRequests: number;
  averageResponseTime: number;
  slowRequests: number;
  recentMetrics: RequestPerformanceMetrics[];
} {
  const now = Date.now();
  const recentMetrics = performanceStore.filter(m => now - m.timestamp < 60 * 60 * 1000); // Last hour

  const totalRequests = recentMetrics.length;
  const averageResponseTime =
    totalRequests > 0 ? recentMetrics.reduce((sum, m) => sum + m.duration, 0) / totalRequests : 0;
  const slowRequests = recentMetrics.filter(m => m.duration > 1000).length;

  return {
    totalRequests,
    averageResponseTime: Math.round(averageResponseTime),
    slowRequests,
    recentMetrics: recentMetrics.slice(-10), // Last 10 requests
  };
}

/**
 * Add performance optimization headers
 */
function addPerformanceHeaders(response: NextResponse, duration: number): NextResponse {
  // Server timing API
  response.headers.set('Server-Timing', `total;dur=${duration}`);

  // Cache control for different response types
  const status = response.status;
  if (status === 200) {
    // Cache successful responses briefly
    response.headers.set('Cache-Control', 'public, max-age=300, s-maxage=600');
  } else if (status >= 400 && status < 500) {
    // Don't cache client errors
    response.headers.set('Cache-Control', 'no-cache, no-store, must-revalidate');
  } else if (status >= 500) {
    // Don't cache server errors
    response.headers.set('Cache-Control', 'no-cache, no-store, must-revalidate');
  }

  return response;
}

/**
 * Performance monitoring middleware
 */
export function withPerformanceMonitoring(
  handler: (request: NextRequest, context: any) => Promise<NextResponse>
) {
  return async (request: NextRequest, context: any): Promise<NextResponse> => {
    const startTime = Date.now();
    const path = new URL(request.url).pathname;

    let response: NextResponse;
    let error: Error | null = null;

    try {
      response = await handler(request, context);
    } catch (err) {
      error = err instanceof Error ? err : new Error('Unknown error');
      response = NextResponse.json({ error: 'Internal server error' }, { status: 500 });
    }

    const duration = Date.now() - startTime;

    // Record metrics
    const metrics: RequestPerformanceMetrics = {
      path,
      method: request.method,
      duration,
      timestamp: startTime,
      statusCode: response.status,
      userAgent: request.headers.get('user-agent') || undefined,
      ip:
        request.headers.get('x-forwarded-for')?.split(',')[0] ||
        request.headers.get('x-real-ip') ||
        undefined,
    };

    recordMetrics(metrics);

    // Add performance headers
    response = addPerformanceHeaders(response, duration);

    // Add request ID for debugging
    const requestId = `req_${Date.now()}_${Math.random().toString(36).substring(2, 8)}`;
    response.headers.set('X-Request-ID', requestId);

    // Log if there was an error
    if (error) {
      console.error('‚ùå API route error:', {
        requestId,
        path,
        method: request.method,
        duration: `${duration}ms`,
        error: error.message,
      });
    }

    return response;
  };
}

/**
 * Database query performance tracking
 */
export class QueryPerformanceTracker {
  private static queries: Array<{
    query: string;
    duration: number;
    timestamp: number;
  }> = [];

  static startTracking(query: string): () => void {
    const startTime = Date.now();

    return () => {
      const duration = Date.now() - startTime;
      this.queries.push({
        query,
        duration,
        timestamp: startTime,
      });

      // Keep only recent queries
      if (this.queries.length > 500) {
        this.queries.shift();
      }

      // Log slow queries
      if (duration > 500) {
        console.warn('üêå Slow database query:', {
          query: query.substring(0, 100) + (query.length > 100 ? '...' : ''),
          duration: `${duration}ms`,
        });
      }
    };
  }

  static getStats() {
    const now = Date.now();
    const recentQueries = this.queries.filter(q => now - q.timestamp < 60 * 60 * 1000);

    return {
      totalQueries: recentQueries.length,
      averageQueryTime:
        recentQueries.length > 0
          ? Math.round(recentQueries.reduce((sum, q) => sum + q.duration, 0) / recentQueries.length)
          : 0,
      slowQueries: recentQueries.filter(q => q.duration > 500).length,
    };
  }
}

/**
 * Memory usage monitoring
 */
export function getMemoryUsage(): {
  used: number;
  total: number;
  percentage: number;
} {
  const memUsage = process.memoryUsage();
  const used = Math.round(memUsage.heapUsed / 1024 / 1024); // MB
  const total = Math.round(memUsage.heapTotal / 1024 / 1024); // MB

  return {
    used,
    total,
    percentage: Math.round((used / total) * 100),
  };
}

/**
 * Rate Limiting Middleware
 * Protect API routes from abuse and DoS attacks
 */

import { NextRequest, NextResponse } from 'next/server';

import { createSecureErrorResponse } from '@/lib/security/api-security';

// In-memory store for rate limiting (use Redis in production)
const rateLimitStore = new Map<string, { count: number; resetTime: number }>();

export interface RateLimitConfig {
  windowMs: number; // Time window in milliseconds
  maxRequests: number; // Maximum requests per window
  skipSuccessfulRequests?: boolean; // Don't count successful requests
  skipFailedRequests?: boolean; // Don't count failed requests
}

// Default rate limit configurations by endpoint type
export const rateLimitConfigs = {
  // Public endpoints
  search: { windowMs: 60 * 1000, maxRequests: 100 }, // 100 requests/minute
  lookup: { windowMs: 60 * 1000, maxRequests: 200 }, // 200 requests/minute

  // Authentication endpoints
  auth: { windowMs: 15 * 60 * 1000, maxRequests: 10 }, // 10 requests/15 minutes

  // CRUD operations
  create: { windowMs: 60 * 1000, maxRequests: 10 }, // 10 creates/minute
  update: { windowMs: 60 * 1000, maxRequests: 20 }, // 20 updates/minute
  delete: { windowMs: 60 * 1000, maxRequests: 5 }, // 5 deletes/minute

  // Admin operations
  admin: { windowMs: 60 * 1000, maxRequests: 50 }, // 50 requests/minute

  // Webhook endpoints
  webhook: { windowMs: 60 * 1000, maxRequests: 100 }, // 100 webhooks/minute

  // Default fallback
  default: { windowMs: 60 * 1000, maxRequests: 60 }, // 60 requests/minute
} as const;

/**
 * Get client identifier for rate limiting
 */
function getClientId(request: NextRequest): string {
  // Try to get authenticated user ID first
  const authHeader = request.headers.get('Authorization');
  if (authHeader?.startsWith('Bearer ')) {
    // Use token hash as identifier (would need to decode in real implementation)
    const token = authHeader.split(' ')[1];
    return `user:${token.substring(0, 16)}`; // First 16 chars as identifier
  }

  // Fall back to IP address
  const forwarded = request.headers.get('x-forwarded-for');
  const ip = forwarded ? forwarded.split(',')[0] : request.headers.get('x-real-ip') || 'unknown';
  return `ip:${ip}`;
}

/**
 * Check rate limit for client
 */
export function checkRateLimit(
  clientId: string,
  config: RateLimitConfig
): { allowed: boolean; remaining: number; resetTime: number } {
  const now = Date.now();
  const key = clientId;
  const existing = rateLimitStore.get(key);

  // If no existing record or window has expired, create new
  if (!existing || now > existing.resetTime) {
    const resetTime = now + config.windowMs;
    rateLimitStore.set(key, { count: 1, resetTime });
    return {
      allowed: true,
      remaining: config.maxRequests - 1,
      resetTime,
    };
  }

  // Check if limit exceeded
  if (existing.count >= config.maxRequests) {
    return {
      allowed: false,
      remaining: 0,
      resetTime: existing.resetTime,
    };
  }

  // Increment count
  existing.count++;
  rateLimitStore.set(key, existing);

  return {
    allowed: true,
    remaining: config.maxRequests - existing.count,
    resetTime: existing.resetTime,
  };
}

/**
 * Clean up expired rate limit entries
 */
export function cleanupRateLimitStore(): void {
  const now = Date.now();
  const entries = Array.from(rateLimitStore.entries());
  for (const [key, value] of entries) {
    if (now > value.resetTime) {
      rateLimitStore.delete(key);
    }
  }
}

// Clean up every 5 minutes
if (typeof setInterval !== 'undefined') {
  setInterval(cleanupRateLimitStore, 5 * 60 * 1000);
}

/**
 * Rate limiting middleware
 */
export function withRateLimit(
  handler: (request: NextRequest, context: any) => Promise<NextResponse>,
  configKey: keyof typeof rateLimitConfigs = 'default'
) {
  return async (request: NextRequest, context: any): Promise<NextResponse> => {
    const config = rateLimitConfigs[configKey];
    const clientId = getClientId(request);
    const rateLimitResult = checkRateLimit(clientId, config);

    // Create response with rate limit headers
    let response: NextResponse;

    if (!rateLimitResult.allowed) {
      response = createSecureErrorResponse('Rate limit exceeded. Too many requests.', 429);
    } else {
      response = await handler(request, context);
    }

    // Add rate limit headers
    response.headers.set('X-RateLimit-Limit', config.maxRequests.toString());
    response.headers.set('X-RateLimit-Remaining', rateLimitResult.remaining.toString());
    response.headers.set(
      'X-RateLimit-Reset',
      Math.ceil(rateLimitResult.resetTime / 1000).toString()
    );
    response.headers.set(
      'Retry-After',
      Math.ceil((rateLimitResult.resetTime - Date.now()) / 1000).toString()
    );

    return response;
  };
}

/**
 * Get rate limit status for monitoring
 */
export function getRateLimitStats(): {
  totalClients: number;
  entries: Array<{ clientId: string; count: number; resetTime: number }>;
} {
  const entries: Array<{ clientId: string; count: number; resetTime: number }> = [];
  rateLimitStore.forEach((data, clientId) => {
    entries.push({
      clientId,
      count: data.count,
      resetTime: data.resetTime,
    });
  });

  return {
    totalClients: rateLimitStore.size,
    entries,
  };
}

