/**
 * Error Boundary Provider Class Component
 */
export class ErrorBoundaryProvider extends Component<
  ErrorBoundaryProviderProps,
  ErrorBoundaryState
> {
  constructor(props: ErrorBoundaryProviderProps) {
    super(props);
    this.state = {
      hasError: false,
      error: undefined,
      errorId: undefined,
    };
  }

  static getDerivedStateFromError(error: Error): Partial<ErrorBoundaryState> {
    const errorId = `ERR_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    return {
      hasError: true,
      error,
      errorId,
    };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    const { onError, enableReporting = true } = this.props;
    const { errorId } = this.state;

    // Log error locally
    clientLogger.error(`React Error Boundary caught error: ${error.message}`, {
      component: 'ErrorBoundary',
      action: 'error_caught',
      error,
      data: {
        errorId,
        componentStack: errorInfo.componentStack,
        errorBoundary: true,
      },
    });

    // Add breadcrumb for debugging
    addSentryBreadcrumb(`Error boundary caught: ${error.message}`, 'error_boundary', 'error');

    // Report to monitoring service
    if (enableReporting) {
      captureError(error, {
        errorId,
        componentStack: errorInfo.componentStack,
        errorBoundary: true,
        timestamp: new Date().toISOString(),
      });
    }

    // Call custom error handler
    if (onError) {
      try {
        onError(error, errorInfo);
      } catch (handlerError) {
        clientLogger.error('Error in error boundary handler', {
          action: 'error_boundary_handler_error',
          data: {
            error: handlerError instanceof Error ? handlerError.message : String(handlerError),
            originalError: error.message,
          },
        });
      }
    }
  }

  resetError = () => {
    this.setState({
      hasError: false,
      error: undefined,
      errorId: undefined,
    });

    clientLogger.info('Error boundary reset', {
      component: 'ErrorBoundary',
      action: 'error_reset',
    });

    addSentryBreadcrumb('Error boundary reset', 'error_boundary', 'info');
  };

  render() {
    const { hasError, error, errorId } = this.state;
    const { children, fallback: FallbackComponent = DefaultErrorFallback } = this.props;

    if (hasError && error && errorId) {
      return <FallbackComponent error={error} errorId={errorId} resetError={this.resetError} />;
    }

    return children;
  }
}

/**
 * Higher-order component for wrapping components with error boundary
 */
export function withErrorBoundary<P extends object>(
  Component: React.ComponentType<P>,
  options?: Partial<ErrorBoundaryProviderProps>
) {
  return createWrappedComponent(
    Component,
    ErrorBoundaryProvider,
    'withErrorBoundary',
    options
  );
}

export default ErrorBoundaryProvider;

/**
 * Error Boundary React Hook
 * Enhanced error handling and reporting for React components
 */

import { useCallback, useState } from 'react';

import { clientLogger } from '@/lib/logging/client-logger';
import type { ErrorBoundaryState } from '@/types/errors';
import type { UseErrorBoundaryOptions, ErrorBoundaryReturn } from '@/types/hooks';

import { captureError, addSentryBreadcrumb } from '../sentry-config';

// Extended error boundary state for hook usage
interface HookErrorBoundaryState extends ErrorBoundaryState {
  errorInfo?: any;
}

/**
 * Hook for enhanced error handling and reporting
 */
export const useErrorBoundary = (options: UseErrorBoundaryOptions): ErrorBoundaryReturn => {
  const { componentName, onError, enableReporting = true } = options;

  const [errorState, setErrorState] = useState<HookErrorBoundaryState>({
    hasError: false,
    error: undefined,
    errorInfo: undefined,
  });

  const resetError = useCallback(() => {
    setErrorState({
      hasError: false,
      error: undefined,
      errorInfo: undefined,
    });

    clientLogger.info(`Error boundary reset in ${componentName}`, {
      component: componentName,
      action: 'error_reset',
    });

    addSentryBreadcrumb(`Error boundary reset in ${componentName}`, 'error_boundary', 'info');
  }, [componentName]);

  const handleError = useCallback(
    (error: Error, context?: Record<string, any>) => {
      const errorInfo = {
        component: componentName,
        timestamp: new Date().toISOString(),
        ...context,
      };

      setErrorState({
        hasError: true,
        error,
        errorInfo,
      });

      // Log error locally
      clientLogger.error(`Error in ${componentName}: ${error.message}`, {
        component: componentName,
        action: 'component_error',
        error,
        data: context,
      });

      // Add breadcrumb for debugging
      addSentryBreadcrumb(`Error in ${componentName}: ${error.message}`, 'error_boundary', 'error');

      // Report to monitoring service
      if (enableReporting) {
        captureError(error, {
          component: componentName,
          errorBoundary: true,
          ...context,
        });
      }

      // Call custom error handler
      if (onError) {
        try {
          onError(error, errorInfo);
        } catch (handlerError) {
          clientLogger.error('Error in custom error handler', {
            action: 'custom_error_handler_error',
            data: {
              component: componentName,
              error: handlerError instanceof Error ? handlerError.message : String(handlerError),
              originalError: error.message,
            },
          });
        }
      }
    },
    [componentName, onError, enableReporting]
  );

  // Wrapper for async operations with automatic error handling
  const wrapAsync = useCallback(
    async <T>(
      operation: () => Promise<T>,
      operationName: string = 'async_operation'
    ): Promise<T> => {
      try {
        addSentryBreadcrumb(
          `Starting ${operationName} in ${componentName}`,
          'async_operation',
          'info'
        );

        const result = await operation();

        addSentryBreadcrumb(
          `Completed ${operationName} in ${componentName}`,
          'async_operation',
          'info'
        );

        return result;
      } catch (error) {
        const errorObj = error instanceof Error ? error : new Error(String(error));

        handleError(errorObj, {
          operation: operationName,
          type: 'async_error',
        });

        throw errorObj;
      }
    },
    [componentName, handleError]
  );

  return {
    error: errorState.error || null,
    hasError: errorState.hasError,
    resetError,
    captureError: handleError,
    wrapAsync,
  };
};

/**
 * Performance Tracking React Hook
 * Track component performance and lifecycle metrics
 */

import { useEffect, useRef, useCallback } from 'react';

import { clientLogger } from '@/lib/logging/client-logger';
import type { UsePerformanceTrackingOptions, PerformanceTrackingReturn } from '@/types/hooks';

import { performanceMonitor } from '../performance';

/**
 * Hook for tracking component performance metrics
 */
export const usePerformanceTracking = (
  options: UsePerformanceTrackingOptions
): PerformanceTrackingReturn => {
  const {
    componentName,
    trackRenders = true,
    trackMounts = true,
    trackUpdates = false,
    metadata = {},
  } = options;

  const renderCountRef = useRef(0);
  const mountTimeRef = useRef<number>(0);
  const lastRenderTimeRef = useRef<number>(0);

  // Track component mount
  useEffect(() => {
    if (trackMounts) {
      mountTimeRef.current = performance.now();
      const metricName = `component_mount_${componentName}`;

      performanceMonitor.startMetric(metricName, {
        component: componentName,
        type: 'mount',
        ...metadata,
      });

      clientLogger.component(componentName, 'mounted', { timestamp: mountTimeRef.current });

      return () => {
        // Track unmount
        const unmountTime = performance.now();
        const lifecycleDuration = unmountTime - mountTimeRef.current;

        performanceMonitor.endMetric(metricName);

        clientLogger.component(componentName, 'unmounted', {
          lifecycleDuration: Math.round(lifecycleDuration * 100) / 100,
          renderCount: renderCountRef.current,
        });
      };
    }
  }, [componentName, trackMounts, metadata]);

  // Track renders
  useEffect(() => {
    if (trackRenders) {
      renderCountRef.current += 1;
      const currentTime = performance.now();

      if (renderCountRef.current > 1 && trackUpdates) {
        const timeSinceLastRender = currentTime - lastRenderTimeRef.current;

        clientLogger.component(componentName, 're-rendered', {
          renderCount: renderCountRef.current,
          timeSinceLastRender: Math.round(timeSinceLastRender * 100) / 100,
        });

        // Warn about frequent re-renders
        if (timeSinceLastRender < 50) {
          // Less than 50ms between renders
          clientLogger.warn(`Frequent re-renders detected in ${componentName}`, {
            action: 'frequent_rerenders',
            data: {
              component: componentName,
              renderCount: renderCountRef.current,
              timeSinceLastRender,
            },
          });
        }
      }

      lastRenderTimeRef.current = currentTime;
    }
  });

  // Track custom operations
  const trackOperation = useCallback(
    (name: string, operationMetadata?: Record<string, any>) => {
      const metricName = `${componentName}_${name}`;
      performanceMonitor.startMetric(metricName, {
        component: componentName,
        operation: name,
        ...metadata,
        ...operationMetadata,
      });

      return () => performanceMonitor.endMetric(metricName);
    },
    [componentName, metadata]
  );

  // Track async operations
  const trackAsyncOperation = useCallback(
    async <T>(
      name: string,
      operation: () => Promise<T>,
      operationMetadata?: Record<string, any>
    ): Promise<T> => {
      const metricName = `${componentName}_async_${name}`;

      return performanceMonitor.measureFunction(metricName, operation, {
        component: componentName,
        operation: name,
        type: 'async',
        ...metadata,
        ...operationMetadata,
      });
    },
    [componentName, metadata]
  );

  return {
    trackOperation,
    trackAsyncOperation,
  };
};

/**
 * Performance Monitoring Utilities
 * Client-side performance tracking and optimization helpers
 */

import { clientLogger } from '@/lib/logging/client-logger';
import type { GenericPerformanceMetrics } from '@/types/performance';

import { startSentryTransaction } from './sentry-config';

class PerformanceMonitor {
  private metrics: Map<string, GenericPerformanceMetrics> = new Map();
  private isProduction = process.env.NODE_ENV === 'production';

  /**
   * Start tracking a performance metric
   */
  startMetric(name: string, metadata?: Record<string, any>): () => void {
    const startTime = performance.now();

    this.metrics.set(name, {
      name,
      startTime,
      metadata,
    });

    // Start Sentry transaction for critical operations
    if (this.isProduction && this.isCriticalOperation(name)) {
      const transaction = startSentryTransaction(name, 'performance');
      if (transaction && metadata) {
        // Add context to transaction
        Object.entries(metadata).forEach(([key, value]) => {
          transaction.setTag(key, String(value));
        });
      }
    }

    clientLogger.debug(`Performance tracking started: ${name}`, {
      action: 'performance_start',
      data: { name, startTime, metadata },
    });

    // Return a function to end the metric
    return () => {
      this.endMetric(name);
    };
  }

  /**
   * End tracking and log performance metric
   */
  endMetric(name: string, additionalData?: Record<string, any>): number | null {
    const metric = this.metrics.get(name);
    if (!metric) {
      clientLogger.warn(`Performance metric not found: ${name}`);
      return null;
    }

    const endTime = performance.now();
    const duration = endTime - metric.startTime;

    const completedMetric: GenericPerformanceMetrics = {
      ...metric,
      endTime,
      duration,
    };

    this.metrics.set(name, completedMetric);

    // Log performance data
    const logData = {
      name,
      duration: Math.round(duration * 100) / 100, // Round to 2 decimal places
      metadata: metric.metadata,
      ...additionalData,
    };

    // Determine if this is a performance concern
    const thresholds = this.getPerformanceThresholds();
    const threshold = thresholds[name] || thresholds.default;

    if (duration > threshold.error) {
      clientLogger.error(`Performance issue detected: ${name} took ${duration.toFixed(2)}ms`, {
        action: 'performance_issue',
        data: logData,
      });
    } else if (duration > threshold.warn) {
      clientLogger.warn(`Performance warning: ${name} took ${duration.toFixed(2)}ms`, {
        action: 'performance_warning',
        data: logData,
      });
    } else {
      clientLogger.info(`Performance metric: ${name} completed in ${duration.toFixed(2)}ms`, {
        action: 'performance_complete',
        data: logData,
      });
    }

    return duration;
  }

  /**
   * Get all recorded metrics
   */
  getMetrics(): GenericPerformanceMetrics[] {
    return Array.from(this.metrics.values());
  }

  /**
   * Clear all metrics
   */
  clearMetrics(): void {
    this.metrics.clear();
  }

  /**
   * Measure a function execution time
   */
  async measureFunction<T>(
    name: string,
    fn: () => T | Promise<T>,
    metadata?: Record<string, any>
  ): Promise<T> {
    this.startMetric(name, metadata);

    try {
      const result = await fn();
      this.endMetric(name, { success: true });
      return result;
    } catch (error) {
      this.endMetric(name, {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      });
      throw error;
    }
  }

  /**
   * Track component render time
   */
  trackComponentRender(componentName: string, props?: any): () => void {
    const metricName = `component_render_${componentName}`;
    this.startMetric(metricName, {
      component: componentName,
      propsCount: props ? Object.keys(props).length : 0,
    });

    return () => this.endMetric(metricName);
  }

  /**
   * Track API call performance
   */
  trackApiCall(method: string, endpoint: string, metadata?: Record<string, any>): () => void {
    const metricName = `api_${method.toLowerCase()}_${endpoint.replace(/[^a-zA-Z0-9]/g, '_')}`;
    this.startMetric(metricName, {
      method,
      endpoint,
      ...metadata,
    });

    return () => this.endMetric(metricName);
  }

  /**
   * Track data loading operations
   */
  trackDataLoad(resource: string, metadata?: Record<string, any>): () => void {
    const metricName = `data_load_${resource}`;
    this.startMetric(metricName, {
      resource,
      ...metadata,
    });

    return () => this.endMetric(metricName);
  }

  /**
   * Get Web Vitals if available
   */
  getWebVitals(): Promise<any> {
    return new Promise(resolve => {
      if (typeof window !== 'undefined' && 'web-vitals' in window) {
        // If web-vitals library is loaded
        resolve((window as any)['web-vitals']);
      } else {
        // Fallback to basic performance API
        const navigation = performance.getEntriesByType(
          'navigation'
        )[0] as PerformanceNavigationTiming;
        resolve({
          fcp: navigation.responseStart - navigation.fetchStart,
          lcp: navigation.loadEventEnd - navigation.fetchStart,
          cls: 0, // Would need proper measurement
          fid: 0, // Would need proper measurement
          ttfb: navigation.responseStart - navigation.requestStart,
        });
      }
    });
  }

  /**
   * Performance thresholds for different operations
   */
  private getPerformanceThresholds(): Record<string, { warn: number; error: number }> {
    return {
      // API calls
      api_get: { warn: 1000, error: 3000 },
      api_post: { warn: 2000, error: 5000 },
      api_put: { warn: 2000, error: 5000 },
      api_delete: { warn: 1000, error: 3000 },

      // Component renders
      component_render: { warn: 100, error: 500 },

      // Data operations
      data_load: { warn: 500, error: 2000 },
      data_transform: { warn: 200, error: 1000 },

      // Default thresholds
      default: { warn: 1000, error: 3000 },
    };
  }

  /**
   * Check if operation is critical for Sentry tracking
   */
  private isCriticalOperation(name: string): boolean {
    const criticalOperations = ['api_', 'data_load_', 'auth_', 'payment_', 'search_'];

    return criticalOperations.some(op => name.startsWith(op));
  }
}

// Export singleton instance
export const performanceMonitor = new PerformanceMonitor();

// Convenience functions
export const startPerformanceMetric = (name: string, metadata?: Record<string, any>) =>
  performanceMonitor.startMetric(name, metadata);

export const endPerformanceMetric = (name: string, additionalData?: Record<string, any>) =>
  performanceMonitor.endMetric(name, additionalData);

export const measureFunction = <T>(
  name: string,
  fn: () => T | Promise<T>,
  metadata?: Record<string, any>
) => performanceMonitor.measureFunction(name, fn, metadata);

export const trackComponentRender = (componentName: string, props?: any) =>
  performanceMonitor.trackComponentRender(componentName, props);

export const trackApiCall = (method: string, endpoint: string, metadata?: Record<string, any>) =>
  performanceMonitor.trackApiCall(method, endpoint, metadata);

export const trackDataLoad = (resource: string, metadata?: Record<string, any>) =>
  performanceMonitor.trackDataLoad(resource, metadata);

