'use client';

/**
 * Performance Optimization Utilities
 * Enhanced performance helpers for React components and data operations
 */

import { useCallback, useMemo, useRef, useEffect, useState } from 'react';

import { logInfo } from '@/lib/logging/client-logger';

/**
 * Enhanced debounce hook with cleanup
 */
export function useDebounce<T extends (...args: unknown[]) => unknown>(
  callback: T,
  delay: number
): T {
  const debounceRef = useRef<NodeJS.Timeout | undefined>(undefined);

  const debouncedCallback = useCallback(
    (...args: Parameters<T>) => {
      clearTimeout(debounceRef.current);
      debounceRef.current = setTimeout(() => callback(...args), delay);
    },
    [callback, delay]
  ) as T;

  useEffect(() => {
    return () => {
      clearTimeout(debounceRef.current);
    };
  }, []);

  return debouncedCallback;
}

/**
 * Memoized search filter for large datasets
 */
export function useSearchFilter<T extends Record<string, unknown>>(
  data: T[],
  searchTerm: string,
  searchFields: (keyof T)[]
): T[] {
  return useMemo(() => {
    if (!searchTerm.trim()) return data;

    const lowercaseSearch = searchTerm.toLowerCase();
    return data.filter(item =>
      searchFields.some(field => {
        const value = item[field];
        return value && typeof value === 'string' && value.toLowerCase().includes(lowercaseSearch);
      })
    );
  }, [data, searchTerm, searchFields]);
}

/**
 * Memoized pagination for large datasets
 */
export function usePagination<T>(data: T[], pageSize: number = 20) {
  const [currentPage, setCurrentPage] = useState(1);

  const paginatedData = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    return data.slice(startIndex, endIndex);
  }, [data, currentPage, pageSize]);

  const totalPages = useMemo(() => Math.ceil(data.length / pageSize), [data.length, pageSize]);

  const hasNextPage = currentPage < totalPages;
  const hasPrevPage = currentPage > 1;

  const goToPage = useCallback(
    (page: number) => {
      setCurrentPage(Math.max(1, Math.min(page, totalPages)));
    },
    [totalPages]
  );

  const nextPage = useCallback(() => {
    if (hasNextPage) setCurrentPage(prev => prev + 1);
  }, [hasNextPage]);

  const prevPage = useCallback(() => {
    if (hasPrevPage) setCurrentPage(prev => prev - 1);
  }, [hasPrevPage]);

  return {
    currentPage,
    totalPages,
    pageSize,
    data: paginatedData,
    totalItems: data.length,
    hasNextPage,
    hasPrevPage,
    goToPage,
    nextPage,
    prevPage,
  };
}

/**
 * Virtual scrolling hook for very large lists
 */
export function useVirtualScroll({
  itemCount,
  itemHeight,
  containerHeight,
  overscan = 5,
}: {
  itemCount: number;
  itemHeight: number;
  containerHeight: number;
  overscan?: number;
}) {
  const [scrollTop, setScrollTop] = useState(0);

  const visibleRange = useMemo(() => {
    const startIndex = Math.floor(scrollTop / itemHeight);
    const endIndex = Math.min(
      itemCount - 1,
      Math.floor((scrollTop + containerHeight) / itemHeight)
    );

    return {
      start: Math.max(0, startIndex - overscan),
      end: Math.min(itemCount - 1, endIndex + overscan),
    };
  }, [scrollTop, itemHeight, containerHeight, itemCount, overscan]);

  const totalHeight = itemCount * itemHeight;
  const offsetY = visibleRange.start * itemHeight;

  return {
    visibleRange,
    totalHeight,
    offsetY,
    setScrollTop,
  };
}

/**
 * Performance monitoring hook
 */
export function usePerformanceMonitor(componentName: string) {
  const renderStartTime = useRef<number | undefined>(undefined);
  const renderCount = useRef(0);

  useEffect(() => {
    renderStartTime.current = performance.now();
    renderCount.current += 1;
  });

  useEffect(() => {
    const endTime = performance.now();
    const renderTime = endTime - (renderStartTime.current || endTime);

    if (renderTime > 16) {
      // > 16ms is concerning for 60fps
      logInfo(`Performance warning: ${componentName} render took ${renderTime.toFixed(2)}ms`, {
        component: componentName,
        action: 'performance_warning',
        data: { renderTime, renderCount: renderCount.current },
      });
    }
  });

  return {
    renderCount: renderCount.current,
  };
}

/**
 * Memoized sort function for tables
 */
export function useSortedData<T extends Record<string, unknown>>(
  data: T[],
  sortKey: keyof T | null,
  sortDirection: 'asc' | 'desc' = 'asc'
): T[] {
  return useMemo(() => {
    if (!sortKey) return data;

    return [...data].sort((a, b) => {
      const aValue = a[sortKey];
      const bValue = b[sortKey];

      // Handle null/undefined values
      if (aValue === null || aValue === undefined) return 1;
      if (bValue === null || bValue === undefined) return -1;

      // Handle different data types
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        const result = aValue.localeCompare(bValue);
        return sortDirection === 'asc' ? result : -result;
      }

      if (typeof aValue === 'number' && typeof bValue === 'number') {
        const result = aValue - bValue;
        return sortDirection === 'asc' ? result : -result;
      }

      // Handle dates
      if (aValue instanceof Date && bValue instanceof Date) {
        const result = aValue.getTime() - bValue.getTime();
        return sortDirection === 'asc' ? result : -result;
      }

      // Fallback to string comparison
      const result = String(aValue).localeCompare(String(bValue));
      return sortDirection === 'asc' ? result : -result;
    });
  }, [data, sortKey, sortDirection]);
}

/**
 * Optimized data fetching with caching
 */
export function useOptimizedFetch<T>(
  fetchFn: () => Promise<T>,
  deps: unknown[] = [],
  cacheKey?: string
) {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  const cache = useRef<Map<string, { data: T; timestamp: number }>>(new Map());
  const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes

  const fetch = useCallback(async () => {
    const key = cacheKey || JSON.stringify(deps);
    const cached = cache.current.get(key);

    // Return cached data if valid
    if (cached && Date.now() - cached.timestamp < CACHE_DURATION) {
      setData(cached.data);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const result = await fetchFn();
      setData(result);

      // Cache the result
      cache.current.set(key, { data: result, timestamp: Date.now() });
    } catch (err) {
      setError(err instanceof Error ? err : new Error('Unknown error'));
    } finally {
      setLoading(false);
    }
  }, [fetchFn, cacheKey, ...deps]);

  useEffect(() => {
    fetch();
  }, [fetch]);

  return { data, loading, error, refetch: fetch };
}

/**
 * Intersection Observer hook for lazy loading
 */
export function useIntersectionObserver(
  options: IntersectionObserverInit = {}
): [React.RefCallback<Element>, boolean] {
  const [isIntersecting, setIsIntersecting] = useState(false);
  const [node, setNode] = useState<Element | null>(null);

  const observer = useMemo(() => {
    return new IntersectionObserver(([entry]) => {
      setIsIntersecting(entry.isIntersecting);
    }, options);
  }, [options]);

  useEffect(() => {
    if (node) {
      observer.observe(node);
      return () => observer.unobserve(node);
    }
  }, [observer, node]);

  const ref = useCallback((node: Element | null) => {
    setNode(node);
  }, []);

  return [ref, isIntersecting];
}

import { logger } from '@/lib/logging';
import type {
  PerformanceMetric,
  ComponentPerformanceData,
  LayoutShiftEntry,
} from '@/types/utilities';

/**
 * Performance monitoring utilities
 * Tracks key metrics and provides insights into app performance
 */

// Performance entry interfaces

// ComponentProps is a generic type for component properties
type ComponentProps = Record<string, unknown>;

class PerformanceMonitor {
  private metrics: PerformanceMetric[] = [];
  private componentMetrics: Map<string, ComponentPerformanceData> = new Map();
  private observers: PerformanceObserver[] = [];
  private isEnabled: boolean = process.env.NODE_ENV === 'development';

  constructor() {
    if (typeof window !== 'undefined' && this.isEnabled) {
      this.initializeObservers();
    }
  }

  private initializeObservers() {
    try {
      // Observe navigation timing
      if ('PerformanceObserver' in window) {
        const navigationObserver = new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            this.recordMetric('navigation', entry.duration, {
              type: entry.entryType,
              name: entry.name,
            });
          }
        });
        navigationObserver.observe({ entryTypes: ['navigation'] });
        this.observers.push(navigationObserver);

        // Observe resource loading
        const resourceObserver = new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            if (entry.duration > 100) {
              // Only log slow resources
              this.recordMetric('resource', entry.duration, {
                name: entry.name,
                type: entry.entryType,
                size: (entry as PerformanceResourceTiming).transferSize,
              });
            }
          }
        });
        resourceObserver.observe({ entryTypes: ['resource'] });
        this.observers.push(resourceObserver);

        // Observe largest contentful paint
        const lcpObserver = new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            const lcpEntry = entry as PerformanceEntry & {
              element?: { tagName: string };
              url?: string;
            };
            this.recordMetric('lcp', entry.startTime, {
              element: lcpEntry.element?.tagName,
              url: lcpEntry.url,
            });
          }
        });
        lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });
        this.observers.push(lcpObserver);

        // Observe cumulative layout shift
        const clsObserver = new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            const layoutShiftEntry = entry as LayoutShiftEntry;
            if (!layoutShiftEntry.hadRecentInput) {
              this.recordMetric('cls', layoutShiftEntry.value, {
                sources: layoutShiftEntry.sources?.map(s => s.node?.tagName),
              });
            }
          }
        });
        clsObserver.observe({ entryTypes: ['layout-shift'] });
        this.observers.push(clsObserver);
      }
    } catch (error) {
      logger.error('Failed to initialize performance observers', { error });
    }
  }

  /**
   * Record a performance metric
   */
  recordMetric(name: string, value: number, metadata?: Record<string, unknown>) {
    if (!this.isEnabled) return;

    const metric: PerformanceMetric = {
      name,
      value,
      timestamp: Date.now(),
      metadata,
    };

    this.metrics.push(metric);

    // Keep only last 1000 metrics
    if (this.metrics.length > 1000) {
      this.metrics = this.metrics.slice(-1000);
    }

    // Log significant metrics
    if (this.isSignificantMetric(name, value)) {
      logger.info(`Performance: ${name}`, {
        value: `${value.toFixed(2)}ms`,
        ...metadata,
      });
    }
  }

  private isSignificantMetric(name: string, value: number): boolean {
    const thresholds: Record<string, number> = {
      navigation: 3000,
      resource: 500,
      lcp: 2500,
      cls: 0.1,
      render: 16, // One frame at 60fps
      api: 1000,
    };

    return value > (thresholds[name] || 100);
  }

  /**
   * Start timing an operation
   */
  startTiming(name: string): () => void {
    if (!this.isEnabled) return () => {};

    const start = performance.now();

    return () => {
      const duration = performance.now() - start;
      this.recordMetric(name, duration);
    };
  }

  /**
   * Time an async operation
   */
  async timeAsync<T>(name: string, operation: () => Promise<T>): Promise<T> {
    if (!this.isEnabled) return operation();

    const endTiming = this.startTiming(name);
    try {
      const result = await operation();
      endTiming();
      return result;
    } catch (error) {
      endTiming();
      throw error;
    }
  }

  /**
   * Record component render performance
   */
  recordComponentRender(
    componentName: string,
    renderTime: number,
    propsSize?: number,
    isRerender = false
  ) {
    if (!this.isEnabled) return;

    const existing = this.componentMetrics.get(componentName);
    const data: ComponentPerformanceData = {
      componentName,
      renderTime,
      propsSize,
      rerenderCount: existing ? existing.rerenderCount + (isRerender ? 1 : 0) : 0,
      timestamp: Date.now(),
    };

    this.componentMetrics.set(componentName, data);

    if (renderTime > 16) {
      // Slower than 60fps
      logger.warn(`Slow component render: ${componentName}`, {
        renderTime: `${renderTime.toFixed(2)}ms`,
        rerenderCount: data.rerenderCount,
        propsSize,
      });
    }
  }

  /**
   * Get performance summary
   */
  getPerformanceSummary() {
    if (!this.isEnabled) return null;

    const recent = this.metrics.filter(m => Date.now() - m.timestamp < 60000); // Last minute

    const summary = {
      totalMetrics: this.metrics.length,
      recentMetrics: recent.length,
      averages: {} as Record<string, number>,
      components: Array.from(this.componentMetrics.entries()).map(([name, data]) => ({
        name,
        averageRenderTime: data.renderTime,
        rerenderCount: data.rerenderCount,
      })),
      slowestOperations: this.metrics
        .sort((a, b) => b.value - a.value)
        .slice(0, 10)
        .map(m => ({ name: m.name, value: m.value })),
    };

    // Calculate averages by metric type
    const grouped = recent.reduce(
      (acc, metric) => {
        if (!acc[metric.name]) acc[metric.name] = [];
        acc[metric.name].push(metric.value);
        return acc;
      },
      {} as Record<string, number[]>
    );

    Object.entries(grouped).forEach(([name, values]) => {
      summary.averages[name] = values.reduce((sum, val) => sum + val, 0) / values.length;
    });

    return summary;
  }

  /**
   * Get Web Vitals
   */
  getWebVitals() {
    if (!this.isEnabled || typeof window === 'undefined') return null;

    const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;

    return {
      // First Contentful Paint
      fcp: this.metrics.find(
        m => m.name === 'paint' && m.metadata?.name === 'first-contentful-paint'
      )?.value,

      // Largest Contentful Paint
      lcp: this.metrics.filter(m => m.name === 'lcp').sort((a, b) => b.timestamp - a.timestamp)[0]
        ?.value,

      // Cumulative Layout Shift
      cls: this.metrics.filter(m => m.name === 'cls').reduce((sum, m) => sum + m.value, 0),

      // Time to First Byte
      ttfb: navigation ? navigation.responseStart - navigation.requestStart : null,

      // DOM Content Loaded
      domContentLoaded: navigation
        ? navigation.domContentLoadedEventEnd - navigation.fetchStart
        : null,

      // Load Complete
      loadComplete: navigation ? navigation.loadEventEnd - navigation.fetchStart : null,
    };
  }

  /**
   * Export metrics for analysis
   */
  exportMetrics() {
    if (!this.isEnabled) return null;

    return {
      metrics: this.metrics,
      components: Object.fromEntries(this.componentMetrics),
      webVitals: this.getWebVitals(),
      summary: this.getPerformanceSummary(),
      timestamp: Date.now(),
    };
  }

  /**
   * Clear all metrics
   */
  clearMetrics() {
    this.metrics = [];
    this.componentMetrics.clear();
  }

  /**
   * Cleanup observers
   */
  destroy() {
    this.observers.forEach(observer => observer.disconnect());
    this.observers = [];
  }
}

// Global performance monitor instance
export const performanceMonitor = new PerformanceMonitor();

// Decorator for timing functions
export function timed(name?: string) {
  return function (target: object, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;
    const timerName = name || `${(target as any).constructor.name}.${propertyKey}`;

    descriptor.value = function (...args: unknown[]) {
      const endTiming = performanceMonitor.startTiming(timerName);
      try {
        const result = originalMethod.apply(this, args);

        // Handle async methods
        if (result instanceof Promise) {
          return result.finally(() => endTiming());
        }

        endTiming();
        return result;
      } catch (error) {
        endTiming();
        throw error;
      }
    };

    return descriptor;
  };
}

// React hook for component performance tracking
export function usePerformanceTracking(componentName: string) {
  const renderStart = performance.now();

  return {
    onRenderComplete: (propsSize?: number, isRerender = false) => {
      const renderTime = performance.now() - renderStart;
      performanceMonitor.recordComponentRender(componentName, renderTime, propsSize, isRerender);
    },
  };
}

// Utility to measure props size
export function measurePropsSize(props: ComponentProps): number {
  try {
    return JSON.stringify(props).length;
  } catch {
    return 0;
  }
}

// Report performance to external service (placeholder)
export async function reportPerformanceMetrics() {
  if (process.env.NODE_ENV === 'production') {
    const metrics = performanceMonitor.exportMetrics();

    // In a real app, send to analytics service
    logger.info('Performance report', { metrics });

    // Clear after reporting
    performanceMonitor.clearMetrics();
  }
}

/**
 * PWA Performance Monitoring
 * Tracks PWA-specific metrics and user interactions
 */

import type { PWAMetrics, PWAPerformanceEntry } from '@/types/utilities';

class PWAPerformanceMonitor {
  private metrics: PWAMetrics;
  private entries: PWAPerformanceEntry[] = [];
  private maxEntries = 1000;

  constructor() {
    this.metrics = {
      installPromptShown: 0,
      installAccepted: 0,
      installDismissed: 0,
      offlineUsage: 0,
      cacheHits: 0,
      cacheMisses: 0,
      syncOperations: 0,
      syncFailures: 0,
    };

    // Only initialize in browser environment
    if (typeof window !== 'undefined') {
      this.loadStoredMetrics();
      this.setupEventListeners();
    }
  }

  /**
   * Load metrics from localStorage
   */
  private loadStoredMetrics(): void {
    if (typeof window === 'undefined') return;

    try {
      const stored = localStorage.getItem('pwa-metrics');
      if (stored) {
        this.metrics = { ...this.metrics, ...JSON.parse(stored) };
      }
    } catch (error) {
      console.warn('Failed to load stored PWA metrics:', error);
    }
  }

  /**
   * Save metrics to localStorage
   */
  private saveMetrics(): void {
    if (typeof window === 'undefined') return;

    try {
      localStorage.setItem('pwa-metrics', JSON.stringify(this.metrics));
    } catch (error) {
      console.warn('Failed to save PWA metrics:', error);
    }
  }

  /**
   * Setup performance monitoring event listeners
   */
  private setupEventListeners(): void {
    if (typeof window === 'undefined') return;

    // Service Worker messages
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.addEventListener('message', event => {
        this.handleServiceWorkerMessage(event);
      });
    }

    // Page visibility for offline usage tracking
    if (typeof document !== 'undefined') {
      document.addEventListener('visibilitychange', () => {
        if (document.visibilityState === 'visible' && !navigator.onLine) {
          this.trackOfflineUsage();
        }
      });
    }

    // Performance observer for navigation timing
    if ('PerformanceObserver' in window) {
      try {
        const observer = new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            if (entry.entryType === 'navigation') {
              this.trackNavigationPerformance(entry as PerformanceNavigationTiming);
            }
          }
        });
        observer.observe({ entryTypes: ['navigation'] });
      } catch (error) {
        console.warn('Performance Observer not supported:', error);
      }
    }
  }

  /**
   * Handle service worker messages
   */
  private handleServiceWorkerMessage(event: MessageEvent): void {
    const { type, data } = event.data || {};

    switch (type) {
      case 'CACHE_HIT':
        this.trackCacheHit(data);
        break;
      case 'CACHE_MISS':
        this.trackCacheMiss(data);
        break;
      case 'SYNC_START':
        this.trackSyncStart(data);
        break;
      case 'SYNC_SUCCESS':
        this.trackSyncSuccess(data);
        break;
      case 'SYNC_FAILURE':
        this.trackSyncFailure(data);
        break;
    }
  }

  /**
   * Track install prompt shown
   */
  trackInstallPromptShown(): void {
    this.metrics.installPromptShown++;
    this.addEntry({
      name: 'install_prompt_shown',
      type: 'install',
      timestamp: Date.now(),
    });
    this.saveMetrics();
  }

  /**
   * Track install accepted
   */
  trackInstallAccepted(): void {
    this.metrics.installAccepted++;
    this.addEntry({
      name: 'install_accepted',
      type: 'install',
      timestamp: Date.now(),
      success: true,
    });
    this.saveMetrics();

    // Track conversion rate
    const conversionRate = this.metrics.installAccepted / this.metrics.installPromptShown;
    console.log(`PWA Install Conversion Rate: ${(conversionRate * 100).toFixed(2)}%`);
  }

  /**
   * Track install dismissed
   */
  trackInstallDismissed(): void {
    this.metrics.installDismissed++;
    this.addEntry({
      name: 'install_dismissed',
      type: 'install',
      timestamp: Date.now(),
      success: false,
    });
    this.saveMetrics();
  }

  /**
   * Track offline usage
   */
  trackOfflineUsage(): void {
    this.metrics.offlineUsage++;
    this.addEntry({
      name: 'offline_usage',
      type: 'offline',
      timestamp: Date.now(),
    });
    this.saveMetrics();
  }

  /**
   * Track cache hit
   */
  trackCacheHit(resource?: string): void {
    this.metrics.cacheHits++;
    this.addEntry({
      name: 'cache_hit',
      type: 'cache',
      timestamp: Date.now(),
      success: true,
      details: { resource },
    });
    this.saveMetrics();
  }

  /**
   * Track cache miss
   */
  trackCacheMiss(resource?: string): void {
    this.metrics.cacheMisses++;
    this.addEntry({
      name: 'cache_miss',
      type: 'cache',
      timestamp: Date.now(),
      success: false,
      details: { resource },
    });
    this.saveMetrics();
  }

  /**
   * Track sync operation start
   */
  trackSyncStart(details?: any): void {
    this.addEntry({
      name: 'sync_start',
      type: 'sync',
      timestamp: Date.now(),
      details,
    });
  }

  /**
   * Track successful sync
   */
  trackSyncSuccess(details?: any): void {
    this.metrics.syncOperations++;
    this.addEntry({
      name: 'sync_success',
      type: 'sync',
      timestamp: Date.now(),
      success: true,
      details,
    });
    this.saveMetrics();
  }

  /**
   * Track failed sync
   */
  trackSyncFailure(details?: any): void {
    this.metrics.syncFailures++;
    this.addEntry({
      name: 'sync_failure',
      type: 'sync',
      timestamp: Date.now(),
      success: false,
      details,
    });
    this.saveMetrics();
  }

  /**
   * Track navigation performance
   */
  private trackNavigationPerformance(entry: PerformanceNavigationTiming): void {
    const loadTime = entry.loadEventEnd - entry.startTime;
    const domContentLoaded = entry.domContentLoadedEventEnd - entry.startTime;

    this.addEntry({
      name: 'navigation_timing',
      type: 'navigation',
      timestamp: Date.now(),
      duration: loadTime,
      details: {
        loadTime,
        domContentLoaded,
        dnsLookup: entry.domainLookupEnd - entry.domainLookupStart,
        tcpConnect: entry.connectEnd - entry.connectStart,
        serverResponse: entry.responseEnd - entry.requestStart,
        domProcessing: entry.domComplete - entry.responseEnd,
      },
    });
  }

  /**
   * Add performance entry
   */
  private addEntry(entry: PWAPerformanceEntry): void {
    this.entries.unshift(entry);

    // Keep only the most recent entries
    if (this.entries.length > this.maxEntries) {
      this.entries = this.entries.slice(0, this.maxEntries);
    }
  }

  /**
   * Get current metrics
   */
  getMetrics(): PWAMetrics {
    return { ...this.metrics };
  }

  /**
   * Get performance entries
   */
  getEntries(type?: string, limit?: number): PWAPerformanceEntry[] {
    let filtered = this.entries;

    if (type) {
      filtered = this.entries.filter(entry => entry.type === type);
    }

    if (limit) {
      filtered = filtered.slice(0, limit);
    }

    return filtered;
  }

  /**
   * Get cache hit rate
   */
  getCacheHitRate(): number {
    const total = this.metrics.cacheHits + this.metrics.cacheMisses;
    return total > 0 ? this.metrics.cacheHits / total : 0;
  }

  /**
   * Get sync success rate
   */
  getSyncSuccessRate(): number {
    const total = this.metrics.syncOperations + this.metrics.syncFailures;
    return total > 0 ? this.metrics.syncOperations / total : 1;
  }

  /**
   * Get install conversion rate
   */
  getInstallConversionRate(): number {
    return this.metrics.installPromptShown > 0
      ? this.metrics.installAccepted / this.metrics.installPromptShown
      : 0;
  }

  /**
   * Generate performance report
   */
  generateReport(): {
    metrics: PWAMetrics;
    rates: {
      cacheHitRate: number;
      syncSuccessRate: number;
      installConversionRate: number;
    };
    recentEntries: PWAPerformanceEntry[];
  } {
    return {
      metrics: this.getMetrics(),
      rates: {
        cacheHitRate: this.getCacheHitRate(),
        syncSuccessRate: this.getSyncSuccessRate(),
        installConversionRate: this.getInstallConversionRate(),
      },
      recentEntries: this.getEntries(undefined, 50),
    };
  }

  /**
   * Reset all metrics
   */
  reset(): void {
    this.metrics = {
      installPromptShown: 0,
      installAccepted: 0,
      installDismissed: 0,
      offlineUsage: 0,
      cacheHits: 0,
      cacheMisses: 0,
      syncOperations: 0,
      syncFailures: 0,
    };
    this.entries = [];
    this.saveMetrics();
  }

  /**
   * Export metrics data
   */
  exportData(): string {
    return JSON.stringify(
      {
        metrics: this.metrics,
        entries: this.entries,
        timestamp: Date.now(),
      },
      null,
      2
    );
  }
}

// Lazy singleton initialization
let _pwaPerformance: PWAPerformanceMonitor | null = null;

export const pwaPerformance = {
  get instance(): PWAPerformanceMonitor | null {
    if (typeof window === 'undefined') return null;
    if (!_pwaPerformance) {
      _pwaPerformance = new PWAPerformanceMonitor();
    }
    return _pwaPerformance;
  },
};

// Utility functions for external use
export const trackPWAEvents = {
  installPromptShown: () => pwaPerformance.instance?.trackInstallPromptShown(),
  installAccepted: () => pwaPerformance.instance?.trackInstallAccepted(),
  installDismissed: () => pwaPerformance.instance?.trackInstallDismissed(),
  offlineUsage: () => pwaPerformance.instance?.trackOfflineUsage(),
  cacheHit: (resource?: string) => pwaPerformance.instance?.trackCacheHit(resource),
  cacheMiss: (resource?: string) => pwaPerformance.instance?.trackCacheMiss(resource),
  syncSuccess: (details?: any) => pwaPerformance.instance?.trackSyncSuccess(details),
  syncFailure: (details?: any) => pwaPerformance.instance?.trackSyncFailure(details),
};

