/**
 * Progressive data filling story component
 */
export function ProgressiveStory<T extends FormData>({
  steps,
  className = '',
  children
}: {
  steps: ProgressiveStep<T>[];
  className?: string;
  children: (props: {
    currentStep: number;
    currentData: T;
    stepLabel: string;
    nextStep: () => void;
    prevStep: () => void;
    goToStep: (index: number) => void;
    isFirstStep: boolean;
    isLastStep: boolean;
  }) => React.ReactNode;
}) {
  const [currentStep, setCurrentStep] = useState(0);

  const nextStep = useCallback(() => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  }, [currentStep, steps.length]);

  const prevStep = useCallback(() => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  }, [currentStep]);

  const goToStep = useCallback((index: number) => {
    if (index >= 0 && index < steps.length) {
      setCurrentStep(index);
    }
  }, [steps.length]);

  const currentStepData = steps[currentStep];

  return (
    <div className={`progressive-story ${className}`}>
      {children({
        currentStep,
        currentData: currentStepData?.data,
        stepLabel: currentStepData?.label || '',
        nextStep,
        prevStep,
        goToStep,
        isFirstStep: currentStep === 0,
        isLastStep: currentStep === steps.length - 1
      })}
    </div>
  );
}

/**
 * Progressive story navigation controls
 */
export function ProgressiveStoryControls({
  steps,
  currentStep,
  onStepChange
}: {
  steps: ProgressiveStep<any>[];
  currentStep: number;
  onStepChange: (index: number) => void;
}) {
  return (
    <div className="space-y-4">
      <div className="flex flex-wrap gap-2">
        {steps.map((step, index) => (
          <button
            key={index}
            onClick={() => onStepChange(index)}
            className={`rounded px-3 py-1 text-sm ${
              currentStep === index
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {index}: {step.label}
          </button>
        ))}
      </div>

      <div className="text-sm text-gray-600">
        Step {currentStep + 1} of {steps.length}: {steps[currentStep]?.label}
        {steps[currentStep]?.description && (
          <div className="mt-1 text-xs text-gray-500">
            {steps[currentStep].description}
          </div>
        )}
      </div>
    </div>
  );
}

