/**
 * Field-level validation schemas and utilities
 */

import { z } from 'zod';

import {
  isValidPhilSysCardNumber,
  isValidMobileNumber,
  isValidBirthdate,
} from '@/lib/business-rules';

// Basic field validators
export const phoneNumberValidator = z.string().refine(isValidMobileNumber, {
  message: 'Please enter a valid Philippine mobile number (09XXXXXXXXX or +639XXXXXXXXX)',
});

export const philsysCardValidator = z.string().refine(isValidPhilSysCardNumber, {
  message: 'PhilSys card number must be 12 digits',
});

export const birthdateValidator = z.string().refine(isValidBirthdate, {
  message: 'Please enter a valid birthdate',
});

export const emailValidator = z.string().email({
  message: 'Please enter a valid email address',
});

// Name validators
export const nameValidator = z
  .string()
  .min(1, 'This field is required')
  .max(100, 'Name cannot exceed 100 characters')
  .regex(
    /^[a-zA-Z\s\-'\.]*$/,
    'Name can only contain letters, spaces, hyphens, apostrophes, and periods'
  );

export const requiredNameValidator = nameValidator.min(1, 'This field is required');

// Numeric validators
export const positiveNumberValidator = z
  .number()
  .positive('Must be a positive number')
  .max(999999, 'Value is too large');

export const heightValidator = z
  .number()
  .min(50, 'Height must be at least 50 cm')
  .max(300, 'Height cannot exceed 300 cm');

export const weightValidator = z
  .number()
  .min(1, 'Weight must be at least 1 kg')
  .max(500, 'Weight cannot exceed 500 kg');

export const ageValidator = z
  .number()
  .min(0, 'Age cannot be negative')
  .max(150, 'Age cannot exceed 150 years');

// Selection validators
export const sexValidator = z.enum(['male', 'female'], {
  message: 'Please select a valid sex',
});

export const requiredSelectValidator = z.string().min(1, 'Please make a selection');

// Field-specific validation functions
export const validateField = (
  fieldName: string,
  value: any
): { isValid: boolean; error?: string } => {
  try {
    switch (fieldName) {
      case 'firstName':
      case 'lastName':
        requiredNameValidator.parse(value);
        break;

      case 'middleName':
      case 'extensionName':
        if (value) nameValidator.parse(value);
        break;

      case 'sex':
        sexValidator.parse(value);
        break;

      case 'birthdate':
        birthdateValidator.parse(value);
        break;

      case 'email':
        if (value) emailValidator.parse(value);
        break;

      case 'mobileNumber':
        if (value) phoneNumberValidator.parse(value);
        break;

      case 'philsysCardNumber':
        if (value) philsysCardValidator.parse(value);
        break;

      case 'height':
        if (value) heightValidator.parse(Number(value));
        break;

      case 'weight':
        if (value) weightValidator.parse(Number(value));
        break;

      case 'civilStatus':
      case 'citizenship':
      case 'educationAttainment':
      case 'employmentStatus':
        requiredSelectValidator.parse(value);
        break;

      default:
        // For fields without specific validation, just check if required
        if (value === null || value === undefined || value === '') {
          return { isValid: false, error: 'This field is required' };
        }
        break;
    }

    return { isValid: true };
  } catch (error) {
    if (error instanceof z.ZodError) {
      return { isValid: false, error: error.issues[0]?.message || 'Invalid value' };
    }
    return { isValid: false, error: 'Validation error' };
  }
};

// Batch validation for multiple fields
export const validateFields = (
  data: Record<string, any>,
  fieldNames: string[]
): Record<string, string> => {
  const errors: Record<string, string> = {};

  fieldNames.forEach(fieldName => {
    const value = data[fieldName];
    const result = validateField(fieldName, value);

    if (!result.isValid && result.error) {
      errors[fieldName] = result.error;
    }
  });

  return errors;
};

// Required fields by form section
export const REQUIRED_FIELDS = {
  basicInformation: ['firstName', 'lastName', 'sex', 'civilStatus'],
  birthInformation: ['birthdate'],
  contactInformation: ['mobileNumber'],
  physicalDetails: [],
  sectoralInformation: [],
  migrationInformation: [],
} as const;

// Get required fields for a section
export const getRequiredFieldsForSection = (section: keyof typeof REQUIRED_FIELDS): string[] => {
  return [...(REQUIRED_FIELDS[section] || [])];
};

// Validate a complete form section
export const validateFormSection = (
  data: Record<string, any>,
  section: keyof typeof REQUIRED_FIELDS
): { isValid: boolean; errors: Record<string, string> } => {
  const requiredFields = getRequiredFieldsForSection(section);
  const errors = validateFields(data, requiredFields);

  return {
    isValid: Object.keys(errors).length === 0,
    errors,
  };
};

// Real-time validation with debouncing
export const createDebouncedValidator = (
  fieldName: string,
  onValidation: (isValid: boolean, error?: string) => void,
  delay = 500
) => {
  let timeoutId: NodeJS.Timeout;

  return (value: any) => {
    clearTimeout(timeoutId);

    timeoutId = setTimeout(() => {
      const result = validateField(fieldName, value);
      onValidation(result.isValid, result.error);
    }, delay);
  };
};

/**
 * Field Validators
 * Reusable field-level validation functions
 */

import type {
  FieldValidator,
  FieldValidationResult,
  ValidationContext,
} from '../../types/validation';

// Note: FieldValidationResult interface is still exported from types for backward compatibility
// but these validators now return string | null per the updated FieldValidator type signature

/**
 * Required field validator
 */
export const validateRequired: FieldValidator = (value, fieldName, formData) => {
  if (value === null || value === undefined || value === '') {
    return 'This field is required';
  }

  if (typeof value === 'string' && value.trim() === '') {
    return 'This field cannot be empty';
  }

  return null;
};

/**
 * Email validator
 */
export const validateEmail: FieldValidator<string> = (email, fieldName, formData) => {
  if (!email) {
    return null; // Allow empty for optional fields
  }

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const isValid = emailRegex.test(email);

  return isValid ? null : 'Please enter a valid email address';
};

/**
 * Philippine mobile number validator
 */
export const validatePhilippineMobile: FieldValidator<string> = (mobile, fieldName, formData) => {
  if (!mobile) {
    return null; // Allow empty for optional fields
  }

  const cleaned = mobile.replace(/\D/g, '');
  const isValid = /^09\d{9}$/.test(cleaned) || /^639\d{9}$/.test(cleaned);

  return isValid
    ? null
    : 'Please enter a valid Philippine mobile number (09XXXXXXXXX or +639XXXXXXXXX)';
};

/**
 * PhilSys number validator
 */
export const validatePhilSysNumber: FieldValidator<string> = (philsys, fieldName, formData) => {
  if (!philsys) {
    return null; // Allow empty for optional fields
  }

  // CORRECTED: PhilSys is 12 digits, not 16 - aligned with database schema VARCHAR(20)
  const pattern = /^\d{4}-\d{4}-\d{4}$/;
  const isValid = pattern.test(philsys);

  return isValid ? null : 'PhilSys number must be in format XXXX-XXXX-XXXX';
};

/**
 * Name validator (letters, spaces, hyphens, apostrophes, periods)
 */
export const validateName: FieldValidator<string> = (name, fieldName, formData) => {
  if (!name) {
    return null; // Allow empty for optional fields
  }

  const namePattern = /^[a-zA-Z\s\-'\.]*$/;
  const isValid = namePattern.test(name) && name.trim().length > 0;

  if (!isValid) {
    return 'Name can only contain letters, spaces, hyphens, apostrophes, and periods';
  }

  if (name.length > 100) {
    return 'Name cannot exceed 100 characters';
  }

  return null;
};

/**
 * Age validator
 */
export const validateAge: FieldValidator<number> = (age, fieldName, formData) => {
  if (age === null || age === undefined) {
    return null; // Allow empty for optional fields
  }

  if (typeof age !== 'number' || !Number.isInteger(age)) {
    return 'Age must be a whole number';
  }

  if (age < 0) {
    return 'Age cannot be negative';
  }

  if (age > 150) {
    return 'Age cannot exceed 150 years';
  }

  return null;
};

/**
 * Length validator factory
 */
export function validateLength(
  minLength?: number,
  maxLength?: number,
  customMessage?: string
): FieldValidator<string> {
  return (value, fieldName, formData) => {
    if (!value) {
      return null; // Allow empty for optional fields
    }

    const length = value.length;

    if (minLength !== undefined && length < minLength) {
      return customMessage || `Minimum length is ${minLength} characters`;
    }

    if (maxLength !== undefined && length > maxLength) {
      return customMessage || `Maximum length is ${maxLength} characters`;
    }

    return null;
  };
}

/**
 * Pattern validator factory
 */
export function validatePattern(pattern: RegExp, errorMessage: string): FieldValidator<string> {
  return (value, fieldName, formData) => {
    if (!value) {
      return null; // Allow empty for optional fields
    }

    const isValid = pattern.test(value);
    return isValid ? null : errorMessage;
  };
}

/**
 * Number range validator factory
 */
export function validateRange(
  min?: number,
  max?: number,
  customMessage?: string
): FieldValidator<number> {
  return (value, fieldName, formData) => {
    if (value === null || value === undefined) {
      return null; // Allow empty for optional fields
    }

    if (typeof value !== 'number') {
      return 'Value must be a number';
    }

    if (min !== undefined && value < min) {
      return customMessage || `Value must be at least ${min}`;
    }

    if (max !== undefined && value > max) {
      return customMessage || `Value cannot exceed ${max}`;
    }

    return null;
  };
}

/**
 * Date validator
 */
export const validateDate: FieldValidator<string | Date> = (date, fieldName, formData) => {
  if (!date) {
    return null; // Allow empty for optional fields
  }

  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isNaN(dateObj.getTime())) {
    return 'Please enter a valid date';
  }

  // Check if date is not in the future for birthdates (simplified - can be enhanced later)
  if (dateObj > new Date()) {
    return 'Date cannot be in the future';
  }

  return null;
};

/**
 * URL validator
 */
export const validateUrl: FieldValidator<string> = (url, fieldName, formData) => {
  if (!url) {
    return null; // Allow empty for optional fields
  }

  try {
    new URL(url);
    return null;
  } catch {
    return 'Please enter a valid URL';
  }
};

/**
 * Compose multiple validators
 */
export function composeValidators<T>(...validators: FieldValidator<T>[]): FieldValidator<T> {
  return async (value, fieldName, formData) => {
    for (const validator of validators) {
      const result = await validator(value, fieldName, formData);
      if (result !== null) {
        return result;
      }
    }
    return null;
  };
}

/**
 * Conditional validator
 */
export function conditionalValidator<T>(
  condition: (value: T, fieldName?: string, formData?: Record<string, any>) => boolean,
  validator: FieldValidator<T>
): FieldValidator<T> {
  return (value, fieldName, formData) => {
    if (!condition(value, fieldName, formData)) {
      return null; // Skip validation if condition not met
    }
    return validator(value, fieldName, formData);
  };
}

/**
 * Async validator wrapper
 */
export function createAsyncValidator<T>(
  asyncFn: (value: T, fieldName?: string, formData?: Record<string, any>) => Promise<boolean>,
  errorMessage: string
): FieldValidator<T> {
  return async (value, fieldName, formData) => {
    if (!value) {
      return null; // Allow empty for optional fields
    }

    try {
      const isValid = await asyncFn(value, fieldName, formData);
      return isValid ? null : errorMessage;
    } catch (error) {
      return 'Validation failed';
    }
  };
}

/**
 * Form Validators
 * Comprehensive form-level validation functions
 */

import type {
  FormValidator,
  ValidationResult,
  ValidationContext,
  FieldValidator,
  FieldValidationResult,
} from './types';

/**
 * Create a validation result
 */
function createValidationResult(
  isValid: boolean,
  errors: Record<string, string> = {},
  warnings: Record<string, string> = {},
  data?: any
): ValidationResult {
  // Convert errors from Record<string, string> to ValidationError[]
  const validationErrors = Object.entries(errors).map(([field, message]) => ({
    field,
    message,
  }));

  // Convert warnings from Record<string, string> to string[]
  const warningList = Object.values(warnings);

  return {
    isValid,
    errors: validationErrors,
    warnings: warningList.length > 0 ? warningList : undefined,
    data,
  };
}

/**
 * Create a form validator from field validators
 */
export function createFormValidator<T extends Record<string, any>>(
  fieldValidators: Record<keyof T, FieldValidator | FieldValidator[]>,
  crossFieldValidators?: ((data: T, context?: ValidationContext) => ValidationResult)[]
): FormValidator<T> {
  return async (data, context) => {
    const errors: Record<string, string> = {};
    const warnings: Record<string, string> = {};
    const sanitizedData: Record<string, any> = { ...data };

    // Validate individual fields
    for (const [fieldName, validators] of Object.entries(fieldValidators)) {
      const fieldValue = data[fieldName as keyof T];
      const validatorArray = Array.isArray(validators) ? validators : [validators];

      for (const validator of validatorArray) {
        try {
          const result = await validator(fieldValue, fieldName, data);

          if (result !== null) {
            errors[fieldName] = result;
            break; // Stop at first error for this field
          }
        } catch (error) {
          errors[fieldName] = 'Validation error occurred';
          console.error(`Validation error for field ${fieldName}:`, error);
        }
      }
    }

    // Run cross-field validations if no individual field errors
    if (Object.keys(errors).length === 0 && crossFieldValidators) {
      for (const crossValidator of crossFieldValidators) {
        try {
          const crossResult = crossValidator(data, context);

          if (!crossResult.isValid) {
            // Convert ValidationError[] back to Record<string, string> for internal processing
            const errorRecord = crossResult.errors.reduce(
              (acc, err) => {
                acc[err.field] = err.message;
                return acc;
              },
              {} as Record<string, string>
            );
            Object.assign(errors, errorRecord);
          }

          if (crossResult.warnings && crossResult.warnings.length > 0) {
            // For cross-field warnings, we'll add them to the form level
            warnings['_form'] = crossResult.warnings.join('; ');
          }
        } catch (error) {
          errors['_form'] = 'Cross-field validation error occurred';
          console.error('Cross-field validation error:', error);
        }
      }
    }

    const isValid = Object.keys(errors).length === 0;

    return createValidationResult(isValid, errors, warnings, isValid ? sanitizedData : undefined);
  };
}

/**
 * Create a field validator with common patterns
 */
export function createFieldValidator(rules: {
  required?: boolean;
  type?: 'string' | 'number' | 'email' | 'phone' | 'date' | 'url';
  minLength?: number;
  maxLength?: number;
  min?: number;
  max?: number;
  pattern?: RegExp;
  custom?: FieldValidator;
  customMessage?: string;
}): FieldValidator {
  return async (value, fieldName, formData) => {
    // Required validation
    if (rules.required) {
      if (value === null || value === undefined || value === '') {
        return rules.customMessage || 'This field is required';
      }
    }

    // Skip other validations if value is empty and not required
    if (!rules.required && (value === null || value === undefined || value === '')) {
      return null;
    }

    // Type validations
    switch (rules.type) {
      case 'string':
        if (typeof value !== 'string') {
          return 'Must be a string';
        }
        break;

      case 'number':
        const numValue = Number(value);
        if (isNaN(numValue)) {
          return 'Must be a valid number';
        }
        value = numValue;
        break;

      case 'email':
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailPattern.test(String(value))) {
          return 'Must be a valid email address';
        }
        break;

      case 'phone':
        const phonePattern = /^(\+63|63|09)\d{9}$/;
        const cleanedPhone = String(value).replace(/\D/g, '');
        if (!phonePattern.test(cleanedPhone)) {
          return 'Must be a valid Philippine phone number';
        }
        break;

      case 'date':
        const dateValue = new Date(String(value));
        if (isNaN(dateValue.getTime())) {
          return 'Must be a valid date';
        }
        break;

      case 'url':
        try {
          new URL(String(value));
        } catch {
          return 'Must be a valid URL';
        }
        break;
    }

    // Length validations for strings
    if (typeof value === 'string') {
      if (rules.minLength !== undefined && value.length < rules.minLength) {
        return `Must be at least ${rules.minLength} characters long`;
      }

      if (rules.maxLength !== undefined && value.length > rules.maxLength) {
        return `Must not exceed ${rules.maxLength} characters`;
      }
    }

    // Range validations for numbers
    if (typeof value === 'number') {
      if (rules.min !== undefined && value < rules.min) {
        return `Must be at least ${rules.min}`;
      }

      if (rules.max !== undefined && value > rules.max) {
        return `Must not exceed ${rules.max}`;
      }
    }

    // Pattern validation
    if (rules.pattern && !rules.pattern.test(String(value))) {
      return rules.customMessage || 'Invalid format';
    }

    // Custom validation
    if (rules.custom) {
      return await rules.custom(value, fieldName, formData);
    }

    return null;
  };
}

/**
 * Validate form data with a schema
 */
export async function validateFormData<T extends Record<string, any>>(
  data: T,
  validator: FormValidator<T>,
  context?: ValidationContext
): Promise<ValidationResult> {
  try {
    return await validator(data, context);
  } catch (error) {
    console.error('Form validation error:', error);
    return createValidationResult(false, {
      _form: 'Validation failed due to an internal error',
    });
  }
}

/**
 * Build validation errors from multiple sources
 */
export function buildValidationErrors(
  ...errorSources: (Record<string, string> | undefined)[]
): Record<string, string> {
  return errorSources.reduce<Record<string, string>>((acc, errors) => {
    if (errors) {
      Object.assign(acc, errors);
    }
    return acc;
  }, {});
}

/**
 * Cross-field validators for common patterns
 */
export const crossFieldValidators = {
  /**
   * Validate that two fields match (e.g., password confirmation)
   */
  fieldsMatch: (field1: string, field2: string, message?: string) => {
    return (data: Record<string, any>) => {
      if (data[field1] !== data[field2]) {
        return createValidationResult(false, {
          [field2]: message || `${field2} must match ${field1}`,
        });
      }
      return createValidationResult(true);
    };
  },

  /**
   * Validate that at least one of the fields is provided
   */
  atLeastOneRequired: (fields: string[], message?: string) => {
    return (data: Record<string, any>) => {
      const hasValue = fields.some(
        field => data[field] !== null && data[field] !== undefined && data[field] !== ''
      );

      if (!hasValue) {
        const errors: Record<string, string> = {};
        fields.forEach(field => {
          errors[field] = message || `At least one of these fields is required`;
        });
        return createValidationResult(false, errors);
      }

      return createValidationResult(true);
    };
  },

  /**
   * Validate that a date range is valid (start <= end)
   */
  validDateRange: (startField: string, endField: string, message?: string) => {
    return (data: Record<string, any>) => {
      const startDate = data[startField];
      const endDate = data[endField];

      if (startDate && endDate) {
        const start = new Date(startDate);
        const end = new Date(endDate);

        if (start > end) {
          return createValidationResult(false, {
            [endField]: message || 'End date must be after start date',
          });
        }
      }

      return createValidationResult(true);
    };
  },

  /**
   * Validate conditional fields (if field A has value, field B is required)
   */
  conditionalRequired: (triggerField: string, requiredField: string, message?: string) => {
    return (data: Record<string, any>) => {
      const triggerValue = data[triggerField];
      const requiredValue = data[requiredField];

      if (triggerValue && (!requiredValue || requiredValue === '')) {
        return createValidationResult(false, {
          [requiredField]:
            message || `${requiredField} is required when ${triggerField} is provided`,
        });
      }

      return createValidationResult(true);
    };
  },
};

/**
