/**
 * Input Sanitizers
 * Security-focused input sanitization functions
 */

import type { SanitizationOptions } from './types';

/**
 * Sanitize general input with configurable options
 */
export function sanitizeInput(input: string, options: SanitizationOptions = {}): string {
  if (!input || typeof input !== 'string') {
    return '';
  }

  let sanitized = input;

  // Normalize unicode characters
  if (options.normalizeUnicode !== false) {
    sanitized = sanitized.normalize('NFC');
  }

  // Trim whitespace
  if (options.trimWhitespace !== false) {
    sanitized = sanitized.trim();
  }

  // Strip HTML tags
  if (options.stripHtml) {
    sanitized = sanitized.replace(/<[^>]*>/g, '');
  }

  // Escape HTML entities
  if (options.escapeHtml) {
    sanitized = sanitized
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/"/g, '&quot;')
      .replace(/'/g, '&#x27;')
      .replace(/\//g, '&#x2F;');
  }

  // Filter allowed characters
  if (options.allowedChars) {
    sanitized = sanitized.replace(new RegExp(`[^${options.allowedChars.source}]`, 'g'), '');
  }

  // Enforce max length
  if (options.maxLength && sanitized.length > options.maxLength) {
    sanitized = sanitized.substring(0, options.maxLength);
  }

  return sanitized;
}

/**
 * Sanitize HTML content (allows safe HTML tags)
 */
export function sanitizeHtml(html: string): string {
  if (!html || typeof html !== 'string') {
    return '';
  }

  // Remove dangerous tags and attributes
  const dangerous = [
    /<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi,
    /<iframe\b[^<]*(?:(?!<\/iframe>)<[^<]*)*<\/iframe>/gi,
    /<object\b[^<]*(?:(?!<\/object>)<[^<]*)*<\/object>/gi,
    /<embed\b[^<]*(?:(?!<\/embed>)<[^<]*)*<\/embed>/gi,
    /<form\b[^<]*(?:(?!<\/form>)<[^<]*)*<\/form>/gi,
    /on\w+\s*=/gi, // Event handlers
    /javascript:/gi,
    /data:/gi,
    /vbscript:/gi,
  ];

  let sanitized = html;
  dangerous.forEach(pattern => {
    sanitized = sanitized.replace(pattern, '');
  });

  return sanitized.trim();
}

/**
 * Sanitize PhilSys number (remove non-digits, format)
 */
export function sanitizePhilSysNumber(philsys: string): string {
  if (!philsys || typeof philsys !== 'string') {
    return '';
  }

  // Remove all non-digit characters
  const digitsOnly = philsys.replace(/\D/g, '');

  // CORRECTED: PhilSys is 12 digits, not 16 - aligned with database schema VARCHAR(20)
  // Format as XXXX-XXXX-XXXX if 12 digits
  if (digitsOnly.length === 12) {
    return `${digitsOnly.slice(0, 4)}-${digitsOnly.slice(4, 8)}-${digitsOnly.slice(8, 12)}`;
  }

  return digitsOnly;
}

/**
 * Sanitize name (letters, spaces, common name characters)
 */
export function sanitizeName(name: string): string {
  if (!name || typeof name !== 'string') {
    return '';
  }

  return (
    sanitizeInput(name, {
      allowedChars: /a-zA-Z\s\-'\.]/,
      maxLength: 100,
      trimWhitespace: true,
    })
      // Remove multiple spaces
      .replace(/\s+/g, ' ')
      // Remove leading/trailing hyphens, apostrophes, periods
      .replace(/^[\-'\.]+|[\-'\.]+$/g, '')
      // Capitalize first letter of each word
      .replace(/\b\w/g, char => char.toUpperCase())
  );
}

/**
 * Sanitize phone number (digits, +, parentheses, hyphens, spaces)
 */
export function sanitizePhone(phone: string): string {
  if (!phone || typeof phone !== 'string') {
    return '';
  }

  return sanitizeInput(phone, {
    allowedChars: /0-9\+\-\(\)\s]/,
    maxLength: 20,
    trimWhitespace: true,
  });
}

/**
 * Sanitize email address
 */
export function sanitizeEmail(email: string): string {
  if (!email || typeof email !== 'string') {
    return '';
  }

  return sanitizeInput(email, {
    allowedChars: /a-zA-Z0-9@\.\-_\+]/,
    maxLength: 254, // RFC 5321 limit
    trimWhitespace: true,
  }).toLowerCase();
}

/**
 * Sanitize URL
 */
export function sanitizeUrl(url: string): string {
  if (!url || typeof url !== 'string') {
    return '';
  }

  // Basic URL sanitization
  const sanitized = sanitizeInput(url, {
    trimWhitespace: true,
    maxLength: 2048,
  });

  // Ensure safe protocols
  const safeProtocols = ['http:', 'https:', 'mailto:', 'tel:'];
  try {
    const urlObj = new URL(sanitized);
    if (!safeProtocols.includes(urlObj.protocol)) {
      return '';
    }
  } catch {
    // Not a valid URL
    return '';
  }

  return sanitized;
}

/**
 * Sanitize text content (remove control characters, normalize whitespace)
 */
export function sanitizeText(text: string, maxLength?: number): string {
  if (!text || typeof text !== 'string') {
    return '';
  }

  return (
    sanitizeInput(text, {
      trimWhitespace: true,
      stripHtml: true,
      maxLength,
      normalizeUnicode: true,
    })
      // Remove control characters except newlines and tabs
      .replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F\u007F]/g, '')
      // Normalize whitespace
      .replace(/\s+/g, ' ')
  );
}

/**
 * Sanitize search query (prevent injection attacks)
 */
export function sanitizeSearchQuery(query: string): string {
  if (!query || typeof query !== 'string') {
    return '';
  }

  return (
    sanitizeInput(query, {
      trimWhitespace: true,
      maxLength: 100,
      stripHtml: true,
    })
      // Remove SQL injection patterns
      .replace(/['";\\]/g, '')
      // Remove script injection patterns
      .replace(/<script|javascript:|data:/gi, '')
      // Remove excessive wildcards
      .replace(/[*%]{3,}/g, '**')
  );
}

/**
 * Sanitize file name (safe for filesystem)
 */
export function sanitizeFileName(fileName: string): string {
  if (!fileName || typeof fileName !== 'string') {
    return '';
  }

  return (
    sanitizeInput(fileName, {
      allowedChars: /a-zA-Z0-9\.\-_\s]/,
      maxLength: 255,
      trimWhitespace: true,
    })
      // Remove dangerous file extensions
      .replace(/\.(exe|bat|cmd|scr|pif|com|jar|sh|ps1|vbs)$/i, '.txt')
      // Remove directory traversal
      .replace(/\.\./g, '')
      // Remove leading dots (hidden files)
      .replace(/^\.+/, '')
      // Replace spaces with underscores
      .replace(/\s+/g, '_')
      // Remove multiple dots
      .replace(/\.{2,}/g, '.')
  );
}

/**
 * Sanitize database input (prevent SQL injection)
 */
export function sanitizeDatabaseInput(input: string): string {
  if (!input || typeof input !== 'string') {
    return '';
  }

  return (
    sanitizeInput(input, {
      trimWhitespace: true,
      escapeHtml: false, // Don't escape HTML for database
    })
      // Remove SQL injection patterns
      .replace(/['"\\;]/g, '')
      // Remove SQL keywords in dangerous contexts
      .replace(/\b(DROP|DELETE|INSERT|UPDATE|SELECT|UNION|EXEC|EXECUTE)\b/gi, '')
      // Remove comment patterns
      .replace(/--|\*\/|\*|\/\*/g, '')
  );
}

/**
 * Sanitize JSON input (prevent JSON injection)
 */
export function sanitizeJsonInput(input: string): string {
  if (!input || typeof input !== 'string') {
    return '';
  }

  return (
    sanitizeInput(input, {
      trimWhitespace: true,
      stripHtml: true,
    })
      // Remove dangerous JSON patterns
      .replace(/\\"|\\'|\\x|\\u/g, '')
      // Remove function calls
      .replace(/\(\s*\)/g, '')
  );
}

/**
 * Deep sanitize object (recursively sanitize all string values)
 */
export function sanitizeObject<T extends Record<string, any>>(
  obj: T,
  options: SanitizationOptions = {}
): T {
  if (!obj || typeof obj !== 'object') {
    return obj;
  }

  const sanitized = { ...obj };

  for (const [key, value] of Object.entries(sanitized)) {
    if (typeof value === 'string') {
      (sanitized as any)[key] = sanitizeInput(value, options);
    } else if (Array.isArray(value)) {
      (sanitized as any)[key] = value.map(item =>
        typeof item === 'string' ? sanitizeInput(item, options) : item
      );
    } else if (value && typeof value === 'object') {
      (sanitized as any)[key] = sanitizeObject(value, options);
    }
  }

  return sanitized;
}

/**
 * Validation Schemas
 * Pre-built validation schemas for common data types
 */

import type { LoginFormData, PasswordUpdateRequest } from '../../types/auth';
import type { HouseholdData } from '../../types/households';
import type { ResidentFormState } from '../../types/residents';
import type {
  SimpleValidationResult as ValidationResult,
  ValidationContext,
} from '../../types/validation';

import {
  validateRequired,
  validateEmail,
  validatePhilippineMobile,
  validatePhilSysNumber,
  validateName,
  validateAge,
  validateLength,
  validateRange,
  validateDate,
  composeValidators,
} from './fieldValidators';

/**
 * Helper function to create validation results
 */
function createValidationResult(
  isValid: boolean,
  errors: Record<string, string> = {}
): ValidationResult {
  return {
    isValid,
    errors,
  };
}

/**
 * Consolidated validateField helper function
 * Eliminates duplicate code across all validation schemas
 */
async function validateField(
  errors: Record<string, string>,
  fieldName: string,
  value: any,
  validators: any[],
  data?: any
): Promise<void> {
  for (const validator of validators) {
    const result = await validator(value, fieldName, data);
    if (result !== null) {
      errors[fieldName] = result;
      break;
    }
  }
}

/**
 * Resident data validation schema
 */
export async function residentSchema(
  data: Partial<ResidentFormState>,
  context?: ValidationContext
): Promise<ValidationResult> {
  const errors: Record<string, string> = {};

  // Validate required fields
  await validateField(errors, 'first_name', data.first_name, [validateRequired, validateName], data);
  await validateField(errors, 'last_name', data.last_name, [validateRequired, validateName], data);
  await validateField(errors, 'birthdate', data.birthdate, [validateRequired, validateDate], data);
  await validateField(errors, 'sex', data.sex, [validateRequired], data);

  // Validate optional fields
  if (data.middle_name !== undefined) {
    await validateField(errors, 'middle_name', data.middle_name, [validateName], data);
  }
  if (data.extension_name !== undefined) {
    await validateField(errors, 'extension_name', data.extension_name, [validateName], data);
  }
  if (data.email !== undefined) {
    await validateField(errors, 'email', data.email, [validateEmail], data);
  }
  if (data.mobile_number !== undefined) {
    await validateField(errors, 'mobile_number', data.mobile_number, [validatePhilippineMobile], data);
  }
  if (data.telephone_number !== undefined) {
    await validateField(errors, 'telephone_number', data.telephone_number, [validateLength(0, 20)], data);
  }
  if (data.civil_status !== undefined) {
    await validateField(errors, 'civil_status', data.civil_status, [validateLength(0, 50)], data);
  }
  if (data.citizenship !== undefined) {
    await validateField(errors, 'citizenship', data.citizenship, [validateLength(0, 50)], data);
  }
  if (data.blood_type !== undefined) {
    await validateField(errors, 'blood_type', data.blood_type, [validateLength(0, 10)], data);
  }
  if (data.ethnicity !== undefined) {
    await validateField(errors, 'ethnicity', data.ethnicity, [validateLength(0, 50)], data);
  }
  if (data.religion !== undefined) {
    await validateField(errors, 'religion', data.religion, [validateLength(0, 50)], data);
  }
  if (data.religion_others_specify !== undefined) {
    await validateField(errors, 'religion_others_specify', data.religion_others_specify, [
      validateLength(0, 100),
    ], data);
  }
  if (data.height !== undefined) {
    await validateField(errors, 'height', data.height, [validateRange(50, 300)], data);
  }
  if (data.weight !== undefined) {
    await validateField(errors, 'weight', data.weight, [validateRange(10, 500)], data);
  }
  if (data.complexion !== undefined) {
    await validateField(errors, 'complexion', data.complexion, [validateLength(0, 50)], data);
  }
  if (data.birth_place_code !== undefined) {
    await validateField(errors, 'birth_place_code', data.birth_place_code, [validateLength(0, 20)], data);
  }
  if (data.philsys_card_number !== undefined) {
    await validateField(errors, 'philsys_card_number', data.philsys_card_number, [validatePhilSysNumber], data);
  }
  if (data.mother_maiden_first !== undefined) {
    await validateField(errors, 'mother_maiden_first', data.mother_maiden_first, [validateName], data);
  }
  if (data.mother_maiden_middle !== undefined) {
    await validateField(errors, 'mother_maiden_middle', data.mother_maiden_middle, [validateName], data);
  }
  if (data.mother_maiden_last !== undefined) {
    await validateField(errors, 'mother_maiden_last', data.mother_maiden_last, [validateName], data);
  }
  if (data.education_attainment !== undefined) {
    await validateField(errors, 'education_attainment', data.education_attainment, [validateLength(0, 50)], data);
  }
  if (data.is_graduate !== undefined) {
    if (typeof data.is_graduate !== 'boolean') {
      errors['is_graduate'] = 'Must be true or false';
    }
  }
  if (data.employment_status !== undefined) {
    await validateField(errors, 'employment_status', data.employment_status, [validateLength(0, 50)], data);
  }
  if (data.occupation_code !== undefined) {
    await validateField(errors, 'occupation_code', data.occupation_code, [validateLength(0, 20)], data);
  }
  if (data.is_voter !== undefined) {
    if (typeof data.is_voter !== 'boolean') {
      errors['is_voter'] = 'Must be true or false';
    }
  }
  if (data.is_resident_voter !== undefined) {
    if (typeof data.is_resident_voter !== 'boolean') {
      errors['is_resident_voter'] = 'Must be true or false';
    }
  }
  if (data.last_voted_date !== undefined) {
    await validateField(errors, 'last_voted_date', data.last_voted_date, [validateDate], data);
  }
  if (data.household_code !== undefined) {
    await validateField(errors, 'household_code', data.household_code, [validateLength(0, 50)], data);
  }

  // Cross-field validations
  if (data.philsys_card_number && !data.birth_place_code) {
    errors['birth_place_code'] = 'Birth place code is required when PhilSys number is provided';
  }
  if (data.is_voter && !data.last_voted_date) {
    errors['last_voted_date'] = 'Last voted date is required when voter status is true';
  }

  return createValidationResult(Object.keys(errors).length === 0, errors);
}

