/**
 * Optimized address fetcher - tries efficient view first, falls back gracefully
 */
export const fetchAddressInfo = async (barangayCode: string): Promise<AddressInfo | undefined> => {
  try {
    logger.debug('Loading address information', { barangayCode });

    // Try address hierarchy view first (most efficient)
    const { data: addressViewData, error: viewError } = await supabase
      .from('psgc_address_hierarchy')
      .select(
        'barangay_name, barangay_code, city_municipality_name, city_municipality_code, province_name, region_name, region_code, full_address'
      )
      .eq('barangay_code', barangayCode)
      .single();

    if (addressViewData && !viewError) {
      return {
        barangay_name: addressViewData.barangay_name,
        barangay_code: addressViewData.barangay_code || barangayCode,
        city_municipality_name: addressViewData.city_municipality_name,
        city_municipality_code: addressViewData.city_municipality_code || '',
        province_name: addressViewData.province_name,
        region_name: addressViewData.region_name,
        region_code: addressViewData.region_code || '',
        full_address: addressViewData.full_address,
      };
    }

    // Fallback to optimized join query (much faster than sequential queries)
    return await fetchAddressFromJoinedTables(barangayCode);
  } catch (addressError) {
    logger.warn('Address data lookup failed', {
      error: addressError instanceof Error ? addressError.message : 'Unknown error',
    });
    return undefined;
  }
};

/**
 * Optimized address fallback - single join query instead of sequential queries
 */
const fetchAddressFromJoinedTables = async (
  barangayCode: string
): Promise<AddressInfo | undefined> => {
  try {
    logger.debug('Using optimized join query for address data');

    // Single optimized query with joins instead of sequential queries
    const { data, error } = await supabase
      .from('psgc_barangays')
      .select(
        `
        name,
        code,
        psgc_cities_municipalities(
          name,
          code,
          is_independent,
          psgc_provinces(
            name,
            code,
            psgc_regions(
              name,
              code
            )
          )
        )
      `
      )
      .eq('code', barangayCode)
      .single();

    if (!data || error) {
      logger.warn('Optimized address query failed', { error: error?.message });
      return undefined;
    }

    const typedData = data as any;
    const city = typedData.psgc_cities_municipalities;
    const province = city?.psgc_provinces;
    const region = province?.psgc_regions;

    const addressParts = [
      data.name,
      city?.name,
      !(city as any)?.is_independent ? province?.name : null,
      region?.name,
    ].filter(Boolean);

    return {
      barangay_name: data.name,
      barangay_code: data.code,
      city_municipality_name: city?.name,
      city_municipality_code: city?.code || '',
      province_name: !(city as any)?.is_independent ? province?.name : undefined,
      region_name: region?.name,
      region_code: region?.code || '',
      full_address: addressParts.join(', '),
    };
  } catch (error) {
    logger.warn('Fallback address query failed', {
      error: error instanceof Error ? error.message : 'Unknown error',
    });
    return undefined;
  }
};

/**
 * Optimized PSOC fetcher with caching
 */
const psocCache = new Map<string, PsocInfo>();

export const fetchPsocInfo = async (occupationCode: string): Promise<PsocInfo | null> => {
  // Check cache first
  if (psocCache.has(occupationCode)) {
    return psocCache.get(occupationCode)!;
  }

  try {
    const { data: psocData, error } = await supabase
      .from('psoc_occupation_search')
      .select('occupation_code, occupation_title, full_hierarchy')
      .eq('occupation_code', occupationCode)
      .single();

    if (psocData && !error) {
      const psocInfo = {
        code: psocData.occupation_code,
        title: psocData.occupation_title,
        hierarchy: psocData.full_hierarchy || psocData.occupation_title,
        level: 'occupation',
      };

      // Cache the result
      psocCache.set(occupationCode, psocInfo);
      return psocInfo;
    }

    return null;
  } catch (psocError) {
    logger.warn('PSOC data lookup failed', {
      error: psocError instanceof Error ? psocError.message : 'Unknown error',
    });
    return null;
  }
};

/**
 * Batch fetch multiple resident details efficiently
 */
export const fetchResidentDetailsOptimized = async (
  barangayCode?: string,
  occupationCode?: string
): Promise<{
  addressInfo?: AddressInfo;
  psocInfo?: PsocInfo;
}> => {
  // Use Promise.allSettled for parallel execution with graceful error handling
  const [addressResult, psocResult] = await Promise.allSettled([
    barangayCode ? fetchAddressInfo(barangayCode) : Promise.resolve(undefined),
    occupationCode ? fetchPsocInfo(occupationCode) : Promise.resolve(null),
  ]);

  return {
    addressInfo: addressResult.status === 'fulfilled' ? addressResult.value : undefined,
    psocInfo: psocResult.status === 'fulfilled' ? psocResult.value || undefined : undefined,
  };
};

/**
 * Clear PSOC cache when needed
 */
export const clearPsocCache = (): void => {
  psocCache.clear();
};

/**
 * Resident Repository
 * Domain-specific repository for resident data operations
 */

import type { SupabaseClient } from '@supabase/supabase-js';

import { validateResidentData } from '@/lib/validation/schemas';
import type { ValidationContext } from '@/lib/validation/types';
import type { ResidentData, ResidentSearchOptions } from '@/types/repositories';
import type { RepositoryResult } from '@/types/services';
import type { ValidationError } from '@/types/validation';

import { BaseRepository } from './base-repository';

// Export types for re-export in services/index.ts
export type { ResidentSearchOptions };

export class ResidentRepository extends BaseRepository<ResidentData> {
  constructor(context?: ValidationContext) {
    super('residents', context);
  }

  /**
   * Create a new resident with validation
   */
  async createResident(
    data: Omit<ResidentData, 'id' | 'created_at' | 'updated_at'>
  ): Promise<RepositoryResult<ResidentData>> {
    try {
      // Validate resident data before creation
      const validationResult = await validateResidentData(data as any, this.context);
      if (!validationResult.isValid) {
        return {
          success: false,
          error: {
            code: 'VALIDATION_ERROR',
            message: 'Resident data validation failed',
            details: Array.isArray(validationResult.errors)
              ? validationResult.errors.reduce(
                  (acc: Record<string, string>, error: ValidationError, index: number) => ({
                    ...acc,
                    [`error_${index}`]: error.message,
                  }),
                  {} as Record<string, string>
                )
              : validationResult.errors,
          },
        };
      }

      return await this.create(data);
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'createResident'),
      };
    }
  }

  /**
   * Update resident with validation
   */
  async updateResident(
    id: string,
    data: Partial<Omit<ResidentData, 'id' | 'created_at'>>
  ): Promise<RepositoryResult<ResidentData>> {
    try {
      // Get existing resident for partial validation
      const existingResult = await this.findById(id);
      if (!existingResult.success || !existingResult.data) {
        return existingResult;
      }

      // Merge with existing data for validation
      const mergedData = { ...existingResult.data, ...data };
      const validationResult = await validateResidentData(mergedData as any, this.context);

      if (!validationResult.isValid) {
        return {
          success: false,
          error: {
            code: 'VALIDATION_ERROR',
            message: 'Resident data validation failed',
            details: Array.isArray(validationResult.errors)
              ? validationResult.errors.reduce(
                  (acc: Record<string, string>, error: ValidationError, index: number) => ({
                    ...acc,
                    [`error_${index}`]: error.message,
                  }),
                  {} as Record<string, string>
                )
              : validationResult.errors,
          },
        };
      }

      return await this.update(id, data);
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'updateResident'),
      };
    }
  }

  /**
   * Search residents with advanced filtering
   */
  async searchResidents(
    options: ResidentSearchOptions = {}
  ): Promise<RepositoryResult<ResidentData[]>> {
    try {
      const queryBuilder = (supabase: SupabaseClient) => {
        let query = supabase.from(this.tableName).select('*', { count: 'exact' });

        // Name search (across first, middle, last names)
        if (options.name) {
          const searchTerm = `%${options.name}%`;
          query = query.or(
            `first_name.ilike.${searchTerm},middle_name.ilike.${searchTerm},last_name.ilike.${searchTerm}`
          );
        }

        // Age search (calculated from birthdate)
        if (options.age) {
          const currentYear = new Date().getFullYear();
          const birthYear = currentYear - options.age;
          query = query
            .gte('birthdate', `${birthYear}-01-01`)
            .lt('birthdate', `${birthYear + 1}-01-01`);
        }

        // Direct field filters
        if (options.sex) query = query.eq('sex', options.sex);
        if (options.civil_status) query = query.eq('civil_status', options.civil_status);
        if (options.household_code) query = query.eq('household_code', options.household_code);
        if (options.is_voter !== undefined) query = query.eq('is_voter', options.is_voter);

        // Apply other filters
        if (options.filters) {
          for (const [key, value] of Object.entries(options.filters)) {
            if (value !== undefined && value !== null) {
              query = query.eq(key, value);
            }
          }
        }

        // Apply ordering
        if (options.orderBy) {
          query = query.order(options.orderBy, {
            ascending: options.orderDirection !== 'desc',
          });
        } else {
          // Default order by last name, first name
          query = query.order('last_name').order('first_name');
        }

        // Apply pagination
        if (options.limit) {
          query = query.limit(options.limit);
        }

        if (options.offset) {
          query = query.range(options.offset, options.offset + (options.limit || 10) - 1);
        }

        return query;
      };

      return await this.executeQuery(queryBuilder, 'SEARCH_RESIDENTS');
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'searchResidents'),
      };
    }
  }

  /**
   * Find residents by household
   */
  async findByHousehold(household_code: string): Promise<RepositoryResult<ResidentData[]>> {
    try {
      return await this.findAll({
        filters: { household_code },
        orderBy: 'last_name',
      });
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'findByHousehold'),
      };
    }
  }

  /**
   * Find residents by age range
   */
  async findByAgeRange(minAge: number, maxAge: number): Promise<RepositoryResult<ResidentData[]>> {
    try {
      const currentDate = new Date();
      const maxBirthDate = new Date(
        currentDate.getFullYear() - minAge,
        currentDate.getMonth(),
        currentDate.getDate()
      );
      const minBirthDate = new Date(
        currentDate.getFullYear() - maxAge - 1,
        currentDate.getMonth(),
        currentDate.getDate()
      );

      const queryBuilder = (supabase: SupabaseClient) => {
        return supabase
          .from(this.tableName)
          .select('*', { count: 'exact' })
          .gte('birthdate', minBirthDate.toISOString().split('T')[0])
          .lte('birthdate', maxBirthDate.toISOString().split('T')[0])
          .order('birthdate');
      };

      return await this.executeQuery(queryBuilder, 'FIND_BY_AGE_RANGE');
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'findByAgeRange'),
      };
    }
  }

  /**
   * Get voter statistics
   */
  async getVoterStatistics(): Promise<
    RepositoryResult<{
      totalResidents: number;
      totalVoters: number;
      residentVoters: number;
      voterTurnoutRate: number;
    }>
  > {
    try {
      const queryBuilder = (supabase: SupabaseClient) => {
        return supabase
          .from(this.tableName)
          .select(
            `
            count(*) as totalResidents,
            is_voter,
            is_resident_voter
          `
          )
          .gte('birthdate', '1900-01-01'); // Basic filter to ensure valid data
      };

      const result = await this.executeQuery(queryBuilder, 'GET_VOTER_STATISTICS');

      if (!result.success || !result.data) {
        return result;
      }

      // Calculate statistics from the data
      const stats = {
        totalResidents: 0,
        totalVoters: 0,
        residentVoters: 0,
        voterTurnoutRate: 0,
      };

      // This would need to be adjusted based on actual Supabase aggregation results
      // For now, we'll return the structure
      return {
        success: true,
        data: stats,
      };
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'getVoterStatistics'),
      };
    }
  }

  /**
   * Check for duplicate residents (same name and birthdate)
   */
  async findPotentialDuplicates(
    first_name: string,
    last_name: string,
    birthdate: string
  ): Promise<RepositoryResult<ResidentData[]>> {
    try {
      const queryBuilder = (supabase: SupabaseClient) => {
        return supabase
          .from(this.tableName)
          .select('*')
          .eq('first_name', first_name)
          .eq('last_name', last_name)
          .eq('birthdate', birthdate);
      };

      return await this.executeQuery(queryBuilder, 'FIND_POTENTIAL_DUPLICATES');
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'findPotentialDuplicates'),
      };
    }
  }

  /**
   * Soft delete resident (mark as inactive)
   */
  async softDeleteResident(id: string): Promise<RepositoryResult<ResidentData>> {
    try {
      return await this.update(id, {
        is_active: false,
        updated_at: new Date().toISOString(),
      });
    } catch (error) {
      return {
        success: false,
        error: this.handleError(error, 'softDeleteResident'),
      };
    }
  }
}

