// Interactive Example
export const Interactive: Story = {
  render: () => {
    const [value, setValue] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const newValue = e.target.value;
      setValue(newValue);

      // Simple validation
      if (newValue.length > 0 && newValue.length < 3) {
        setError('Must be at least 3 characters');
      } else {
        setError('');
      }
    };

    const handleClear = () => {
      setValue('');
      setError('');
    };

    return (
      <div className="space-y-4">
        <div>
          <label htmlFor="interactive-input" className="mb-1 block text-sm font-medium">
            Interactive Input with Clear
          </label>
          <Input
            id="interactive-input"
            value={value}
            onChange={handleChange}
            onClear={handleClear}
            placeholder="Type something..."
            error={error}
            clearable={true}
          />
        </div>
        <div className="text-sm text-gray-600">
          Value: "{value}" (Length: {value.length})
        </div>
      </div>
    );
  },
  parameters: {
    docs: {
      description: {
        story:
          'Interactive example showing real-time validation, state management, and clearable functionality.',
      },
    },
  },
};

