export const SearchExample: Story = {
  render: () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState<string[]>([]);
    const [isSearching, setIsSearching] = useState(false);

    // Mock data
    const mockData = [
      'React',
      'Vue.js',
      'Angular',
      'Svelte',
      'Next.js',
      'Nuxt.js',
      'Gatsby',
      'TypeScript',
      'JavaScript',
      'Node.js',
      'Express',
      'Fastify',
      'MongoDB',
      'PostgreSQL',
      'Redis',
    ];

    const performSearch = async (query: string) => {
      if (!query.trim()) {
        setResults([]);
        return;
      }

      setIsSearching(true);

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 300));

      const filtered = mockData.filter(item => item.toLowerCase().includes(query.toLowerCase()));

      setResults(filtered);
      setIsSearching(false);
    };

    const handleSearch = (query: string) => {
      performSearch(query);
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      setSearchTerm(e.target.value);
      // Debounced search as user types
      performSearch(e.target.value);
    };

    const handleClear = () => {
      setSearchTerm('');
      setResults([]);
      setIsSearching(false);
    };

    return (
      <div className="w-80">
        <SearchBar
          value={searchTerm}
          onChange={handleInputChange}
          onSearch={handleSearch}
          onClear={handleClear}
          placeholder="Search technologies..."
        />

        {isSearching && (
          <div className="mt-4 rounded-sm bg-gray-50 p-3 text-sm text-gray-600 dark:text-gray-400">
            Searching...
          </div>
        )}

        {results.length > 0 && !isSearching && (
          <div className="mt-4 rounded-md border">
            <div className="border-b bg-gray-50 p-2 text-sm font-medium text-gray-700 dark:text-gray-300">
              Found {results.length} result{results.length !== 1 ? 's' : ''}
            </div>
            <ul className="divide-y">
              {results.map((result, index) => (
                <li key={index} className="cursor-pointer p-3 hover:bg-gray-50">
                  {result}
                </li>
              ))}
            </ul>
          </div>
        )}

        {searchTerm && results.length === 0 && !isSearching && (
          <div className="mt-4 rounded-sm bg-gray-50 p-3 text-sm text-gray-500 dark:text-gray-500">
            No results found for "{searchTerm}"
          </div>
        )}
      </div>
    );
  },
};

