      } else {
        setSelectedItems([]);
      }
    };

    const handleSelectItem = (id: number, checked: boolean) => {
      if (checked) {
        setSelectedItems([...selectedItems, id]);
      } else {
        setSelectedItems(selectedItems.filter(itemId => itemId !== id));
      }
    };

    return (
      <Table>
