export const WithControls: Story = {
  parameters: {
    docs: {
      description: {
        story: 'Complete table with controls including search, selection, and actions.',
      },
    },
  },
  render: () => {
    const [searchTerm, setSearchTerm] = React.useState('');
    const [selectedItems, setSelectedItems] = React.useState<number[]>([]);

    const filteredContacts = mockContacts.filter(
      contact =>
        contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        contact.email.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const handleSelectAll = (checked: boolean) => {
      if (checked) {
        setSelectedItems(filteredContacts.map(contact => contact.id));
      } else {
        setSelectedItems([]);
      }
    };

    const handleSelectItem = (id: number, checked: boolean) => {
      if (checked) {
        setSelectedItems([...selectedItems, id]);
      } else {
        setSelectedItems(selectedItems.filter(itemId => itemId !== id));
      }
    };

    return (
      <div className="space-y-4">
        <TableControls
          selectAll={{
            checked:
              selectedItems.length === filteredContacts.length && filteredContacts.length > 0,
            onChange: handleSelectAll,
            label: 'Select all contacts',
          }}
          search={{
            value: searchTerm,
            onChange: setSearchTerm,
            placeholder: 'Search contacts...',
          }}
          actions={
            selectedItems.length > 0 && (
              <div className="flex gap-2">
                <button
                  onClick={() => action('bulk-delete')(selectedItems)}
                  className="rounded bg-red-600 px-3 py-2 text-sm text-white hover:bg-red-700 dark:text-black"
                >
                  Delete Selected
                </button>
                <button
                  onClick={() => action('bulk-export')(selectedItems)}
                  className="rounded bg-blue-600 px-3 py-2 text-sm text-white hover:bg-blue-700 dark:text-black"
                >
                  Export Selected
                </button>
              </div>
            )
          }
        />

        <Table>
          <TableBody>
            {/* Header Row */}
            <TableRow className="border-b bg-gray-50">
              <TableCell
                type="checkbox"
                checkbox={{
                  checked:
                    selectedItems.length === filteredContacts.length && filteredContacts.length > 0,
                  onChange: handleSelectAll,
                }}
              />
              <TableCell>
                <strong>Name</strong>
              </TableCell>
              <TableCell>
                <strong>Email</strong>
              </TableCell>
              <TableCell>
                <strong>Role</strong>
              </TableCell>
              <TableCell>
                <strong>Actions</strong>
              </TableCell>
            </TableRow>

            {/* Data Rows */}
            {filteredContacts.length > 0 ? (
              filteredContacts.map(contact => (
                <TableRow key={contact.id} className="border-b hover:bg-gray-50">
                  <TableCell
                    type="checkbox"
                    checkbox={{
                      checked: selectedItems.includes(contact.id),
                      onChange: checked => handleSelectItem(contact.id, checked),
                    }}
                  />
                  <TableCell>{contact.name}</TableCell>
                  <TableCell>{contact.email}</TableCell>
                  <TableCell>{contact.role}</TableCell>
                  <TableCell type="action" />
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell
                  className="py-8 text-center text-gray-500 dark:text-gray-500"
                  style={{ gridColumn: '1 / -1' }}
                >
                  No contacts found matching "{searchTerm}"
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>

        {selectedItems.length > 0 && (
          <div className="rounded border border-blue-200 bg-blue-50 p-3">
            <p className="text-sm text-gray-800 dark:text-gray-200">
              {selectedItems.length} of {filteredContacts.length} contacts selected
            </p>
          </div>
        )}
      </div>
    );
  },
};

