// Mock the authentication context for Storybook
const MockAuthProvider = ({
  children,
  user = null,
  loading = false,
}: {
  children: React.ReactNode;
  user?: { user_metadata?: { first_name?: string; last_name?: string; email?: string } } | null;
  loading?: boolean;
}) => {
  const mockAuthValue = {
    user,
    userProfile: user
      ? {
          id: 'profile-id',
          first_name: user.user_metadata?.first_name || 'John',
          last_name: user.user_metadata?.last_name || 'Doe',
          email: user.email,
          barangay_code: '137404001',
          role_id: 'admin-role',
        }
      : null,
    role: user
      ? {
          id: 'admin-role',
          name: 'Barangay Administrator',
          permissions: [],
        }
      : null,
    signOut: async () => {
      console.log('Mock sign out');
    },
    loading,
    initialized: true,
  };

  // Mock the useAuth hook
  React.useEffect(() => {
    const originalUseAuth = (window as any).useAuth;
    (window as any).useAuth = () => mockAuthValue;

    return () => {
      (window as any).useAuth = originalUseAuth;
    };
  }, []);

  return <>{children}</>;
};

