/**
 * API Types
 * Consolidated API request/response interfaces and error types
 */

// =============================================================================
// STANDARD API RESPONSE TYPES
// =============================================================================

/**
 * Standard API success response
 */
export interface ApiSuccessResponse<T = any> {
  success: true;
  data: T;
  message?: string;
  meta?: {
    total?: number;
    page?: number;
    limit?: number;
    hasMore?: boolean;
  };
}

/**
 * Standard API error response
 */
export interface ApiErrorResponse {
  success: false;
  error: {
    message: string;
    code: string;
    details?: Record<string, any>;
    timestamp: string;
  };
}

/**
 * Generic API response
 */
export type ApiResponse<T = any> = ApiSuccessResponse<T> | ApiErrorResponse;

// =============================================================================
// SPECIFIC ERROR RESPONSE TYPES
// =============================================================================

/**
 * Field validation error
 */
export interface FieldValidationError {
  field: string;
  message: string;
  code?: string;
  value?: any;
}

/**
 * Validation error response
 */
export interface ValidationErrorResponse extends ApiErrorResponse {
  error: {
    message: string;
    code: 'VALIDATION_ERROR';
    details: {
      fields: FieldValidationError[];
    };
    timestamp: string;
  };
}

/**
 * Rate limit error response
 */
export interface RateLimitErrorResponse extends ApiErrorResponse {
  error: {
    message: string;
    code: 'RATE_LIMIT_EXCEEDED';
    details: {
      limit: number;
      windowMs: number;
      retryAfter: number;
    };
    timestamp: string;
  };
}

/**
 * Authentication error response
 */
export interface AuthErrorResponse extends ApiErrorResponse {
  error: {
    message: string;
    code: 'UNAUTHORIZED' | 'FORBIDDEN' | 'SESSION_EXPIRED';
    details?: {
      requiredPermissions?: string[];
      currentRole?: string;
    };
    timestamp: string;
  };
}

// =============================================================================
// API REQUEST TYPES
// =============================================================================

/**
 * Paginated request parameters
 */
export interface PaginatedRequest {
  page?: number;
  limit?: number;
  sort?: string;
  order?: 'asc' | 'desc';
}

/**
 * Search request parameters
 */
export interface SearchRequest extends PaginatedRequest {
  query?: string;
  filters?: Record<string, any>;
}

/**
 * Bulk operation request
 */
export interface BulkOperationRequest<T = any> {
  items: T[];
  options?: {
